import React, { useRef, useEffect, useState } from 'react';

/**
 * SlidingBackground Component
 * 
 * A simplified, industry-standard parallax background component that can be easily
 * integrated into existing layouts.
 * 
 * @param {Object} props - Component props
 * @param {string} props.image - URL of the background image
 * @param {string} [props.position='center'] - Position of the background ('top', 'center', 'bottom')
 * @param {number} [props.parallaxIntensity=0.2] - Intensity of the parallax effect (0-1)
 * @param {boolean} [props.enableParallax=true] - Enable/disable parallax effect
 * @param {number} [props.fadeSize=0.3] - Size of the fade effect (0-1, as a fraction of the component)
 * @param {string} [props.backgroundColor='#000'] - Background color for the fade effect
 * @param {number} [props.opacity=1] - Opacity of the background image
 * @param {string} [props.backgroundSize='cover'] - Background size CSS property
 * @param {boolean} [props.fadeTop=false] - Whether to fade the top edge
 * @param {boolean} [props.fadeBottom=false] - Whether to fade the bottom edge
 * @param {React.ReactNode} [props.children] - Content to render on top of the background
 * @param {Object} [props.style] - Additional styles to apply to the container
 * @param {Object} [props.className] - Additional CSS classes to apply to the container
 */
const SlidingBackground = ({
  image,
  position = 'center',
  parallaxIntensity = 0.2,
  enableParallax = true,
  fadeSize = 0.3,
  backgroundColor = '#000',
  opacity = 1,
  backgroundSize = 'cover',
  fadeTop = false,
  fadeBottom = false,
  children,
  style = {},
  className = '',
  ...restProps
}) => {
  const [scrollPosition, setScrollPosition] = useState(0);
  const animationFrameRef = useRef(null);

  // Handle scroll events for parallax
  useEffect(() => {
    const handleScroll = () => {
      if (!enableParallax) return;

      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }

      animationFrameRef.current = requestAnimationFrame(() => {
        setScrollPosition(window.scrollY);
      });
    };

    if (enableParallax) {
      window.addEventListener('scroll', handleScroll, { passive: true });
    }

    return () => {
      window.removeEventListener('scroll', handleScroll);
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }
    };
  }, [enableParallax]);

  // Calculate parallax offset or set to 0 if disabled
  const parallaxOffset = enableParallax ? scrollPosition * parallaxIntensity : 0;

  // Determine background position based on position setting
  let bgPosition;
  switch (position) {
    case 'top':
      bgPosition = 'center top';
      break;
    case 'bottom':
      bgPosition = 'center bottom';
      break;
    default:
      bgPosition = 'center center';
  }

  // Combine user styles with component styles
  const containerStyles = {
    position: 'relative',
    overflow: 'hidden',
    ...style
  };

  return (
    <div
      className={className}
      style={containerStyles}
      {...restProps}
    >
      {/* Background image with parallax effect */}
      <div
        style={{
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          backgroundImage: `url(${image})`,
          backgroundSize,
          backgroundPosition: bgPosition,
          backgroundRepeat: 'no-repeat',
          transform: `translateY(${parallaxOffset}px)`,
          transition: enableParallax ? 'transform 0.1s cubic-bezier(0.25, 0.1, 0.25, 1)' : 'none',
          opacity,
          willChange: enableParallax ? 'transform' : 'auto'
        }}
        aria-hidden="true"
      />

      {/* Top fade gradient */}
      {fadeTop && (
        <div
          style={{
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            height: `${fadeSize * 100}%`,
            background: `linear-gradient(to bottom, ${backgroundColor} 0%, rgba(0,0,0,0) 100%)`,
            pointerEvents: 'none',
            zIndex: 1
          }}
          aria-hidden="true"
        />
      )}

      {/* Bottom fade gradient */}
      {fadeBottom && (
        <div
          style={{
            position: 'absolute',
            bottom: 0,
            left: 0,
            right: 0,
            height: `${fadeSize * 100}%`,
            background: `linear-gradient(to top, ${backgroundColor} 0%, rgba(0,0,0,0) 100%)`,
            pointerEvents: 'none',
            zIndex: 1
          }}
          aria-hidden="true"
        />
      )}

      {/* Content container */}
      {children && (
        <div
          style={{
            position: 'relative',
            zIndex: 2,
            height: '100%',
            width: '100%'
          }}
        >
          {children}
        </div>
      )}
    </div>
  );
};

export default SlidingBackground;
