      { x: 0.27, y: 0.25 }, { x: 0.33, y: 0.25 }, // Cross horizontal
      { x: 0.3, y: 0.35 }, // Bottom of circle
      { x: 0.25, y: 0.32 }, { x: 0.35, y: 0.32 }, // Middle circle points
      { x: 0.25, y: 0.28 }, { x: 0.35, y: 0.28 } // Upper circle points
    ],
    edges: [[0, 1], [0, 4], [4, 5], [5, 7], [7, 8], [8, 6], [6, 4], [2, 3]]
  },
  {
    name: 'Cassiopeia',
    vertices: [
      { x: 0.7, y: 0.15 }, { x: 0.75, y: 0.2 }, { x: 0.8, y: 0.15 },
      { x: 0.85, y: 0.2 }, { x: 0.9, y: 0.15 }
    ],
    edges: [[0, 1], [1, 2], [2, 3], [3, 4]]
  },
  {
    name: 'Lyra',
    vertices: [
      { x: 0.6, y: 0.65 }, { x: 0.65, y: 0.6 }, { x: 0.65, y: 0.7 },
      { x: 0.7, y: 0.65 }, { x: 0.64, y: 0.65 }
    ],
    edges: [[0, 1], [0, 2], [1, 3], [2, 3], [4, 0], [4, 1], [4, 2], [4, 3]]
  }
];

