    // Create three distinct parallax layers
    parallaxLayersRef.current = [
      {
        depth: 0.2, // Distant layer (moves slowest)
        particles: Array(30).fill().map(() => ({
          x: Math.random() * window.innerWidth,
          y: Math.random() * window.innerHeight,
          initialX: Math.random() * window.innerWidth,
          initialY: Math.random() * window.innerHeight,
          size: Math.random() * 1 + 0.3,
          baseSize: Math.random() * 1 + 0.3,
          speedX: (Math.random() - 0.5) * 0.05,
          speedY: (Math.random() - 0.5) * 0.05,
          opacity: Math.random() * 0.2 + 0.05,
          baseOpacity: Math.random() * 0.2 + 0.05,
          twinkleSpeed: Math.random() * 0.01 + 0.002,
          twinklePhase: Math.random() * Math.PI * 2,
          twinkleProbability: Math.random() * 0.0003 + 0.0001
        }))
      },
      {
        depth: 0.5, // Middle layer
        particles: Array(40).fill().map(() => ({
          x: Math.random() * window.innerWidth,
          y: Math.random() * window.innerHeight,
          initialX: Math.random() * window.innerWidth,
          initialY: Math.random() * window.innerHeight,
          size: Math.random() * 1.5 + 0.5,
          baseSize: Math.random() * 1.5 + 0.5,
          speedX: (Math.random() - 0.5) * 0.1,
          speedY: (Math.random() - 0.5) * 0.1,
          opacity: Math.random() * 0.25 + 0.1,
          baseOpacity: Math.random() * 0.25 + 0.1,
          twinkleSpeed: Math.random() * 0.015 + 0.004,
          twinklePhase: Math.random() * Math.PI * 2,
          twinkleProbability: Math.random() * 0.0004 + 0.0002
        }))
      },
      {
        depth: 0.8, // Close layer (moves fastest)
        particles: Array(20).fill().map(() => ({
          x: Math.random() * window.innerWidth,
          y: Math.random() * window.innerHeight,
          initialX: Math.random() * window.innerWidth,
          initialY: Math.random() * window.innerHeight,
          size: Math.random() * 1.8 + 0.8,
          baseSize: Math.random() * 1.8 + 0.8,
          speedX: (Math.random() - 0.5) * 0.15,
          speedY: (Math.random() - 0.5) * 0.15,
          opacity: Math.random() * 0.35 + 0.15,
          baseOpacity: Math.random() * 0.35 + 0.15,
          twinkleSpeed: Math.random() * 0.02 + 0.006,
          twinklePhase: Math.random() * Math.PI * 2,
          twinkleProbability: Math.random() * 0.0005 + 0.0003
        }))
      }
