      const vertices = constellation.vertices.map(vertex => ({
        x: vertex.x * window.innerWidth,
        y: vertex.y * window.innerHeight,
        size: 1.8, // Slightly larger than regular particles
        baseSize: 1.8,
        opacity: 0.25,
        baseOpacity: 0.25,
        isTwinkling: false,
        twinkleProgress: 0,
        twinkleSpeed: Math.random() * 0.03 + 0.01,
        lastTwinkle: 0,
        twinkleInterval: Math.random() * 5000 + 2000 // Random interval between twinkles
      }));

      return {
        name: constellation.name,
        vertices,
        edges: constellation.edges
      };
    });

    return () => {
      if (requestRef.current) {
        cancelAnimationFrame(requestRef.current);
      }
    };
  }, []);

  // Canvas animation for background particles and constellations
  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    // Calculate parallax offset based on cursor movement
    const calculateParallaxOffset = (depth) => {
      // Only calculate parallax if we have previous cursor position
      if (!lastCursorPosition.current.x || !cursorPosition.x) return { x: 0, y: 0 };

      // Get movement delta
      const deltaX = cursorPosition.x - lastCursorPosition.current.x;
      const deltaY = cursorPosition.y - lastCursorPosition.current.y;

      // Apply movement at different rates based on depth (deeper = slower movement)
      return {
        x: deltaX * depth,
        y: deltaY * depth
      };
    };

    const animate = (timestamp) => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      timeRef.current = timestamp;

      // Update cursor positions and calculate movement
      if (cursorPosition.x && cursorPosition.y) {
        // Store current position for next frame's comparison
        lastCursorPosition.current = { ...cursorPosition };
      }

      // Process and draw each parallax layer
      parallaxLayersRef.current.forEach(layer => {
        const parallaxOffset = calculateParallaxOffset(layer.depth);

        layer.particles.forEach(particle => {
          // Update natural movement
          particle.x += particle.speedX;
          particle.y += particle.speedY;

          // Apply parallax effect
          if (parallaxOffset.x || parallaxOffset.y) {
            particle.x += parallaxOffset.x;
            particle.y += parallaxOffset.y;
          }

          // Wrap around screen
          if (particle.x < 0) particle.x = canvas.width;
          if (particle.x > canvas.width) particle.x = 0;
          if (particle.y < 0) particle.y = canvas.height;
          if (particle.y > canvas.height) particle.y = 0;

          // Handle twinkling effect
          if (Math.random() < particle.twinkleProbability) {
            particle.isTwinkling = true;
          }

          if (particle.isTwinkling) {
            // Use sine wave for smooth twinkling effect
            const twinkleFactor = Math.sin(timeRef.current * particle.twinkleSpeed + particle.twinklePhase);
            const normalizedTwinkle = (twinkleFactor + 1) / 2; // Convert from [-1,1] to [0,1]

            particle.size = particle.baseSize + (particle.baseSize * 0.7 * normalizedTwinkle);
            particle.opacity = particle.baseOpacity + (0.4 * normalizedTwinkle);

            // Complete one twinkle cycle
            if (timeRef.current * particle.twinkleSpeed + particle.twinklePhase > particle.twinklePhase + Math.PI * 2) {
              particle.isTwinkling = false;
              particle.twinklePhase = Math.random() * Math.PI * 2; // Reset phase for next time
              particle.size = particle.baseSize;
              particle.opacity = particle.baseOpacity;
            }
          }

          // Draw particle with color slightly based on layer (deeper = bluer, closer = warmer)
          let r = 191;
          let g = 173;
          let b = 127 + (1 - layer.depth) * 30; // Adjust blue component based on depth

          ctx.beginPath();
          ctx.arc(particle.x, particle.y, particle.size, 0, Math.PI * 2);
          ctx.fillStyle = `rgba(${r}, ${g}, ${b}, ${particle.opacity})`;
          ctx.fill();
        });
      });

      // Draw constellations
      constellationsRef.current.forEach(constellation => {
        // Draw edges (lines) first so they appear behind vertices
        ctx.beginPath();
        ctx.strokeStyle = 'rgba(191, 173, 127, 0.1)'; // Very subtle lines
        ctx.lineWidth = 0.5;

        constellation.edges.forEach(edge => {
