import React, { useRef, useState, useEffect, useCallback, useMemo } from 'react';

/**
 * StellarBackground Component - Enterprise Performance Optimized Version
 * 
 * Creates a highly performant animated stellar background with constellations,
 * subtle star movement, and occasional rare effects like twinkling and meteor bursts.
 * 
 * Features:
 * - Advanced performance optimizations with adaptive quality scaling
 * - Memory and battery-efficient rendering pipeline
 * - Accessibility compliance with reduced motion support
 * - Supports hardware acceleration and high-DPI displays
 * - Smooth, fluid motion independent of cursor position
 * - Subtle constellation effects and rare "twinkle burst" events
 * 
 * @param {Object} props - Component props
 * @param {string|number} [props.height='100vh'] - Height of the container
 * @param {number} [props.zIndex=0] - Z-index for the container
 * @param {boolean} [props.active=true] - Whether the animation is active
 * @param {number} [props.starDensity=100] - Number of stars to render
 * @param {number} [props.starMaxSize=2] - Maximum size of stars
 * @param {number} [props.constellationCount=3] - Number of constellations to render
 * @param {number} [props.constellationComplexity=6] - Number of stars per constellation
 * @param {number} [props.horizontalSpeed=0.2] - Base speed of horizontal movement
 * @param {number} [props.starTwinkleFrequency=0.003] - Frequency of star twinkling (0-1)
 * @param {number} [props.rareEffectProbability=0.0005] - Probability of rare effects (0-1)
 * @param {string} [props.starColor='rgba(255, 255, 255, 0.8)'] - Base color for stars
 * @param {string} [props.constellationColor='rgba(191, 173, 127, 0.2)'] - Color for constellation lines
 * @param {boolean} [props.adaptiveQuality=true] - Enable adaptive quality based on device
 * @param {boolean} [props.respectReducedMotion=true] - Respect reduced motion preferences
 * @param {number} [props.maxFPS=30] - Target maximum frames per second
 */
const StellarBackground = ({
  height = '100vh',
  zIndex = 0,
  active = true,
  starDensity = 100,
  starMaxSize = 2,
  constellationCount = 3,
  constellationComplexity = 6,
  horizontalSpeed = 0.2,
  starTwinkleFrequency = 0.003,
  rareEffectProbability = 0.0005,
  starColor = 'rgba(255, 255, 255, 0.8)',
  constellationColor = 'rgba(191, 173, 127, 0.2)',
  adaptiveQuality = true,
  respectReducedMotion = true,
  maxFPS = 30
}) => {
  // Refs for DOM elements and animation state
  const containerRef = useRef(null);
  const canvasRef = useRef(null);
  const ctxRef = useRef(null);
  const starsRef = useRef([]);
  const constellationsRef = useRef([]);
  const animationFrameRef = useRef(null);
  const lastTimestampRef = useRef(0);
  const fpsTimestampRef = useRef(0);
  const frameCountRef = useRef(0);
  const currentFpsRef = useRef(30);
  const worldOffsetRef = useRef({ x: 0, y: 0 });
  const visibilityChangeTimeRef = useRef(0);
  const qualityChangeTimerRef = useRef(null);
  const stableFrameCountRef = useRef(0);
  const twinkleEffectsRef = useRef([]);
  const burstEffectsRef = useRef([]);

  // Component state
  const [dimensions, setDimensions] = useState({ width: 0, height: 0, pixelRatio: 1 });
  const [isInitialized, setIsInitialized] = useState(false);
  const [prefersReducedMotion, setPrefersReducedMotion] = useState(false);
  const [isVisible, setIsVisible] = useState(false);
  const [qualityFactor, setQualityFactor] = useState(1);
  const [deviceCapabilities, setDeviceCapabilities] = useState({
    memory: 4,
    cores: 4,
    batteryLevel: 1,
    isMobile: false
  });

  // Pre-allocate objects to avoid garbage collection during animation
  const pointCache = useRef({
    current: { x: 0, y: 0 }
  }).current;

  // Precompute color variants to avoid string operations during animation
  const colorCacheRef = useRef(new Map());

  // Adaptive settings based on quality factor
  const adaptedSettings = useMemo(() => {
    if (!adaptiveQuality || qualityFactor === 1) {
      return {
        starDensity,
        constellationCount,
        useShadow: true,
        useGlow: true,
        useHighQualityRendering: true
      };
    }

    // Adjust quality-dependent parameters
    return {
      starDensity: Math.max(40, Math.floor(starDensity * qualityFactor)),
      constellationCount: Math.max(1, Math.floor(constellationCount * qualityFactor)),
      useShadow: qualityFactor > 0.5,
      useGlow: qualityFactor > 0.3,
      useHighQualityRendering: qualityFactor > 0.7
    };
  }, [adaptiveQuality, qualityFactor, starDensity, constellationCount]);

  // Detects device capabilities and sets up optimization strategies
  const detectCapabilities = useCallback(() => {
    // Device memory, hardware concurrency, and mobile detection
    const memory = navigator.deviceMemory || 4;
    const cores = navigator.hardwareConcurrency || 4;
    const isMobile = /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent);

    // Set device capabilities state
    setDeviceCapabilities({
      memory,
      cores,
      batteryLevel: 1,
      isCharging: true,
      isMobile
    });

    // Calculate quality factor based on capabilities
    if (adaptiveQuality) {
      // Base score from hardware
      const performanceScore = (memory * cores) / (isMobile ? 2 : 1);

      // Scale quality based on performance score
      let quality = 1;

      if (performanceScore > 16) {
        quality = 1; // High-end devices
      } else if (performanceScore > 8) {
        quality = 0.8; // Mid-range devices
      } else if (performanceScore > 4) {
        quality = 0.6; // Low-end devices
      } else {
        quality = 0.4; // Very low-end devices
      }

      setQualityFactor(quality);
    }
  }, [adaptiveQuality]);

  // Get cached color with opacity
  const getCachedColor = useCallback((baseColor, opacity) => {
    // Ensure opacity is always valid to prevent rendering glitches
    const safeOpacity = Math.max(0, Math.min(1, opacity || 0));

    // Round opacity to reduce cache size while maintaining visual quality
    const roundedOpacity = Math.round(safeOpacity * 100) / 100;
    const key = `${baseColor}-${roundedOpacity}`;

    if (!colorCacheRef.current.has(key)) {
      const newColor = baseColor.replace(/[\d.]+\)$/, roundedOpacity + ')');
      colorCacheRef.current.set(key, newColor);
    }

    return colorCacheRef.current.get(key);
  }, []);

  // Detect device capabilities and preferences on mount
  useEffect(() => {
    // Check for reduced motion preference
    const reducedMotionQuery = window.matchMedia('(prefers-reduced-motion: reduce)');
    setPrefersReducedMotion(reducedMotionQuery.matches);

    const handleReducedMotionChange = (e) => {
      setPrefersReducedMotion(e.matches);
    };

    reducedMotionQuery.addEventListener('change', handleReducedMotionChange);

    // Detect device capabilities
    detectCapabilities();

    // Setup visibility change detection to pause when tab is inactive
    const handleVisibilityChange = () => {
      if (document.hidden) {
        visibilityChangeTimeRef.current = performance.now();
      } else {
        // Adjust timing references after visibility changes
        const timeDelta = performance.now() - visibilityChangeTimeRef.current;
        lastTimestampRef.current += timeDelta;
      }
    };

    document.addEventListener('visibilitychange', handleVisibilityChange);

    // Cleanup
    return () => {
      reducedMotionQuery.removeEventListener('change', handleReducedMotionChange);
      document.removeEventListener('visibilitychange', handleVisibilityChange);
    };
  }, [detectCapabilities]);

  // Initialize canvas with proper resolution
  const setupCanvas = useCallback(() => {
    if (!canvasRef.current || !containerRef.current) return false;

    const canvas = canvasRef.current;
    const container = containerRef.current;
    const rect = container.getBoundingClientRect();
    const pixelRatio = window.devicePixelRatio || 1;

    // Calculate dimensions - ensure valid integers to prevent rendering issues
    const displayWidth = Math.floor(rect.width);
    const displayHeight = typeof height === 'string' && height.endsWith('vh') 
      ? Math.floor((parseInt(height, 10) / 100) * window.innerHeight)
      : Math.floor(parseInt(height, 10) || window.innerHeight);

    // Set canvas size accounting for pixel ratio
    canvas.width = displayWidth * pixelRatio;
    canvas.height = displayHeight * pixelRatio;
    canvas.style.width = `${displayWidth}px`;
    canvas.style.height = `${displayHeight}px`;

    // 2D Canvas context
    const ctx = canvas.getContext('2d', {
      alpha: true,
      desynchronized: true,
      willReadFrequently: false
    });

    if (!ctx) return false;

    // Scale context for high-DPI displays
    ctx.scale(pixelRatio, pixelRatio);

    // Configure context for high-quality rendering
    if (adaptedSettings.useHighQualityRendering) {
      ctx.imageSmoothingEnabled = true;
      ctx.imageSmoothingQuality = 'high';
    }

    ctxRef.current = ctx;

    setDimensions({
      width: displayWidth,
      height: displayHeight,
      pixelRatio
    });

    return true;
  }, [height, adaptedSettings.useHighQualityRendering]);

  // Setup canvas resize observer
  useEffect(() => {
    if (!containerRef.current) return;

    // Create a ResizeObserver to detect container size changes
    const resizeObserver = new ResizeObserver(() => {
      // Debounce resize operations
      if (resizeObserverRef.current?.timeout) {
        clearTimeout(resizeObserverRef.current.timeout);
      }

      const timeout = setTimeout(() => {
        if (setupCanvas()) {
          // Re-initialize stars and constellations when canvas is resized
          initializeStars();
          initializeConstellations();
          setIsInitialized(true);
        }
      }, 100); // 100ms debounce

      resizeObserverRef.current = { timeout };
    });

    // Capture current reference to avoid closure issues in cleanup
    const currentContainer = containerRef.current;

    // Start observing the container
    resizeObserver.observe(currentContainer);
    const resizeObserverRef = { observer: resizeObserver };

    // Initial setup
    setupCanvas();

    return () => {
      if (resizeObserverRef.observer) {
        resizeObserverRef.observer.disconnect();
      }
      if (resizeObserverRef.timeout) {
        clearTimeout(resizeObserverRef.timeout);
      }
    };
  }, [setupCanvas]);

  // Setup intersection observer to only animate when visible
  useEffect(() => {
    if (!containerRef.current) return;

    const observer = new IntersectionObserver(
      entries => {
        const isIntersecting = entries[0].isIntersecting;

        // Only change state if visibility actually changed
        if (isIntersecting !== isVisible) {
          setIsVisible(isIntersecting);

          if (isIntersecting) {
            // Reset timing references when becoming visible again
            lastTimestampRef.current = 0;
          }
        }
      },
      {
        threshold: 0.01,
        rootMargin: '100px'
      }
    );

    // Store current reference to avoid closure issues
    const currentContainerRef = containerRef.current;
    observer.observe(currentContainerRef);
    const visibilityObserverRef = { current: observer };

    return () => {
      if (visibilityObserverRef.current) {
        visibilityObserverRef.current.disconnect();
      }
    };
  }, [isVisible]);

  // Initialize stars
  const initializeStars = useCallback(() => {
    if (!dimensions.width || !dimensions.height) return;

    const stars = [];
    const count = adaptedSettings.starDensity;

    // Create stars with varied properties
    for (let i = 0; i < count; i++) {
      // Base star parameters
      const star = {
        x: Math.random() * dimensions.width,
        y: Math.random() * dimensions.height,
        z: Math.random() * 3 + 0.5, // Z-depth for parallax effect
        size: Math.random() * starMaxSize + 0.5,
        twinkleSpeed: Math.random() * 0.02 + 0.005,
        twinklePhase: Math.random() * Math.PI * 2,
        brightness: 0.5 + Math.random() * 0.5,
        originalBrightness: 0.5 + Math.random() * 0.5,
        // Slightly varying colors for natural look
        colorVariation: Math.random() * 0.2,
        // Random layer factor for movement speed
        layer: Math.floor(Math.random() * 3) // 0, 1, or 2
      };

      stars.push(star);
    }

    starsRef.current = stars;
  }, [dimensions, adaptedSettings.starDensity, starMaxSize]);

  // Initialize constellations
  const initializeConstellations = useCallback(() => {
    if (!dimensions.width || !dimensions.height || starsRef.current.length === 0) return;

    const constellations = [];
    const allStars = starsRef.current;
    const count = adaptedSettings.constellationCount;

    // Create constellations
    for (let c = 0; c < count; c++) {
      // Select a random star as the constellation center
      const centerStarIndex = Math.floor(Math.random() * allStars.length);
      const centerStar = allStars[centerStarIndex];

      // Create complex connections between nearby stars
      const points = [centerStarIndex];
      const connections = [];

      // Find nearby stars to connect
      const potentialConnections = [];

      for (let i = 0; i < allStars.length; i++) {
        if (i === centerStarIndex) continue;

        const star = allStars[i];
        const dx = star.x - centerStar.x;
        const dy = star.y - centerStar.y;
        const distance = Math.sqrt(dx * dx + dy * dy);

        // Only consider stars within a reasonable distance
        const maxDistance = Math.min(dimensions.width, dimensions.height) * 0.25;
        if (distance < maxDistance) {
          potentialConnections.push({
            index: i,
            distance
          });
        }
      }

      // Sort by distance and take the closest ones
      potentialConnections.sort((a, b) => a.distance - b.distance);

      // Connect stars to form the constellation
      // Only use as many as we have available, up to constellationComplexity
      const starCount = Math.min(potentialConnections.length, constellationComplexity - 1);

      for (let i = 0; i < starCount; i++) {
        const starIndex = potentialConnections[i].index;
        points.push(starIndex);

        // Connect to center star or previous star in sequence
        if (i === 0) {
          connections.push([centerStarIndex, starIndex]);
        } else {
          // Sometimes connect back to earlier stars for more complex shapes
          const connectToIndex = Math.random() < 0.7
            ? potentialConnections[i - 1].index // Connect to previous star
            : points[Math.floor(Math.random() * points.length)]; // Connect to random star in constellation

          connections.push([connectToIndex, starIndex]);
        }

        // Add occasional branches for visual interest
        if (i > 1 && Math.random() < 0.3 && i < starCount - 1) {
          const branchTarget = points[Math.floor(Math.random() * (points.length - 1))];
          connections.push([starIndex, branchTarget]);
        }
      }

      // Set constellation properties
      const constellation = {
        points,
        connections,
        progress: 0,
        fadeState: 'in', // 'in', 'visible', 'out'
        opacity: 0,
        lastStateChange: 0,
        visibleDuration: 40000 + Math.random() * 20000, // 40-60 seconds visibility
        transitionDuration: 5000 + Math.random() * 3000, // 5-8 seconds fade in/out
        lineWidth: 0.5 + Math.random() * 0.5,
        pulsePhase: Math.random() * Math.PI * 2,
        pulseSpeed: 0.0001 + Math.random() * 0.0002
      };

      constellations.push(constellation);
    }

    constellationsRef.current = constellations;
  }, [dimensions, adaptedSettings.constellationCount, constellationComplexity]);

  // Create a twinkling effect at a specific star
  const createTwinkleEffect = useCallback((star) => {
    if (prefersReducedMotion && respectReducedMotion) return;

    // Create a subtle shine effect
    const twinkle = {
      x: star.x,
      y: star.y,
      size: star.size * (1.5 + Math.random() * 1),
      brightness: star.originalBrightness * (1.2 + Math.random() * 0.5),
      // More gradual and natural fading
      life: 1.0,
      decay: 0.01 + Math.random() * 0.02,
      // Adding subtle drift to appear more natural
      drift: {
        x: (Math.random() * 2 - 1) * 0.2,
        y: (Math.random() * 2 - 1) * 0.2
      },
      // Random chance for different qualities
      enhancedEffect: Math.random() < 0.15, // 15% chance for enhanced effect
      // Layer matching for consistent movement
      layer: star.layer
    };

    twinkleEffectsRef.current.push(twinkle);
  }, [prefersReducedMotion, respectReducedMotion]);

  // Create a rare burst effect (similar to meteor burst but more subtle)
  const createBurstEffect = useCallback((x, y, size) => {
    if (prefersReducedMotion && respectReducedMotion) return;

    // Skip if we already have too many burst effects (performance optimization)
    if (burstEffectsRef.current.length > 15) return;

    // Number of particles in burst - lower than meteor for subtlety
    const particleCount = Math.floor(4 + Math.random() * 8);

    // Create a subtle burst of particles
    for (let i = 0; i < particleCount; i++) {
      // Calculate particle direction - full 360Â° radial
      const angle = Math.random() * Math.PI * 2;

      // Speed - very slow for a subtle effect
      const speed = 0.05 + Math.random() * 0.15;

      // Calculate velocity components
      const vx = Math.cos(angle) * speed;
      const vy = Math.sin(angle) * speed;

      // Create small particles
      const particleSize = size * (0.3 + Math.random() * 0.4);

      // Create the burst particle
      const burst = {
        x: x,
        y: y,
        vx: vx,
        vy: vy,
        size: particleSize,
        life: 1.0,
        decay: 0.005 + Math.random() * 0.01, // Slower decay for longer effect
        // Golden hues for subtle elegance
        color: Math.random() < 0.7 
          ? 'rgba(255, 255, 255, 0.8)' 
          : 'rgba(191, 173, 127, 0.7)',
        // Elegant diminishing velocity
        damping: 0.98 + Math.random() * 0.01
      };

      burstEffectsRef.current.push(burst);
    }
  }, [prefersReducedMotion, respectReducedMotion]);

  // Update stars and world position
  const updateStarsAndBackground = useCallback((deltaTime) => {
    // Apply a maximum delta time to prevent large jumps after tab switching
    const cappedDeltaTime = Math.min(deltaTime, 50);

    // Update world offset for horizontal scrolling effect
    // Apply different speeds based on quality factor to prevent excessive motion on low-end devices
    const speedFactor = Math.min(1, qualityFactor + 0.4);
    worldOffsetRef.current.x += horizontalSpeed * speedFactor * (cappedDeltaTime / 16);

    // Ensure world offset wraps around cleanly
    if (worldOffsetRef.current.x > dimensions.width) {
      worldOffsetRef.current.x -= dimensions.width;
    }

    // Update each star
    for (const star of starsRef.current) {
      // Apply twinkle effect using sin wave
      const timeFactor = performance.now() * 0.001;
      const twinkleEffect = Math.pow(Math.sin(timeFactor * star.twinkleSpeed + star.twinklePhase) * 0.5 + 0.5, 1.5);

      // More subtle brightness variation
      star.brightness = star.originalBrightness * (0.8 + twinkleEffect * 0.4);

      // Move stars based on their layer (parallax effect)
      // Layer 0 (distant): slowest, Layer 2 (closest): fastest
      const layerSpeedFactor = 0.5 + (star.layer * 0.25);

      // Apply horizontal movement based on world offset
      star.movementX = -worldOffsetRef.current.x * layerSpeedFactor * (star.z * 0.4);

      // Wrap stars around when they move off-screen
      // Calculate effective position with wrapped movement
      const effectiveX = (star.x + star.movementX) % dimensions.width;
      if (effectiveX < 0) {
        star.effectiveX = effectiveX + dimensions.width;
      } else {
        star.effectiveX = effectiveX;
      }

      star.effectiveY = star.y;

      // Random chance to create twinkle effect
      if (Math.random() < starTwinkleFrequency * (cappedDeltaTime / 16) * qualityFactor) {
        createTwinkleEffect(star);
      }

      // Very rare chance for a burst effect - slightly higher for brighter stars
      if (Math.random() < rareEffectProbability * star.brightness * (cappedDeltaTime / 16) * qualityFactor) {
        createBurstEffect(star.effectiveX, star.effectiveY, star.size * 2);
      }
    }

    // Update twinkle effects
    for (let i = twinkleEffectsRef.current.length - 1; i >= 0; i--) {
      const twinkle = twinkleEffectsRef.current[i];

      // Update life
      twinkle.life -= twinkle.decay * (cappedDeltaTime / 16);

      // Apply subtle drift for more natural movement
      twinkle.x += twinkle.drift.x * (cappedDeltaTime / 16);
      twinkle.y += twinkle.drift.y * (cappedDeltaTime / 16);

      // Apply same world offset as the parent star's layer
      const layerSpeedFactor = 0.5 + (twinkle.layer * 0.25);
      twinkle.movementX = -worldOffsetRef.current.x * layerSpeedFactor * 0.4;

      // Calculate effective position
      const effectiveX = (twinkle.x + twinkle.movementX) % dimensions.width;
      if (effectiveX < 0) {
        twinkle.effectiveX = effectiveX + dimensions.width;
      } else {
        twinkle.effectiveX = effectiveX;
      }

      twinkle.effectiveY = twinkle.y;

      // Remove expired effects
      if (twinkle.life <= 0) {
        twinkleEffectsRef.current.splice(i, 1);
      }
    }

    // Update burst effects
    for (let i = burstEffectsRef.current.length - 1; i >= 0; i--) {
      const burst = burstEffectsRef.current[i];

      // Update position
      burst.x += burst.vx * (cappedDeltaTime / 16);
      burst.y += burst.vy * (cappedDeltaTime / 16);

      // Apply damping to velocity for natural deceleration
      burst.vx *= burst.damping;
      burst.vy *= burst.damping;

      // Update life
      burst.life -= burst.decay * (cappedDeltaTime / 16);

      // Remove expired effects
      if (burst.life <= 0) {
        burstEffectsRef.current.splice(i, 1);
      }
    }

    // Update constellations
    const now = performance.now();
    for (const constellation of constellationsRef.current) {
      // Update constellation state based on timing
      const timeSinceStateChange = now - constellation.lastStateChange;

      if (constellation.fadeState === 'in') {
        // Fading in
        constellation.progress = Math.min(1, timeSinceStateChange / constellation.transitionDuration);
        constellation.opacity = constellation.progress * 0.7; // Max opacity 0.7

        if (constellation.progress >= 1) {
          constellation.fadeState = 'visible';
          constellation.lastStateChange = now;
        }
      } else if (constellation.fadeState === 'visible') {
        // Fully visible
        if (timeSinceStateChange >= constellation.visibleDuration) {
          constellation.fadeState = 'out';
          constellation.lastStateChange = now;
          constellation.progress = 1;
        }

        // Subtle pulsing while visible
        const pulsePhase = constellation.pulsePhase + now * constellation.pulseSpeed;
        const pulseFactor = Math.sin(pulsePhase) * 0.1 + 0.9; // 0.8-1.0 range
        constellation.opacity = 0.7 * pulseFactor;
      } else if (constellation.fadeState === 'out') {
        // Fading out
        constellation.progress = 1 - Math.min(1, timeSinceStateChange / constellation.transitionDuration);
        constellation.opacity = constellation.progress * 0.7;

        if (constellation.progress <= 0) {
          // Reset constellation with new parameters
          constellation.fadeState = 'in';
          constellation.lastStateChange = now + Math.random() * 10000; // Staggered reappearance
          constellation.visibleDuration = 40000 + Math.random() * 20000;
          constellation.transitionDuration = 5000 + Math.random() * 3000;
          constellation.pulsePhase = Math.random() * Math.PI * 2;

          // Occasional reshuffling of constellation connections for variety
          if (Math.random() < 0.3) {
            const connections = constellation.connections;
            for (let i = 0; i < connections.length; i++) {
              if (Math.random() < 0.4) {
                // Rewire this connection to a different point
                const targetOptions = constellation.points.filter(p => 
                  p !== connections[i][0] && p !== connections[i][1]);

                if (targetOptions.length > 0) {
                  const newTarget = targetOptions[Math.floor(Math.random() * targetOptions.length)];
                  connections[i][Math.random() < 0.5 ? 0 : 1] = newTarget;
                }
              }
            }
          }
        }
      }
    }
  }, [
    dimensions, 
    horizontalSpeed, 
    qualityFactor, 
    starTwinkleFrequency, 
    rareEffectProbability, 
    createTwinkleEffect, 
    createBurstEffect
  ]);

  // Render stars and constellations
  const renderStarsAndConstellations = useCallback((ctx) => {
    // Clear canvas with optimized clear method
    ctx.clearRect(0, 0, dimensions.width, dimensions.height);

    // Determine if we should use quality renderings based on settings
    const useQualityRendering = adaptedSettings.useHighQualityRendering;
    const useGlow = adaptedSettings.useGlow;

    // Render stars first (background layer)
    for (const star of starsRef.current) {
      // Skip if not in visible area
      if (star.effectiveX < -10 || star.effectiveX > dimensions.width + 10 ||
          star.effectiveY < -10 || star.effectiveY > dimensions.height + 10) {
        continue;
      }

      // Basic star rendering for all quality levels
      ctx.beginPath();

      if (useQualityRendering) {
        // High quality rendering with soft glow
        // Set shadow for glow effect if enabled
        if (useGlow) {
          ctx.shadowColor = getCachedColor(starColor, star.brightness * 0.8);
          ctx.shadowBlur = star.size * 3;
        }

        // Draw star with gradient for more natural appearance
        const gradient = ctx.createRadialGradient(
          star.effectiveX, star.effectiveY, 0,
          star.effectiveX, star.effectiveY, star.size * 1.2
        );

        gradient.addColorStop(0, getCachedColor(starColor, star.brightness));
        gradient.addColorStop(0.5, getCachedColor(starColor, star.brightness * 0.6));
        gradient.addColorStop(1, getCachedColor(starColor, 0));

        ctx.fillStyle = gradient;
        ctx.arc(star.effectiveX, star.effectiveY, star.size, 0, Math.PI * 2);
        ctx.fill();
      } else {
        // Simplified rendering for lower performance devices
        ctx.fillStyle = getCachedColor(starColor, star.brightness);
        ctx.arc(star.effectiveX, star.effectiveY, star.size * 0.8, 0, Math.PI * 2);
        ctx.fill();
      }
    }

    // Reset shadow to avoid affecting other elements
    if (useGlow) {
      ctx.shadowBlur = 0;
    }

    // Render constellations
    for (const constellation of constellationsRef.current) {
      if (constellation.opacity <= 0.01) continue;

      const allStars = starsRef.current;

      // Set constellation line style
      ctx.strokeStyle = getCachedColor(constellationColor, constellation.opacity);
      ctx.lineWidth = constellation.lineWidth;

      // Optimized rendering by batching lines
      ctx.beginPath();

      // Draw constellation connections
      for (const [fromIndex, toIndex] of constellation.connections) {
        const fromStar = allStars[fromIndex];
        const toStar = allStars[toIndex];

        if (!fromStar || !toStar) continue;

        // Use effective positions that account for movement
        ctx.moveTo(fromStar.effectiveX, fromStar.effectiveY);
        ctx.lineTo(toStar.effectiveX, toStar.effectiveY);
      }

      // Apply glow to constellation lines if enabled
      if (useGlow) {
        ctx.shadowColor = getCachedColor(constellationColor, constellation.opacity * 0.5);
        ctx.shadowBlur = 3;
      }

      ctx.stroke();

      // Reset shadow
      if (useGlow) {
        ctx.shadowBlur = 0;
      }
    }

    // Render twinkle effects above regular stars
    for (const twinkle of twinkleEffectsRef.current) {
      // Skip if not in visible area
      if (twinkle.effectiveX < -10 || twinkle.effectiveX > dimensions.width + 10 ||
          twinkle.effectiveY < -10 || twinkle.effectiveY > dimensions.height + 10) {
        continue;
      }

      const opacity = twinkle.life * twinkle.brightness;

      if (useQualityRendering && twinkle.enhancedEffect) {
        // Enhanced twinkle effect for some stars
        // Outer glow
        if (useGlow) {
          ctx.shadowColor = getCachedColor(starColor, opacity * 0.6);
          ctx.shadowBlur = twinkle.size * 3;
        }

        // Draw twinkle with gradient
        const gradient = ctx.createRadialGradient(
          twinkle.effectiveX, twinkle.effectiveY, 0,
          twinkle.effectiveX, twinkle.effectiveY, twinkle.size * 1.5
        );

        gradient.addColorStop(0, getCachedColor(starColor, opacity));
        gradient.addColorStop(0.4, getCachedColor(starColor, opacity * 0.7));
        gradient.addColorStop(1, getCachedColor(starColor, 0));

        ctx.beginPath();
        ctx.fillStyle = gradient;
        ctx.arc(twinkle.effectiveX, twinkle.effectiveY, twinkle.size, 0, Math.PI * 2);
        ctx.fill();

        // Inner core
        ctx.beginPath();
        ctx.fillStyle = getCachedColor(starColor, opacity * 1.2);
        ctx.arc(twinkle.effectiveX, twinkle.effectiveY, twinkle.size * 0.4, 0, Math.PI * 2);
        ctx.fill();
      } else {
        // Simple twinkle effect
        ctx.beginPath();
        if (useGlow) {
          ctx.shadowColor = getCachedColor(starColor, opacity * 0.5);
          ctx.shadowBlur = twinkle.size * 2;
        }
        ctx.fillStyle = getCachedColor(starColor, opacity);
        ctx.arc(twinkle.effectiveX, twinkle.effectiveY, twinkle.size * 0.9, 0, Math.PI * 2);
        ctx.fill();
      }
    }

    // Reset shadow
    if (useGlow) {
      ctx.shadowBlur = 0;
    }

    // Render burst effects
    for (const burst of burstEffectsRef.current) {
      const opacity = burst.life * 0.8;

      if (useQualityRendering) {
        // Apply shadow/glow
        if (useGlow) {
          ctx.shadowColor = getCachedColor(burst.color, opacity * 0.6);
          ctx.shadowBlur = burst.size * 2;
        }

        // Draw particle with fade out
        ctx.beginPath();
        ctx.fillStyle = getCachedColor(burst.color, opacity);
        ctx.arc(burst.x, burst.y, burst.size * burst.life, 0, Math.PI * 2);
        ctx.fill();
      } else {
        // Simple particle rendering
        ctx.beginPath();
        ctx.fillStyle = getCachedColor(burst.color, opacity);
        ctx.arc(burst.x, burst.y, burst.size * burst.life * 0.8, 0, Math.PI * 2);
        ctx.fill();
      }
    }
  }, [
    dimensions, 
    adaptedSettings.useHighQualityRendering, 
    adaptedSettings.useGlow, 
    starColor, 
    constellationColor, 
    getCachedColor
  ]);

  // FPS limiter for consistent animation speed
  const fpsLimiter = useCallback((timestamp, callback) => {
    // Skip animation if hidden, inactive, or reduced motion
    if (!isVisible || !active || (respectReducedMotion && prefersReducedMotion) || document.hidden) {
      animationFrameRef.current = requestAnimationFrame(time => fpsLimiter(time, callback));
      return;
    }

    const targetFrameTime = 1000 / maxFPS;
    const elapsed = timestamp - lastTimestampRef.current;

    if (elapsed >= targetFrameTime || lastTimestampRef.current === 0) {
      // Calculate correct delta
      const delta = lastTimestampRef.current === 0 ? 16 : elapsed;

      // Update timestamp, limiting delta to avoid jumps after inactivity
      lastTimestampRef.current = timestamp - (elapsed % targetFrameTime);

      // Run animation callback with capped delta time
      callback(Math.min(delta, 50));

      // FPS tracking
      frameCountRef.current++;
      if (timestamp - fpsTimestampRef.current >= 1000) {
        currentFpsRef.current = frameCountRef.current;
        frameCountRef.current = 0;
        fpsTimestampRef.current = timestamp;

        // Dynamic quality adjustment based on performance
        if (adaptiveQuality) {
          // Check if FPS is stable
          if (currentFpsRef.current >= maxFPS * 0.9) {
            stableFrameCountRef.current += 1;
          } else if (currentFpsRef.current < maxFPS * 0.7) {
            stableFrameCountRef.current = 0;
          }

          // Avoid frequent quality changes by using a timer
          if (qualityChangeTimerRef.current) {
            clearTimeout(qualityChangeTimerRef.current);
          }

          qualityChangeTimerRef.current = setTimeout(() => {
            // Only change quality if FPS has been stable or is very low
            if (currentFpsRef.current < maxFPS * 0.7) {
              // If FPS is below 70% of target, reduce quality
              setQualityFactor(prev => Math.max(0.4, prev * 0.9));
            } else if (stableFrameCountRef.current >= 3 && qualityFactor < 1) {
              // If FPS has been high for several frames, gradually increase quality
              setQualityFactor(prev => Math.min(1, prev * 1.05));
            }
          }, 500);
        }
      }
    }

    // Schedule next frame
    animationFrameRef.current = requestAnimationFrame(time => fpsLimiter(time, callback));
  }, [
    isVisible,
    active,
    respectReducedMotion,
    prefersReducedMotion,
    maxFPS,
    adaptiveQuality,
    qualityFactor
  ]);

  // Main animation handler
  const handleAnimation = useCallback((deltaTime) => {
    // Skip if component is not ready
    if (!canvasRef.current || !isInitialized || !dimensions.width || !dimensions.height) return;

    // Update star positions and background
    updateStarsAndBackground(deltaTime);

    // Render the scene
    renderStarsAndConstellations(ctxRef.current);
  }, [
    isInitialized,
    dimensions,
    updateStarsAndBackground,
    renderStarsAndConstellations
  ]);

  // Initialize stars and constellations once dimensions are available
  useEffect(() => {
    if (dimensions.width && dimensions.height) {
      initializeStars();
      initializeConstellations();
      setIsInitialized(true);
    }
  }, [dimensions, initializeStars, initializeConstellations]);

  // Main animation loop
  useEffect(() => {
    if (!active || !isInitialized || !dimensions.width || !dimensions.height) {
      return;
    }

    // Start animation with FPS limiter
    fpsTimestampRef.current = performance.now();
    frameCountRef.current = 0;
    lastTimestampRef.current = 0;

    animationFrameRef.current = requestAnimationFrame(timestamp => {
      fpsLimiter(timestamp, handleAnimation);
    });

    // Cleanup
    return () => {
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }

      if (qualityChangeTimerRef.current) {
        clearTimeout(qualityChangeTimerRef.current);
      }
    };
  }, [
    active,
    isInitialized,
    dimensions,
    fpsLimiter,
    handleAnimation
  ]);

  // Apply reduced motion adjustments
  const reducedMotionStyles = useMemo(() => {
    if (prefersReducedMotion && respectReducedMotion) {
      return {
        // Subtle static look for reduced motion preference
        animation: 'none',
        transition: 'none'
      };
    }
    return {};
  }, [prefersReducedMotion, respectReducedMotion]);

  return (
    <div 
      ref={containerRef}
      style={{ 
        position: 'absolute',
        top: 0, 
        left: 0, 
        width: '100%', 
        height: height,
        overflow: 'hidden', 
        pointerEvents: 'none', 
        zIndex: zIndex,
        ...reducedMotionStyles,
        // Hardware acceleration
        backfaceVisibility: 'hidden',
        transform: 'translateZ(0)',
        willChange: 'transform'
      }}
      aria-hidden="true"
    >
      <canvas 
        ref={canvasRef} 
        style={{ 
          position: 'absolute', 
          top: 0, 
          left: 0, 
          width: '100%', 
          height: '100%',
          // Additional rendering optimizations
          imageRendering: 'high-quality',
          // Hardware acceleration
          backfaceVisibility: 'hidden',
          transform: 'translateZ(0)',
          willChange: 'transform',
          // Ensures proper subpixel rendering
          filter: 'none'
        }} 
      />
    </div>
  );
};

/**
 * Dark Fantasy Stellar Background - Preset Configuration
 * A background with richer golden-hued stars and elegant constellations
 */
export const DarkFantasyStellarBackground = (props) => {
  const darkFantasyPreset = {
    starDensity: 120,
    starMaxSize: 2.2,
    constellationCount: 4,
    constellationComplexity: 8,
    horizontalSpeed: 0.15,
    starTwinkleFrequency: 0.004,
    rareEffectProbability: 0.0006,
    starColor: 'rgba(255, 253, 240, 0.85)',
    constellationColor: 'rgba(207, 181, 59, 0.25)',
    maxFPS: 30
  };

  return <StellarBackground {...darkFantasyPreset} {...props} />;
};

export default StellarBackground;
