    const cellKey = `${cellX},${cellY}`;

    // Check if we already generated stars for this cell
    if (starMapRef.current.has(cellKey)) {
      return starMapRef.current.get(cellKey);
    }

    const stars = [];
    const cellWidth = CONFIG.cellSize;
    const cellHeight = CONFIG.cellSize;
    const startX = cellX * cellWidth;
    const startY = cellY * cellHeight;

    // Calculate how many stars based on density
    const starCount = Math.floor(cellWidth * cellHeight * CONFIG.starDensity);

    // Create stars with deterministic positions based on cell coordinates
    for (let i = 0; i < starCount; i++) {
      // Use seeded random for deterministic star placement
      const rx = seededRandom(cellX, i, 1);
      const ry = seededRandom(cellY, i, 2);
      const rz = seededRandom(cellX, cellY, i * 3);

      const star = {};

      // Position within cell
      star.x = startX + rx * cellWidth;
      star.y = startY + ry * cellHeight;
      star.z = rz * 0.8 + 0.1; // Depth (0.1 to 0.9)

      // Visual properties with seeded random
      star.size = (seededRandom(cellX, i, 4) * (CONFIG.starSizeMax - CONFIG.starSizeMin) + CONFIG.starSizeMin) * star.z;
      star.baseOpacity = seededRandom(cellX, i, 5) * (CONFIG.starOpacityMax - CONFIG.starOpacityMin) + CONFIG.starOpacityMin;
      star.opacity = star.baseOpacity;

      // Animation properties
      star.pulsePhase = seededRandom(cellX, i, 6) * Math.PI * 2;
      star.pulseSpeed = seededRandom(cellX, i, 7) * 0.002 + 0.001;

      // Motion properties - very subtle
      star.movementX = (seededRandom(cellX, i, 8) * 2 - 1) * CONFIG.baseMovementSpeed;
      star.movementY = (seededRandom(cellX, i, 9) * 2 - 1) * CONFIG.baseMovementSpeed;
      star.initialX = star.x;
      star.initialY = star.y;

      // Star shape properties - add variation for more realistic stars
      star.rays = seededRandom(cellX, i, 10) > 0.7 ? Math.floor(seededRandom(cellX, i, 11) * 2) + 4 : 0;
      star.rayLength = star.size * (seededRandom(cellX, i, 12) * 0.7 + 0.3);
      star.rayIntensity = seededRandom(cellX, i, 13) * 0.4 + 0.3;

      // Color variation
      const colorIndex = Math.floor(seededRandom(cellX, i, 14) * COLORS.stars.length);
      star.color = COLORS.stars[colorIndex];

      stars.push(star);
    }

    // Store in map
    starMapRef.current.set(cellKey, stars);

    return stars;
  }, [CONFIG, COLORS.stars, seededRandom]);

  // Generate stars for the visible area and buffer
  const generateStarsForVisibleArea = useCallback(() => {
    if (!isInitialized) return;

    const { top, bottom } = visibleAreaRef.current;

    // Calculate which cells are visible
    const startCellY = Math.floor(top / CONFIG.cellSize) - CONFIG.bufferCells;
    const endCellY = Math.ceil(bottom / CONFIG.cellSize) + CONFIG.bufferCells;

    // Width cells (always visible)
    const startCellX = 0;
    const endCellX = Math.ceil(dimensions.width / CONFIG.cellSize);

    // Generate stars for all visible cells
    for (let y = startCellY; y <= endCellY; y++) {
      for (let x = startCellX; x < endCellX; x++) {
        generateStarsForCell(x, y);
      }
    }
  }, [isInitialized, dimensions.width, CONFIG.cellSize, CONFIG.bufferCells, generateStarsForCell]);

  // Get all visible stars
  const getVisibleStars = useCallback(() => {
    const visibleStars = [];
    const buffer = dimensions.height; // Extra buffer to account for parallax

    // Calculate which cells could be visible
    const startCellY = Math.floor((visibleAreaRef.current.top - buffer) / CONFIG.cellSize);
    const endCellY = Math.ceil((visibleAreaRef.current.bottom + buffer) / CONFIG.cellSize);
    const startCellX = 0;
    const endCellX = Math.ceil(dimensions.width / CONFIG.cellSize);

    // Collect stars from all potentially visible cells
    for (let y = startCellY; y <= endCellY; y++) {
      for (let x = startCellX; x < endCellX; x++) {
        const cellKey = `${x},${y}`;
        if (starMapRef.current.has(cellKey)) {
          visibleStars.push(...starMapRef.current.get(cellKey));
        }
      }
    }

    return visibleStars;
  }, [dimensions.height, dimensions.width, CONFIG.cellSize]);

  // Update animation state
  const updateAnimation = useCallback((deltaTime, timestamp) => {
    // Global time factor for animations
    const timeFactor = timestamp * 0.001;

    // Parallax effect from scroll - extremely subtle
    const parallaxOffset = scrollPositionRef.current * CONFIG.parallaxIntensity;

    // Motion dampening for reduced motion preference
    const motionFactor = prefersReducedMotion ? 0.1 : 1;

    // Get all visible stars
    const visibleStars = getVisibleStars();

    // Update stars
    for (let i = 0; i < visibleStars.length; i++) {
      const star = visibleStars[i];

      // Small positional oscillation around initial position
      star.x = star.initialX + Math.sin(timeFactor * 0.2 + star.pulsePhase) * 0.5 * motionFactor;
      star.y = star.initialY + Math.cos(timeFactor * 0.3 + star.pulsePhase * 1.3) * 0.5 * motionFactor;

      // Apply parallax offset based on depth (z) - extremely subtle
      const starParallax = parallaxOffset * (1 - star.z) * 0.5;
      star.parallaxY = starParallax;

      // Subtle pulsing effect - more natural by combining multiple sine waves
      const pulseFactor = (
        Math.sin(timeFactor * star.pulseSpeed + star.pulsePhase) * 0.5 + 
        Math.sin(timeFactor * star.pulseSpeed * 0.4 + star.pulsePhase * 1.3) * 0.3
      ) * 0.15 + 0.85;

      star.opacity = star.baseOpacity * pulseFactor;

      // Vary ray intensity based on pulse
      if (star.rays > 0) {
        star.currentRayIntensity = star.rayIntensity * pulseFactor;
      }
    }
  }, [CONFIG.parallaxIntensity, prefersReducedMotion, getVisibleStars]);

  // Render a more realistic star with optional rays
  const drawStar = useCallback((ctx, star, y) => {
    // Skip stars that are far outside the visible area (accounting for parallax)
    if (y < visibleAreaRef.current.top - 50 || y > visibleAreaRef.current.bottom + 50) {
      return;
    }

    // Draw outer glow for all stars
    const glowRadius = star.size * 2;
    const gradient = ctx.createRadialGradient(star.x, y, 0, star.x, y, glowRadius);
    gradient.addColorStop(0, getColor(star.color, star.opacity * 0.9));
    gradient.addColorStop(0.5, getColor(star.color, star.opacity * 0.3));
    gradient.addColorStop(1, getColor(star.color, 0));

    ctx.beginPath();
    ctx.fillStyle = gradient;
    ctx.arc(star.x, y, glowRadius, 0, Math.PI * 2);
    ctx.fill();

    // Draw bright core
    ctx.beginPath();
    ctx.fillStyle = getColor(star.color, star.opacity);
    ctx.arc(star.x, y, star.size * 0.5, 0, Math.PI * 2);
    ctx.fill();

    // Draw rays for some stars
    if (star.rays > 0 && star.opacity > 0.3) {
      ctx.save();
      ctx.translate(star.x, y);
      ctx.rotate(Math.sin(Date.now() * 0.0001) * 0.05); // Very slight rotation

      for (let i = 0; i < star.rays; i++) {
        const angle = (i / star.rays) * Math.PI * 2;
        const rayLength = star.rayLength * (0.7 + Math.sin(Date.now() * 0.001 + i) * 0.3);

        ctx.beginPath();
        ctx.moveTo(0, 0);
        ctx.lineTo(Math.cos(angle) * rayLength, Math.sin(angle) * rayLength);

        // Create gradient for ray
        const rayGradient = ctx.createLinearGradient(
          0, 0,
          Math.cos(angle) * rayLength, Math.sin(angle) * rayLength
        );
        rayGradient.addColorStop(0, getColor(star.color, star.opacity * 0.9));
        rayGradient.addColorStop(1, getColor(star.color, 0));

        ctx.strokeStyle = rayGradient;
        ctx.lineWidth = star.size * 0.3;
        ctx.stroke();
      }

      ctx.restore();
    }
  }, [getColor]);

  // Render the scene
  const renderScene = useCallback(() => {
    if (!canvasRef.current) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    if (!ctx) return;

    // Apply device pixel ratio scaling
    ctx.setTransform(dimensions.pixelRatio, 0, 0, dimensions.pixelRatio, 0, 0);

    // Clear canvas
    ctx.clearRect(0, 0, dimensions.width, dimensions.height);

    // Draw background gradient
    const gradient = ctx.createLinearGradient(0, 0, 0, dimensions.height);
    gradient.addColorStop(0, COLORS.background.topColor);
    gradient.addColorStop(1, COLORS.background.bottomColor);
    ctx.fillStyle = gradient;
    ctx.fillRect(0, 0, dimensions.width, dimensions.height);

    // Enable global composition for better glow effect
    ctx.globalCompositeOperation = 'lighter';

    // Get all visible stars
    const visibleStars = getVisibleStars();

    // Draw stars
    for (const star of visibleStars) {
      // Skip almost invisible stars
      if (star.opacity < 0.02) continue;

      // Calculate screen position based on scroll
      // Adjust y position relative to canvas (which is fixed to the viewport)
      const screenY = star.y - visibleAreaRef.current.top + (star.parallaxY || 0);

      // Only render stars that are visible in the viewport plus a small buffer
      if (screenY >= -50 && screenY <= dimensions.height + 50) {
        // Draw star with enhanced rendering
        drawStar(ctx, star, screenY);
      }
    }

    // Reset global composition
    ctx.globalCompositeOperation = 'source-over';
  }, [dimensions, COLORS, getVisibleStars, drawStar]);

  // Main animation loop
  const animate = useCallback((timestamp) => {
    if (!isInitialized) {
      animationFrameRef.current = requestAnimationFrame(animate);
      return;
    }

    // Calculate delta time with cap to prevent large jumps
    const deltaTime = lastTimeRef.current ? Math.min(timestamp - lastTimeRef.current, 33) : 16;
    lastTimeRef.current = timestamp;

    // Update animation state
    updateAnimation(deltaTime, timestamp);

    // Render scene
    renderScene();

    // Schedule next frame
    animationFrameRef.current = requestAnimationFrame(animate);
  }, [isInitialized, updateAnimation, renderScene]);

  // Initialize component on mount
  useEffect(() => {
    // Initialize canvas
    const canvasInitialized = setupCanvas();

    if (canvasInitialized) {
      // Set initial viewport
      const viewportHeight = window.innerHeight;
      visibleAreaRef.current = {
        top: window.scrollY,
        bottom: window.scrollY + viewportHeight
      };

      // Update document height
      updateDocumentHeight();

      // Generate initial stars
      generateStarsForVisibleArea();

      setIsInitialized(true);
    }
  }, [setupCanvas, updateDocumentHeight, generateStarsForVisibleArea]);

  // Start animation loop
  useEffect(() => {
    if (!isInitialized) return;

    // Start animation
    animationFrameRef.current = requestAnimationFrame(animate);

    // Cleanup
    return () => {
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }
    };
  }, [isInitialized, animate]);

  return (
    <div
      ref={containerRef}
      style={{
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',  // Will be updated to full document height
        overflow: 'hidden',
        zIndex: -1,
        pointerEvents: 'none'
      }}
      aria-hidden="true"
    >
      <canvas
        ref={canvasRef}
        style={{
          position: 'fixed',  // Canvas stays fixed but renders content based on scroll
          top: 0,
          left: 0,
          width: '100%',
          height: '100%'
        }}
      />
    </div>
  );
};

export default Background;
