import React, { useState, useRef, useCallback } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import './landingNavigation.css';

// Standardized animation variants
const expandableContainerVariants = {
  hidden: { 
    opacity: 0, 
    height: 0,
    overflow: 'hidden'
  },
  visible: { 
    opacity: 1, 
    height: 'auto',
    overflow: 'visible',
    transition: {
      height: { duration: 0.3, ease: "easeOut" },
      opacity: { duration: 0.3, ease: "easeOut" },
      overflow: { delay: 0.25 },
      staggerChildren: 0.07,
      delayChildren: 0.05
    }
  },
  exit: { 
    opacity: 0, 
    height: 0,
    overflow: 'hidden',
    transition: {
      height: { duration: 0.25, ease: "easeIn" },
      opacity: { duration: 0.25, ease: "easeIn" }
    }
  }
};

// Consistent item animation variants for all levels
const itemVariants = {
  hidden: { opacity: 0, x: -10 },
  visible: { 
    opacity: 1, 
    x: 0,
    transition: { duration: 0.25, ease: "easeOut" }
  }
};

// Reusable ExpandableItem component for consistent behavior
const ExpandableItem = ({ 
  item, 
  level = 'section', 
  isExpanded = false,
  isActive = false,
  onClick,
  renderContent,
  renderExpandedContent,
  animationLock
}) => {
  const className = `${level}-row ${isActive ? 'active' : ''} ${isExpanded ? 'expanded' : ''}`;

  return (
    <motion.div 
      className={`${level}-wrapper ${isExpanded ? `expanded-${level}` : ''}`}
      variants={itemVariants}
    >
      <div 
        className={className}
        onClick={onClick}
      >
        {renderContent(item, isExpanded)}
      </div>

      {item.expandable && (
        <div className={`${level}s-outer-container`}>
          <AnimatePresence mode="wait">
            {isExpanded && (
              <motion.div 
                className={`${level}s-container`}
                key={`${level}-${item.id}-expanded`}
                variants={expandableContainerVariants}
                initial="hidden"
                animate="visible"
                exit="exit"
              >
                {renderExpandedContent(item)}
              </motion.div>
            )}
          </AnimatePresence>
        </div>
      )}
    </motion.div>
  );
};

const LandingNavigation = ({ mainSections }) => {
  const [activeSection, setActiveSection] = useState(null);
  const [expandedSection, setExpandedSection] = useState(null);
  const [expandedSubsection, setExpandedSubsection] = useState(null);
  const [isAnimating, setIsAnimating] = useState(false);
  const animationTimeoutRef = useRef(null);
  const sectionRefs = useRef({});

  // Clear any pending timeouts to prevent animation conflicts
  const clearPendingAnimations = useCallback(() => {
    if (animationTimeoutRef.current) {
      clearTimeout(animationTimeoutRef.current);
      animationTimeoutRef.current = null;
    }
  }, []);

  // Unified expand/collapse handler for all navigation levels
  const handleExpandToggle = useCallback((id, currentExpanded, setExpanded, clearChildExpanded = null, event) => {
    event.stopPropagation();

    if (isAnimating) return;

    clearPendingAnimations();
    setIsAnimating(true);

    // Common animation timing for all levels
    setTimeout(() => {
      if (currentExpanded === id) {
        setExpanded(null);
        if (clearChildExpanded) clearChildExpanded(null);
      } else {
        setExpanded(id);
        if (clearChildExpanded) clearChildExpanded(null);
      }

      animationTimeoutRef.current = setTimeout(() => {
        setIsAnimating(false);
      }, 400); // Consistent release time for animation lock
    }, 10);
  }, [isAnimating, clearPendingAnimations]);

  // Handle navigation
  const handleNavigate = (path, event) => {
    event.stopPropagation();
    console.log(`Navigating to: ${path}`);
    // In a real app: window.location.href = path; or history.push(path);
  };

  // Render SVG icons for main sections (unchanged)
  const renderIcon = (iconType) => {
    switch(iconType) {
      case 'circles':
        return (
          <div className="icon-container">
            <svg width="42" height="42" viewBox="0 0 42 42" fill="none" xmlns="http://www.w3.org/2000/svg">
              <circle cx="21" cy="21" r="14" stroke="#A08E61" strokeWidth="1.5" opacity="0.9" />
              <circle cx="16" cy="21" r="9" stroke="#A08E61" strokeWidth="1.5" opacity="0.7" />
              <circle cx="26" cy="21" r="9" stroke="#A08E61" strokeWidth="1.5" opacity="0.7" />
            </svg>
          </div>
        );
      case 'triangle':
        return (
          <div className="icon-container">
            <svg width="42" height="42" viewBox="0 0 42 42" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M21 7L35 32H7L21 7Z" stroke="#A08E61" strokeWidth="1.5" opacity="0.9" />
            </svg>
          </div>
        );
      case 'targetCircle':
        return (
          <div className="icon-container">
            <svg width="42" height="42" viewBox="0 0 42 42" fill="none" xmlns="http://www.w3.org/2000/svg">
              <circle cx="21" cy="21" r="14" stroke="#A08E61" strokeWidth="1.5" opacity="0.9" />
              <circle cx="21" cy="21" r="5" fill="#A08E61" opacity="0.7" />
            </svg>
          </div>
        );
      default:
        return null;
    }
  };

  // Render section content consistently
  const renderSectionContent = (section, isExpanded) => (
    <div className="section-content">
      <div className="section-icon-wrapper">
        {renderIcon(section.icon)}
      </div>
      <div className="section-text">
        <h2 className="section-title">{section.title}</h2>
        <p className="section-description">{section.description}</p>
      </div>
      {section.expandable && (
        <div className={`expand-indicator ${isExpanded ? 'expanded' : ''}`}>
          <svg width="16" height="16" viewBox="0 0 16 16" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M4 6L8 10L12 6" stroke="#A08E61" strokeWidth="1.5" strokeLinecap="round" />
          </svg>
        </div>
      )}
      <div className="diagonal-cut" />
    </div>
  );

  // Render subsection content consistently
  const renderSubsectionContent = (subsection, isExpanded) => (
    <div className="subsection-content">
      <h3 className="subsection-title">{subsection.title}</h3>
      <p className="subsection-description">{subsection.description}</p>
      {subsection.expandable && (
        <div className={`expand-indicator small ${isExpanded ? 'expanded' : ''}`}>
          <svg width="12" height="12" viewBox="0 0 16 16" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M4 6L8 10L12 6" stroke="#A08E61" strokeWidth="1.5" strokeLinecap="round" />
          </svg>
        </div>
      )}
    </div>
  );

  // Render nested subsection content consistently
  const renderNestedSubsectionContent = (nestedSubsection) => (
    <div className="nested-subsection-content">
      <h4 className="nested-subsection-title">{nestedSubsection.title}</h4>
      <p className="nested-subsection-description">{nestedSubsection.description}</p>
    </div>
  );

  return (
    <div className={`sections-container ${isAnimating ? 'animating' : ''}`}>
      {mainSections.map((section, index) => (
        <motion.div 
          key={section.id} 
          className={`section-wrapper ${expandedSection === section.id ? 'expanded-section' : ''}`}
          ref={el => sectionRefs.current[section.id] = el}
          initial={{ opacity: 0, x: -15 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ 
            duration: 0.4, 
            ease: "easeInOut",
            delay: index * 0.15 + 0.3
          }}
        >
          <div 
            className={`section-row ${activeSection === section.id ? 'active' : ''} ${expandedSection === section.id ? 'expanded' : ''}`}
            onMouseEnter={() => setActiveSection(section.id)}
            onMouseLeave={() => setActiveSection(null)}
            onClick={(e) => {
              if (section.expandable) {
                handleExpandToggle(section.id, expandedSection, setExpandedSection, setExpandedSubsection, e);
              } else {
                handleNavigate(section.path, e);
              }
            }}
            style={{ animationDelay: `${index * 0.15 + 0.3}s` }}
          >
            {renderSectionContent(section, expandedSection === section.id)}
          </div>

          {/* Expandable Subsections with standardized animation approach */}
          {section.expandable && (
            <div className="subsections-outer-container">
              <AnimatePresence mode="wait">
                {expandedSection === section.id && (
                  <motion.div 
                    className="subsections-container"
                    key={`section-${section.id}-subsections`}
                    variants={expandableContainerVariants}
                    initial="hidden"
                    animate="visible"
                    exit="exit"
                  >
                    {section.subsections.map((subsection) => (
                      <motion.div 
                        key={subsection.id} 
                        className={`subsection-wrapper ${expandedSubsection === subsection.id ? 'expanded-subsection' : ''}`}
                        variants={itemVariants}
                      >
                        <div 
                          className={`subsection-row ${expandedSubsection === subsection.id ? 'active' : ''}`}
                          onClick={(e) => {
                            if (subsection.expandable) {
                              handleExpandToggle(subsection.id, expandedSubsection, setExpandedSubsection, null, e);
                            } else {
                              handleNavigate(subsection.path, e);
                            }
                          }}
                        >
                          {renderSubsectionContent(subsection, expandedSubsection === subsection.id)}
                        </div>

                        {/* Nested Subsections with same standardized approach */}
                        {subsection.expandable && (
                          <div className="nested-subsections-outer-container">
                            <AnimatePresence mode="wait">
                              {expandedSubsection === subsection.id && (
                                <motion.div 
                                  className="nested-subsections-container"
                                  key={`subsection-${subsection.id}-nested`}
                                  variants={expandableContainerVariants}
                                  initial="hidden"
                                  animate="visible"
                                  exit="exit"
                                >
                                  {subsection.subsections.map((nestedSubsection) => (
                                    <motion.div 
                                      key={nestedSubsection.id}
                                      className="nested-subsection-row"
                                      onClick={(e) => handleNavigate(nestedSubsection.path, e)}
                                      variants={itemVariants}
                                    >
                                      {renderNestedSubsectionContent(nestedSubsection)}
                                    </motion.div>
                                  ))}
                                </motion.div>
                              )}
                            </AnimatePresence>
                          </div>
                        )}
                      </motion.div>
                    ))}
                  </motion.div>
                )}
              </AnimatePresence>
            </div>
          )}
        </motion.div>
      ))}
    </div>
  );
};

export default LandingNavigation;
