import React, { useRef, useEffect, useState, useMemo } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';
import { createUseStyles } from 'react-jss';

// Optimized styles with fewer GPU-forcing properties
const useStyles = createUseStyles({
  animatedContainer: {
    position: 'relative',
    width: '100%',
    textAlign: props => props.textAlign || 'center',
    margin: props => props.margin || '0 0 3rem 0',
    zIndex: 2,
    // Simplified GPU acceleration - only essential properties
    willChange: 'transform',
    transform: 'translateZ(0)'
  },
  content: {
    width: '100%',
  },
  divider: props => ({
    width: props.dividerWidth || '150px',
    height: props.dividerHeight || '1px',
    background: props.dividerBackground || 'linear-gradient(to right, rgba(160, 142, 97, 0), rgba(160, 142, 97, 0.6), rgba(160, 142, 97, 0))',
    margin: props.dividerMargin || '1rem auto',
    display: props.showDivider ? 'block' : 'none'
  })
});

/**
 * ScrollingTextAnimation Component
 * 
 * A highly optimized, butter-smooth scroll-based animation component
 * for text elements with exceptional performance on both mobile and desktop.
 */
const ScrollingTextAnimation = ({
  children,
  scrollConfig = {},
  textAlign = 'center',
  margin = '0 0 3rem 0',
  showDivider = true,
  dividerWidth = '150px',
  dividerHeight = '1px',
  dividerBackground,
  dividerMargin = '1rem auto',
  style = {},
  className = ''
}) => {
  // Set default scroll configuration with performance-optimized defaults
  const {
    startPosition = 0,
    endPosition = 200, // Narrowed animation range for better performance
    initialY = -50,    // Reduced initial offset for smoother animation
    finalY = 0,
    clampValues = true,
    opacityValues = [0, 1],
    opacityScrollPositions = [startPosition, endPosition],
    reverseDirection = false,
    throttleAmount = 16, // ~60fps throttling for balanced performance
    enableReducedMotion = true,
    disableOnMobile = false
  } = scrollConfig;

  const containerRef = useRef(null);

  // Combined state for better performance
  const [state, setState] = useState({
    isInView: false,
    isMobile: false,
    prefersReducedMotion: false
  });

  // Calculate the actual Y values considering direction
  const yInputRange = [startPosition, endPosition];
  const yOutputRange = reverseDirection ? [finalY, initialY] : [initialY, finalY];

  // Setup device and preference detection once
  useEffect(() => {
    // One-time checks on mount
    const isMobileDevice = 
      typeof window !== 'undefined' && 
      (window.innerWidth <= 768 || 
       /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent));

    const prefersReducedMotion = 
      typeof window !== 'undefined' && 
      window.matchMedia('(prefers-reduced-motion: reduce)').matches;

    setState(prev => ({
      ...prev,
      isMobile: isMobileDevice,
      prefersReducedMotion
    }));

    // Optimized resize handler with debounce
    let resizeTimer;
    const handleResize = () => {
      clearTimeout(resizeTimer);
      resizeTimer = setTimeout(() => {
        setState(prev => ({
          ...prev,
          isMobile: window.innerWidth <= 768
        }));
      }, 100); // 100ms debounce
    };

    if (typeof window !== 'undefined') {
      window.addEventListener('resize', handleResize, { passive: true });

      return () => {
        window.removeEventListener('resize', handleResize);
        clearTimeout(resizeTimer);
      };
    }
  }, []);

  // Optimized scroll tracking with throttling
  const { scrollY } = useScroll({
    container: typeof window !== 'undefined' ? window : undefined,
    layoutEffect: false,
    offset: ['start', 'end'],
    throttleDelay: throttleAmount
  });

  // Single transform for better performance - no chaining with useSpring
  const translateY = useTransform(
    scrollY,
    yInputRange,
    yOutputRange,
    { clamp: clampValues }
  );

  // Single opacity transform
  const opacity = useTransform(
    scrollY,
    opacityScrollPositions,
    opacityValues,
    { clamp: clampValues }
  );

  // Optimized intersection observer setup - only runs once
  useEffect(() => {
    if (!containerRef.current || typeof IntersectionObserver === 'undefined') return;

    const currentRef = containerRef.current;

    const observer = new IntersectionObserver(
      entries => {
        if (entries[0].isIntersecting) {
          setState(prev => ({ ...prev, isInView: true }));
          // Once element is in view, we can disconnect the observer
          observer.disconnect();
        }
      },
      {
        root: null,
        rootMargin: '50px 0px', // Smaller margin for more precise triggering
        threshold: 0.1
      }
    );

    observer.observe(currentRef);

    return () => {
      observer.disconnect();
    };
  }, []);

  // Determine if we should use simplified animations - memoized
  const shouldUseSimplifiedAnimations = useMemo(() => 
    (enableReducedMotion && state.prefersReducedMotion) || 
    (disableOnMobile && state.isMobile),
  [enableReducedMotion, disableOnMobile, state.prefersReducedMotion, state.isMobile]);

  // Create styles for component
  const styleProps = {
    textAlign,
    margin,
    showDivider,
    dividerWidth,
    dividerHeight,
    dividerBackground,
    dividerMargin
  };

  // Memoized styles to prevent recreation on every render
  const classes = useMemo(() => useStyles(styleProps), [
    textAlign,
    margin,
    showDivider,
    dividerWidth,
    dividerHeight,
    dividerBackground,
    dividerMargin
  ]);

  // Precompute animation styles based on current state - memoized to avoid recalculation
  const animationStyle = useMemo(() => {
    if (!state.isInView) {
      return {
        y: reverseDirection ? finalY : initialY,
        opacity: opacityValues[0],
      };
    }

    if (shouldUseSimplifiedAnimations) {
      return {
        y: finalY,
        opacity: 1,
        transition: {
          duration: 0.5,
          ease: [0.25, 0.1, 0.25, 1.0],
        }
      };
    }

    return {
      y: translateY,
      opacity: opacity,
    };
  }, [
    state.isInView, 
    shouldUseSimplifiedAnimations, 
    translateY, 
    opacity, 
    reverseDirection, 
    finalY, 
    initialY, 
    opacityValues
  ]);

  return (
    <motion.div 
      ref={containerRef}
      className={`${classes.animatedContainer} ${className}`}
      style={{ ...style }}
      animate={animationStyle}
      initial={false}
    >
      <div className={classes.content}>
        {children}
      </div>
      {showDivider && <div className={classes.divider} />}
    </motion.div>
  );
};

export default ScrollingTextAnimation;
