import React, { useState, useEffect, useRef } from 'react';
import './introSequence.css';

const IntroSequence = ({ showIntro, setShowIntro, setIsLoaded, quotes }) => {
  const moonCanvasRef = useRef(null);
  const moonAnimationRef = useRef(null);
  const [quoteVisible, setQuoteVisible] = useState(false);
  const [currentQuote, setCurrentQuote] = useState(null);
  const [moonDrawn, setMoonDrawn] = useState(0); // 0: not started, 1: drawing, 2: complete



  // Setup intro sequence
  useEffect(() => {
    if (showIntro) {
      // Select a random quote
      const randomQuote = quotes[Math.floor(Math.random() * quotes.length)];
      setCurrentQuote(randomQuote);

      // Fast 3-second intro sequence
      setTimeout(() => setMoonDrawn(1), 200); // Start drawing moon quickly
      setTimeout(() => setQuoteVisible(true), 400); // Show quote almost immediately
      setTimeout(() => setMoonDrawn(2), 2500); // Complete moon animation at 2.5s
      setTimeout(() => {
        setQuoteVisible(false);
        // Start fade out of intro
        const introElement = document.querySelector('.intro-sequence');
        if (introElement) introElement.classList.add('fade-out');
      }, 2600);
      setTimeout(() => {
        setShowIntro(false);
        setIsLoaded(true);
      }, 3000); // Complete entire sequence at 3s
    }
  }, [showIntro, quotes, setShowIntro, setIsLoaded]);

  // Vector moon animation for intro
  useEffect(() => {
    if (!moonCanvasRef.current || moonDrawn === 0) return;

    const canvas = moonCanvasRef.current;
    const ctx = canvas.getContext('2d');
    const centerX = canvas.width / 2;
    const centerY = canvas.height / 2;
    const radius = 70;
    let angle = 0;
    let direction = 1; // 1 for drawing, -1 for undoing
    let lastTimestamp = 0;

    // Clear canvas
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    const drawMoonAnimation = (timestamp) => {
      if (!lastTimestamp) lastTimestamp = timestamp;
      const deltaTime = timestamp - lastTimestamp;
      lastTimestamp = timestamp;

      // Speed control - faster animation to complete in ~2.3 seconds
      const speed = 1.3 * deltaTime / 16.67; // Significantly faster

      angle += speed * direction;

      // Clear canvas
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Draw moon outline
      ctx.beginPath();
      ctx.arc(centerX, centerY, radius, 0, angle * Math.PI / 180, false);
      ctx.strokeStyle = 'rgba(191, 173, 127, 0.7)';
      ctx.lineWidth = 1.5;
      ctx.stroke();

      // Add subtle glow
      ctx.shadowColor = 'rgba(191, 173, 127, 0.3)';
      ctx.shadowBlur = 8;

      // Reverse direction when full circle is drawn
      if (angle >= 360 && direction === 1) {
        direction = -1; // Start undoing
      }

      // Stop when animation is complete or when we reach moonDrawn state 2
      if ((angle <= 0 && direction === -1) || moonDrawn === 2) {
        cancelAnimationFrame(moonAnimationRef.current);
        return;
      }

      moonAnimationRef.current = requestAnimationFrame(drawMoonAnimation);
    };

    if (moonDrawn === 1) {
      moonAnimationRef.current = requestAnimationFrame(drawMoonAnimation);
    }

    return () => {
      if (moonAnimationRef.current) {
        cancelAnimationFrame(moonAnimationRef.current);
      }
    };
  }, [moonDrawn]);

  // Skip intro
  const handleSkipIntro = () => {
    setShowIntro(false);
    setIsLoaded(true);
  };

  // Format quote with line breaks
  const formatQuote = (text) => {
    return text.split('\n').map((line, i) => (
      <span key={i} className="quote-line">
        {line}
        {i < text.split('\n').length - 1 && <br />}
      </span>
    ));
  };

  if (!showIntro) return null;

  return (
    <div className="intro-sequence" onClick={handleSkipIntro}>
      <div className="moon-animation">
        <canvas 
          ref={moonCanvasRef} 
          width="200" 
          height="200"
          className="moon-canvas"
        />
      </div>

      <div className={`quote-container ${quoteVisible ? 'visible' : ''}`}>
        <div className="quote-text">
          {currentQuote && formatQuote(currentQuote.text)}
        </div>
        <div className="quote-author">
          {currentQuote && currentQuote.author}
        </div>
      </div>

      <div className="skip-intro">tap to skip</div>
    </div>
  );
};

export default IntroSequence;
