import React, { useRef, useState, useEffect, useCallback } from 'react';
import { motion, useInView } from 'framer-motion';
import CometBorderEffect from '../bordereffects/CometBorderEffect';

/**
 * PersonProfileCard Component
 * 
 * A sophisticated profile card with a three-phase scroll behavior:
 * 1. Normal Flow: Initially scrolls with the page
 * 2. Fixed Position: Sticks to viewport when scrolling through content
 * 3. Release: Returns to normal flow after scrolling past component
 * 
 * @param {Object} props - Component props
 * @param {Object} props.person - Person data (name, role, image, bio, stats, etc.)
 * @param {Object} props.animationConfig - Animation configuration
 * @param {Object} props.cometConfig - Comet animation configuration
 * @param {Object} props.imagePosition - Image position configuration
 * @param {Array} props.additionalSections - Additional content sections
 * @param {Function} props.onSectionChange - Callback when active section changes
 * @param {number} props.topOffset - Offset from top for sticky position
 * @param {boolean} props.showProfileImage - Whether to show profile image
 * @param {boolean} props.allowImageToggle - Whether to show image toggle button
 * @param {string} props.highlightColor - Color for active/hover elements
 * @param {string} props.textColor - Default text color
 * @param {number} props.minLineWidth - Minimum width for nav indicator lines
 * @param {number} props.maxLineWidth - Maximum width for nav indicator lines
 * @param {string} props.fontFamily - Font family for text elements
 * @param {Object} props.navigationLabels - Custom navigation section labels
 * @param {string} props.navigationLabels.about - Label for about section
 * @param {string} props.navigationLabels.experience - Label for experience section
 * @param {string} props.navigationLabels.projects - Label for projects section
 */
const PersonProfileCard = ({
  person,
  animationConfig = {
    threshold: 0.2,
    once: true,
    initialY: 30,
    duration: 0.8
  },
  cometConfig = {
    size: 1.5,
    trailLength: 100,
    speed: 0.001,
    targetFPS: 30,
    respectReducedMotion: true
  },
  imagePosition = {
    x: 50,
    y: 50,
    scale: 1
  },
  additionalSections = [],
  onSectionChange = null,
  topOffset = 100,
  showProfileImage = true,
  allowImageToggle = true,
  highlightColor = '#bfad7f',
  textColor = 'rgba(224, 224, 224, 0.7)',
  minLineWidth = 10,
  maxLineWidth = 20,
  fontFamily = '"Garamond", "Adobe Caslon Pro", serif',
  navigationLabels = {
    about: 'ABOUT',
    experience: 'EXPERIENCE', 
    projects: 'PROJECTS'
  }
}) => {
  // State management
  const [isHovered, setIsHovered] = useState(false);
  const [activeSection, setActiveSection] = useState('about');
  const [showImageState, setShowImageState] = useState(showProfileImage);
  const [expandedNavItem, setExpandedNavItem] = useState(null);

  // Refs for DOM elements
  const containerRef = useRef(null);
  const sidebarRef = useRef(null);
  const wrapperRef = useRef(null);
  const contentRef = useRef(null);

  // Refs for sections
  const aboutRef = useRef(null);
  const experienceRef = useRef(null);
  const projectsRef = useRef(null);

  // Map of section IDs to refs for easier navigation
  const sectionRefs = {
    about: aboutRef,
    experience: experienceRef,
    projects: projectsRef
  };

  // Animation inView detection
  const isInView = useInView(containerRef, {
    threshold: animationConfig?.threshold ?? 0.2,
    once: animationConfig?.once ?? true
  });

  // Mouse event handlers for profile image hover effect
  const handleMouseEnter = useCallback(() => {
    setIsHovered(true);
  }, []);

  const handleMouseLeave = useCallback(() => {
    setIsHovered(false);
  }, []);

  // Toggle profile image visibility
  const toggleProfileImage = useCallback(() => {
    setShowImageState(prev => !prev);
  }, []);

  // Handle navigation item hover
  const handleNavItemHover = useCallback((section) => {
    setExpandedNavItem(section);
  }, []);

  const handleNavItemLeave = useCallback(() => {
    setExpandedNavItem(null);
  }, []);

  // Scroll to section handler
  const scrollToSection = useCallback((sectionId) => {
    setActiveSection(sectionId);

    // Notify parent component if callback provided
    if (onSectionChange) {
      onSectionChange(sectionId);
    }

    // Scroll to section with smooth behavior
    const sectionRef = sectionRefs[sectionId];
    if (sectionRef?.current) {
      sectionRef.current.scrollIntoView({ 
        behavior: 'smooth',
        block: 'start'
      });
    }
  }, [onSectionChange, sectionRefs]);

  // Set up intersection observer to detect which section is in view
  useEffect(() => {
    const sectionElements = Object.values(sectionRefs)
      .map(ref => ref.current)
      .filter(Boolean);

    if (sectionElements.length === 0) return;

    const options = {
      root: null,
      rootMargin: '-10% 0px -70% 0px', // Consider element in view when in the top 30% of viewport
      threshold: 0
    };

    const callback = (entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          const sectionId = entry.target.getAttribute('data-section');
          if (sectionId && sectionId !== activeSection) {
            setActiveSection(sectionId);

            // Notify parent component if callback provided
            if (onSectionChange) {
              onSectionChange(sectionId);
            }
          }
        }
      });
    };

    const observer = new IntersectionObserver(callback, options);

    // Observe all section refs
    sectionElements.forEach(element => observer.observe(element));

    return () => {
      observer.disconnect();
    };
  }, [activeSection, onSectionChange, sectionRefs]);

  // Implement the three-phase scrolling behavior
  useEffect(() => {
    const elements = {
      sidebar: sidebarRef.current,
      wrapper: wrapperRef.current,
      container: containerRef.current,
      content: contentRef.current
    };

    // Ensure all required elements exist
    if (!elements.sidebar || !elements.wrapper || !elements.container || !elements.content) return;

    // Skip this behavior on mobile
    const checkIsMobile = () => window.innerWidth <= 768;
    let isMobile = checkIsMobile();

    const handleScroll = () => {
      // Skip behavior if on mobile
      if (isMobile) return;

      // Get element dimensions and positions
      const containerRect = elements.container.getBoundingClientRect();
      const wrapperRect = elements.wrapper.getBoundingClientRect();
      const sidebarHeight = elements.sidebar.offsetHeight;

      // Calculate phase transition points
      const startFixPoint = wrapperRect.top <= topOffset;
      const stopFixPoint = containerRect.bottom <= (sidebarHeight + topOffset);

      // Reset all position styles before applying new ones
      elements.sidebar.style.position = '';
      elements.sidebar.style.top = '';
      elements.sidebar.style.bottom = '';

      // Apply appropriate styles based on scroll phase
      if (!startFixPoint) {
        // Phase 1: Normal flow - sidebar scrolls with the page
        elements.sidebar.style.position = 'relative';
        elements.sidebar.style.top = '0';
      } else if (startFixPoint && !stopFixPoint) {
        // Phase 2: Fixed position - sidebar sticks to the viewport
        elements.sidebar.style.position = 'fixed';
        elements.sidebar.style.top = `${topOffset}px`;
      } else if (stopFixPoint) {
        // Phase 3: End position - sidebar moves with page again, but from bottom
        elements.sidebar.style.position = 'absolute';
        elements.sidebar.style.top = 'auto';
        elements.sidebar.style.bottom = '0';
      }
    };

    // Handle window resize and check if mobile
    const handleResize = () => {
      const wasMobile = isMobile;
      isMobile = checkIsMobile();

      // Reset styles if switching between mobile and desktop
      if (wasMobile !== isMobile) {
        if (isMobile) {
          // Reset styles for mobile
          elements.sidebar.style.position = '';
          elements.sidebar.style.top = '';
          elements.sidebar.style.bottom = '';
        } else {
          // Re-apply scroll behavior for desktop
          handleScroll();
        }
      }
    };

    // Add event listeners
    window.addEventListener('scroll', handleScroll, { passive: true });
    window.addEventListener('resize', handleResize);

    // Initial setup
    handleResize();
    handleScroll();

    // Cleanup event listeners on unmount
    return () => {
      window.removeEventListener('scroll', handleScroll);
      window.removeEventListener('resize', handleResize);
    };
  }, [topOffset]);

  return (
    <motion.div 
      ref={containerRef}
      className="profile-container"
      initial={{ opacity: 0, y: animationConfig?.initialY ?? 30 }}
      animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: animationConfig?.initialY ?? 30 }}
      transition={{ 
        duration: animationConfig?.duration ?? 0.8, 
        ease: "easeOut" 
      }}
      style={{
        display: 'flex',
        flexDirection: 'row',
        width: '100%',
        maxWidth: '1300px',
        margin: '0 auto',
        minHeight: '70vh',
        position: 'relative',
      }}
    >
      {/* Left sidebar wrapper */}
      <div 
        ref={wrapperRef}
        className="sidebar-wrapper"
        style={{
          width: '40%',
          position: 'relative',
        }}
      >
        {/* Profile sidebar with dynamic positioning handled by JS */}
        <div 
          ref={sidebarRef}
          className="profile-sidebar"
          style={{
            padding: '3rem 2rem 2rem 0',
            display: 'flex',
            flexDirection: 'column',
            width: '100%',
            position: 'relative',
            top: 0,
          }}
        >
          {/* Toggle profile image button - only shown when allowImageToggle is true */}
          {allowImageToggle && (
            <button 
              onClick={toggleProfileImage}
              className="toggle-profile-image"
              style={{
                position: 'absolute',
                top: '10px',
                right: '10px',
                background: 'transparent',
                color: highlightColor,
                border: `1px solid ${highlightColor}`,
                borderRadius: '4px',
                padding: '5px 10px',
                cursor: 'pointer',
                fontSize: '0.8rem',
                transition: 'all 0.3s ease',
                zIndex: 5,
                opacity: 0.7
              }}
              onMouseEnter={(e) => {
                e.currentTarget.style.backgroundColor = `${highlightColor}20`;
                e.currentTarget.style.opacity = '1';
              }}
              onMouseLeave={(e) => {
                e.currentTarget.style.backgroundColor = 'transparent';
                e.currentTarget.style.opacity = '0.7';
              }}
            >
              {showImageState ? 'Hide Image' : 'Show Image'}
            </button>
          )}

          {/* Profile image with comet animation - conditionally rendered */}
          {showImageState && (
            <div 
              className="profile-image-container"
              style={{
                position: 'relative',
                width: '240px',
                height: '240px',
                borderRadius: '50%',
                boxShadow: '0 5px 15px rgba(0, 0, 0, 0.3)',
                transition: 'box-shadow 0.3s ease, transform 0.3s ease',
                overflow: 'hidden',
                boxSizing: 'border-box',
                marginBottom: '2rem',
                border: '1px solid rgba(191, 173, 127, 0.2)',
                transform: isHovered ? 'scale(1.02)' : 'scale(1)',
              }}
              onMouseEnter={handleMouseEnter}
              onMouseLeave={handleMouseLeave}
            >
              {/* Comet animation around the border */}
              <CometBorderEffect 
                isHovered={isHovered}
                size={cometConfig.size}
                trailLength={cometConfig.trailLength}
                speed={cometConfig.speed}
                hoverSpeedMultiplier={cometConfig.hoverSpeedMultiplier || 2}
                trailSegments={cometConfig.trailSegments || 25}
                glowIntensity={cometConfig.glowIntensity || 0.8}
                targetFPS={cometConfig.targetFPS || 30}
                respectReducedMotion={cometConfig.respectReducedMotion || true}
                paddingTop={20}
                paddingRight={20}
                paddingBottom={20}
                paddingLeft={20}
              />

              <div 
                className="profile-image"
                style={{
                  position: 'absolute',
                  top: 0,
                  left: 0,
                  width: '100%',
                  height: '100%',
                  borderRadius: '50%',
                  overflow: 'hidden',
                }}
              >
                <div 
                  className="image-wrapper"
                  style={{
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    width: '100%',
                    height: '100%',
                    overflow: 'hidden',
                    borderRadius: '50%'
                  }}
                >
                  <img 
                    src={person.image} 
                    alt={person.name} 
                    style={{
                      width: '100%',
                      height: '100%',
                      objectFit: 'cover',
                      transition: 'transform 0.6s ease, object-position 0.6s ease',
                      display: 'block',
                      objectPosition: `${imagePosition?.x ?? 50}% ${imagePosition?.y ?? 50}%`,
                      transform: `scale(${imagePosition?.scale ?? 1})`
                    }}
                    loading="lazy"
                  />
                </div>
              </div>
            </div>
          )}

          {/* Profile information */}
          <div 
            className="profile-info"
            style={{
              marginBottom: '2rem',
              paddingLeft: '5px',
            }}
          >
            <h1 
              className="profile-name"
              style={{
                fontSize: '2.8rem',
                fontWeight: '300',
                color: highlightColor,
                letterSpacing: '0.05em',
                marginBottom: '0.5rem',
                transition: 'transform 0.3s ease',
                transform: isHovered ? 'translateY(-3px)' : 'none',
              }}
            >
              {person.name}
            </h1>

            <h2 
              className="profile-role"
              style={{
                fontSize: '1.2rem',
                fontWeight: '300',
                marginBottom: '2rem',
                color: `${highlightColor}B3`, // 70% opacity
                letterSpacing: '0.05em',
                fontFamily: fontFamily,
                fontStyle: 'italic',
              }}
            >
              {person.role}
            </h2>

            {/* Short tagline */}
            {person.tagline && (
              <p 
                className="profile-tagline"
                style={{
                  fontSize: '1.1rem',
                  lineHeight: '1.6',
                  marginBottom: '3rem',
                  maxWidth: '90%',
                  color: textColor,
                  fontFamily: fontFamily,
                  fontWeight: '300',
                }}
              >
                {person.tagline}
              </p>
            )}
          </div>

          {/* Navigation links with fluid expansion on hover */}
          <div className="nav-links" style={{ marginTop: '2rem' }}>
            {/* About link */}
            <div 
              className="nav-link-container"
              style={{
                position: 'relative',
                marginBottom: '1rem',
                display: 'flex',
                alignItems: 'center',
              }}
              onMouseEnter={() => handleNavItemHover('about')}
              onMouseLeave={handleNavItemLeave}
            >
              <button 
                className="nav-link"
                onClick={() => scrollToSection('about')}
                style={{
                  position: 'relative',
                  display: 'block',
                  padding: '0.5rem 0 0.5rem 25px',
                  fontSize: '0.85rem',
                  letterSpacing: '0.1em',
                  background: 'transparent',
                  border: 'none',
                  textAlign: 'left',
                  transition: 'color 0.3s, transform 0.3s',
                  color: activeSection === 'about' ? '#bfad7f' : 'rgba(224, 224, 224, 0.7)',
                  cursor: 'pointer',
                  transform: activeSection === 'about' || expandedNavItem === 'about' ? 'translateX(3px)' : 'none',
                }}
              >
                ABOUT
              </button>
              <div 
                className="nav-line"
                style={{
                  position: 'absolute',
                  left: '0',
                  top: '50%',
                  transform: 'translateY(-50%)',
                  width: activeSection === 'about' || expandedNavItem === 'about' ? `${maxLineWidth}px` : `${minLineWidth}px`,
                  height: '2px',
                  backgroundColor: activeSection === 'about' || expandedNavItem === 'about'
                    ? `${highlightColor}E6` // 90% opacity 
                    : `${highlightColor}80`, // 50% opacity
                  transition: 'width 0.3s ease, background-color 0.3s ease',
                }}
              ></div>
            </div>

            {/* Experience link */}
            <div 
              className="nav-link-container"
              style={{
                position: 'relative',
                marginBottom: '1rem',
                display: 'flex',
                alignItems: 'center',
              }}
              onMouseEnter={() => handleNavItemHover('experience')}
              onMouseLeave={handleNavItemLeave}
            >
              <button
                className="nav-link"
                onClick={() => scrollToSection('experience')}
                style={{
                  position: 'relative',
                  display: 'block',
                  padding: '0.5rem 0 0.5rem 25px',
                  fontSize: '0.85rem',
                  letterSpacing: '0.1em',
                  background: 'transparent',
                  border: 'none',
                  textAlign: 'left',
                  transition: 'color 0.3s, transform 0.3s',
                  color: activeSection === 'experience' ? '#bfad7f' : 'rgba(224, 224, 224, 0.7)',
                  cursor: 'pointer',
                  transform: activeSection === 'experience' || expandedNavItem === 'experience' ? 'translateX(3px)' : 'none',
                }}
              >
                EXPERIENCE
              </button>
              <div 
                className="nav-line"
                style={{
                  position: 'absolute',
                  left: '0',
                  top: '50%',
                  transform: 'translateY(-50%)',
                  width: activeSection === 'experience' || expandedNavItem === 'experience' ? `${maxLineWidth}px` : `${minLineWidth}px`,
                  height: '2px',
                  backgroundColor: activeSection === 'experience' || expandedNavItem === 'experience'
                    ? `${highlightColor}E6` // 90% opacity 
                    : `${highlightColor}80`, // 50% opacity
                  transition: 'width 0.3s ease, background-color 0.3s ease',
                }}
              ></div>
            </div>

            {/* Projects link */}
            <div 
              className="nav-link-container"
              style={{
                position: 'relative',
                marginBottom: '1rem',
                display: 'flex',
                alignItems: 'center',
              }}
              onMouseEnter={() => handleNavItemHover('projects')}
              onMouseLeave={handleNavItemLeave}
            >
              <button
                className="nav-link"
                onClick={() => scrollToSection('projects')}
                style={{
                  position: 'relative',
                  display: 'block',
                  padding: '0.5rem 0 0.5rem 25px',
                  fontSize: '0.85rem',
                  letterSpacing: '0.1em',
                  background: 'transparent',
                  border: 'none',
                  textAlign: 'left',
                  transition: 'color 0.3s, transform 0.3s',
                  color: activeSection === 'projects' ? '#bfad7f' : 'rgba(224, 224, 224, 0.7)',
                  cursor: 'pointer',
                  transform: activeSection === 'projects' || expandedNavItem === 'projects' ? 'translateX(3px)' : 'none',
                }}
              >
                PROJECTS
              </button>
              <div 
                className="nav-line"
                style={{
                  position: 'absolute',
                  left: '0',
                  top: '50%',
                  transform: 'translateY(-50%)',
                  width: activeSection === 'projects' || expandedNavItem === 'projects' ? `${maxLineWidth}px` : `${minLineWidth}px`,
                  height: '2px',
                  backgroundColor: activeSection === 'projects' || expandedNavItem === 'projects'
                    ? `${highlightColor}E6` // 90% opacity 
                    : `${highlightColor}80`, // 50% opacity
                  transition: 'width 0.3s ease, background-color 0.3s ease',
                }}
              ></div>
            </div>
          </div>
        </div>
      </div>

      {/* Right content section */}
      <div 
        ref={contentRef}
        className="content-section"
        style={{
          width: '60%',
          padding: '3rem 0 2rem 2rem',
          marginLeft: 'auto',
        }}
      >
        {/* About section */}
        <div 
          ref={aboutRef} 
          data-section="about"
          className="section"
          id="about"
          style={{
            marginBottom: '3rem',
            scrollMarginTop: '2rem',
          }}
        >
          {person.bio && person.bio.map((paragraph, index) => (
            <p 
              key={index} 
              className="section-content"
              style={{
                fontSize: '1rem',
                lineHeight: '1.8',
                marginBottom: '1.5rem',
                color: textColor,
                fontFamily: fontFamily,
                fontWeight: '300',
              }}
            >
              {paragraph}
            </p>
          ))}

          {/* Stats display */}
          {person.stats && person.stats.length > 0 && (
            <div 
              className="stats-container"
              style={{
                display: 'flex',
                justifyContent: 'space-between',
                flexWrap: 'wrap',
                marginTop: '3rem',
                width: '100%',
              }}
            >
              {person.stats.map((stat, index) => (
                <div 
                  key={index} 
                  className="stat"
                  style={{
                    flex: '1',
                    textAlign: 'center',
                    padding: '0 1rem',
                    minWidth: '100px',
                    transition: 'transform 0.3s ease',
                  }}
                  onMouseEnter={(e) => {
                    e.currentTarget.style.transform = 'translateY(-5px)';
                  }}
                  onMouseLeave={(e) => {
                    e.currentTarget.style.transform = 'none';
                  }}
                >
                  <div 
                    className="stat-value"
                    style={{
                      fontSize: '2.5rem',
                      fontWeight: '100',
                      color: highlightColor,
                      marginBottom: '0.5rem'
                    }}
                  >
                    {stat.value}
                  </div>
                  <div 
                    className="stat-label"
                    style={{
                      fontSize: '0.85rem',
                      color: `${textColor}CC`, // 80% opacity
                      textTransform: 'uppercase',
                      letterSpacing: '0.1em'
                    }}
                  >
                    {stat.label}
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Experience section */}
        <div 
          ref={experienceRef}
          data-section="experience"
          className="section"
          id="experience"
          style={{
            marginBottom: '3rem',
            scrollMarginTop: '2rem',
          }}
        >
          {person.experience ? (
            person.experience.map((item, index) => (
              <p 
                key={index} 
                className="section-content"
                style={{
                  fontSize: '1rem',
                  lineHeight: '1.8',
                  marginBottom: '1.5rem',
                  color: 'rgba(224, 224, 224, 0.7)',
                  fontFamily: fontFamily,
                  fontWeight: '300',
                }}
              >
                {item.content}
              </p>
            ))
          ) : (
            <p 
              className="section-content"
              style={{
                fontSize: '1rem',
                lineHeight: '1.8',
                marginBottom: '1.5rem',
                color: 'rgba(224, 224, 224, 0.7)',
                fontFamily: '"Garamond", "Adobe Caslon Pro", serif',
                fontWeight: '300',
              }}
            >
              Throughout my career, I've specialized in developing software systems that seamlessly 
              integrate front-end experiences with robust back-end architectures. My experience spans 
              various domains, from interactive media to data visualization systems.
            </p>
          )}
        </div>

        {/* Projects section */}
        <div 
          ref={projectsRef}
          data-section="projects"
          className="section"
          id="projects"
          style={{
            marginBottom: '3rem',
            scrollMarginTop: '2rem',
          }}
        >
          {person.projects ? (
            person.projects.map((item, index) => (
              <p 
                key={index} 
                className="section-content"
                style={{
                  fontSize: '1rem',
                  lineHeight: '1.8',
                  marginBottom: '1.5rem',
                  color: 'rgba(224, 224, 224, 0.7)',
                  fontFamily: '"Garamond", "Adobe Caslon Pro", serif',
                  fontWeight: '300',
                }}
              >
                {item.content}
              </p>
            ))
          ) : (
            <p 
              className="section-content"
              style={{
                fontSize: '1rem',
                lineHeight: '1.8',
                marginBottom: '1.5rem',
                color: 'rgba(224, 224, 224, 0.7)',
                fontFamily: '"Garamond", "Adobe Caslon Pro", serif',
                fontWeight: '300',
              }}
            >
              My personal projects merge the scientific principles of evolution and adaptation with 
              mythological themes of rebirth and renewal. I create 'things' that are both mechanically 
              sound and narratively compelling, allowing users to explore complex themes through 
              immersive experiences.
            </p>
          )}
        </div>

        {/* Dynamic sections from props */}
        {additionalSections.length > 0 && (
          additionalSections.map((section, index) => (
            <div 
              key={index} 
              className="section"
              style={{
                marginBottom: '3rem',
                scrollMarginTop: '2rem',
              }}
            >
              {section.title && (
                <h3 style={{
                  fontSize: '1.4rem',
                  color: highlightColor,
                  marginBottom: '1rem',
                  fontWeight: '300',
                }}>
                  {section.title}
                </h3>
              )}
              {section.content && (
                typeof section.content === 'string' 
                  ? <p 
                      className="section-content"
                      style={{
                        fontSize: '1rem',
                        lineHeight: '1.8',
                        marginBottom: '1.5rem',
                        color: 'rgba(224, 224, 224, 0.7)',
                        fontFamily: '"Garamond", "Adobe Caslon Pro", serif',
                        fontWeight: '300',
                      }}
                    >
                      {section.content}
                    </p>
                  : section.content
              )}
            </div>
          ))
        )}
      </div>

      {/* Media query styles for mobile */}
      <style jsx>{`
        @media (max-width: 768px) {
          .profile-container {
            flex-direction: column;
          }

          .sidebar-wrapper {
            width: 100% !important;
          }

          .profile-sidebar {
            position: relative !important;
            padding: 2rem 1rem !important;
            align-items: center;
            text-align: center;
            top: 0 !important;
            bottom: auto !important;
          }

          .profile-image-container {
            width: 200px !important;
            height: 200px !important;
            margin: 0 auto 2rem !important;
          }

          .profile-name {
            font-size: 2.2rem !important;
          }

          .profile-tagline {
            max-width: 100% !important;
            text-align: center;
          }

          .content-section {
            width: 100% !important;
            padding: 2rem 1rem !important;
            margin-left: 0 !important;
          }

          .stats-container {
            justify-content: center !important;
            gap: 2rem;
          }

          .nav-links {
            display: flex;
            justify-content: center;
            width: 100%;
          }

          .nav-link-container {
            margin: 0 1rem !important;
          }

          .toggle-profile-image {
            position: static !important;
            margin-bottom: 1rem;
          }
        }

        @media (max-width: 480px) {
          .stats-container {
            flex-direction: column !important;
            align-items: center !important;
            gap: 1.5rem !important;
          }

          .stat {
            width: 100% !important;
            max-width: 180px !important;
          }
        }
      `}</style>
    </motion.div>
  );
};

export default PersonProfileCard;
