// src/components/sections/LandingPage.js
import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import Layout from '../layout/Layout';

const HeroContainer = styled.div`
  height: 100vh;
  width: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  position: relative;
  background-color: ${props => props.theme.colors.background};
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: radial-gradient(circle, rgba(0,0,0,0) 0%, rgba(0,0,0,0.8) 100%);
    z-index: 1;
  }

  &::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-image: url('/assets/images/moon-background.jpg');
    background-size: cover;
    background-position: center;
    opacity: 0.4;
    z-index: 0;
  }
`;

const Content = styled.div`
  position: relative;
  z-index: 2;
  text-align: center;
  max-width: 800px;
  padding: ${props => props.theme.spacing.xl};
`;

const Title = styled.h1`
  font-size: 5rem;
  letter-spacing: 0.1em;
  margin-bottom: ${props => props.theme.spacing.md};
  color: ${props => props.theme.colors.primary};
  text-shadow: 0 0 15px rgba(160, 142, 97, 0.5);

  @media (min-width: ${props => props.theme.breakpoints.medium}) {
    font-size: 7rem;
  }
`;

const Subtitle = styled.p`
  font-size: 1.5rem;
  margin-bottom: ${props => props.theme.spacing.xl};
  max-width: 600px;
  margin-left: auto;
  margin-right: auto;
  color: ${props => props.theme.colors.text};

  @media (min-width: ${props => props.theme.breakpoints.medium}) {
    font-size: 1.75rem;
  }
`;

const NavSection = styled.div`
  display: grid;
  grid-template-columns: 1fr;
  gap: ${props => props.theme.spacing.lg};
  width: 100%;
  max-width: 800px;
  margin-top: ${props => props.theme.spacing.xl};

  @media (min-width: ${props => props.theme.breakpoints.medium}) {
    grid-template-columns: 1fr 1fr;
  }
`;

const NavCard = styled(Link)`
  background-color: rgba(10, 10, 10, 0.7);
  border: 1px solid ${props => props.theme.colors.primary};
  padding: ${props => props.theme.spacing.lg};
  border-radius: ${props => props.theme.borderRadius.medium};
  transition: all ${props => props.theme.transitions.default};
  text-align: left;
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 4px;
    height: 100%;
    background-color: ${props => props.theme.colors.primary};
    transition: width ${props => props.theme.transitions.default};
  }

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.3);
    background-color: rgba(20, 20, 20, 0.8);

    &::before {
      width: 8px;
    }
  }
`;

const CardTitle = styled.h2`
  color: ${props => props.theme.colors.primary};
  margin-bottom: ${props => props.theme.spacing.sm};
  font-size: 1.75rem;
`;

const CardDescription = styled.p`
  color: ${props => props.theme.colors.text};
  font-size: 1rem;
  opacity: 0.9;
`;

const LandingPage = () => {
  return (
    <Layout hideHeader>
      <HeroContainer>
        <Content>
          <Title>Crucible</Title>
          <Subtitle>
            A dark fantasy game set in a mysterious world of your design. 
            You play to find out if you can fulfill your motivations without losing your heart.
          </Subtitle>

          <NavSection>
            <NavCard to="/creator">
              <CardTitle>Creator</CardTitle>
              <CardDescription>
                About the author behind Crescent and their creative vision.
              </CardDescription>
            </NavCard>

            <NavCard to="/codex">
              <CardTitle>Codex</CardTitle>
              <CardDescription>
                Explore the rules, lore, and narrative elements that shape the world of Crescent.
              </CardDescription>
            </NavCard>

            <NavCard to="/cycles">
              <CardTitle>Cycles</CardTitle>
              <CardDescription>
                Discover how worlds evolve through cycles of creation and destruction.
              </CardDescription>
            </NavCard>

            <NavCard to="/characters">
              <CardTitle>Characters</CardTitle>
              <CardDescription>
                Create and manage the moon-touched protagonists of your stories.
              </CardDescription>
            </NavCard>
          </NavSection>
        </Content>
      </HeroContainer>
    </Layout>
  );
};

export default LandingPage;
