import React, { useState, useEffect, useRef } from 'react';
import { motion, useScroll, useTransform, useSpring, useInView } from 'framer-motion';
import Background from '../../core/Background';
import './creatorsPage.css'; // Matches your renamed file

// Custom Meteor component inspired by your existing comet implementation
const Meteor = ({ 
  width = 150, 
  height = 150, 
  speed = 0.12,
  delay = 0,
  direction = 1, // 1 for left to right, -1 for right to left
  size = 3,
  opacity = 0.8,
  top = "50%",
  left = "0%",
}) => {
  const meteorRef = useRef(null);
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    setTimeout(() => setIsVisible(true), delay);

    return () => setIsVisible(false);
  }, [delay]);

  return (
    <div 
      className={`meteor ${isVisible ? 'visible' : ''}`} 
      style={{
        width: `${width}px`,
        height: `${height}px`,
        top,
        left,
        opacity: 0, // Start invisible, will be animated
      }}
      ref={meteorRef}
    >
      <div 
        className="meteor-body"
        style={{
          animationDuration: `${8 / speed}s`,
          animationDelay: `${delay}ms`,
          animationDirection: direction === 1 ? 'normal' : 'reverse',
        }}
      >
        <div 
          className="meteor-head"
          style={{ 
            width: `${size}px`, 
            height: `${size}px`,
          }}
        ></div>
        <div 
          className="meteor-tail"
          style={{ opacity }}
        ></div>
      </div>
    </div>
  );
};

// Text reveal animation component
const RevealText = ({ children, threshold = 0.5, delay = 0 }) => {
  const ref = useRef(null);
  const isInView = useInView(ref, { once: false, threshold });

  return (
    <div ref={ref} className="reveal-text-container">
      <div
        className={`reveal-text ${isInView ? 'revealed' : ''}`}
        style={{ transitionDelay: `${delay}ms` }}
      >
        {children}
      </div>
    </div>
  );
};

// Creator card with animation
const CreatorCard = ({ creator, index }) => {
  const cardRef = useRef(null);
  const isInView = useInView(cardRef, { once: true, threshold: 0.2 });

  return (
    <motion.div 
      ref={cardRef}
      className={`creator-card ${isInView ? 'in-view' : ''}`}
      initial={{ opacity: 0, y: 50 }}
      animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 50 }}
      transition={{ 
        duration: 0.8, 
        ease: "easeOut",
        delay: index * 0.15
      }}
    >
      <div className="creator-portrait">
        <div className="portrait-container">
          <div 
            className="portrait-placeholder"
            style={{ 
              backgroundImage: creator.image ? `url(${creator.image})` : 'none'
            }}
          />
        </div>
        <div className="portrait-frame" />
      </div>
      <div className="creator-info">
        <h2 className="creator-name">{creator.name}</h2>
        <h3 className="creator-role">{creator.role}</h3>
        <p className="creator-bio">{creator.bio}</p>
      </div>
      <div className="diagonal-cut" />
    </motion.div>
  );
};

