import React, { useState, useEffect, useRef, useCallback, useMemo } from 'react';
import { motion, useInView, AnimatePresence, useSpring, useTransform, useReducedMotion } from 'framer-motion';
import { createUseStyles } from 'react-jss';

// Optimized and responsive styles
const useStyles = createUseStyles({
  projectCard: {
    position: 'relative',
    backgroundColor: 'rgba(15, 15, 15, 0.7)',
    borderRadius: '12px',
    overflow: 'hidden',
    padding: '0',
    margin: '1.5rem 0',
    backdropFilter: 'blur(10px)',
    boxShadow: '0 8px 32px rgba(0, 0, 0, 0.2)',
    transition: 'transform 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275), box-shadow 0.4s ease',
    cursor: 'pointer',
    transform: 'translateZ(0)', // Force GPU acceleration
    willChange: 'transform, box-shadow', // Optimize for animations
    '&:hover': {
      transform: 'translateY(-6px) scale(1.01)',
      boxShadow: '0 12px 40px rgba(0, 0, 0, 0.25), 0 0 20px rgba(191, 173, 127, 0.2)'
    },
    '@media (max-width: 768px)': {
      margin: '1rem 0',
    }
  },
  cardInner: {
    position: 'relative',
    display: 'grid',
    gridTemplateColumns: '1fr 1.2fr',
    gap: '1.5rem',
    padding: '1.5rem',
    height: '100%',
    '@media (max-width: 768px)': {
      gridTemplateColumns: '1fr',
      gap: '1rem',
      padding: '1.2rem'
    }
  },
  imageContainer: {
    position: 'relative',
    width: '100%',
    borderRadius: '8px',
    overflow: 'hidden',
    aspectRatio: '16/9',
    backgroundColor: 'rgba(10, 10, 10, 0.3)',
    transformStyle: 'preserve-3d', // For 3D hover effect
    '@media (max-width: 768px)': {
      aspectRatio: '16/9',
    }
  },
  projectImage: {
    width: '100%',
    height: '100%',
    objectFit: 'cover',
    display: 'block',
    transition: 'transform 1.2s cubic-bezier(0.165, 0.84, 0.44, 1)',
    backfaceVisibility: 'hidden', // Prevent flickering during transform
    transform: 'translateZ(0)', // Force GPU rendering
    willChange: 'transform', // Hint for browser optimization
  },
  imageOverlay: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    background: 'linear-gradient(to top, rgba(0,0,0,0.8) 0%, rgba(0,0,0,0.2) 60%, rgba(0,0,0,0) 100%)',
    opacity: 0.6,
    transition: 'opacity 0.4s ease',
    '$projectCard:hover &': {
      opacity: 0.4
    }
  },
  contentContainer: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'space-between',
    height: '100%',
    '@media (max-width: 768px)': {
      marginTop: '0.5rem',
    }
  },
  projectTitle: {
    fontSize: '1.5rem',
    fontWeight: 600,
    color: '#bfad7f',
    margin: '0 0 0.6rem 0',
    letterSpacing: '0.03em',
    transition: 'transform 0.3s ease, color 0.3s ease',
    '@media (max-width: 768px)': {
      fontSize: '1.3rem',
    },
    '$projectCard:hover &': {
      color: '#d6c295',
      transform: 'translateY(-2px)'
    }
  },
  projectDescription: {
