/**
 * Optimized Comet Animation Component
 * Renders a subtle comet effect around the card edges on hover
 */
const CometEffect = ({ isHovered }) => {
  const canvasRef = useRef(null);
  const requestRef = useRef(null);
  const prefersReducedMotion = useReducedMotion();

  // Only render comet effect if motion is not reduced
  const shouldRender = isHovered && !prefersReducedMotion;

  useEffect(() => {
    if (!canvasRef.current || !shouldRender) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const width = canvas.width;
    const height = canvas.height;

    // Set proper canvas size accounting for pixel ratio
    const pixelRatio = window.devicePixelRatio || 1;
    canvas.width = canvas.offsetWidth * pixelRatio;
    canvas.height = canvas.offsetHeight * pixelRatio;
    ctx.scale(pixelRatio, pixelRatio);

    // Animation variables
    let position = 0;
    let lastTimestamp = 0;

    const animate = (timestamp) => {
      if (!lastTimestamp) lastTimestamp = timestamp;
      const deltaTime = timestamp - lastTimestamp;
      lastTimestamp = timestamp;

      // Clear canvas
      ctx.clearRect(0, 0, width, height);

      // Update comet position - very slow traversal
      position = (position + 0.0006 * deltaTime) % 4;

      // Calculate point coordinates based on rectangle path
      let x, y;
      if (position < 1) {
        // Top edge: left to right
        x = width * position;
        y = 0;
      } else if (position < 2) {
        // Right edge: top to bottom
        x = width;
        y = height * (position - 1);
      } else if (position < 3) {
        // Bottom edge: right to left
        x = width - (width * (position - 2));
        y = height;
      } else {
        // Left edge: bottom to top
        x = 0;
        y = height - (height * (position - 3));
      }

      // Draw comet trail
      const trailLength = Math.min(width, height) * 0.4; // Proportional to card size
      const segments = 20;

      // Calculate trail direction
      let angleOffset = 0;
      if (position < 1) angleOffset = Math.PI / 2;
      else if (position < 2) angleOffset = Math.PI;
      else if (position < 3) angleOffset = -Math.PI / 2;
      else angleOffset = 0;

      for (let i = 0; i < segments; i++) {
        // Calculate segment position
        const segmentPosition = i / segments;
        const segmentLength = trailLength * segmentPosition;

        // Calculate segment coordinates
        const segX = x + Math.cos(angleOffset) * segmentLength;
        const segY = y + Math.sin(angleOffset) * segmentLength;

        // Calculate segment opacity and width
        const opacity = 0.5 * (1 - segmentPosition);
        const width = 3 * (1 - segmentPosition) + 0.5;

        // Draw segment
        if (i === 0) {
          // Draw comet head
          ctx.beginPath();
          ctx.arc(x, y, 2, 0, Math.PI * 2);
          ctx.fillStyle = `rgba(191, 173, 127, ${opacity * 2})`;
          ctx.shadowBlur = 10;
          ctx.shadowColor = 'rgba(191, 173, 127, 0.8)';
          ctx.fill();
        } else {
          // Draw trail segment
          ctx.beginPath();
          ctx.moveTo(x, y);
          ctx.lineTo(segX, segY);
          ctx.strokeStyle = `rgba(191, 173, 127, ${opacity})`;
          ctx.lineWidth = width;
          ctx.lineCap = 'round';
          ctx.stroke();
        }
      }

      // Request next frame
      requestRef.current = requestAnimationFrame(animate);
    };

    // Start animation
    requestRef.current = requestAnimationFrame(animate);

    // Cleanup
    return () => {
      if (requestRef.current) {
        cancelAnimationFrame(requestRef.current);
      }
    };
  }, [shouldRender]);

  return (
    <canvas 
      ref={canvasRef} 
      style={{
        width: '100%',
        height: '100%',
        position: 'absolute',
        top: 0,
        left: 0,
        opacity: shouldRender ? 1 : 0,
        transition: 'opacity 0.3s ease',
        pointerEvents: 'none'
      }}
    />
  );
};

/**
 * ProjectCard Component
 * 
 * A modern, animated project card component with smooth entrance animations,
 * hover effects, and optimized performance.
 * 
 * @param {Object} props
 * @param {Object} props.project - Project data
 * @param {string} props.project.title - Project title
 * @param {string} props.project.description - Project description
 * @param {string} props.project.image - Project image URL
 * @param {string[]} props.project.tags - Array of project tags
 * @param {string|number} props.project.year - Project year
 * @param {Function} props.onClick - Click handler for the card
 * @param {number} [props.index=0] - Index for staggered animations
 * @param {Object} [props.animationConfig] - Optional animation configuration
 */
const ProjectCard = ({
  project,
  onClick,
  index = 0,
  animationConfig = {
    staggerDelay: 0.1,
    initialY: 30,
    duration: 0.6,
    viewThreshold: 0.1
  }
}) => {
  const classes = useStyles();
  const cardRef = useRef(null);
  const imageRef = useRef(null);
  const [isHovered, setIsHovered] = useState(false);
  const prefersReducedMotion = useReducedMotion();

  // Track if component is in view for animation
  const isInView = useInView(cardRef, {
    once: true,
    margin: "0px 0px -100px 0px",
    amount: animationConfig.viewThreshold || 0.1
  });

  // Mouse position for parallax effect
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

  // Calculate animation delay based on index
  const delay = prefersReducedMotion ? 0 : (index * (animationConfig.staggerDelay || 0.1));

  // Handle mouse enter/leave for hover effects
  const handleMouseEnter = useCallback(() => {
    setIsHovered(true);
  }, []);

  const handleMouseLeave = useCallback(() => {
    setIsHovered(false);
    // Reset mouse position when not hovering
    setMousePosition({ x: 0, y: 0 });
  }, []);

  // Handle mouse move for parallax effect
  const handleMouseMove = useCallback((e) => {
    if (!cardRef.current || !isHovered) return;

    // Calculate mouse position relative to card center
    const rect = cardRef.current.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;

    // Normalize values between -1 and 1
    const x = (e.clientX - centerX) / (rect.width / 2);
    const y = (e.clientY - centerY) / (rect.height / 2);

    // Update mouse position state for parallax effect
    setMousePosition({ x, y });
  }, [isHovered]);

  // Handle image parallax effect
  useEffect(() => {
    if (!imageRef.current || !isHovered) return;

    // Apply parallax transform to image
    const { x, y } = mousePosition;
    imageRef.current.style.transform = `scale(1.05) translate(${x * -10}px, ${y * -10}px)`;

    return () => {
      // Reset transform when effect is cleaned up
      if (imageRef.current) {
        imageRef.current.style.transform = '';
      }
    };
  }, [mousePosition, isHovered]);

  // Card entrance animation variants
  const cardVariants = {
    hidden: { 
      opacity: 0, 
      y: prefersReducedMotion ? 0 : (animationConfig.initialY || 30),
      scale: 0.98
    },
    visible: { 
      opacity: 1, 
      y: 0,
      scale: 1,
      transition: {
        duration: animationConfig.duration || 0.6,
        ease: [0.23, 1, 0.32, 1], // Custom cubic-bezier for smooth entrance
        delay: delay
      }
    }
  };

  // Content stagger animation variants
  const contentVariants = {
    hidden: { opacity: 0, y: 15 },
    visible: { 
      opacity: 1, 
      y: 0,
      transition: {
        duration: 0.4,
        ease: "easeOut",
        delay: delay + 0.1 // Slightly delayed after card appears
      }
    }
  };

  return (
    <motion.div
      ref={cardRef}
      className={classes.projectCard}
      variants={cardVariants}
      initial="hidden"
      animate={isInView ? "visible" : "hidden"}
      onClick={onClick}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      onMouseMove={handleMouseMove}
      whileTap={{ scale: 0.98 }}
    >
      {/* Border and glow effects */}
      <div className={classes.borderEffect} />
      <div className={classes.glowEffect} />

      {/* Comet animation effect */}
      <div className={classes.cometCanvas}>
        <CometEffect isHovered={isHovered} />
      </div>

      {/* Card content */}
      <div className={classes.cardInner}>
        <div className={classes.imageContainer}>
          <img 
            ref={imageRef}
            src={project.image} 
            alt={project.title} 
            className={classes.projectImage}
            loading="lazy"
          />
          <div className={classes.imageOverlay} />
        </div>

        <motion.div 
          className={classes.contentContainer}
          variants={contentVariants}
        >
          <div>
            <h3 className={classes.projectTitle}>{project.title}</h3>
            <p className={classes.projectDescription}>{project.description}</p>
          </div>

          <div className={classes.metaContainer}>
            <div className={classes.tags}>
              {project.tags.map((tag, i) => (
                <span key={i} className={classes.tag}>
                  {tag}
                </span>
              ))}
            </div>
            <span className={classes.yearLabel}>{project.year}</span>
          </div>
        </motion.div>
      </div>
    </motion.div>
  );
};

/**
 * ProjectGrid Component
 * 
 * A grid container for multiple ProjectCards with staggered animations
 * 
 * @param {Object} props
 * @param {Array} props.projects - Array of project objects
 * @param {Function} props.onProjectClick - Function to handle project clicks
 */
const ProjectGrid = ({ projects, onProjectClick }) => {
  return (
    <div style={{ 
      display: 'grid', 
      gridTemplateColumns: 'repeat(auto-fill, minmax(min(100%, 600px), 1fr))',
      gap: '2rem',
      width: '100%'
    }}>
      {projects.map((project, index) => (
        <ProjectCard 
          key={project.id || index}
          project={project}
          index={index}
          onClick={() => onProjectClick(project)}
        />
      ))}
    </div>
  );
};

