import React, { useRef, useState, useCallback, useEffect } from 'react';
import { motion, useInView } from 'framer-motion';
import CometBorderEffect from '../bordereffects/CometBorderEffect';

/**
 * PersonProfileCard Component
 * 
 * A sophisticated profile card with a sticky sidebar that follows a three-phase scroll behavior:
 * 1. Normal Flow: Initially scrolls with the page
 * 2. Fixed Position: Sticks to viewport when scrolling through content
 * 3. Release: Returns to normal flow after scrolling past component
 * 
 * @param {Object} props - Component props
 * @param {Object} props.person - Person data object
 * @param {string} props.person.name - Person's name
 * @param {string} props.person.role - Person's role or title
 * @param {string} props.person.image - URL to person's image
 * @param {string} props.person.tagline - Short tagline about the person
 * @param {string[]} props.person.bio - Array of bio paragraphs for About section
 * @param {Object[]} props.person.stats - Array of statistic objects with value and label
 * @param {Object[]} props.person.experience - Array of experience objects with content
 * @param {Object[]} props.person.projects - Array of project objects with content
 * @param {Object} props.animationConfig - Animation configuration for entrance
 * @param {Object} props.cometConfig - Comet animation configuration
 * @param {Object} props.imagePosition - Image position configuration (x, y, scale)
 * @param {Array} props.additionalSections - Additional content sections
 * @param {Function} props.onSectionChange - Callback when active section changes
 * @param {number} props.topOffset - Offset from top for sticky position (default: 100px)
 */
const PersonProfileCard = ({ 
  person,
  animationConfig = {
    threshold: 0.2,
    once: true,
    initialY: 30,
    duration: 0.8
  },
  cometConfig = {
    size: 1.5,
    trailLength: 100,
    speed: 0.001,
    targetFPS: 30,
    respectReducedMotion: true
  },
  imagePosition = {
    x: 50,
    y: 50,
    scale: 1
  },
  additionalSections = [],
  onSectionChange = null,
  topOffset = 100
}) => {
  const [isHovered, setIsHovered] = useState(false);
  const [activeSection, setActiveSection] = useState('about');

  // Refs for sections to enable scrolling
  const containerRef = useRef(null);
  const aboutRef = useRef(null);
  const experienceRef = useRef(null);
  const projectsRef = useRef(null);

  // Create a mapping of section IDs to refs
  const sectionRefs = {
    about: aboutRef,
    experience: experienceRef,
    projects: projectsRef
  };

  // Set up inView detection for entrance animation
  const isInView = useInView(containerRef, {
    threshold: animationConfig?.threshold ?? 0.2,
    once: animationConfig?.once ?? true
  });

  // Mouse event handlers for profile image hover effect
  const handleMouseEnter = useCallback(() => setIsHovered(true), []);
  const handleMouseLeave = useCallback(() => setIsHovered(false), []);

  // Scroll to section function
  const scrollToSection = useCallback((sectionId) => {
    setActiveSection(sectionId);

    // Notify parent component if callback provided
    if (onSectionChange) {
      onSectionChange(sectionId);
    }

    // Scroll to section with smooth behavior
    const sectionRef = sectionRefs[sectionId];
    if (sectionRef?.current) {
      sectionRef.current.scrollIntoView({ 
        behavior: 'smooth',
        block: 'start'
      });
    }
  }, [onSectionChange, sectionRefs]);

  // Set up intersection observer to detect which section is in view
  useEffect(() => {
    if (!aboutRef.current || !experienceRef.current || !projectsRef.current) return;

    const options = {
      root: null,
      rootMargin: '-10% 0px -70% 0px', // Consider element in view when in the top 30% of viewport
      threshold: 0
    };

    const callback = (entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          const sectionId = entry.target.getAttribute('data-section');
          if (sectionId && sectionId !== activeSection) {
            setActiveSection(sectionId);

            // Notify parent component if callback provided
            if (onSectionChange) {
              onSectionChange(sectionId);
            }
          }
        }
      });
    };

    const observer = new IntersectionObserver(callback, options);

    // Observe all section refs
    Object.values(sectionRefs).forEach(ref => {
      if (ref.current) observer.observe(ref.current);
    });

    return () => {
      observer.disconnect();
    };
  }, [activeSection, onSectionChange, sectionRefs]);

  return (
    <motion.div 
      ref={containerRef}
      className="profile-container"
      initial={{ opacity: 0, y: animationConfig?.initialY ?? 30 }}
      animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: animationConfig?.initialY ?? 30 }}
      transition={{ 
        duration: animationConfig?.duration ?? 0.8, 
        ease: "easeOut" 
      }}
      style={{
        display: 'flex',
        flexDirection: 'row',
        width: '100%',
        maxWidth: '1300px',
        margin: '0 auto',
        minHeight: '70vh',
        position: 'relative',
      }}
    >
      {/* Left sidebar wrapper */}
      <div 
        className="sidebar-wrapper"
        style={{
          width: '40%',
          position: 'relative',
        }}
      >
        {/* Sticky sidebar */}
        <div 
          className="profile-sidebar"
          style={{
            padding: '3rem 2rem 2rem 0',
            display: 'flex',
            flexDirection: 'column',
            position: 'sticky',
            top: `${topOffset}px`,
            width: 'inherit',
            maxWidth: 'inherit',
          }}
        >
          {/* Profile image with comet animation */}
          <div 
            className="profile-image-container"
            style={{
              position: 'relative',
              width: '240px',
              height: '240px',
              borderRadius: '50%',
              boxShadow: '0 5px 15px rgba(0, 0, 0, 0.3)',
              transition: 'box-shadow 0.3s ease',
              overflow: 'hidden',
              boxSizing: 'border-box',
              marginBottom: '2rem',
              border: '1px solid rgba(191, 173, 127, 0.2)',
            }}
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
          >
            {/* Comet animation around the border */}
            <CometBorderEffect 
              isHovered={isHovered}
              size={cometConfig.size}
              trailLength={cometConfig.trailLength}
              speed={cometConfig.speed}
              hoverSpeedMultiplier={cometConfig.hoverSpeedMultiplier || 2}
              trailSegments={cometConfig.trailSegments || 25}
              glowIntensity={cometConfig.glowIntensity || 0.8}
              targetFPS={cometConfig.targetFPS || 30}
              respectReducedMotion={cometConfig.respectReducedMotion || true}
              paddingTop={20}
              paddingRight={20}
              paddingBottom={20}
              paddingLeft={20}
            />

            <div 
              className="profile-image"
              style={{
                position: 'absolute',
                top: 0,
                left: 0,
                width: '100%',
                height: '100%',
                borderRadius: '50%',
                overflow: 'hidden',
              }}
            >
              <div 
                className="image-wrapper"
                style={{
                  position: 'absolute',
                  top: 0,
                  left: 0,
                  width: '100%',
                  height: '100%',
                  overflow: 'hidden',
                  borderRadius: '50%'
                }}
              >
                <img 
                  src={person.image} 
                  alt={person.name} 
                  style={{
                    width: '100%',
                    height: '100%',
                    objectFit: 'cover',
                    transition: 'transform 0.6s ease, object-position 0.6s ease',
                    display: 'block',
                    objectPosition: `${imagePosition?.x ?? 50}% ${imagePosition?.y ?? 50}%`,
                    transform: `scale(${imagePosition?.scale ?? 1})`
                  }}
                  loading="lazy"
                />
              </div>
            </div>
          </div>

          {/* Profile information */}
          <div 
            className="profile-info"
            style={{
              marginBottom: '2rem',
              paddingLeft: '5px',
            }}
          >
            <h1 
              className="profile-name"
              style={{
                fontSize: '2.8rem',
                fontWeight: '300',
                color: '#bfad7f',
                letterSpacing: '0.05em',
                marginBottom: '0.5rem',
              }}
            >
              {person.name}
            </h1>

            <h2 
              className="profile-role"
              style={{
                fontSize: '1.2rem',
                fontWeight: '300',
                marginBottom: '2rem',
                color: 'rgba(191, 173, 127, 0.7)',
                letterSpacing: '0.05em',
                fontFamily: '"Garamond", "Adobe Caslon Pro", serif',
                fontStyle: 'italic',
              }}
            >
              {person.role}
            </h2>

            {/* Short tagline */}
            {person.tagline && (
              <p 
                className="profile-tagline"
                style={{
                  fontSize: '1.1rem',
                  lineHeight: '1.6',
                  marginBottom: '3rem',
                  maxWidth: '90%',
                  color: 'rgba(224, 224, 224, 0.7)',
                  fontFamily: '"Garamond", "Adobe Caslon Pro", serif',
                  fontWeight: '300',
                }}
              >
                {person.tagline}
              </p>
            )}
          </div>

          {/* Navigation links */}
          <div className="nav-links" style={{ marginTop: '2rem' }}>
            <div 
              className={`nav-link-container ${activeSection === 'about' ? 'active' : ''}`}
              style={{
                position: 'relative',
                marginBottom: '1rem',
                display: 'flex',
                alignItems: 'center',
              }}
            >
              <a 
                className="nav-link"
                onClick={() => scrollToSection('about')}
                style={{
                  position: 'relative',
                  display: 'block',
                  padding: '0.5rem 0 0.5rem 25px',
                  fontSize: '0.85rem',
                  letterSpacing: '0.1em',
                  transition: 'color 0.3s, transform 0.3s',
                  textDecoration: 'none',
                  color: activeSection === 'about' ? '#bfad7f' : 'rgba(224, 224, 224, 0.7)',
                  cursor: 'pointer',
                }}
              >
                ABOUT
              </a>
              <div 
                className="nav-line"
                style={{
                  position: 'absolute',
                  left: '0',
                  top: '50%',
                  transform: 'translateY(-50%)',
                  width: activeSection === 'about' ? '20px' : '10px',
                  height: '2px',
                  backgroundColor: activeSection === 'about' 
                    ? 'rgba(191, 173, 127, 0.9)' 
                    : 'rgba(191, 173, 127, 0.5)',
                  transition: 'width 0.3s ease, background-color 0.3s ease',
                }}
              ></div>
            </div>

            <div 
              className={`nav-link-container ${activeSection === 'experience' ? 'active' : ''}`}
              style={{
                position: 'relative',
                marginBottom: '1rem',
                display: 'flex',
                alignItems: 'center',
              }}
            >
              <a 
                className="nav-link"
                onClick={() => scrollToSection('experience')}
                style={{
                  position: 'relative',
                  display: 'block',
                  padding: '0.5rem 0 0.5rem 25px',
                  fontSize: '0.85rem',
                  letterSpacing: '0.1em',
                  transition: 'color 0.3s, transform 0.3s',
                  textDecoration: 'none',
                  color: activeSection === 'experience' ? '#bfad7f' : 'rgba(224, 224, 224, 0.7)',
                  cursor: 'pointer',
                }}
              >
                EXPERIENCE
              </a>
              <div 
                className="nav-line"
                style={{
                  position: 'absolute',
                  left: '0',
                  top: '50%',
                  transform: 'translateY(-50%)',
                  width: activeSection === 'experience' ? '20px' : '10px',
                  height: '2px',
                  backgroundColor: activeSection === 'experience' 
                    ? 'rgba(191, 173, 127, 0.9)' 
                    : 'rgba(191, 173, 127, 0.5)',
                  transition: 'width 0.3s ease, background-color 0.3s ease',
                }}
              ></div>
            </div>

            <div 
              className={`nav-link-container ${activeSection === 'projects' ? 'active' : ''}`}
              style={{
                position: 'relative',
                marginBottom: '1rem',
                display: 'flex',
                alignItems: 'center',
              }}
            >
              <a 
                className="nav-link"
                onClick={() => scrollToSection('projects')}
                style={{
                  position: 'relative',
                  display: 'block',
                  padding: '0.5rem 0 0.5rem 25px',
                  fontSize: '0.85rem',
                  letterSpacing: '0.1em',
                  transition: 'color 0.3s, transform 0.3s',
                  textDecoration: 'none',
                  color: activeSection === 'projects' ? '#bfad7f' : 'rgba(224, 224, 224, 0.7)',
                  cursor: 'pointer',
                }}
              >
                PROJECTS
              </a>
              <div 
                className="nav-line"
                style={{
                  position: 'absolute',
                  left: '0',
                  top: '50%',
                  transform: 'translateY(-50%)',
                  width: activeSection === 'projects' ? '20px' : '10px',
                  height: '2px',
                  backgroundColor: activeSection === 'projects' 
                    ? 'rgba(191, 173, 127, 0.9)' 
                    : 'rgba(191, 173, 127, 0.5)',
                  transition: 'width 0.3s ease, background-color 0.3s ease',
                }}
              ></div>
            </div>
          </div>
        </div>
      </div>

      {/* Right content section */}
      <div 
        className="content-section"
        style={{
          width: '60%',
          padding: '3rem 0 2rem 2rem',
          marginLeft: 'auto',
        }}
      >
        {/* About section */}
        <div 
          ref={aboutRef} 
          data-section="about"
          className="section"
          id="about"
          style={{
            marginBottom: '3rem',
            scrollMarginTop: '2rem',
          }}
        >
          {person.bio && person.bio.map((paragraph, index) => (
            <p 
              key={index} 
              className="section-content"
              style={{
                fontSize: '1rem',
                lineHeight: '1.8',
                marginBottom: '1.5rem',
                color: 'rgba(224, 224, 224, 0.7)',
                fontFamily: '"Garamond", "Adobe Caslon Pro", serif',
                fontWeight: '300',
              }}
            >
              {paragraph}
            </p>
          ))}

          {/* Stats display */}
          {person.stats && person.stats.length > 0 && (
            <div 
              className="stats-container"
              style={{
                display: 'flex',
                justifyContent: 'space-between',
                flexWrap: 'wrap',
                marginTop: '3rem',
                width: '100%',
              }}
            >
              {person.stats.map((stat, index) => (
                <div 
                  key={index} 
                  className="stat"
                  style={{
                    flex: '1',
                    textAlign: 'center',
                    padding: '0 1rem',
                    minWidth: '100px',
                  }}
                >
                  <div 
                    className="stat-value"
                    style={{
                      fontSize: '2.5rem',
                      fontWeight: '100',
                      color: 'rgba(191, 173, 127, 0.9)',
                      marginBottom: '0.5rem'
                    }}
                  >
                    {stat.value}
                  </div>
                  <div 
                    className="stat-label"
                    style={{
                      fontSize: '0.85rem',
                      color: 'rgba(224, 224, 224, 0.5)',
                      textTransform: 'uppercase',
                      letterSpacing: '0.1em'
                    }}
                  >
                    {stat.label}
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Experience section */}
        <div 
          ref={experienceRef}
          data-section="experience"
          className="section"
          id="experience"
          style={{
            marginBottom: '3rem',
            scrollMarginTop: '2rem',
          }}
        >
          {person.experience ? (
            person.experience.map((item, index) => (
              <p 
                key={index} 
                className="section-content"
                style={{
                  fontSize: '1rem',
                  lineHeight: '1.8',
                  marginBottom: '1.5rem',
                  color: 'rgba(224, 224, 224, 0.7)',
                  fontFamily: '"Garamond", "Adobe Caslon Pro", serif',
                  fontWeight: '300',
                }}
              >
                {item.content}
              </p>
            ))
          ) : (
            <p 
              className="section-content"
              style={{
                fontSize: '1rem',
                lineHeight: '1.8',
                marginBottom: '1.5rem',
                color: 'rgba(224, 224, 224, 0.7)',
                fontFamily: '"Garamond", "Adobe Caslon Pro", serif',
                fontWeight: '300',
              }}
            >
              Throughout my career, I've specialized in developing software systems that seamlessly 
              integrate front-end experiences with robust back-end architectures. My experience spans 
              various domains, from interactive media to data visualization systems.
            </p>
          )}
        </div>

        {/* Projects section */}
        <div 
          ref={projectsRef}
          data-section="projects"
          className="section"
          id="projects"
          style={{
            marginBottom: '3rem',
            scrollMarginTop: '2rem',
          }}
        >
          {person.projects ? (
            person.projects.map((item, index) => (
              <p 
                key={index} 
                className="section-content"
                style={{
                  fontSize: '1rem',
                  lineHeight: '1.8',
                  marginBottom: '1.5rem',
                  color: 'rgba(224, 224, 224, 0.7)',
                  fontFamily: '"Garamond", "Adobe Caslon Pro", serif',
                  fontWeight: '300',
                }}
              >
                {item.content}
              </p>
            ))
          ) : (
            <p 
              className="section-content"
              style={{
                fontSize: '1rem',
                lineHeight: '1.8',
                marginBottom: '1.5rem',
                color: 'rgba(224, 224, 224, 0.7)',
                fontFamily: '"Garamond", "Adobe Caslon Pro", serif',
                fontWeight: '300',
              }}
            >
              My personal projects merge the scientific principles of evolution and adaptation with 
              mythological themes of rebirth and renewal. I create 'things' that are both mechanically 
              sound and narratively compelling, allowing users to explore complex themes through 
              immersive experiences.
            </p>
          )}
        </div>

        {/* Dynamic sections from props */}
        {additionalSections.length > 0 && (
          additionalSections.map((section, index) => (
            <div 
              key={index} 
              className="section"
              style={{
                marginBottom: '3rem',
                scrollMarginTop: '2rem',
              }}
            >
              {section.content && (
                typeof section.content === 'string' 
                  ? <p 
                      className="section-content"
                      style={{
                        fontSize: '1rem',
                        lineHeight: '1.8',
                        marginBottom: '1.5rem',
                        color: 'rgba(224, 224, 224, 0.7)',
                        fontFamily: '"Garamond", "Adobe Caslon Pro", serif',
                        fontWeight: '300',
                      }}
                    >
                      {section.content}
                    </p>
                  : section.content
              )}
            </div>
          ))
        )}
      </div>

      {/* Media query styles for mobile */}
      <style jsx>{`
        @media (max-width: 768px) {
          .profile-container {
            flex-direction: column;
          }

          .sidebar-wrapper {
            width: 100%;
          }

          .profile-sidebar {
            position: relative;
            padding: 2rem 1rem;
            align-items: center;
            text-align: center;
            width: 100%;
            top: 0;
          }

          .profile-image-container {
            width: 200px;
            height: 200px;
          }

          .profile-name {
            font-size: 2.2rem;
          }

          .profile-tagline {
            max-width: 100%;
            text-align: center;
          }

          .content-section {
            width: 100%;
            padding: 2rem 1rem;
            margin-left: 0;
          }

          .stats-container {
            justify-content: center;
            gap: 2rem;
          }

          .nav-link-container {
            display: inline-block;
            margin: 0 1rem;
          }
        }

        @media (max-width: 480px) {
          .stats-container {
            flex-direction: column;
            align-items: center;
            gap: 1.5rem;
          }

          .stat {
            width: 100%;
            max-width: 180px;
          }
        }
      `}</style>
    </motion.div>
  );
};

export default PersonProfileCard;
