import React, { useState, useEffect } from 'react';
import Background from '../../../layout/Background';
import RevealText from '../../../core/effects/texteffects/RevealText';
import ProjectCard from '../../../core/effects/holdereffects/ProjectCard';
import PersonProfileCard from '../../../core/effects/holdereffects/PersonProfileCard';
import ScrollingTextAnimation from '../../../core/effects/texteffects/ScrollingTextAnimation';
import MeteorShower from '../../../core/effects/cosmiceffects/MeteorShower';
import { createUseStyles } from 'react-jss';

// Styles for the CreatorsPage component
const useStyles = createUseStyles({
  creatorsPage: {
    minHeight: '100vh',
    color: '#fff',
    overflowX: 'hidden',
  },

  content: {
    position: 'relative',
    zIndex: 2,
    paddingTop: '40vh', // Space for header and title
    '@media (max-width: 768px)': {
      paddingTop: '30vh',
    }
  },

  header: {
    position: 'relative',
    marginBottom: '2rem',
    textAlign: 'center',
    height: '200px',
    overflow: 'visible',
  },

  mainTitle: {
    fontSize: '3.5rem',
    fontWeight: 300,
    color: '#bfad7f',
    textTransform: 'uppercase',
    letterSpacing: '0.25em',
    margin: 0,
    textShadow: '0 0 10px rgba(191, 173, 127, 0.3)',
    '@media (max-width: 768px)': {
      fontSize: '2.2rem',
      letterSpacing: '0.2em',
    }
  },

  scrollableContent: {
    position: 'relative',
    zIndex: 5,
    opacity: 0,
    transition: 'opacity 0.5s ease',
  },

  loaded: {
    opacity: 1,
  },

  section: {
    padding: '5rem 2rem',
    maxWidth: '1200px',
    margin: '0 auto',
    position: 'relative',
    '@media (max-width: 768px)': {
      padding: '3rem 1.5rem',
    }
  },

  profileSection: {
    padding: '2rem',
    '@media (max-width: 768px)': {
      padding: '1rem',
    }
  },

  intro: {
    textAlign: 'center',
    paddingTop: '2rem',
  },

  introQuote: {
    fontFamily: '"Garamond", "Adobe Caslon Pro", serif',
    fontSize: '1.3rem',
    lineHeight: 1.7,
    color: 'rgba(191, 173, 127, 0.8)',
    marginBottom: '0.5rem',
    fontWeight: 300,
    fontStyle: 'italic',
    '@media (max-width: 768px)': {
      fontSize: '1.1rem',
    }
  },

  quoteAuthor: {
    fontSize: '1rem',
    color: 'rgba(224, 224, 224, 0.6)',
    textAlign: 'right',
    maxWidth: '600px',
    margin: '0 auto 3rem',
    fontWeight: 300,
  },

  subtitle: {
    fontSize: '1.1rem',
    color: 'rgba(224, 224, 224, 0.7)',
    maxWidth: '700px',
    margin: '0 auto',
    lineHeight: 1.6,
    fontFamily: '"Garamond", "Adobe Caslon Pro", serif',
    fontWeight: 300,
    '@media (max-width: 768px)': {
      fontSize: '0.9rem',
    }
  },

  // Other styles...
});

/**
 * CreatorsPage Component
 */
const CreatorsPage = () => {
  // Hooks
  const classes = useStyles();
  const [isLoaded, setIsLoaded] = useState(false);
  const [showMeteors] = useState(true);

  // Creator profile data
  const creatorData = {
    name: "Rosalia Fialkova",
    role: "Systems Designer & Technical Artist",
    image: "/assets/images/creators/rosalia.png",

    // Short bio for the sidebar
    shortBio: "I blend software engineering with creative vision, crafting high-quality, reusable systems across frontend interfaces and backend architectures.",

    // Full bio paragraphs for the main content
    bio: [
      "I aspire to blend software engineering knowledge with creative vision. I'm in my element when I'm crafting high-quality, reusable (I hate seeing a good program not find multiple homes!) systems across frontend interfaces and backend architectures. Combining mathematical modeling with aesthetic intuition and developing modular components is fun!",
      "My personal projects merge the scientific principles of evolution and adaptation with mythological themes of rebirth and renewal. I create 'things' that are both mechanically sound and narratively compelling, allowing users to explore complex themes through immersive experiences."
    ],

    stats: [
      { value: "Stellar", label: "Motivation to Design" },
      { 
        value: <span className="math-formula">tanh(Ïƒ(i))</span>, 
        label: "Favorite Function" 
      },
      { value: "âˆž", label: "Cycles of Debugging" }
    ]
  };

  // Section content
  const sectionContent = {
    experience: {
      paragraphs: [
        "Throughout my career, I've specialized in developing software systems that seamlessly integrate front-end experiences with robust back-end architectures. My experience spans various domains, from interactive media to data visualization systems.",
        "I've collaborated with cross-functional teams to implement solutions that are not only mechanically sound but also narratively compelling, allowing users to explore complex themes through immersive experiences."
      ]
    },
    projects: {
      paragraphs: [
        "My personal projects merge the scientific principles of evolution and adaptation with mythological themes of rebirth and renewal. I create 'things' that are both mechanically sound and narratively compelling, allowing users to explore complex themes through immersive experiences.",
        "Each project begins with a conceptual framework that explores how systems evolve and adapt, drawing inspiration from both natural processes and cultural narratives about transformation and growth."
      ]
    }
  };

  // Define additional content sections
  const additionalSections = [
    {
      id: "design-philosophy",
      title: "Design Philosophy",
      content: "I believe in creating systems that are both elegant and adaptable, capable of evolving alongside user needs. My approach draws inspiration from both natural processes and cultural narratives about transformation and growth."
    },
    {
      id: "technical-framework",
      title: "Technical Framework",
      content: "My work combines traditional software engineering practices with experimental approaches to user experience. I'm particularly interested in the intersection of mathematical modeling, procedural generation, and narrative design."
    }
  ];

  // Project data array
  const projects = [
    {
      id: "crescent",
      title: "Crescent TTRPG",
      description: "A dark fantasy tabletop roleplaying game centered around cycles of creation, evolution, and destruction.",
      image: "/assets/images/backgrounds/Melting_Moon.png",
      tags: ["Game Design", "World Building", "System Design"]
    },
    // Additional projects...
  ];

  // Initialize loaded state after a short delay
  useEffect(() => {
    const loadTimer = setTimeout(() => {
      setIsLoaded(true);
    }, 300);

    return () => clearTimeout(loadTimer);
  }, []);

  return (
    <div className={classes.creatorsPage}>
      {/* Background component with interactive stars */}
      <Background />

      {/* Meteor Shower Effect */}
      {showMeteors && (
        <MeteorShower 
          height="200vh"
          zIndex={3}
          trailColor="rgba(191, 173, 127, 0.8)"
          glowColor="rgba(207, 185, 130, 0.6)"   
          coreColor="rgba(255, 248, 220, 1)"
          meteorDensity={12}
          meteorMinSize={0.8}          
          meteorMaxSize={2.0}          
          meteorSpeed={0.04}
          trailLength={400}
          trailSegments={200}           
          enableParallax={true}
          parallaxIntensity={0.10}
          mode="linear"
          direction="top"
          baseAngle={30}
          angleVariation={15}
          burstParticleSize={2}
          burstParticleCount={8}
          burstProbability={0.55}
        />
      )}

      {/* Main content area */}
      <div className={classes.content}>
        {/* Title with scroll-then-stop effect */}
        <header className={classes.header}>
          <ScrollingTextAnimation scrollConfig={{
            startPosition: 0,
            endPosition: 300,
            initialY: -100,
            finalY: 185,
            clampValues: true,
            opacityValues: [1, 0.95, 0.9],
            opacityScrollPositions: [0, 300, 400]
          }}>
            <h1 className={classes.mainTitle}>About the Author</h1>
          </ScrollingTextAnimation>
        </header>

        {/* Scrollable content that fades in on load */}
        <div className={`${classes.scrollableContent} ${isLoaded ? classes.loaded : ''}`}>
          {/* Introduction section with reveal animations */}
          <section className={`${classes.section} ${classes.intro}`}>
            <RevealText threshold={0.2}>
              <p className={classes.introQuote}>
                "Though my soul may set in darkness,<br />
                it will rise in perfect light;<br />
                I have loved the stars too fondly<br />
                to be fearful of the night."
              </p>
              <p className={classes.quoteAuthor}>â€” Sarah Williams, "The Old Astronomer to His Pupil"</p>
            </RevealText>

            <RevealText delay={300}>
              <p className={classes.subtitle}>
                My personal projects are born from my fascination with cycles of rebirth and destructionâ€”the eternal dance between light and darkness. I try to weave together mathematical modeling, narrative structure, and visual aesthetics to craft immersive tools, simulations, and models.
              </p>
            </RevealText>
          </section>

          {/* Enhanced profile card section */}
          <section className={`${classes.section} ${classes.profileSection}`}>
            <PersonProfileCard
              person={creatorData}
              sections={sectionContent}
              additionalSections={additionalSections}
              animationConfig={{
                threshold: 0.2,
                once: true,
                initialY: 30,
                duration: 0.8
              }}
              cometConfig={{
                size: 1.5,
                trailLength: 100,
                speed: 0.001,
                targetFPS: 30,
                respectReducedMotion: true
              }}
              imagePosition={{
                x: 50,
                y: 0,
                scale: 1
              }}
            />
          </section>

          {/* Projects showcase and other sections would follow... */}
        </div>
      </div>
    </div>
  );
};

export default CreatorsPage;
