import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import Background from '../../../../core/Background';
import MeteorShower from '../../../../core/effects/cosmiceffects/MeteorShower';
import RevealText from '../../../../core/effects/RevealText';
import { createUseStyles } from 'react-jss';

const useStyles = createUseStyles({
  // Main container and layout
  evolutionSimulator: {
    position: 'relative',
    width: '100%',
    height: '100vh',
    overflow: 'hidden',
    color: '#e0e0e0',
    fontFamily: '"Garamond", "Adobe Caslon Pro", serif',
    display: 'flex',
    flexDirection: 'column'
  },

  // Enhanced Navbar
  navbar: {
    position: 'relative',
    width: '100%',
    backgroundColor: 'rgba(15, 15, 20, 0.85)',
    borderBottom: '1px solid rgba(191, 173, 127, 0.4)',
    padding: '0.5rem 1rem',
    zIndex: 100,
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'space-between',
    alignItems: 'center',
    boxShadow: '0 4px 15px rgba(0, 0, 0, 0.3)'
  },
  title: {
    color: '#bfad7f',
    fontSize: '1.5rem',
    margin: 0,
    fontWeight: 300,
    letterSpacing: '0.05em',
    textShadow: '0 2px 5px rgba(0, 0, 0, 0.5)',
    display: 'flex',
    alignItems: 'center',
    gap: '0.5rem'
  },
  logoIcon: {
    width: '24px',
    height: '24px',
    borderRadius: '50%',
    background: 'linear-gradient(135deg, #bfad7f 0%, #7D6B9E 100%)',
    display: 'inline-block'
  },

  // Navigation Elements
  navControls: {
    display: 'flex',
    gap: '1rem',
    alignItems: 'center',
    flexWrap: 'wrap'
  },
  dropdown: {
    position: 'relative',
    marginRight: '0.5rem'
  },
  dropdownButton: {
    backgroundColor: 'rgba(30, 30, 40, 0.6)',
    border: '1px solid rgba(191, 173, 127, 0.3)',
    color: '#e0e0e0',
    padding: '0.4rem 0.8rem',
    borderRadius: '4px',
    fontFamily: 'inherit',
    fontSize: '0.9rem',
    cursor: 'pointer',
    transition: 'all 0.2s ease',
    display: 'flex',
    alignItems: 'center',
    gap: '0.5rem',
    '&:hover': {
      backgroundColor: 'rgba(191, 173, 127, 0.2)',
      borderColor: 'rgba(191, 173, 127, 0.6)'
    }
  },
  dropdownContent: {
    position: 'absolute',
    top: '100%',
    left: 0,
    backgroundColor: 'rgba(20, 20, 25, 0.95)',
    minWidth: '200px',
    maxHeight: '70vh',
    overflowY: 'auto',
    borderRadius: '4px',
    padding: '0.5rem',
    boxShadow: '0 5px 15px rgba(0, 0, 0, 0.5)',
    zIndex: 101,
    border: '1px solid rgba(191, 173, 127, 0.3)'
  },
  menuItem: {
    padding: '0.5rem 0.75rem',
    cursor: 'pointer',
    borderRadius: '3px',
    transition: 'all 0.15s ease',
    '&:hover': {
      backgroundColor: 'rgba(191, 173, 127, 0.2)'
    }
  },
  menuItemActive: {
    backgroundColor: 'rgba(191, 173, 127, 0.3)',
    fontWeight: 'bold'
  },
  menuItemTitle: {
    fontSize: '0.95rem',
    color: '#bfad7f'
  },
  menuItemDescription: {
    fontSize: '0.8rem',
    color: 'rgba(224, 224, 224, 0.7)',
    marginTop: '0.25rem'
  },

  // Tabs for different controls
  navTabs: {
    display: 'flex',
    gap: '0.25rem',
    marginLeft: 'auto'
  },
  navTab: {
    padding: '0.4rem 0.8rem',
    backgroundColor: 'transparent',
    border: 'none',
    borderBottom: '2px solid transparent',
    color: 'rgba(224, 224, 224, 0.7)',
    cursor: 'pointer',
    fontSize: '0.9rem',
    transition: 'all 0.2s ease',
    '&:hover': {
      color: '#bfad7f',
      borderBottomColor: 'rgba(191, 173, 127, 0.3)'
    }
  },
  navTabActive: {
    color: '#bfad7f',
    borderBottomColor: '#bfad7f'
  },

  // View controls
  viewControls: {
    display: 'flex',
    alignItems: 'center',
    gap: '0.75rem'
  },
  viewButton: {
    backgroundColor: 'rgba(30, 30, 40, 0.6)',
    border: '1px solid rgba(191, 173, 127, 0.3)',
    color: '#e0e0e0',
    width: '32px',
    height: '32px',
    borderRadius: '4px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    cursor: 'pointer',
    transition: 'all 0.2s ease',
    '&:hover': {
      backgroundColor: 'rgba(191, 173, 127, 0.2)',
      borderColor: 'rgba(191, 173, 127, 0.6)'
    }
  },

  // Environmental controls panel
  environmentPanel: {
    position: 'absolute',
    top: '100%',
    right: 0,
    backgroundColor: 'rgba(20, 20, 25, 0.95)',
    border: '1px solid rgba(191, 173, 127, 0.3)',
    borderRadius: '4px',
    padding: '1rem',
    zIndex: 100,
    width: '300px',
    boxShadow: '0 5px 15px rgba(0, 0, 0, 0.5)'
  },
  environmentTitle: {
    color: '#bfad7f',
    fontSize: '1rem',
    marginTop: 0,
