  // Setup the main visualization canvas
  useEffect(() => {
    if (!canvasRef.current || !containerRef.current) return;

    const canvas = canvasRef.current;
    const container = containerRef.current;
    const ctx = canvas.getContext('2d');

    // Set up canvas dimensions
    const updateCanvasDimensions = () => {
      const rect = container.getBoundingClientRect();
      canvas.width = rect.width;
      canvas.height = rect.height;

      networkRef.current = {
        width: rect.width,
        height: rect.height,
        centerX: rect.width / 2,
        centerY: rect.height / 2
      };
    };

    updateCanvasDimensions();
    window.addEventListener('resize', updateCanvasDimensions);

    // Animation function for the network visualization
    const animate = () => {
      if (!networkRef.current) return;

      // Clear canvas
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Apply zoom and pan transformations
      ctx.save();
      ctx.translate(panOffset.x, panOffset.y);
      ctx.scale(zoom, zoom);

      // Draw network visualization - this would contain the visualization logic
      // from your original code, adapted to this component structure

      // Example placeholder visualization
      const { centerX, centerY } = networkRef.current;

      // Draw body shape at center
      ctx.beginPath();
      ctx.arc(centerX, centerY, 50, 0, Math.PI * 2);
      ctx.fillStyle = 'rgba(191, 173, 127, 0.8)';
      ctx.fill();
      ctx.strokeStyle = 'rgba(255, 255, 255, 0.5)';
      ctx.lineWidth = 2;
      ctx.stroke();

      // Draw body shape text
      ctx.font = 'bold 14px Arial';
      ctx.fillStyle = 'rgba(0, 0, 0, 0.8)';
      ctx.textAlign = 'center';
      ctx.textBaseline = 'middle';
      const bodyShape = bodyShapes.find(shape => shape.id === selectedBodyShape);
      ctx.fillText(bodyShape?.name || 'Body Shape', centerX, centerY);

      // Draw trait nodes in a circle around center
      const traitRadius = 180;
      traitCategories.forEach((category, index) => {
        const angle = (index / traitCategories.length) * Math.PI * 2;
        const x = centerX + Math.cos(angle) * traitRadius;
        const y = centerY + Math.sin(angle) * traitRadius;

        // Draw node
        ctx.beginPath();
        ctx.arc(x, y, 20, 0, Math.PI * 2);
        ctx.fillStyle = category.color;
        ctx.fill();
        ctx.strokeStyle = 'white';
        ctx.lineWidth = 2;
        ctx.stroke();

        // Draw connection to center
        ctx.beginPath();
        ctx.moveTo(centerX, centerY);
        ctx.lineTo(x, y);
        ctx.strokeStyle = category.color;
        ctx.lineWidth = 2;
        ctx.stroke();

        // Draw trait name
        ctx.font = '12px Arial';
        ctx.fillStyle = 'white';
        ctx.textAlign = 'center';
        ctx.textBaseline = 'middle';
        ctx.fillText(category.name, x, y);

        // This would be expanded with the full network visualization logic
      });

      ctx.restore();

      // Continue animation
      animationFrameRef.current = requestAnimationFrame(animate);
    };

    // Start animation loop
    animate();

    // Set loaded after a delay
    const timer = setTimeout(() => {
      setIsLoaded(true);
    }, 1000);

    // Cleanup on unmount
    return () => {
      window.removeEventListener('resize', updateCanvasDimensions);
      clearTimeout(timer);
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }
    };
  }, [selectedBodyShape, selectedTraits, viewMode, zoom, panOffset]);

  // Calculate stress and adaptation probability when environment changes
  useEffect(() => {
    // Using simplified stress calculation
    const stressFromEnvironment = Object.values(environment).reduce((sum, value) => sum + Math.abs(value), 0) / 6;
    setTotalEffectiveStress(stressFromEnvironment);

    // Simplified adaptation probability calculation
    const probability = 1 / (1 + Math.exp(-10 * (stressFromEnvironment - 0.5)));
    setAdaptationProbability(probability);
  }, [environment, selectedBodyShape, selectedTraits]);

  // Handle mouse move for background effect
  const handleMouseMove = (e) => {
    setCursorPosition({ x: e.clientX, y: e.clientY });

    // Handle dragging
    if (isDragging) {
      const dx = e.clientX - dragStart.x;
      const dy = e.clientY - dragStart.y;
      setPanOffset({
        x: panOffset.x + dx,
        y: panOffset.y + dy
      });
      setDragStart({
        x: e.clientX,
        y: e.clientY
      });
    }
  };

  // Handle mouse down for drag start
  const handleMouseDown = (e) => {
    setIsDragging(true);
    setDragStart({
      x: e.clientX,
      y: e.clientY
    });

    // Change cursor style
    if (containerRef.current) {
      containerRef.current.style.cursor = 'grabbing';
    }
  };

  // Handle mouse up for drag end
  const handleMouseUp = () => {
    setIsDragging(false);

    // Reset cursor style
    if (containerRef.current) {
      containerRef.current.style.cursor = 'grab';
    }
  };

  // Handle mouse wheel for zoom
  const handleWheel = (e) => {
    e.preventDefault();
    const zoomFactor = 0.1;
    const newZoom = e.deltaY < 0 
      ? Math.min(zoom + zoomFactor, 3) 
      : Math.max(zoom - zoomFactor, 0.5);

    setZoom(newZoom);
  };

  // Update environmental stressor
  const handleEnvironmentChange = (stressor, value) => {
    setEnvironment(prev => ({
      ...prev,
      [stressor]: parseFloat(value)
    }));
  };

  // Handle body shape selection
  const handleBodyShapeSelect = (shapeId) => {
    setSelectedBodyShape(shapeId);
    setShowBodyShapeMenu(false);
  };

  // Handle trait selection
  const handleTraitSelect = (categoryId, traitId) => {
    setSelectedTraits(prev => ({
      ...prev,
      [categoryId]: traitId
    }));
    setShowTraitMenus(prev => ({
      ...prev,
      [categoryId]: false
    }));
  };

  // Toggle trait menu visibility
  const handleTraitMenuToggle = (categoryId) => {
    setShowTraitMenus(prev => ({
      ...prev,
      [categoryId]: !prev[categoryId]
    }));

    // Close other menus
    setShowBodyShapeMenu(false);
    setShowCategoryMenu(false);
    Object.keys(showTraitMenus).forEach(key => {
      if (key !== categoryId) {
        setShowTraitMenus(prev => ({
          ...prev,
          [key]: false
        }));
      }
    });
  };

