          </button>
          <button 
            className={`${classes.navTab} ${activeTab === 'environment' ? classes.navTabActive : ''}`}
            onClick={() => {
              setActiveTab('environment');
              setShowEnvironmentPanel(!showEnvironmentPanel);
              setShowBodyShapeMenu(false);
              setShowCategoryMenu(false);
              setShowTraitMenus({});
            }}
          >
            Environment
          </button>
          <button 
            className={`${classes.navTab} ${activeTab === 'synthesis' ? classes.navTabActive : ''}`}
            onClick={() => setActiveTab('synthesis')}
          >
            Synthesis
          </button>
        </div>

        {/* View Controls */}
        <div className={classes.viewControls}>
          <button 
            className={classes.viewButton}
            onClick={() => setZoom(Math.min(zoom + 0.1, 3))}
            title="Zoom In"
          >
            +
          </button>
          <button 
            className={classes.viewButton}
            onClick={() => setZoom(Math.max(zoom - 0.1, 0.5))}
            title="Zoom Out"
          >
            -
          </button>
          <button 
            className={classes.viewButton}
            onClick={handleResetView}
            title="Reset View"
          >

          </button>
        </div>
      </div>

      {/* Environment Panel */}
      <AnimatePresence>
        {renderEnvironmentPanel()}
      </AnimatePresence>

      {/* Main Visualization Container */}
      <div 
        ref={containerRef}
        className={classes.visualizationContainer}
        onMouseDown={handleMouseDown}
        onMouseUp={handleMouseUp}
        onMouseLeave={handleMouseUp}
        onWheel={handleWheel}
      >
        <canvas 
          ref={canvasRef}
          className={classes.networkCanvas}
        />

        {/* Category Filter Buttons */}
        <div className={classes.categoryFilters}>
          <button 
            className={`${classes.categoryButton} ${viewMode === 'all' ? classes.categoryButtonActive : ''}`}
            onClick={() => setViewMode('all')}
          >
            All Traits
          </button>

          {traitCategories.map(category => (
            <button 
              key={category.id}
              className={`${classes.categoryButton} ${viewMode === category.id ? classes.categoryButtonActive : ''}`}
              onClick={() => setViewMode(category.id)}
              style={{ 
                borderBottom: `2px solid ${category.color}`
              }}
            >
              {category.name}
            </button>
          ))}

          <button 
            className={`${classes.categoryButton} ${viewMode === 'relevant' ? classes.categoryButtonActive : ''}`}
            onClick={() => setViewMode('relevant')}
          >
            By Relevance
          </button>
        </div>
      </div>
    </div>
  );
};

export default EnhancedEvolutionSimulator;
