import React, { useState, useEffect, useRef } from 'react';

const EnhancedLandingPage = () => {
  const [activeSection, setActiveSection] = useState(null);
  const [cursorPosition, setCursorPosition] = useState({ x: 0, y: 0 });
  const [isLoaded, setIsLoaded] = useState(false);
  const [introComplete, setIntroComplete] = useState(false);
  const [currentQuoteIndex, setCurrentQuoteIndex] = useState(0);

  const canvasRef = useRef(null);
  const moonCanvasRef = useRef(null);
  const particlesRef = useRef([]);
  const requestRef = useRef(null);
  const moonRequestRef = useRef(null);
  const scrollRef = useRef(null);

  const quotes = [
    {
      text: "My candle burns at both ends;\nIt will not last the night;\nBut, ah my foes, and\noh my friend —\nit gives a\nlovely\nlight",
      author: "Edna St. Vincent Millay"
    },
    {
      text: "And he will come this, light insouciant lover,\nblowing his bugles to a rising morrow,\nto rend her veil, her wizened mind uncover,\nto leave her conquered by life's stinging sorrow",
      author: "Virginia McCormick"
    },
    {
      text: "We are all in the gutter,\nbut some of us\nare looking\nat the stars.",
      author: "Oscar Wilde"
    },
    {
      text: "Hope is the thing with feathers\nThat perches in the soul,\nAnd sings the tune without the words,\nAnd never stops at all.",
      author: "Emily Dickinson"
    },
    {
      text: "The universe is made of stories,\nnot of atoms.",
      author: "Muriel Rukeyser"
    }
  ];

  const sections = [
    {
      id: "creator",
      title: "Creator",
      description: "The mind behind the mysteries, shaping worlds from shadow and light.",
      icon: "◉", // Circle symbol
      path: "/creator"
    },
    {
      id: "creativity",
      title: "Creativity",
      description: "Visualizations of complex systems, revealing patterns beneath the chaos.",
      icon: "△", // Triangle symbol
      path: "/creativity"
    },
    {
      id: "cycles",
      title: "Cycles",
      description: "An intricate framework modeling the evolution of worlds through endless creation and destruction.",
      icon: "⌁", // Lightning symbol
      path: "/cycles"
    },
    {
      id: "codex",
      title: "Codex",
      description: "Repository of arcane knowledge, rules, and lore from across the multiverse.",
      icon: "⊕", // Circled plus symbol
      path: "/codex"
    },
    {
      id: "characters",
      title: "Characters",
      description: "Beings shaped by their environments, each with a unique story to tell.",
      icon: "✧", // Star symbol
      path: "/characters"
    }
  ];

  // Intro sequence timing
  useEffect(() => {
    // Run through quotes
    const quoteInterval = setInterval(() => {
      setCurrentQuoteIndex(prev => {
        const nextIndex = prev + 1;
        if (nextIndex >= quotes.length) {
          clearInterval(quoteInterval);

          // After quotes finish, delay before showing main interface
          setTimeout(() => {
            setIntroComplete(true);

            // After intro animation completes, set loaded state
            setTimeout(() => {
              setIsLoaded(true);
            }, 800);
          }, 1000);

          return prev;
        }
        return nextIndex;
      });
    }, 3500); // Each quote shows for 3.5 seconds

    return () => clearInterval(quoteInterval);
  }, []);

  // Initialize particles
  useEffect(() => {
    particlesRef.current = Array(50).fill().map(() => ({
      x: Math.random() * window.innerWidth,
      y: Math.random() * window.innerHeight,
      size: Math.random() * 2 + 1,
      speedX: (Math.random() - 0.5) * 0.3,
      speedY: (Math.random() - 0.5) * 0.3,
      opacity: Math.random() * 0.5 + 0.1
    }));

    return () => {
      if (requestRef.current) {
        cancelAnimationFrame(requestRef.current);
      }
    };
  }, []);

  // Canvas animation
  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Draw particles
      particlesRef.current.forEach(particle => {
        // Update position
        particle.x += particle.speedX;
        particle.y += particle.speedY;

        // Wrap around screen
        if (particle.x < 0) particle.x = canvas.width;
        if (particle.x > canvas.width) particle.x = 0;
        if (particle.y < 0) particle.y = canvas.height;
        if (particle.y > canvas.height) particle.y = 0;

        // Draw particle
        ctx.beginPath();
        ctx.arc(particle.x, particle.y, particle.size, 0, Math.PI * 2);
        ctx.fillStyle = `rgba(191, 173, 127, ${particle.opacity})`;
        ctx.fill();
      });

      // Draw cursor influence
      if (cursorPosition.x && cursorPosition.y) {
        const gradient = ctx.createRadialGradient(
          cursorPosition.x, cursorPosition.y, 0,
          cursorPosition.x, cursorPosition.y, 150
        );
        gradient.addColorStop(0, 'rgba(160, 142, 97, 0.1)');
        gradient.addColorStop(1, 'rgba(160, 142, 97, 0)');

        ctx.beginPath();
        ctx.arc(cursorPosition.x, cursorPosition.y, 150, 0, Math.PI * 2);
        ctx.fillStyle = gradient;
        ctx.fill();
      }

      requestRef.current = requestAnimationFrame(animate);
    };

    animate();

    // Resize handler
    const handleResize = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };

    window.addEventListener('resize', handleResize);
    return () => {
      window.removeEventListener('resize', handleResize);
      cancelAnimationFrame(requestRef.current);
    };
  }, [cursorPosition]);

  // Moon canvas animation
  useEffect(() => {
    const canvas = moonCanvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    canvas.width = window.innerWidth;
    canvas.height = 400; // Height for moon section

    let phase = 0;

    const animateMoon = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Draw stars
      for (let i = 0; i < 100; i++) {
        const x = Math.random() * canvas.width;
        const y = Math.random() * canvas.height;
        const size = Math.random() * 1.5;
        const opacity = Math.random() * 0.8 + 0.2;

        ctx.beginPath();
        ctx.arc(x, y, size, 0, Math.PI * 2);
        ctx.fillStyle = `rgba(255, 255, 255, ${opacity})`;
        ctx.fill();
      }

      // Draw moon
      const centerX = canvas.width / 2;
      const centerY = 150;
      const moonRadius = 80;

      // Full moon
      const moonGradient = ctx.createRadialGradient(
        centerX - 20, centerY - 20, 10,
        centerX, centerY, moonRadius
      );
      moonGradient.addColorStop(0, 'rgba(255, 255, 255, 1)');
      moonGradient.addColorStop(0.2, 'rgba(240, 240, 230, 0.9)');
      moonGradient.addColorStop(0.5, 'rgba(220, 220, 210, 0.8)');
      moonGradient.addColorStop(1, 'rgba(200, 200, 190, 0.6)');

      ctx.beginPath();
      ctx.arc(centerX, centerY, moonRadius, 0, Math.PI * 2);
      ctx.fillStyle = moonGradient;
      ctx.fill();

      // Moon details/craters
      for (let i = 0; i < 6; i++) {
        const craterX = centerX - 30 + Math.random() * 60;
        const craterY = centerY - 30 + Math.random() * 60;
        const craterSize = 2 + Math.random() * 6;

        const distToCenter = Math.sqrt(
          Math.pow(craterX - centerX, 2) + 
          Math.pow(craterY - centerY, 2)
        );

        if (distToCenter < moonRadius - 10) {
          ctx.beginPath();
          ctx.arc(craterX, craterY, craterSize, 0, Math.PI * 2);
          ctx.fillStyle = 'rgba(180, 180, 180, 0.3)';
          ctx.fill();
        }
      }

      // Muted glow effect
      const glowSize = moonRadius + 20 + Math.sin(phase) * 5;
      const outerGlow = ctx.createRadialGradient(
        centerX, centerY, moonRadius,
        centerX, centerY, glowSize
      );
      outerGlow.addColorStop(0, 'rgba(255, 255, 255, 0.3)');
      outerGlow.addColorStop(1, 'rgba(255, 255, 255, 0)');

      ctx.beginPath();
      ctx.arc(centerX, centerY, glowSize, 0, Math.PI * 2);
      ctx.fillStyle = outerGlow;
      ctx.fill();

      // Update phase
      phase += 0.02;

      moonRequestRef.current = requestAnimationFrame(animateMoon);
    };

    animateMoon();

    // Resize handler
    const handleResize = () => {
      canvas.width = window.innerWidth;
    };

    window.addEventListener('resize', handleResize);
    return () => {
      window.removeEventListener('resize', handleResize);
      cancelAnimationFrame(moonRequestRef.current);
    };
  }, []);

  // Track mouse movement
  const handleMouseMove = (e) => {
    setCursorPosition({ x: e.clientX, y: e.clientY });
  };

  // Navigate to section
  const handleNavigate = (path) => {
    // For demo purposes, just log the navigation
    console.log(`Navigating to: ${path}`);
    // In a real app, you'd use your routing library:
    // window.location.href = path; or history.push(path);
  };

  // Scroll to moon section
  const scrollToMoon = () => {
    if (scrollRef.current) {
      scrollRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  // Format quote text with line breaks
  const formatQuoteText = (text) => {
    return text.split('\n').map((line, i) => (
      <span key={i} className="quote-line">
        {line}
        <br />
      </span>
    ));
  };

  // Render intro animation or main content
  const renderContent = () => {
    if (!introComplete) {
      const quote = quotes[currentQuoteIndex];
      return (
        <div className="intro-container">
          <div className="intro-moon">
            <div className="moon-circle"></div>
            <div className="moon-glow"></div>
          </div>
          <div className="quote-container">
            <p className="quote-text">
              {quote && formatQuoteText(quote.text)}
            </p>
            <p className="quote-author">
              {quote && quote.author}
            </p>
          </div>
        </div>
      );
    }

    return (
      <div className={`content-container ${isLoaded ? 'loaded' : ''}`}>
        <div className="header-container">
          <h1 className="main-title">CRESCENT</h1>
          <div className="title-divider" />
          <p className="subtitle">A Dark Fantasy Experience</p>
        </div>

        <div className="sections-container">
          {sections.map((section, index) => (
            <div 
              key={section.id}
              className={`section-row ${activeSection === section.id ? 'active' : ''}`}
              onMouseEnter={() => setActiveSection(section.id)}
              onMouseLeave={() => setActiveSection(null)}
              onClick={() => handleNavigate(section.path)}
              style={{ animationDelay: `${index * 0.15 + 0.2}s` }}
            >
              <div className="section-indicator">
                <div className="indicator-dot"></div>
              </div>
              <div className="section-content">
                <div className="section-icon-wrapper">
                  <div className="section-icon">{section.icon}</div>
                </div>
                <div className="section-text">
                  <h2 className="section-title">{section.title.toUpperCase()}</h2>
                  <p className="section-description">{section.description}</p>
                </div>
              </div>
              <div className="diagonal-cut" />
            </div>
          ))}
        </div>

        <div className="scroll-indicator" onClick={scrollToMoon}>
          <span>▼</span>
        </div>

        <div className="features-section">
          <h3 className="features-title">RECENT UPDATES</h3>

          <div className="features-group">
            <h4 className="features-subtitle">Improved Section Interaction</h4>
            <ul className="features-list">
              <li>Refined hover animations for smoother transitions</li>
              <li>Added dot indicators that illuminate when sections are active</li>
              <li>Enhanced active state with subtle glow effects</li>
            </ul>
          </div>

          <div className="features-group">
            <h4 className="features-subtitle">Responsive Layout</h4>
            <ul className="features-list">
              <li>Ensured the design adapts well to different screen sizes</li>
              <li>Prioritized content visibility on smaller screens</li>
            </ul>
          </div>
        </div>

        <div ref={scrollRef} className="moon-section">
          <canvas ref={moonCanvasRef} className="moon-canvas" />
          <div className="brand-mark">
            <span className="symbol">◊</span>
            <span className="year">2025</span>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div 
      className="landing-container"
      onMouseMove={handleMouseMove}
    >
      <canvas ref={canvasRef} className="background-canvas" />
      {renderContent()}

