          text-transform: uppercase;
          position: relative;
          line-height: 0.9;
        }

        .title-divider {
          height: 1px;
          width: 100px;
          background: linear-gradient(to right, rgba(160, 142, 97, 0.7), rgba(160, 142, 97, 0));
          margin: 0.5rem 0;
        }

        .subtitle {
          font-size: 1.1rem;
          color: rgba(224, 224, 224, 0.7);
          font-family: 'Adobe Caslon Pro', 'Caslon', serif;
          font-style: italic;
        }

        .sections-container {
          display: flex;
          flex-direction: column;
          gap: 0.75rem;
          margin-bottom: 4rem;
        }

        .section-row {
          position: relative;
          height: 5rem;
          background-color: rgba(15, 15, 15, 0.5);
          border: 1px solid rgba(160, 142, 97, 0.3);
          border-radius: 4px;
          cursor: pointer;
          transition: all 0.3s ease;
          overflow: hidden;
          animation: slideInFromLeft 0.8s ease backwards;
          display: flex;
          align-items: center;
          padding-right: 3rem; /* Space for diagonal cut */
        }

        .section-row::before {
          content: '';
          position: absolute;
          top: 0;
          left: 0;
          width: 0;
          height: 2px;
          background: linear-gradient(to right, rgba(160, 142, 97, 0.7), rgba(160, 142, 97, 0));
          transition: width 0.6s ease;
        }

        .section-row.active {
          background-color: rgba(20, 20, 20, 0.7);
          transform: translateX(15px);
          box-shadow: 0 5px 20px rgba(0, 0, 0, 0.3), 0 0 15px rgba(160, 142, 97, 0.2);
          border-color: rgba(160, 142, 97, 0.5);
          height: 5.5rem;
        }

        .section-row.active::before {
          width: 100%;
        }

        .section-indicator {
          width: 20px;
          height: 100%;
          display: flex;
          align-items: center;
          justify-content: center;
          margin-left: 0.5rem;
        }

        .indicator-dot {
          width: 6px;
          height: 6px;
          border-radius: 50%;
          background-color: rgba(160, 142, 97, 0.3);
          transition: all 0.3s ease;
        }

        .section-row.active .indicator-dot {
          background-color: #bfad7f;
          box-shadow: 0 0 8px rgba(160, 142, 97, 0.6);
          width: 8px;
          height: 8px;
        }

        .section-content {
          display: flex;
          align-items: center;
          width: 100%;
          height: 100%;
          position: relative;
          z-index: 2;
        }

        .section-icon-wrapper {
          width: 60px;
          display: flex;
          justify-content: center;
          align-items: center;
        }

        .section-icon {
          font-size: 1.5rem;
          color: #a08e61;
          opacity: 0.9;
          transition: all 0.3s ease;
        }

        .section-row.active .section-icon {
          transform: scale(1.2);
          color: #bfad7f;
        }

        .section-text {
          flex: 1;
        }

        .section-title {
          font-size: 1.25rem;
          margin-bottom: 0.25rem;
          color: #bfad7f;
          font-weight: 500;
          letter-spacing: 0.05em;
          transition: all 0.3s ease;
        }

        .section-row.active .section-title {
          transform: translateY(-2px);
        }

        .section-description {
          font-size: 0.85rem;
          color: rgba(224, 224, 224, 0.7);
          line-height: 1.4;
          max-width: 90%;
          font-family: 'Adobe Caslon Pro', 'Caslon', serif;
          opacity: 0;
          height: 0;
          transition: all 0.3s ease;
          overflow: hidden;
        }

        .section-row.active .section-description {
          opacity: 1;
          height: auto;
        }

        .diagonal-cut {
          position: absolute;
          top: 0;
          right: 0;
          width: 40px;
          height: 100%;
          overflow: hidden;
          z-index: 1;
        }

        .diagonal-cut::before {
          content: '';
          position: absolute;
          top: 0;
          right: 0;
          width: 80px;
          height: 150%;
          background-color: #080808;
          transform: rotate(15deg) translateX(20px);
        }

        .diagonal-cut::after {
          content: '';
          position: absolute;
          top: 0;
          right: 0;
          width: 1px;
          height: 150%;
          background-color: rgba(160, 142, 97, 0.3);
          transform: rotate(15deg) translateX(20px);
        }

        .scroll-indicator {
          text-align: center;
          color: rgba(160, 142, 97, 0.5);
          font-size: 1.2rem;
          margin: 1rem 0 3rem;
          cursor: pointer;
          animation: bounce 2s infinite ease-in-out;
          transition: color 0.3s ease;
        }

        .scroll-indicator:hover {
          color: rgba(160, 142, 97, 0.8);
        }

        .features-section {
          width: 100%;
          margin-bottom: 6rem;
          color: rgba(224, 224, 224, 0.7);
          animation: fadeIn 1.5s ease backwards;
          animation-delay: 1s;
        }

        .features-title {
          font-size: 1.3rem;
          color: #bfad7f;
          margin-bottom: 2rem;
          letter-spacing: 0.05em;
          font-weight: 400;
        }

        .features-group {
          margin-bottom: 2rem;
          padding-left: 1rem;
          border-left: 1px solid rgba(160, 142, 97, 0.3);
        }

        .features-subtitle {
          font-size: 1.1rem;
          color: rgba(224, 224, 224, 0.9);
          margin-bottom: 1rem;
          font-weight: 400;
        }

        .features-list {
          list-style-type: none;
          padding-left: 1rem;
        }

        .features-list li {
          margin-bottom: 0.75rem;
          position: relative;
          font-size: 0.9rem;
          line-height: 1.6;
        }

        .features-list li::before {
          content: '•';
          position: absolute;
          left: -1rem;
          color: rgba(160, 142, 97, 0.6);
        }

        .moon-section {
          position: relative;
          width: 100%;
          height: 400px;
          margin-top: 2rem;
        }

        .moon-canvas {
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
        }

        .brand-mark {
          position: absolute;
          bottom: 40px;
          left: 50%;
          transform: translateX(-50%);
          display: flex;
          flex-direction: column;
          align-items: center;
          opacity: 0.5;
          transition: opacity 0.3s ease;
          text-align: center;
        }

        .brand-mark:hover {
          opacity: 0.8;
        }

        .symbol {
          font-size: 1.5rem;
          color: #a08e61;
          margin-bottom: 0.25rem;
        }

        .year {
          font-size: 0.75rem;
          color: rgba(224, 224, 224, 0.5);
          letter-spacing: 0.2em;
        }

        /* Animations */
        @keyframes fadeIn {
          from {
            opacity: 0;
          }
          to {
            opacity: 1;
          }
        }

        @keyframes fadeQuote {
          0% {
            opacity: 0;
            transform: translateY(20px);
          }
          20% {
            opacity: 1;
            transform: translateY(0);
          }
          80% {
            opacity: 1;
            transform: translateY(0);
          }
          100% {
            opacity: 0;
            transform: translateY(-20px);
          }
        }

        @keyframes slideInFromLeft {
          from {
            opacity: 0;
            transform: translateX(-40px);
          }
          to {
            opacity: 1;
            transform: translateX(0);
          }
        }

        @keyframes bounce {
          0%, 20%, 50%, 80%, 100% {
            transform: translateY(0);
          }
          40% {
            transform: translateY(-10px);
          }
          60% {
            transform: translateY(-5px);
          }
        }

        @keyframes pulseMoon {
          0% {
            transform: scale(0.95);
          }
          50% {
            transform: scale(1.05);
          }
          100% {
            transform: scale(0.95);
          }
        }

        @keyframes glowPulse {
          0% {
            opacity: 0.7;
          }
          50% {
            opacity: 1;
          }
          100% {
            opacity: 0.7;
          }
        }

        /* Media Queries */
        @media (max-width: 768px) {
          .header-container {
            margin-bottom: 2rem;
          }

          .main-title {
            font-size: 2.5rem;
          }

          .section-row {
            height: 4rem;
          }

          .section-row.active {
            height: 5rem;
          }

          .section-icon-wrapper {
            width: 40px;
          }

          .section-description {
            font-size: 0.8rem;
          }

          .quote-text {
            font-size: 1.1rem;
          }
        }
      `}</style>
    </div>
  );
};

export default EnhancedLandingPage;
