import React, { useRef, useState, useEffect, useCallback, useMemo } from 'react';
import { motion, useInView, animate } from 'framer-motion';

/**
 * PersonProfileCard Component
 * 
 * A profile card with manually implemented scroll animation.
 * Uses direct DOM scroll listener and Framer Motion animations without relying on hooks
 * that might not be working in this context.
 */
const PersonProfileCard = ({
  person,
  animationConfig = {
    threshold: 0.2,
    once: true,
    initialY: 30,
    duration: 0.8
  },
  imagePosition = {
    x: 50,
    y: 50,
    scale: 1
  },
  additionalSections = [],
  onSectionChange = null,
  topOffset = 100,
  showProfileImage = true,
  allowImageToggle = true,
  showStats = true,
  highlightColor = '#bfad7f',
  textColor = 'rgba(224, 224, 224, 0.7)',
  minLineWidth = 10,
  maxLineWidth = 40,
  fontFamily = '"Garamond", "Adobe Caslon Pro", serif',
  navigationItems = [
    { id: 'about', label: 'ABOUT', content: null },
    { id: 'experience', label: 'EXPERIENCE', content: null },
    { id: 'projects', label: 'PROJECTS', content: null }
  ],
  contentCompression = 0,
  // Navigation scroll animation configuration
  navScrollConfig = {
    startPosition: 0,     // Start position for scroll detection
    endPosition: 300,     // End position for scroll detection
    initialY: 30,         // Initial Y position (pixels off-screen)
    finalY: 0,            // Final Y position
    opacityValues: [0.3, 1, 1], // Opacity values during animation
    springConfig: {       // Spring physics configuration
      stiffness: 50,
      damping: 25,
      mass: 0.5
    }
  }
}) => {
  // State management
  const [isHovered, setIsHovered] = useState(false);
  const [activeSection, setActiveSection] = useState(navigationItems[0]?.id || 'about');
  const [showImageState, setShowImageState] = useState(showProfileImage);
  const [expandedNavItem, setExpandedNavItem] = useState(null);
  const [scrollYValue, setScrollYValue] = useState(0);
  const [animatedY, setAnimatedY] = useState(navScrollConfig.initialY);
  const [animatedOpacity, setAnimatedOpacity] = useState(0.3);

  // Calculate widths based on contentCompression (0-10 scale)
  const compressionFactor = Math.min(Math.max(contentCompression, 0), 10) / 10;
  const sidebarWidth = 40 - (compressionFactor * 5); // 35-40% range
  const contentWidth = 60 - (compressionFactor * 5); // 55-60% range
  const contentPadding = 2 + (compressionFactor * 2); // 2-4rem range

  // Refs for DOM elements
  const containerRef = useRef(null);
  const sidebarRef = useRef(null);
  const contentRef = useRef(null);
  const navContainerRef = useRef(null);
  const animatedElementRef = useRef(null);

  // Create refs for sections
  const section1Ref = useRef(null);
  const section2Ref = useRef(null);
  const section3Ref = useRef(null);
  const section4Ref = useRef(null);
  const section5Ref = useRef(null);
  const section6Ref = useRef(null);
  const section7Ref = useRef(null);
  const section8Ref = useRef(null);

  // Array of all section refs memoized to prevent recreation on every render
  const allSectionRefs = useMemo(() => [
    section1Ref, section2Ref, section3Ref, section4Ref,
    section5Ref, section6Ref, section7Ref, section8Ref
  ], []);

  // Map section IDs to refs
  const sectionRefs = useMemo(() => {
    const refsMap = {};
    navigationItems.forEach((item, index) => {
      if (index < allSectionRefs.length) {
        refsMap[item.id] = allSectionRefs[index];
      }
    });
    return refsMap;
  }, [navigationItems, allSectionRefs]);

  // Animation inView detection
  const isInView = useInView(containerRef, {
    threshold: animationConfig?.threshold ?? 0.2,
    once: animationConfig?.once ?? true
  });

  // Manual scroll listener implementation
  useEffect(() => {
    const handleScroll = () => {
      if (typeof window !== 'undefined') {
        setScrollYValue(window.scrollY);

        // Calculate animation values based on scroll position
        const scrollProgress = Math.max(0, Math.min(1, 
          (window.scrollY - navScrollConfig.startPosition) / 
          (navScrollConfig.endPosition - navScrollConfig.startPosition)
        ));

        // Calculate y position with easing
        const easedProgress = easeOutCubic(scrollProgress);
        const targetY = navScrollConfig.initialY + 
                      (navScrollConfig.finalY - navScrollConfig.initialY) * easedProgress;

        // Calculate opacity
        const opacityProgress = Math.max(0, Math.min(1,
          (window.scrollY - navScrollConfig.startPosition) / 100
        ));
        const targetOpacity = 0.3 + (1 - 0.3) * opacityProgress;

        // Apply animations with spring-like behavior
        applySpringAnimation(targetY, targetOpacity);
      }
    };

    // Simple easing function
    const easeOutCubic = (x) => {
      return 1 - Math.pow(1 - x, 3);
    };

    // Apply spring-like animation
    const applySpringAnimation = (targetY, targetOpacity) => {
      if (animatedElementRef.current) {
        // Update states - this triggers a re-render but gives us smoother animation control
        setAnimatedY(targetY);
        setAnimatedOpacity(targetOpacity);
      }
    };

    // Initial call to set starting position
    handleScroll();

    // Add scroll listener
    window.addEventListener('scroll', handleScroll, { passive: true });

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [navScrollConfig.startPosition, navScrollConfig.endPosition, 
      navScrollConfig.initialY, navScrollConfig.finalY]);

  // Mouse event handlers for profile image hover effect
  const handleMouseEnter = useCallback(() => {
    setIsHovered(true);
  }, []);

  const handleMouseLeave = useCallback(() => {
    setIsHovered(false);
  }, []);

  // Toggle profile image visibility
  const toggleProfileImage = useCallback(() => {
    setShowImageState(prev => !prev);
  }, []);

  // Handle navigation item hover
  const handleNavItemHover = useCallback((section) => {
    setExpandedNavItem(section);
  }, []);

  const handleNavItemLeave = useCallback(() => {
    setExpandedNavItem(null);
  }, []);

  // Scroll to section handler
  const scrollToSection = useCallback((sectionId) => {
    setActiveSection(sectionId);

    // Notify parent component if callback provided
    if (onSectionChange) {
      onSectionChange(sectionId);
    }

    // Scroll to section with smooth behavior
    const sectionRef = sectionRefs[sectionId];
    if (sectionRef?.current) {
      sectionRef.current.scrollIntoView({ 
        behavior: 'smooth',
        block: 'start'
      });
    }
  }, [onSectionChange, sectionRefs]);

  // Set up intersection observer to detect which section is in view
  useEffect(() => {
    const sectionElements = Object.values(sectionRefs)
      .map(ref => ref.current)
      .filter(Boolean);

    if (sectionElements.length === 0) return;

    const options = {
      root: null,
      rootMargin: '-10% 0px -70% 0px',
      threshold: 0
    };

    const callback = (entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          const sectionId = entry.target.getAttribute('data-section');
          if (sectionId && sectionId !== activeSection) {
            setActiveSection(sectionId);

            // Notify parent component if callback provided
            if (onSectionChange) {
              onSectionChange(sectionId);
            }
          }
        }
      });
    };

    const observer = new IntersectionObserver(callback, options);

    // Observe all section refs
    sectionElements.forEach(element => observer.observe(element));

    return () => {
      observer.disconnect();
    };
  }, [activeSection, onSectionChange, sectionRefs]);

  return (
    <motion.div 
      ref={containerRef}
      className="profile-container"
      initial={{ opacity: 0, y: animationConfig?.initialY ?? 30 }}
      animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: animationConfig?.initialY ?? 30 }}
      transition={{ 
        duration: animationConfig?.duration ?? 0.8, 
        ease: "easeOut" 
      }}
      style={{
        display: 'flex',
        flexDirection: 'row',
        width: '100%',
        maxWidth: '1300px',
        margin: '0 auto',
        minHeight: '70vh',
        position: 'relative',
      }}
    >
      {/* Debug information - visible in development only */}
      {process.env.NODE_ENV === 'development' && (
        <div style={{
          position: 'fixed',
          top: '10px',
          right: '10px',
          padding: '10px',
          background: 'rgba(0,0,0,0.8)',
          color: 'white',
          zIndex: 9999,
          fontSize: '12px'
        }}>
          scrollY: {scrollYValue}<br/>
          animatedY: {animatedY.toFixed(2)}<br/>
          opacity: {animatedOpacity.toFixed(2)}
        </div>
      )}

      {/* Left sidebar wrapper */}
      <div 
        className="sidebar-wrapper"
        style={{
          width: `${sidebarWidth}%`,
          position: 'relative',
        }}
      >
        {/* Profile sidebar */}
        <div 
          ref={sidebarRef}
          className="profile-sidebar"
          style={{
            padding: '3rem 2rem 2rem 0',
            display: 'flex',
            flexDirection: 'column',
            width: '100%',
            position: 'sticky',
            top: `${topOffset}px`,
          }}
        >
          {/* Toggle profile image button - only shown when allowImageToggle is true */}
          {allowImageToggle && (
            <button 
              onClick={toggleProfileImage}
              className="toggle-profile-image"
              style={{
                position: 'absolute',
                top: '10px',
                right: '10px',
                background: 'transparent',
                color: highlightColor,
                border: `1px solid ${highlightColor}`,
                borderRadius: '4px',
                padding: '5px 10px',
                cursor: 'pointer',
                fontSize: '0.8rem',
                transition: 'all 0.3s ease',
                zIndex: 5,
                opacity: 0.7
              }}
              onMouseEnter={(e) => {
                e.currentTarget.style.backgroundColor = `${highlightColor}20`;
                e.currentTarget.style.opacity = '1';
              }}
              onMouseLeave={(e) => {
                e.currentTarget.style.backgroundColor = 'transparent';
                e.currentTarget.style.opacity = '0.7';
              }}
            >
              {showImageState ? 'Hide Image' : 'Show Image'}
            </button>
          )}

          {/* Profile image - conditionally rendered */}
          {showImageState && (
            <div 
              className="profile-image-container"
              style={{
                position: 'relative',
                width: '240px',
                height: '240px',
                borderRadius: '50%',
                boxShadow: '0 5px 15px rgba(0, 0, 0, 0.3)',
                transition: 'box-shadow 0.3s ease, transform 0.3s ease',
                overflow: 'hidden',
                boxSizing: 'border-box',
                marginBottom: '2rem',
                border: '1px solid rgba(191, 173, 127, 0.2)',
                transform: isHovered ? 'scale(1.02)' : 'scale(1)',
              }}
              onMouseEnter={handleMouseEnter}
              onMouseLeave={handleMouseLeave}
            >
              <div 
                className="profile-image"
                style={{
                  position: 'absolute',
                  top: 0,
                  left: 0,
                  width: '100%',
                  height: '100%',
                  borderRadius: '50%',
                  overflow: 'hidden',
                }}
              >
                <div 
                  className="image-wrapper"
                  style={{
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    width: '100%',
                    height: '100%',
                    overflow: 'hidden',
                    borderRadius: '50%'
                  }}
                >
                  <img 
                    src={person.image} 
                    alt={person.name} 
                    style={{
                      width: '100%',
                      height: '100%',
                      objectFit: 'cover',
                      transition: 'transform 0.6s ease, object-position 0.6s ease',
                      display: 'block',
                      objectPosition: `${imagePosition?.x ?? 50}% ${imagePosition?.y ?? 50}%`,
                      transform: `scale(${imagePosition?.scale ?? 1})`
                    }}
                    loading="lazy"
                  />
                </div>
              </div>
            </div>
          )}

          {/* Profile information with direct style-based animation */}
          <div
            ref={animatedElementRef}
            className="profile-sidebar-animated"
            style={{
              transform: `translateY(${animatedY}px)`,
              opacity: animatedOpacity,
              transition: 'transform 0.1s linear, opacity 0.1s linear',
              willChange: 'transform, opacity',
            }}
          >
            <div 
              className="profile-info"
              style={{
                marginBottom: '2rem',
                paddingLeft: '5px',
              }}
            >
              <h1 
                className="profile-name"
                style={{
                  fontSize: '2.8rem',
                  fontWeight: '300',
                  color: highlightColor,
                  letterSpacing: '0.05em',
                  marginBottom: '0.5rem',
                  transition: 'transform 0.3s ease',
                  transform: isHovered ? 'translateY(-3px)' : 'none',
                }}
              >
                {person.name}
              </h1>

              <h2 
                className="profile-role"
                style={{
                  fontSize: '1.2rem',
                  fontWeight: '300',
                  marginBottom: '2rem',
                  color: `${highlightColor}B3`, // 70% opacity
                  letterSpacing: '0.05em',
                  fontFamily: fontFamily,
                  fontStyle: 'italic',
                }}
              >
                {person.role}
              </h2>

              {/* Short tagline */}
              {person.tagline && (
                <p 
                  className="profile-tagline"
                  style={{
                    fontSize: '1.1rem',
                    lineHeight: '1.6',
                    marginBottom: '3rem',
                    maxWidth: '90%',
                    color: textColor,
                    fontFamily: fontFamily,
                    fontWeight: '300',
                  }}
                >
                  {person.tagline}
                </p>
              )}
            </div>

            {/* Navigation links - directly animated */}
            <div 
              ref={navContainerRef}
              className="nav-links" 
              style={{ marginTop: '3rem' }}
            >
              {navigationItems.map((navItem) => (
                <div 
                  key={navItem.id}
                  className="nav-link-container"
                  style={{
                    position: 'relative',
                    marginBottom: '1rem',
                    display: 'flex',
                    alignItems: 'center',
                  }}
                  onMouseEnter={() => handleNavItemHover(navItem.id)}
                  onMouseLeave={handleNavItemLeave}
                >
                  <div
                    className="nav-line"
                    style={{
                      position: 'absolute',
                      left: '0',
                      top: '50%',
                      transform: 'translateY(-50%)',
                      width: activeSection === navItem.id || expandedNavItem === navItem.id 
                        ? `${maxLineWidth}px` 
                        : `${minLineWidth}px`,
                      height: '0.75px',
                      backgroundColor: activeSection === navItem.id || expandedNavItem === navItem.id
                        ? `${highlightColor}E6` // 90% opacity 
                        : `${highlightColor}80`, // 50% opacity
                      transition: 'width 0.3s ease, background-color 0.3s ease',
                    }}
                  ></div>
                  <button 
                    className="nav-link"
                    onClick={() => scrollToSection(navItem.id)}
                    style={{
                      position: 'relative',
                      display: 'block',
                      padding: '0.5rem 0 0.5rem 40px',
                      fontSize: '0.85rem',
                      letterSpacing: '0.1em',
                      background: 'transparent',
                      border: 'none',
                      textAlign: 'left',
                      outline: 'none',
                      boxShadow: 'none',
                      transition: 'color 0.5s ease, transform 0.5s ease',
                      color: activeSection === navItem.id || expandedNavItem === navItem.id 
                        ? highlightColor 
                        : textColor,
                      cursor: 'pointer',
                      transform: activeSection === navItem.id || expandedNavItem === navItem.id 
                        ? 'translateX(10px)' 
                        : 'none',
                      width: 'fit-content', // Make button fit to content
                    }}
                  >
                    {navItem.label}
                  </button>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>

      {/* Right content section */}
      <div 
        ref={contentRef}
        className="content-section"
        style={{
          width: `${contentWidth}%`,
          padding: `3rem 0 2rem ${contentPadding}rem`,
          marginLeft: 'auto',
        }}
      >
        {/* Dynamically render sections based on navigationItems */}
        {navigationItems.map((section, index) => {
          // Only render sections that have corresponding refs
          if (index >= allSectionRefs.length) return null;

          return (
            <div 
              key={section.id}
              ref={allSectionRefs[index]}
              data-section={section.id}
              className="section"
              id={section.id}
              style={{
                marginBottom: '3rem',
                scrollMarginTop: '2rem',
              }}
            >
              {/* About section */}
              {section.id === 'about' && (
                <>
                  {person.bio && person.bio.map((paragraph, idx) => (
                    <p 
                      key={idx} 
                      className="section-content"
                      style={{
                        fontSize: '1rem',
                        lineHeight: '1.8',
                        marginBottom: '1.5rem',
                        color: textColor,
                        fontFamily: fontFamily,
                        fontWeight: '300',
                      }}
                    >
                      {paragraph}
                    </p>
                  ))}

                  {/* Stats display - conditionally rendered based on showStats prop */}
                  {showStats && person.stats && person.stats.length > 0 && (
                    <div 
                      className="stats-container"
                      style={{
                        display: 'flex',
                        justifyContent: 'space-between',
                        flexWrap: 'wrap',
                        marginTop: '3rem',
                        width: '100%',
                      }}
                    >
                      {person.stats.map((stat, statIdx) => (
                        <div 
                          key={statIdx} 
                          className="stat"
                          style={{
                            flex: '1',
                            textAlign: 'center',
                            padding: '0 1rem',
                            minWidth: '100px',
                            transition: 'transform 0.3s ease',
                          }}
                          onMouseEnter={(e) => {
                            e.currentTarget.style.transform = 'translateY(-5px)';
                          }}
                          onMouseLeave={(e) => {
                            e.currentTarget.style.transform = 'none';
                          }}
                        >
                          <div 
                            className="stat-value"
                            style={{
                              fontSize: '2.5rem',
                              fontWeight: '100',
                              color: highlightColor,
                              marginBottom: '0.5rem'
                            }}
                          >
                            {stat.value}
                          </div>
                          <div 
                            className="stat-label"
                            style={{
                              fontSize: '0.85rem',
                              color: `${textColor}CC`, // 80% opacity
                              textTransform: 'uppercase',
                              letterSpacing: '0.1em'
                            }}
                          >
                            {stat.label}
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </>
              )}

              {/* Experience section */}
              {section.id === 'experience' && (
                <>
                  {person.experience && person.experience.length > 0 ? (
                    person.experience.map((item, expIdx) => (
                      <p 
                        key={expIdx} 
                        className="section-content"
                        style={{
                          fontSize: '1rem',
                          lineHeight: '1.8',
                          marginBottom: '1.5rem',
                          color: textColor,
                          fontFamily: fontFamily,
                          fontWeight: '300',
                        }}
                      >
                        {item.content}
                      </p>
                    ))
                  ) : (
                    <p 
                      className="section-content"
                      style={{
                        fontSize: '1rem',
                        lineHeight: '1.8',
                        marginBottom: '1.5rem',
                        color: textColor,
                        fontFamily: fontFamily,
                        fontWeight: '300',
                      }}
                    >
                      {section.content || "Experience content will appear here."}
                    </p>
                  )}
                </>
              )}

              {/* Projects section */}
              {section.id === 'projects' && (
                <>
                  {person.projects && person.projects.length > 0 ? (
                    person.projects.map((item, projIdx) => (
                      <p 
                        key={projIdx} 
                        className="section-content"
                        style={{
                          fontSize: '1rem',
                          lineHeight: '1.8',
                          marginBottom: '1.5rem',
                          color: textColor,
                          fontFamily: fontFamily,
                          fontWeight: '300',
                        }}
                      >
                        {item.content}
                      </p>
                    ))
                  ) : (
                    <p 
                      className="section-content"
                      style={{
                        fontSize: '1rem',
                        lineHeight: '1.8',
                        marginBottom: '1.5rem',
                        color: textColor,
                        fontFamily: fontFamily,
                        fontWeight: '300',
                      }}
                    >
                      {section.content || "Projects content will appear here."}
                    </p>
                  )}
                </>
              )}

              {/* Custom section content */}
              {section.content && section.id !== 'experience' && section.id !== 'projects' && section.id !== 'about' && (
                typeof section.content === 'string' 
                  ? <p 
                      className="section-content"
                      style={{
                        fontSize: '1rem',
                        lineHeight: '1.8',
                        marginBottom: '1.5rem',
                        color: textColor,
                        fontFamily: fontFamily,
                        fontWeight: '300',
                      }}
                    >
                      {section.content}
                    </p>
                  : section.content
              )}
            </div>
          );
        })}

        {/* Dynamic sections from props */}
        {additionalSections.length > 0 && (
          additionalSections.map((section, index) => (
            <div 
              key={index} 
              className="section"
              style={{
                marginBottom: '3rem',
                scrollMarginTop: '2rem',
              }}
            >
              {section.title && (
                <h3 style={{
                  fontSize: '1.4rem',
                  color: highlightColor,
                  marginBottom: '1rem',
                  fontWeight: '300',
                }}>
                  {section.title}
                </h3>
              )}
              {section.content && (
                typeof section.content === 'string' 
                  ? <p 
                      className="section-content"
                      style={{
                        fontSize: '1rem',
                        lineHeight: '1.8',
                        marginBottom: '1.5rem',
                        color: textColor,
                        fontFamily: fontFamily,
                        fontWeight: '300',
                      }}
                    >
                      {section.content}
                    </p>
                  : section.content
              )}
            </div>
          ))
        )}
      </div>

      {/* Media query styles for mobile */}
      <style jsx>{`
        @media (max-width: 768px) {
          .profile-container {
            flex-direction: column;
          }

          .sidebar-wrapper {
            width: 100% !important;
          }

