import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence, useInView } from 'framer-motion';
import Background from '../../../../core/Background';
import MeteorShower from '../../../../core/effects/cosmiceffects/MeteorShower';
import ScrollingTextAnimation from '../../../../core/effects/ScrollingTextAnimation';
import RevealText from '../../../../core/effects/RevealText';
import useStyles from './evolutionSimulatorStyles';

const EvolutionSimulator = () => {
  // State management
  const [cursorPosition, setCursorPosition] = useState({ x: 0, y: 0 });
  const [isLoaded, setIsLoaded] = useState(false);
  const [selectedBodyShape, setSelectedBodyShape] = useState('quadrupedal');
  const [selectedTrait, setSelectedTrait] = useState(null);
  const [selectedTraitConnections, setSelectedTraitConnections] = useState([]);
  const [showBodyShapeSelector, setShowBodyShapeSelector] = useState(false);
  const [totalEffectiveStress, setTotalEffectiveStress] = useState(0);
  const [adaptationProbability, setAdaptationProbability] = useState(0);
  const [hoveredTrait, setHoveredTrait] = useState(null);
  const [showAllTraits, setShowAllTraits] = useState(true);
  const [filterMode, setFilterMode] = useState('all'); // 'all', 'category', 'relevance'

  // Environment state with default values
  const [environment, setEnvironment] = useState({
    temperature: 0.4,
    light_conditions: 0.3,
    terrain_harshness: 0.5,
    resource_scarcity: 0.2,
    ether_density: 0.6,
    pressure_gravity: 0.4
  });

  // Canvas and animation refs
  const canvasRef = useRef(null);
  const containerRef = useRef(null);
  const networkRef = useRef(null);
  const animationFrameRef = useRef(null);
  const bodyShapeIconRef = useRef(null);
  const traitRefs = useRef({});
  const headerRef = useRef(null);
  const contentRef = useRef(null);

  // Header in view animation
  const headerInView = useInView(headerRef, { once: true, threshold: 0.2 });
  const contentInView = useInView(contentRef, { once: true, threshold: 0.1 });

  // Custom styling hook
  const classes = useStyles();

  // Body shapes data
  const bodyShapes = [
    { id: 'bipedal', name: 'Bipedal', description: 'Upright forms with manipulative upper limbs' },
    { id: 'quadrupedal', name: 'Quadrupedal', description: 'Four-limbed forms optimized for stability and movement' },
    { id: 'serpentine', name: 'Serpentine', description: 'Elongated, limbless forms specialized for flexibility' },
    { id: 'amorphous', name: 'Amorphous', description: 'Malleable forms without fixed structure' },
    { id: 'insectoid', name: 'Multi-limbed', description: 'Multi-limbed, segmented forms' },
    { id: 'avian', name: 'Avian', description: 'Forms optimized for flight and aerial movement' },
    { id: 'aquatic', name: 'Aquatic', description: 'Forms specialized for water environments' }
  ];

  // Trait categories with color assignments
  const traitCategories = [
    { id: 'locomotion', name: 'Locomotion', color: '#7D6B9E' },
    { id: 'metabolism', name: 'Metabolism', color: '#5C9D8B' },
    { id: 'sensory', name: 'Sensory Acuity', color: '#C99846' },
    { id: 'etheric', name: 'Etheric Adaptation', color: '#B54B4B' },
    { id: 'thermal', name: 'Thermal Regulation', color: '#A67C52' }
  ];

  // Get traits based on selected body shape
  const getTraitsByBodyShape = (bodyShapeId) => {
    // Base traits that all body shapes share
    const baseTraits = {
      locomotion: [
        {
          id: 'running',
          name: 'Swift Limbs',
          tier: 1,
          affinities: {
            terrain_harshness: 0.7,
            pressure_gravity: 0.4,
            temperature: 0.2,
            resource_scarcity: -0.3,
            light_conditions: 0.1,
            ether_density: 0
          },
          description: 'Limbs designed for quick locomotion across varied terrain.',
          connections: ['enhanced_muscle', 'reflective_scales'],
          compatible: ['quadrupedal', 'bipedal', 'insectoid'],
          synthesis: {
            condition: 'high_stress',
            result: {
              name: 'Phase Shifting Appendages',
              description: 'Limbs that can temporarily phase through solid matter, allowing movement through obstacles.'
            }
          }
        },
        {
          id: 'climbing',
          name: 'Grasping Appendages',
          tier: 1,
          affinities: {
            terrain_harshness: 0.8,
            pressure_gravity: 0.3,
            resource_scarcity: -0.2,
            temperature: 0,
            light_conditions: 0.1,
            ether_density: 0
          },
          description: 'Specialized limb structures with superior grip for vertical movement.',
          connections: ['enhanced_muscle', 'sonar_mapping'],
          compatible: ['bipedal', 'quadrupedal', 'insectoid'],
          synthesis: {
            condition: 'extreme_gravity',
            result: {
              name: 'Gravity-Defying Grips',
              description: 'Appendages capable of selectively negating gravitational forces at contact points.'
            }
          }
        },
        {
          id: 'webbed',
          name: 'Webbed Appendages',
          tier: 1,
          affinities: {
            terrain_harshness: -0.4,
            pressure_gravity: 0.1,
            resource_scarcity: -0.1,
            temperature: -0.2,
            light_conditions: 0,
            ether_density: 0
          },
          description: 'Limbs with membrane structures optimized for aquatic movement.',
          connections: ['ether_sensing', 'thick_blubber'],
          compatible: ['aquatic', 'bipedal', 'quadrupedal'],
          synthesis: {
            condition: 'aquatic_etheric',
            result: {
              name: 'Dimensional Current Webs',
              description: 'Membranes that can manipulate water currents through subtle etheric manipulations.'
            }
          }
        },
        {
          id: 'slithering',
          name: 'Sinuous Musculature',
          tier: 1,
          affinities: {
            terrain_harshness: 0.5,
            pressure_gravity: 0.3,
            resource_scarcity: -0.1,
            temperature: 0.2,
            light_conditions: -0.2,
            ether_density: 0
          },
          description: 'Highly coordinated muscle structure allowing movement without limbs.',
          connections: ['vibration_detection', 'reflective_scales'],
          compatible: ['serpentine'],
          synthesis: {
            condition: 'extreme_adaptability',
            result: {
              name: 'Undulatory Phase Motion',
              description: 'Movement through wave-like dimensional shifts rather than physical undulation.'
            }
          }
        },
        {
          id: 'flight',
          name: 'Aerial Locomotion',
          tier: 1,
          affinities: {
            terrain_harshness: -0.3,
            pressure_gravity: -0.7,
            resource_scarcity: -0.2,
            temperature: 0.1,
            light_conditions: 0.6,
            ether_density: 0.2
          },
          description: 'Wing structures and lightweight skeletal system enabling flight.',
          connections: ['acute_vision', 'heat_vents'],
          compatible: ['avian', 'insectoid'],
          synthesis: {
            condition: 'ethereal_skies',
            result: {
              name: 'Aetheric Glide',
              description: 'Wings that interact with etheric currents, enabling flight through magical energy rather than air.'
            }
          }
        },
        {
          id: 'shapeshifting',
          name: 'Morphic Structure',
          tier: 1,
          affinities: {
            terrain_harshness: 0.3,
            pressure_gravity: 0.2,
            resource_scarcity: -0.3,
            temperature: 0.1,
            light_conditions: 0,
            ether_density: 0.6
          },
          description: 'Fluid body structure capable of adapting shape for mobility.',
          connections: ['omnivore', 'ether_channeling'],
          compatible: ['amorphous'],
          synthesis: {
            condition: 'etheric_saturation',
            result: {
              name: 'Dimensional Fluidity',
              description: 'Ability to flow between dimensional planes, existing partially in multiple realities simultaneously.'
            }
          }
        }
      ],
      metabolism: [
        {
          id: 'carnivore',
          name: 'Carnivore Digestion',
          tier: 1,
          affinities: {
            resource_scarcity: 0.5,
            temperature: 0.3,
            terrain_harshness: 0.1,
            light_conditions: 0,
            pressure_gravity: 0,
            ether_density: 0
          },
          description: 'Specialized digestive system optimized for meat processing.',
          connections: ['enhanced_muscle', 'acute_vision'],
          compatible: ['all'],
          synthesis: {
            condition: 'apex_predator',
            result: {
              name: 'Essence Extraction',
              description: 'Ability to consume and metabolize the life essence of prey beyond mere physical substance.'
            }
          }
        },
        {
          id: 'omnivore',
          name: 'Omnivore Adaptation',
          tier: 1,
          affinities: {
            resource_scarcity: 0.6,
            terrain_harshness: 0.2,
            temperature: 0.1,
            light_conditions: 0,
            pressure_gravity: 0,
            ether_density: 0
          },
          description: 'Versatile digestive system capable of processing diverse food sources.',
          connections: ['bioluminescence', 'reflective_scales'],
          compatible: ['all'],
          synthesis: {
            condition: 'scarce_resources',
            result: {
              name: 'Universal Digestion',
              description: 'Capacity to derive nutrients from almost any organic or inorganic substance.'
            }
          }
        },
        {
          id: 'photosynthetic',
          name: 'Photosynthetic Cells',
          tier: 1,
          affinities: {
            light_conditions: 0.9,
            resource_scarcity: 0.7,
            temperature: 0.4,
            terrain_harshness: -0.2,
            pressure_gravity: 0,
            ether_density: 0.1
          },
          description: 'Cellular structures that can convert light into energy.',
          connections: ['ether_sensing', 'reflective_scales'],
          compatible: ['all'],
          synthesis: {
            condition: 'high_light_ether',
            result: {
              name: 'Etheric Photosynthesis',
              description: 'Advanced cells that can convert ambient etheric energy into biological energy.'
            }
          }
        },
        {
          id: 'ether_consumption',
          name: 'Ether Consumption',
          tier: 1,
          affinities: {
            ether_density: 0.9,
            resource_scarcity: 0.6,
            temperature: -0.1,
            terrain_harshness: -0.1,
            pressure_gravity: 0,
            light_conditions: -0.3
          },
          description: 'Metabolic pathway that processes ambient etheric energy.',
          connections: ['ether_channeling', 'bioluminescence'],
          compatible: ['all'],
          synthesis: {
            condition: 'etheric_saturation',
            result: {
              name: 'Reality Consumption',
              description: 'Ability to metabolize the fabric of reality itself, subsisting on dimensional energies.'
            }
          }
        }
      ],
      sensory: [
        {
          id: 'acute_vision',
          name: 'Acute Vision',
          tier: 1,
          affinities: {
            light_conditions: 0.8,
            terrain_harshness: 0.3,
            resource_scarcity: 0.2,
            temperature: 0,
            pressure_gravity: 0,
            ether_density: 0
          },
          description: 'Highly developed visual organs with exceptional acuity.',
          connections: ['enhanced_muscle', 'eagle_eyes'],
          compatible: ['all'],
          synthesis: {
            condition: 'extreme_light',
            result: {
              name: 'Reality Perception',
              description: 'Vision that perceives multiple planes of reality simultaneously, seeing beyond physical existence.'
            }
          }
        },
        {
          id: 'sonar_mapping',
          name: 'Echolocation',
          tier: 1,
          affinities: {
            light_conditions: -0.8,
            terrain_harshness: 0.5,
            pressure_gravity: 0.1,
            temperature: 0,
            resource_scarcity: 0,
            ether_density: 0.2
          },
          description: 'Ability to emit and detect sound waves for environmental mapping.',
          connections: ['vibration_detection', 'bioluminescence'],
          compatible: ['all'],
          synthesis: {
            condition: 'extreme_darkness',
            result: {
              name: 'Dimensional Sonar',
              description: 'Echolocation that can detect entities and structures across dimensional boundaries.'
            }
          }
        },
        {
          id: 'vibration_detection',
          name: 'Vibration Detection',
          tier: 1,
          affinities: {
            terrain_harshness: 0.6,
            pressure_gravity: 0.4,
            light_conditions: -0.3,
            temperature: 0,
            resource_scarcity: 0,
            ether_density: 0.1
          },
          description: 'Specialized organs that can detect minute vibrations through various media.',
          connections: ['sonar_mapping', 'thick_blubber'],
          compatible: ['all'],
          synthesis: {
            condition: 'extreme_sensitivity',
            result: {
              name: 'Temporal Tremor Sense',
              description: 'Ability to detect vibrations through time, sensing events before they occur.'
            }
          }
        },
        {
          id: 'ether_sensing',
          name: 'Ether Sensing',
          tier: 1,
          affinities: {
            ether_density: 0.8,
            light_conditions: 0.2,
            terrain_harshness: 0,
            temperature: 0,
            resource_scarcity: 0,
            pressure_gravity: 0.1
          },
          description: 'Specialized organs that can detect and interpret etheric energy patterns.',
          connections: ['photosynthetic', 'ether_channeling'],
          compatible: ['all'],
          synthesis: {
            condition: 'etheric_saturation',
            result: {
              name: 'Soul Reading',
              description: 'Perception that can detect and analyze the essence of living beings beyond physical form.'
            }
          }
        }
      ],
      etheric: [
        {
          id: 'ether_sensing',
          name: 'Ether Sensing',
          tier: 1,
          affinities: {
            ether_density: 0.8,
            light_conditions: 0.2,
            terrain_harshness: 0,
            temperature: 0,
            resource_scarcity: 0,
            pressure_gravity: 0.1
          },
          description: 'Specialized organs that can detect and interpret etheric energy patterns.',
          connections: ['photosynthetic', 'ether_channeling'],
          compatible: ['all'],
          synthesis: {
            condition: 'etheric_saturation',
            result: {
              name: 'Soul Reading',
              description: 'Perception that can detect and analyze the essence of living beings beyond physical form.'
            }
          }
        },
        {
          id: 'ether_channeling',
          name: 'Ether Channeling',
          tier: 1,
          affinities: {
            ether_density: 0.9,
            temperature: 0.3,
            light_conditions: 0.3,
            terrain_harshness: 0,
            resource_scarcity: -0.2,
            pressure_gravity: 0.1
          },
          description: 'Biological structures capable of directing and manipulating etheric energies.',
          connections: ['ether_sensing', 'bioluminescence'],
          compatible: ['all'],
          synthesis: {
            condition: 'etheric_mastery',
            result: {
              name: 'Reality Weaving',
              description: 'Ability to manipulate the fabric of reality through precise etheric control.'
            }
          }
        },
        {
          id: 'bioluminescence',
          name: 'Bioluminescence',
          tier: 1,
          affinities: {
            light_conditions: -0.6,
            ether_density: 0.5,
            resource_scarcity: -0.3,
            temperature: 0,
            terrain_harshness: 0,
            pressure_gravity: 0
          },
          description: 'Ability to produce light through biochemical processes.',
          connections: ['ether_channeling', 'omnivore'],
          compatible: ['all'],
          synthesis: {
            condition: 'dark_ethereal',
            result: {
              name: 'Hypnotic Patterns',
              description: 'Light displays that can induce altered states of consciousness in observers.'
            }
          }
        },
        {
          id: 'dimensional_anchor',
          name: 'Dimensional Anchor',
          tier: 1,
          affinities: {
            ether_density: 0.7,
            pressure_gravity: 0.6,
            resource_scarcity: -0.2,
            temperature: 0,
            terrain_harshness: 0.2,
            light_conditions: 0.1
          },
          description: 'Etheric organ that stabilizes the creature\'s position within reality.',
          connections: ['ether_sensing', 'vibration_detection'],
          compatible: ['all'],
          synthesis: {
            condition: 'reality_flux',
            result: {
              name: 'Dimensional Phasing',
              description: 'Control over personal dimensional state, allowing phasing between planes of existence.'
            }
          }
        }
      ],
      thermal: [
        {
          id: 'thick_blubber',
          name: 'Thick Blubber',
          tier: 1,
          affinities: {
            temperature: -0.8,
            pressure_gravity: 0.3,
            resource_scarcity: -0.4,
            terrain_harshness: 0.1,
            light_conditions: 0,
            ether_density: 0
          },
          description: 'Dense fatty tissue layer providing insulation in cold environments.',
          connections: ['vibration_detection', 'enhanced_muscle'],
          compatible: ['all'],
          synthesis: {
            condition: 'extreme_cold',
            result: {
              name: 'Thermal Void',
              description: 'Tissues that create a pocket of absolute thermal isolation from the environment.'
            }
          }
        },
        {
          id: 'reflective_scales',
          name: 'Reflective Scales',
          tier: 1,
          affinities: {
            temperature: 0.7,
            light_conditions: 0.6,
            terrain_harshness: 0.3,
            resource_scarcity: -0.2,
            pressure_gravity: 0,
            ether_density: 0.1
          },
          description: 'Light-reflecting scales that help regulate heat absorption.',
          connections: ['running', 'photosynthetic'],
          compatible: ['all'],
          synthesis: {
            condition: 'extreme_heat',
            result: {
              name: 'Thermal Manipulator Scales',
              description: 'Scales that can actively redirect and manipulate thermal energy in the environment.'
            }
          }
        },
        {
          id: 'heat_vents',
          name: 'Heat Vents',
          tier: 1,
          affinities: {
            temperature: 0.8,
            ether_density: 0.3,
            resource_scarcity: -0.3,
            terrain_harshness: 0,
            light_conditions: 0,
            pressure_gravity: 0
          },
          description: 'Specialized structures that can rapidly dissipate excess heat.',
          connections: ['running', 'ether_channeling'],
          compatible: ['all'],
          synthesis: {
            condition: 'volcanic_environment',
            result: {
              name: 'Plasma Channeling',
              description: 'Ability to vent superheated plasma as a defensive weapon or environmental tool.'
            }
          }
        },
        {
          id: 'thermal_conversion',
          name: 'Thermal Conversion',
          tier: 1,
          affinities: {
            temperature: 0.4,
            temperature: -0.4, // Adapts to both extremes
            resource_scarcity: 0.5,
            ether_density: 0.3,
            terrain_harshness: 0,
            light_conditions: 0.1,
            pressure_gravity: 0
          },
          description: 'Cellular structures that convert temperature differentials into usable energy.',
          connections: ['omnivore', 'ether_sensing'],
          compatible: ['all'],
          synthesis: {
            condition: 'thermal_extremes',
            result: {
              name: 'Entropic Manipulation',
              description: 'Control over thermodynamic entropy, creating areas of perfect order or chaos.'
            }
          }
        }
      ]
    };

    // Advanced traits (tier 2)
    const advancedTraits = {
