  // Get all traits as a flattened array
  const getAllTraits = () => {
    const traitsByCategory = getTraitsByBodyShape(selectedBodyShape);
    let allTraits = [];

    Object.keys(traitsByCategory).forEach(category => {
      traitsByCategory[category].forEach(trait => {
        // Add category to trait for easier reference
        allTraits.push({
          ...trait,
          category
        });
      });
    });

    return allTraits;
  };

  // Find trait by ID
  const findTraitById = (id) => {
    const allTraits = getAllTraits();
    return allTraits.find(trait => trait.id === id);
  };

  // Calculate trait compatibility with body shape
  const isTraitCompatibleWithBodyShape = (trait) => {
    if (!trait.compatible) return true;
    if (trait.compatible.includes('all')) return true;
    return trait.compatible.includes(selectedBodyShape);
  };

  // Calculate trait affinity compatibility
  const calculateCompatibility = (trait1, trait2) => {
    if (!trait1 || !trait2) return 0.3; // Baseline compatibility

    const stressors = ['terrain_harshness', 'resource_scarcity', 'light_conditions', 
                     'ether_density', 'temperature', 'pressure_gravity'];

    let totalSimilarity = 0;
    let totalWeight = 0;

    stressors.forEach(stressor => {
      const val1 = trait1.affinities[stressor] || 0;
      const val2 = trait2.affinities[stressor] || 0;

      // Calculate similarity (1 for identical, 0 for completely opposite)
      const similarity = 1 - Math.min(1, Math.abs(val1 - val2) / 2);

      // Weight based on how important this stressor is to either trait
      const weight = (Math.abs(val1) + Math.abs(val2)) / 2;

      totalSimilarity += similarity * (1 + weight * 0.5);
      totalWeight += 1;
    });

    // Calculate compatibility score (0.3 to 1.0)
    return 0.3 + 0.5 * (totalSimilarity / (totalWeight * 1.5));
  };

  // Calculate combined trait affinity for a stressor
  const calculateCombinedAffinity = (stressor) => {
    const allTraits = getAllTraits();
    const traitsWithAffinity = allTraits.filter(trait => 
      trait.affinities && trait.affinities[stressor] !== undefined && 
      trait.affinities[stressor] !== 0 && 
      isTraitCompatibleWithBodyShape(trait)
    );

    if (traitsWithAffinity.length === 0) return 0;

    // Sum affinities and normalize by square root of trait count
    const sum = traitsWithAffinity.reduce(
      (total, trait) => total + trait.affinities[stressor], 0
    );

    // Using tanh to constrain to [-1, 1] range
    return Math.tanh(sum / Math.sqrt(traitsWithAffinity.length));
  };

  // Calculate effective stress for a stressor
  const calculateEffectiveStress = (stressor) => {
    const stressorValue = environment[stressor];
    if (stressorValue === 0) return 0;

    const combinedAffinity = calculateCombinedAffinity(stressor);

    // Sigmoid ratio calculation
    const ratio = combinedAffinity / stressorValue;
    const sigmoid = 1 / (1 + Math.exp(-3 * (ratio - 0.8)));

    // Effective stress calculation
    return stressorValue * (1 - sigmoid);
  };

  // Calculate total effective stress
  const calculateTotalEffectiveStress = () => {
    const stressors = ['terrain_harshness', 'resource_scarcity', 'light_conditions', 
                      'ether_density', 'temperature', 'pressure_gravity'];

    // Calculate effective stress for all stressors
    const stressorValues = stressors.map(stressor => calculateEffectiveStress(stressor));

    // Sum and normalize
    const sum = stressorValues.reduce((total, stress) => total + stress, 0);
    const numStressors = stressors.length;

    // Using tanh to constrain to [0, 1] range (mapping from [-1, 1] to [0, 1])
    const stress = (Math.tanh(sum / Math.sqrt(numStressors)) + 1) / 2;

    return stress;
  };

  // Calculate adaptation probability
  const calculateAdaptationProbability = (stress, duration = 10, threshold = 0.5) => {
    const adaptationPressure = (stress * duration / 25) - threshold;
    return 1 / (1 + Math.exp(-10 * adaptationPressure));
  };

  // Calculate environmental relevance for a trait
  const calculateEnvironmentalRelevance = (trait) => {
    if (!trait || !trait.affinities) return 0;

    let relevanceScore = 0;

    Object.keys(environment).forEach(stressor => {
      if (trait.affinities[stressor] !== undefined) {
        const stressorValue = environment[stressor];
        const traitAffinity = trait.affinities[stressor];

        // Calculate relevance based on formula from documentation
        relevanceScore += Math.abs(stressorValue) * stressorValue * traitAffinity;
      }
    });

    // Normalize using hyperbolic tangent
    return Math.tanh(relevanceScore);
  };

  // Get trait category color
  const getTraitCategoryColor = (categoryId) => {
    const category = traitCategories.find(c => c.id === categoryId);
    return category ? category.color : '#BFAD7F';
