  // Get trait glow intensity based on environmental relevance
  const getTraitGlowIntensity = (trait) => {
    const relevance = calculateEnvironmentalRelevance(trait);
    // Scale from 0 to 1
    const scaledRelevance = (relevance + 1) / 2;
    return scaledRelevance;
