  // Handle trait selection
  const handleTraitClick = (trait) => {
    if (selectedTrait && selectedTrait.id === trait.id) {
      setSelectedTrait(null);
      setSelectedTraitConnections([]);
    } else {
      setSelectedTrait(trait);

      // Get connected traits
      const connections = [];
      if (trait.connections && trait.connections.length > 0) {
        trait.connections.forEach(connectionId => {
          const connectedTrait = findTraitById(connectionId);
          if (connectedTrait) {
            connections.push(connectedTrait);
          }
        });
      }

      // If this is a tier > 0 trait, add parent trait
      if (trait.tier > 0 && trait.parentId) {
        const parentTrait = findTraitById(trait.parentId);
        if (parentTrait && !connections.includes(parentTrait)) {
          connections.push(parentTrait);
        }
      }

      // Find traits where this trait is the parent
      const allTraits = getAllTraits();
      allTraits.forEach(t => {
        if (t.parentId === trait.id && !connections.includes(t)) {
          connections.push(t);
        }
      });

      setSelectedTraitConnections(connections);
