  // Setup the canvas and animation when component mounts or body shape changes
  useEffect(() => {
    if (!canvasRef.current || !containerRef.current) return;

    const canvas = canvasRef.current;
    const container = containerRef.current;
    const ctx = canvas.getContext('2d');

    // Set up canvas dimensions
    const updateCanvasDimensions = () => {
      const rect = container.getBoundingClientRect();
      canvas.width = rect.width;
      canvas.height = rect.height;

      networkRef.current = {
        width: rect.width,
        height: rect.height,
        centerX: rect.width / 2,
        centerY: rect.height / 2
      };
    };

    updateCanvasDimensions();
    window.addEventListener('resize', updateCanvasDimensions);

    // Animation function
    const animate = () => {
      if (!networkRef.current) return;

      // Clear canvas
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      const { centerX, centerY } = networkRef.current;
      const allTraits = getAllTraits();
      const traitPositions = {};
      traitRefs.current = {};

      // Draw background circles representing tiers
      const tierRadius = [0, 180, 300, 420];

      // Draw tier circles
      for (let tier = 1; tier <= 3; tier++) {
        ctx.beginPath();
        ctx.arc(centerX, centerY, tierRadius[tier], 0, Math.PI * 2);
        ctx.strokeStyle = 'rgba(191, 173, 127, 0.1)';
        ctx.setLineDash(tier === 1 ? [] : [5, 5]);
        ctx.lineWidth = 1;
        ctx.stroke();
        ctx.setLineDash([]);
      }

      // Group traits by category and tier
      const traitsByCategory = {};
      traitCategories.forEach(category => {
        traitsByCategory[category.id] = {
          tier1: [],
          tier2: [],
          tier3: []
        };
      });

      allTraits.forEach(trait => {
        const tier = trait.tier || 1;
        if (tier <= 3 && traitsByCategory[trait.category]) {
          traitsByCategory[trait.category][`tier${tier}`].push(trait);
        }
      });

      // Calculate positions for all traits
      traitCategories.forEach((category, categoryIndex) => {
        const categoryId = category.id;
        const categoryAngleOffset = (categoryIndex / traitCategories.length) * Math.PI * 2;

        [1, 2, 3].forEach(tier => {
          const traits = traitsByCategory[categoryId][`tier${tier}`] || [];
          const traitCount = traits.length;

          if (traitCount === 0) return;

          const angleSpread = (Math.PI * 2) / traitCategories.length * 0.8;

          traits.forEach((trait, index) => {
            const traitAngle = categoryAngleOffset + 
              (angleSpread / Math.max(1, traitCount - 1) * index) - 
              (angleSpread / 2);

            const radius = tierRadius[tier];
            const x = centerX + Math.cos(traitAngle) * radius;
            const y = centerY + Math.sin(traitAngle) * radius;

            traitPositions[trait.id] = { x, y, trait };
            traitRefs.current[trait.id] = { x, y, radius: getNodeRadius(trait), trait };
          });
        });
      });

      // Draw connections
      allTraits.forEach(trait => {
        if (trait.connections) {
          const sourcePos = traitPositions[trait.id];

          if (!sourcePos) return;

          trait.connections.forEach(targetId => {
            const targetPos = traitPositions[targetId];

            if (!targetPos) return;

            const sourceVisible = showAllTraits || 
              (selectedTrait && (selectedTrait.id === trait.id || 
                selectedTraitConnections.some(t => t.id === trait.id)));

            const targetVisible = showAllTraits || 
              (selectedTrait && (selectedTrait.id === targetId || 
                selectedTraitConnections.some(t => t.id === targetId)));

            if (sourceVisible && targetVisible) {
              const compatibility = calculateCompatibility(sourcePos.trait, targetPos.trait);

              // Draw connection
              ctx.beginPath();
              ctx.moveTo(sourcePos.x, sourcePos.y);

              // Create a gentle curve for the connection
              const midX = (sourcePos.x + targetPos.x) / 2;
              const midY = (sourcePos.y + targetPos.y) / 2;

              // Pull toward center based on tier difference
              const pullFactor = 0.2;
              const controlX = midX + (centerX - midX) * pullFactor;
              const controlY = midY + (centerY - midY) * pullFactor;

              ctx.quadraticCurveTo(controlX, controlY, targetPos.x, targetPos.y);

              const isSelected = selectedTrait && 
                (selectedTrait.id === trait.id || selectedTrait.id === targetId);

              // Gradient for connection
              const gradient = ctx.createLinearGradient(
                sourcePos.x, sourcePos.y, targetPos.x, targetPos.y
              );

              const sourceColor = getTraitCategoryColor(sourcePos.trait.category);
              const targetColor = getTraitCategoryColor(targetPos.trait.category);

              gradient.addColorStop(0, sourceColor);
              gradient.addColorStop(1, targetColor);

              // Set line style
              ctx.strokeStyle = gradient;
              ctx.lineWidth = isSelected ? 3 : 2;
              ctx.globalAlpha = isSelected ? 0.8 : 0.5 * compatibility;

              if (isSelected) {
                ctx.shadowColor = 'rgba(255, 253, 227, 0.6)';
                ctx.shadowBlur = 8;
              }

              ctx.stroke();
              ctx.globalAlpha = 1;
              ctx.shadowBlur = 0;
            }
          });
        }

        // Draw parent-child connections
        if (trait.tier > 0 && trait.parentId) {
          const childPos = traitPositions[trait.id];
          const parentPos = traitPositions[trait.parentId];

          if (childPos && parentPos) {
            const childVisible = showAllTraits || 
              (selectedTrait && (selectedTrait.id === trait.id || 
                selectedTraitConnections.some(t => t.id === trait.id)));

            const parentVisible = showAllTraits || 
              (selectedTrait && (selectedTrait.id === trait.parentId || 
                selectedTraitConnections.some(t => t.id === trait.parentId)));

            if (childVisible && parentVisible) {
              const isSelected = selectedTrait && 
                (selectedTrait.id === trait.id || selectedTrait.id === trait.parentId);

              // Draw evolutionary connection
              ctx.beginPath();
              ctx.moveTo(childPos.x, childPos.y);

              // Create a curve toward center
              const midX = (childPos.x + parentPos.x) / 2;
              const midY = (childPos.y + parentPos.y) / 2;

              // Pull toward center
              const pullFactor = 0.25;
              const controlX = midX + (centerX - midX) * pullFactor;
              const controlY = midY + (centerY - midY) * pullFactor;

              ctx.quadraticCurveTo(controlX, controlY, parentPos.x, parentPos.y);

              // Special styling for evolutionary connections
              const evolutionGradient = ctx.createLinearGradient(
                childPos.x, childPos.y, parentPos.x, parentPos.y
              );

              const childColor = getTraitCategoryColor(childPos.trait.category);
              const parentColor = getTraitCategoryColor(parentPos.trait.category);

              evolutionGradient.addColorStop(0, childColor);
              evolutionGradient.addColorStop(1, parentColor);

              ctx.strokeStyle = evolutionGradient;
              ctx.lineWidth = isSelected ? 3 : 2;
              ctx.setLineDash([3, 3]);
              ctx.globalAlpha = isSelected ? 0.9 : 0.6;

              if (isSelected) {
                ctx.shadowColor = 'rgba(255, 253, 227, 0.6)';
                ctx.shadowBlur = 8;
              }

              ctx.stroke();
              ctx.setLineDash([]);
              ctx.globalAlpha = 1;
              ctx.shadowBlur = 0;
            }
          }
        }
      });

      // Draw nodes for each trait
      Object.keys(traitPositions).forEach(traitId => {
        const { x, y, trait } = traitPositions[traitId];
        const category = trait.category;
        const categoryColor = getTraitCategoryColor(category);

        const isSelected = selectedTrait && selectedTrait.id === trait.id;
        const isConnected = selectedTrait && selectedTraitConnections.some(t => t.id === trait.id);
        const isVisible = showAllTraits || isSelected || isConnected;

        if (!isVisible) return;

        // Get trait size based on tier and state
        const baseSize = trait.tier === 1 ? 16 : trait.tier === 2 ? 20 : 24;
        const nodeSize = isSelected ? baseSize * 1.3 : isConnected ? baseSize * 1.1 : baseSize;

        // Calculate glow based on environmental relevance
        const glowIntensity = getTraitGlowIntensity(trait);

        // Draw trait node with glow effect
        ctx.beginPath();
        ctx.arc(x, y, nodeSize, 0, Math.PI * 2);
        ctx.fillStyle = categoryColor;

        // Add glow based on environmental relevance
        ctx.shadowColor = 'rgba(255, 253, 227, 0.8)';
        ctx.shadowBlur = 10 + glowIntensity * 15;
        ctx.fill();

        // Add border
        ctx.strokeStyle = isSelected ? '#FFFFFF' : categoryColor;
        ctx.lineWidth = isSelected ? 3 : 2;
        ctx.stroke();

        // Reset shadow for text
        ctx.shadowBlur = 0;

        // Draw compatibility indicator for selected trait connections
        if (isConnected && selectedTrait) {
          const compatibility = calculateCompatibility(selectedTrait, trait);

          // Draw compatibility arc around node
          ctx.beginPath();
          ctx.arc(x, y, nodeSize + 5, 0, Math.PI * 2 * compatibility);
          ctx.strokeStyle = 'rgba(255, 255, 255, 0.8)';
          ctx.lineWidth = 2;
          ctx.stroke();
        }

        // Store node position and size for interaction
        traitRefs.current[traitId] = { x, y, radius: nodeSize, trait };
      });

      // Draw central body shape
      if (bodyShapeIconRef.current) {
        const bodyShape = bodyShapes.find(shape => shape.id === selectedBodyShape);
        const bodyShapeSize = 50;

        // Draw body shape background
        ctx.beginPath();
        ctx.arc(centerX, centerY, bodyShapeSize, 0, Math.PI * 2);
        ctx.fillStyle = 'rgba(191, 173, 127, 0.8)';
        ctx.shadowColor = 'rgba(191, 173, 127, 0.6)';
        ctx.shadowBlur = 20;
        ctx.fill();

        // Draw body shape border
        ctx.strokeStyle = 'rgba(255, 255, 255, 0.5)';
        ctx.lineWidth = 2;
        ctx.stroke();

        // Reset shadow
        ctx.shadowBlur = 0;

        // Draw body shape text
        ctx.font = 'bold 14px Arial';
        ctx.fillStyle = 'rgba(0, 0, 0, 0.8)';
        ctx.textAlign = 'center';
        ctx.textBaseline = 'middle';
        ctx.fillText(bodyShape?.name || 'Body Shape', centerX, centerY);

        // Store body shape position for interaction
        bodyShapeIconRef.current = {
          x: centerX,
          y: centerY,
          radius: bodyShapeSize
        };
      } else {
        bodyShapeIconRef.current = {
          x: centerX,
          y: centerY,
          radius: 50
        };
      }

      // Continue animation
      animationFrameRef.current = requestAnimationFrame(animate);
    };

    // Start animation loop
    animate();

    // Set loaded after a delay
    const timer = setTimeout(() => {
      setIsLoaded(true);
    }, 1000);

    // Cleanup on unmount
    return () => {
      window.removeEventListener('resize', updateCanvasDimensions);
      clearTimeout(timer);
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }
    };
  }, [
    selectedBodyShape, 
    selectedTrait, 
    selectedTraitConnections, 
    environment, 
    showAllTraits,
    getTraitCategoryColor,
    calculateCompatibility,
    getTraitGlowIntensity
  ]);

  // Calculate stress and adaptation probability when environment changes
  useEffect(() => {
    const stress = calculateTotalEffectiveStress();
    setTotalEffectiveStress(stress);

    const probability = calculateAdaptationProbability(stress);
    setAdaptationProbability(probability);
  }, [environment, selectedBodyShape]);

  // Handle canvas clicks to detect trait and body shape selection
  const handleCanvasClick = (e) => {
    if (!canvasRef.current || !bodyShapeIconRef.current) return;

    const rect = canvasRef.current.getBoundingClientRect();
    const mouseX = e.clientX - rect.left;
    const mouseY = e.clientY - rect.top;

    // Check if clicked on body shape
    const { x, y, radius } = bodyShapeIconRef.current;
    const distance = Math.sqrt((mouseX - x) ** 2 + (mouseY - y) ** 2);

    if (distance <= radius) {
      handleBodyShapeClick();
      return;
    }

    // Check if clicked on any trait
    let clickedTrait = null;

    Object.keys(traitRefs.current).forEach(traitId => {
      const traitRef = traitRefs.current[traitId];
      const traitDistance = Math.sqrt(
        (mouseX - traitRef.x) ** 2 + (mouseY - traitRef.y) ** 2
      );

      if (traitDistance <= traitRef.radius) {
        clickedTrait = traitRef.trait;
      }
    });

    if (clickedTrait) {
      handleTraitClick(clickedTrait);
    } else if (selectedTrait) {
      setSelectedTrait(null);
      setSelectedTraitConnections([]);
    }
  };

  // Handle canvas mouse movement for hover effects
  const handleCanvasMouseMove = (e) => {
    if (!canvasRef.current) return;

    const rect = canvasRef.current.getBoundingClientRect();
    const mouseX = e.clientX - rect.left;
    const mouseY = e.clientY - rect.top;

    // Check if mouse is over any trait
    let hovered = null;

    Object.keys(traitRefs.current).forEach(traitId => {
      const traitRef = traitRefs.current[traitId];
      const distance = Math.sqrt(
        (mouseX - traitRef.x) ** 2 + (mouseY - traitRef.y) ** 2
      );

      if (distance <= traitRef.radius) {
        hovered = traitRef.trait;
      }
    });

    setHoveredTrait(hovered);
  };

  // Handle filter mode changes
  const handleFilterModeChange = (mode) => {
    setFilterMode(mode);

    if (mode === 'all') {
      setShowAllTraits(true);
    } else if (mode === 'relevance') {
      // Show only traits with positive environmental relevance
      setShowAllTraits(false);
    } else if (mode === 'category' && selectedTrait) {
      // Show only traits in the same category as selected trait
      setShowAllTraits(false);
    }
  };

  // Helper function to get node radius based on trait tier
  const getNodeRadius = (trait) => {
    if (!trait) return 16;
    const tier = trait.tier || 1;
    return tier === 1 ? 16 : tier === 2 ? 20 : 24;
  };

  // Render trait tooltip when hovering
  const renderTraitTooltip = () => {
    if (!hoveredTrait || selectedTrait) return null;

    const trait = hoveredTrait;
    const relevance = calculateEnvironmentalRelevance(trait);

    return (
      <motion.div
        initial={{ opacity: 0, y: 10 }}
        animate={{ opacity: 1, y: 0 }}
        exit={{ opacity: 0, y: 10 }}
        transition={{ duration: 0.2 }}
        className={classes.traitTooltip}
      >
        <div className={classes.traitTooltipHeader} style={{ 
          backgroundColor: getTraitCategoryColor(trait.category) 
        }}>
          <h3>{trait.name}</h3>
          <span>Tier {trait.tier} {trait.category}</span>
        </div>
        <div className={classes.traitTooltipContent}>
          <p>{trait.description}</p>
          <div className={classes.traitTooltipStats}>
            <div className={classes.traitTooltipStat}>
              <span>Environmental Relevance</span>
              <span className={`${classes.traitTooltipStatValue} ${relevance > 0 ? classes.positive : relevance < 0 ? classes.negative : ''}`}>
                {relevance.toFixed(2)}
              </span>
            </div>
          </div>
        </div>
      </motion.div>
    );
  };

  // Render environmental controls
  const renderEnvironmentControls = () => {
    const environmentControls = [
      { id: 'temperature', name: 'Temperature', min: -1, max: 1 },
      { id: 'light_conditions', name: 'Light Conditions', min: -1, max: 1 },
      { id: 'terrain_harshness', name: 'Terrain Harshness', min: -1, max: 1 },
      { id: 'resource_scarcity', name: 'Resource Scarcity', min: -1, max: 1 },
      { id: 'ether_density', name: 'Ether Density', min: -1, max: 1 },
      { id: 'pressure_gravity', name: 'Pressure/Gravity', min: -1, max: 1 }
    ];

    return (
      <div className={classes.environmentControls}>
        <RevealText>
          <h3 className={classes.controlsTitle}>Environmental Stressors</h3>
        </RevealText>

        <div className={classes.environmentSliders}>
          {environmentControls.map(control => (
            <div key={control.id} className={classes.sliderContainer}>
              <div className={classes.sliderLabel}>
                <span>{control.name}</span>
                <span className={`
                  ${classes.sliderValue} 
                  ${environment[control.id] > 0 ? classes.positive : environment[control.id] < 0 ? classes.negative : ''}
                `}>
                  {environment[control.id].toFixed(1)}
                </span>
              </div>
              <input
                type="range"
                min={control.min}
                max={control.max}
                step="0.1"
                value={environment[control.id]}
                onChange={(e) => 
                  setEnvironment(prev => ({
                    ...prev,
                    [control.id]: parseFloat(e.target.value)
                  }))
                }
                className={classes.slider}
              />
            </div>
          ))}
        </div>

        <div className={classes.adaptationStats}>
          <div className={classes.adaptationStat}>
            <span>Total Effective Stress:</span>
            <div className={classes.stressBar}>
              <div 
                className={classes.stressBarFill} 
                style={{ 
                  width: `${totalEffectiveStress * 100}%`,
                  backgroundColor: 
                    totalEffectiveStress > 0.7 ? '#B54B4B' : 
                    totalEffectiveStress > 0.4 ? '#C99846' : 
                    '#5C9D8B'
                }}
              ></div>
              <span>{(totalEffectiveStress).toFixed(2)}</span>
            </div>
          </div>
          <div className={classes.adaptationStat}>
            <span>Adaptation Probability:</span>
            <div className={classes.stressBar}>
              <div 
                className={classes.stressBarFill}
                style={{ 
                  width: `${adaptationProbability * 100}%`,
                  backgroundColor: 
                    adaptationProbability > 0.7 ? '#B54B4B' : 
                    adaptationProbability > 0.4 ? '#C99846' : 
                    '#5C9D8B'
                }}
              ></div>
              <span>{(adaptationProbability).toFixed(2)}</span>
            </div>
          </div>
        </div>
      </div>
    );
  };

  // Render body shape selector
  const renderBodyShapeSelector = () => {
    if (!showBodyShapeSelector) return null;

    return (
      <motion.div 
        className={classes.bodyShapeSelector}
        initial={{ opacity: 0, scale: 0.95 }}
        animate={{ opacity: 1, scale: 1 }}
        exit={{ opacity: 0, scale: 0.95 }}
        transition={{ duration: 0.3 }}
      >
        <h3 className={classes.selectorTitle}>Select Body Shape</h3>
        <div className={classes.bodyShapeOptions}>
          {bodyShapes.map(shape => (
            <div 
              key={shape.id}
              className={`${classes.bodyShapeOption} ${selectedBodyShape === shape.id ? classes.selected : ''}`}
              onClick={() => changeBodyShape(shape.id)}
            >
              <div className={classes.bodyShapeName}>{shape.name}</div>
              <div className={classes.bodyShapeDescription}>{shape.description}</div>
            </div>
          ))}
        </div>
      </motion.div>
    );
  };

  // Render trait details panel
  const renderTraitDetails = () => {
    if (!selectedTrait) return null;

    const trait = selectedTrait;
    const categoryName = traitCategories.find(c => c.id === trait.category)?.name || trait.category;
    const relevance = calculateEnvironmentalRelevance(trait);

    // Find parent trait if available
    let parentTrait = null;
    if (trait.tier > 0 && trait.parentId) {
      parentTrait = findTraitById(trait.parentId);
    }

    // Get synthesized trait if available
    const synthesizedTrait = trait.synthesis?.result;

    return (
      <motion.div 
        className={classes.traitDetails}
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        exit={{ opacity: 0, y: 20 }}
        transition={{ duration: 0.3 }}
      >
        <div 
          className={classes.traitDetailsHeader}
          style={{ backgroundColor: getTraitCategoryColor(trait.category) }}
        >
          <div className={classes.traitHeaderContent}>
            <h2 className={classes.traitName}>{trait.name}</h2>
            <div className={classes.traitMeta}>
              {categoryName}  Tier {trait.tier}
            </div>
          </div>
          <button 
            className={classes.closeButton}
            onClick={() => setSelectedTrait(null)}
          >

          </button>
        </div>

        <div className={classes.traitDetailsContent}>
          <div className={classes.traitDescription}>
            {trait.description}
          </div>

          <div className={classes.traitDetailsSection}>
            <h3 className={classes.sectionTitle}>Environmental Affinities</h3>
            <div className={classes.affinitiesGrid}>
              {Object.entries(trait.affinities).map(([stressor, value]) => {
                if (value === 0) return null;

                const stressorName = stressor.split('_').map(word => 
                  word.charAt(0).toUpperCase() + word.slice(1)
                ).join(' ');

                return (
                  <div key={stressor} className={classes.affinityItem}>
                    <span className={classes.affinityName}>{stressorName}</span>
                    <span className={`
                      ${classes.affinityValue} 
                      ${value > 0 ? classes.positive : classes.negative}
                    `}>
                      {value > 0 ? '+' : ''}{value.toFixed(1)}
                    </span>
                  </div>
                );
              })}
            </div>

            <div className={classes.environmentalRelevance}>
              <div className={classes.relevanceLabel}>
                Environmental Relevance
              </div>
              <div className={`
                ${classes.relevanceValue} 
                ${relevance > 0 ? classes.positive : relevance < 0 ? classes.negative : ''}
              `}>
                {relevance.toFixed(2)}
              </div>
            </div>
          </div>

          {parentTrait && (
            <div className={classes.traitDetailsSection}>
              <h3 className={classes.sectionTitle}>Evolutionary Origin</h3>
              <div
                className={classes.parentTrait}
                onClick={() => handleTraitClick(parentTrait)}
              >
                <div className={classes.parentTraitIcon} style={{ 
                  backgroundColor: getTraitCategoryColor(parentTrait.category) 
                }}></div>
                <div className={classes.parentTraitDetails}>
                  <div className={classes.parentTraitName}>{parentTrait.name}</div>
                  <div className={classes.parentTraitCategory}>
                    {traitCategories.find(c => c.id === parentTrait.category)?.name}  Tier {parentTrait.tier}
                  </div>
                </div>
              </div>
            </div>
          )}

          {selectedTraitConnections.length > 0 && (
            <div className={classes.traitDetailsSection}>
              <h3 className={classes.sectionTitle}>Connected Traits</h3>
              <div className={classes.connectionsList}>
                {selectedTraitConnections.map(connection => {
                  const compatibility = calculateCompatibility(trait, connection);

                  return (
                    <div
                      key={connection.id}
                      className={classes.connectionItem}
                      onClick={() => handleTraitClick(connection)}
                    >
                      <div className={classes.connectionItemLeft}>
                        <div 
                          className={classes.connectionDot} 
                          style={{ backgroundColor: getTraitCategoryColor(connection.category) }}
                        ></div>
                        <div className={classes.connectionDetails}>
                          <div className={classes.connectionName}>{connection.name}</div>
                          <div className={classes.connectionCategory}>
                            {traitCategories.find(c => c.id === connection.category)?.name}  Tier {connection.tier}
                          </div>
                        </div>
                      </div>
                      <div className={`
                        ${classes.compatibilityBadge}
                        ${compatibility > 0.7 ? classes.highCompatibility : 
                          compatibility > 0.5 ? classes.mediumCompatibility : 
                          classes.lowCompatibility}
                      `}>
                        {compatibility.toFixed(2)}
                      </div>
                    </div>
                  );
                })}
              </div>
            </div>
          )}

          {synthesizedTrait && (
            <div className={classes.traitDetailsSection}>
              <h3 className={classes.sectionTitle}>Synthesis Potential</h3>
              <div className={classes.synthesisPotential}>
                <div className={classes.synthesisName}>
                  {synthesizedTrait.name}
                </div>
                <div className={classes.synthesisDescription}>
                  {synthesizedTrait.description}
                </div>
                <div className={classes.synthesisCondition}>
                  <span>Trigger Condition:</span> 
                  <span className={classes.conditionName}>
                    {trait.synthesis.condition.split('_').map(word => 
                      word.charAt(0).toUpperCase() + word.slice(1)
                    ).join(' ')}
                  </span>
                </div>
              </div>
            </div>
          )}
        </div>
      </motion.div>
    );
  };

  // Main render
  return (
    <div 
      className={classes.evolutionSimulator}
      onMouseMove={handleMouseMove}
    >
      {/* Background with stars */}
      <Background cursorPosition={cursorPosition} setCursorPosition={setCursorPosition} />

      {/* Meteor shower effect */}
      <MeteorShower 
        height="100vh"
        zIndex={2}
        trailColor="rgba(191, 173, 127, 0.8)"
        glowColor="rgba(207, 185, 130, 0.6)"
        coreColor="rgba(255, 248, 220, 1)"
        meteorDensity={15}
        meteorMinSize={1}
        meteorMaxSize={2}
        meteorSpeed={0.08}
        trailLength={300}
        trailSegments={80}
        enableParallax={true}
        parallaxIntensity={0.15}
        mode="arc"
        staggered={true}
      />

      {/* Header section */}
      <header ref={headerRef} className={classes.header}>
        <ScrollingTextAnimation
          scrollConfig={{
            startPosition: 0,
            endPosition: 300,
            initialY: -30,
            finalY: 0,
            clampValues: true
          }}
        >
          <h1 className={classes.title}>Crescent Evolution System</h1>
          <p className={classes.subtitle}>
            Explore the interconnected trait networks that drive evolution through Bayesian natural selection
          </p>
        </ScrollingTextAnimation>
      </header>

      {/* Main content */}
      <div 
        ref={contentRef}
        className={`${classes.content} ${isLoaded ? classes.loaded : ''}`}
      >
        {/* Filter modes */}
        <div className={classes.filterControls}>
          <button 
            className={`${classes.filterButton} ${filterMode === 'all' ? classes.active : ''}`}
            onClick={() => handleFilterModeChange('all')}
          >
            All Traits
          </button>
          <button 
            className={`${classes.filterButton} ${filterMode === 'relevance' ? classes.active : ''}`}
            onClick={() => handleFilterModeChange('relevance')}
          >
            By Relevance
          </button>
          <button 
            className={`${classes.filterButton} ${filterMode === 'category' ? classes.active : ''}`}
            onClick={() => handleFilterModeChange('category')}
            disabled={!selectedTrait}
          >
            By Category
          </button>
        </div>

        {/* Network visualization */}
        <div 
          ref={containerRef}
          className={classes.networkContainer}
          onClick={handleCanvasClick}
          onMouseMove={handleCanvasMouseMove}
        >
          <canvas 
            ref={canvasRef}
            className={classes.networkCanvas}
          />

          {/* Trait tooltip on hover */}
          <AnimatePresence>
            {hoveredTrait && renderTraitTooltip()}
          </AnimatePresence>

          {/* Body shape selector popup */}
          <AnimatePresence>
            {renderBodyShapeSelector()}
          </AnimatePresence>
        </div>

        {/* Side panel */}
        <div className={classes.sidePanel}>
          {/* Environmental controls */}
          {renderEnvironmentControls()}

          {/* Trait details when selected */}
          <AnimatePresence>
            {selectedTrait && renderTraitDetails()}
          </AnimatePresence>

          {/* Category legend */}
          <div className={classes.categoryLegend}>
            <h3 className={classes.controlsTitle}>Trait Categories</h3>
            <div className={classes.legendItems}>
              {traitCategories.map(category => (
                <div key={category.id} className={classes.legendItem}>
                  <div 
                    className={classes.legendColor}
                    style={{ backgroundColor: category.color }}
                  ></div>
                  <span className={classes.legendName}>{category.name}</span>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>

      {/* Usage instructions */}
      <div className={classes.instructions}>
        Click on body shape or traits to explore connections and adaptations
      </div>

      {/* Accessibility skip link */}
      <a href="#content" className={classes.skipLink}>
        Skip to main content
      </a>
    </div>
  );
};

export default EvolutionSimulator;
