import React, { useRef } from 'react';
import { motion, useInView } from 'framer-motion';
import { createUseStyles } from 'react-jss';

const useStyles = createUseStyles({
  profile: {
    backgroundColor: 'rgba(15, 15, 15, 0.7)',
    border: '1px solid rgba(160, 142, 97, 0.2)',
    borderRadius: '3px',
    padding: '3rem',
    margin: '4rem auto',
    maxWidth: '1000px',
    display: 'grid',
    gridTemplateColumns: '1fr 2fr',
    gap: '3rem',
    boxShadow: '0 10px 30px rgba(0, 0, 0, 0.3)',
    position: 'relative',
    '@media (max-width: 768px)': {
      gridTemplateColumns: '1fr',
      padding: '2rem'
    }
  },
  profileBorder: {
    '&::before': {
      content: '""',
      position: 'absolute',
      top: 0,
      left: 0,
      width: 0,
      height: '1px',
      background: 'linear-gradient(to right, rgba(160, 142, 97, 0.7), rgba(160, 142, 97, 0))',
      transition: 'width 1s ease 0.5s'
    },
    '&$inView::before': {
      width: '100%'
    }
  },
  inView: {},
  profileImage: {
    position: 'relative',
    width: '100%',
    aspectRatio: '1/1',
    borderRadius: '3px',
    overflow: 'hidden',
    boxShadow: '0 5px 15px rgba(0, 0, 0, 0.3)',
    '@media (max-width: 768px)': {
      maxWidth: '250px',
      margin: '0 auto'
    }
  },
  image: {
    width: '100%',
    height: '100%',
    objectFit: 'cover',
    transition: 'transform 0.6s ease'
  },
  profileDetails: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    '@media (max-width: 768px)': {
      textAlign: 'center'
    }
  },
  profileName: {
    fontSize: '2.2rem',
    color: '#bfad7f',
    fontWeight: 300,
    letterSpacing: '0.1em',
    marginBottom: '0.5rem',
    '@media (max-width: 480px)': {
      fontSize: '1.8rem'
    }
  },
  profileRole: {
    fontSize: '1.1rem',
    color: 'rgba(191, 173, 127, 0.7)',
    marginBottom: '2rem',
    letterSpacing: '0.05em',
    fontWeight: 300,
    fontFamily: '"Garamond", "Adobe Caslon Pro", serif',
    fontStyle: 'italic'
  },
  profileBio: {
    fontSize: '1rem',
    color: 'rgba(224, 224, 224, 0.7)',
    lineHeight: 1.8,
    marginBottom: '2rem',
    fontFamily: '"Garamond", "Adobe Caslon Pro", serif',
    fontWeight: 300
  },
  profileStats: {
    display: 'grid',
    gridTemplateColumns: 'repeat(3, 1fr)',
    gap: '1.5rem',
    marginTop: '2rem',
    borderTop: '1px solid rgba(160, 142, 97, 0.2)',
    paddingTop: '1.5rem',
    '@media (max-width: 480px)': {
      gridTemplateColumns: '1fr',
      gap: '1.5rem'
    }
  },
  stat: {
    textAlign: 'center'
  },
  statValue: {
    fontSize: '2.5rem',
    color: 'rgba(191, 173, 127, 0.9)',
    fontWeight: 300
  },
  statLabel: {
    fontSize: '0.85rem',
    color: 'rgba(224, 224, 224, 0.5)',
    textTransform: 'uppercase',
    letterSpacing: '0.1em',
    marginTop: '0.5rem'
  }
});

/**
 * PersonProfileCard Component
 * 
 * A reusable component for displaying a person's profile with image, bio, and statistics.
 * Features scroll-based animation and responsive design.
 * 
 * @param {Object} props - Component props
 * @param {Object} props.person - Person data object
 * @param {string} props.person.name - Person's name
 * @param {string} props.person.role - Person's role or title
 * @param {string} props.person.image - URL to person's image
 * @param {string[]} props.person.bio - Array of bio paragraphs
 * @param {Object[]} props.person.stats - Array of statistic objects
 * @param {string|number} props.person.stats[].value - The statistic value
 * @param {string} props.person.stats[].label - The statistic label
 * @param {Object} [props.animationConfig] - Optional animation configuration
 * @param {boolean} [props.animationConfig.useInternalRef=true] - Whether to use internal ref or external
 * @param {Object} [props.animationConfig.ref] - External ref (when useInternalRef is false)
 * @param {boolean} [props.animationConfig.isInView] - External isInView state (when useInternalRef is false)
 * @param {number} [props.animationConfig.threshold=0.2] - Visibility threshold to trigger animation
 * @param {boolean} [props.animationConfig.once=true] - Whether animation should only trigger once
 * @param {number} [props.animationConfig.initialY=50] - Initial Y offset for animation
 * @param {number} [props.animationConfig.duration=0.8] - Animation duration in seconds
 * @param {string} [props.animationConfig.ease="easeOut"] - Animation easing function
 */
const PersonProfileCard = ({ 
  person, 
  animationConfig = {
    useInternalRef: true,
    threshold: 0.2,
    once: true,
    initialY: 50,
    duration: 0.8,
    ease: "easeOut"
  }
}) => {
  const classes = useStyles();

  // Set up ref and inView detection
  const internalRef = useRef(null);

  // Always call useInView (to follow React hook rules)
  const internalInView = useInView(
    internalRef, 
    { 
      once: animationConfig.once, 
      threshold: animationConfig.threshold 
    }
  );

  // Use either internal or external ref/inView based on config
  const ref = animationConfig.useInternalRef ? internalRef : animationConfig.ref;
  const isInView = animationConfig.useInternalRef ? internalInView : animationConfig.isInView;

  return (
    <motion.div 
      ref={ref}
      className={`${classes.profile} ${classes.profileBorder} ${isInView ? classes.inView : ''}`}
      initial={{ opacity: 0, y: animationConfig.initialY }}
      animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: animationConfig.initialY }}
      transition={{ 
        duration: animationConfig.duration, 
        ease: animationConfig.ease 
      }}
    >
      <div className={classes.profileImage}>
        <img 
          src={person.image} 
          alt={person.name} 
          className={classes.image}
        />
      </div>

      <div className={classes.profileDetails}>
        <h2 className={classes.profileName}>{person.name}</h2>
        <p className={classes.profileRole}>{person.role}</p>

        {person.bio.map((paragraph, index) => (
          <p key={index} className={classes.profileBio}>{paragraph}</p>
        ))}

        {person.stats && person.stats.length > 0 && (
          <div className={classes.profileStats}>
            {person.stats.map((stat, index) => (
              <div key={index} className={classes.stat}>
                <div className={classes.statValue}>{stat.value}</div>
                <div className={classes.statLabel}>{stat.label}</div>
              </div>
            ))}
          </div>
        )}
      </div>
    </motion.div>
  );
};

export default PersonProfileCard;
