import React, { useState, useRef, useEffect, useCallback, useMemo } from 'react';

/**
 * Background Component
 * 
 * Creates an animated starfield background with moving constellations
 * that work independently of cursor position.
 */
const Background = ({
  // For backward compatibility
  cursorPosition,
  setCursorPosition,

  // Configuration options
  zIndex = -1,
  active = true,
  starCount = 150,
  starMinSize = 0.5,
  starMaxSize = 2.5,
  starMinOpacity = 0.2,
  starMaxOpacity = 0.8,
  constellationCount = 8,
  constellationDensity = 0.3,
  constellationLineOpacity = 0.15,
  constellationLineColor = 'rgba(160, 142, 97, 0.4)',
  coreStarColor = 'rgba(255, 253, 227, 1)',
  glowStarColor = 'rgba(191, 173, 127, 0.6)',
  vertexEffectIntensity = 0.5,
  backgroundMotionSpeed = 0.0005,
  starPulseDuration = 3000,
  stellarDriftSpeed = 0.02,
  adaptiveQuality = true,
  respectReducedMotion = true,
  debugMode = false
}) => {
  // Console log to confirm component is rendering
  console.log("Background component rendering");

  // Component state
  const [dimensions, setDimensions] = useState({ width: 0, height: 0 });
  const [initialized, setInitialized] = useState(false);
  const [error, setError] = useState(null);

  // Refs
  const containerRef = useRef(null);
  const canvasRef = useRef(null);
  const ctxRef = useRef(null);
  const starsRef = useRef([]);
  const constellationsRef = useRef([]);
  const animationFrameRef = useRef(null);
  const lastFrameTimeRef = useRef(0);

  // Setup canvas
  useEffect(() => {
    console.log("Background setup effect running");

    if (!containerRef.current) {
      console.warn("Container ref not available");
      return;
    }

    try {
      const canvas = canvasRef.current;
      const container = containerRef.current;

      // Log container dimensions to debug sizing issues
      const rect = container.getBoundingClientRect();
      console.log("Container dimensions:", rect.width, rect.height);

      // Set canvas size
      const pixelRatio = window.devicePixelRatio || 1;
      canvas.width = rect.width * pixelRatio;
      canvas.height = rect.height * pixelRatio;

      // Set CSS display size
      canvas.style.width = `${rect.width}px`;
      canvas.style.height = `${rect.height}px`;

      // Get canvas context
      const ctx = canvas.getContext('2d');
      if (!ctx) {
        throw new Error("Could not get canvas context");
      }

      // Scale context for high-DPI displays
      ctx.scale(pixelRatio, pixelRatio);
      ctxRef.current = ctx;

      // Update dimensions state
      setDimensions({
        width: rect.width,
        height: rect.height
      });

      console.log("Canvas initialized:", canvas.width, canvas.height);

      // Draw a test shape to confirm canvas is working
      ctx.fillStyle = 'rgba(160, 142, 97, 0.2)';
      ctx.fillRect(0, 0, rect.width, rect.height);
      ctx.fillStyle = 'rgba(255, 255, 255, 0.5)';
      ctx.fillRect(rect.width/2 - 50, rect.height/2 - 50, 100, 100);

    } catch (err) {
      console.error("Error setting up canvas:", err);
      setError(err.message);
    }
  }, []);

  // Initialize stars when dimensions change
  useEffect(() => {
    if (!dimensions.width || !dimensions.height) return;

    console.log("Initializing stars with dimensions:", dimensions);

    try {
      // Create stars
      const stars = [];
      for (let i = 0; i < starCount; i++) {
        const x = Math.random() * dimensions.width;
        const y = Math.random() * dimensions.height;
        const size = Math.random() * (starMaxSize - starMinSize) + starMinSize;
        const opacity = Math.random() * (starMaxOpacity - starMinOpacity) + starMinOpacity;

        stars.push({
          x,
          y,
          size,
          opacity,
          originalX: x,
          originalY: y,
          pulsePhase: Math.random() * Math.PI * 2,
          zDepth: Math.random() * 0.8 + 0.2
        });
      }

      starsRef.current = stars;

      // Create constellations
      const constellations = [];
      // Simplified constellation initialization for debugging
      for (let i = 0; i < constellationCount; i++) {
        const points = [];
        const pointCount = Math.floor(Math.random() * 4) + 3; // 3-6 points

        // Add random points
        for (let j = 0; j < pointCount; j++) {
          points.push({
            x: Math.random() * dimensions.width,
            y: Math.random() * dimensions.height
          });
        }

        constellations.push({
          points,
          opacity: constellationLineOpacity,
          rotation: (Math.random() * 0.0002 - 0.0001) * (Math.random() < 0.5 ? -1 : 1),
          rotationCenter: { 
            x: dimensions.width / 2 + (Math.random() * 200 - 100),
            y: dimensions.height / 2 + (Math.random() * 200 - 100)
          }
        });
      }

      constellationsRef.current = constellations;

      // Mark initialization as complete
      setInitialized(true);
      console.log("Stars and constellations initialized");

    } catch (err) {
      console.error("Error initializing stars:", err);
      setError(err.message);
    }
  }, [dimensions, starCount, starMinSize, starMaxSize, starMinOpacity, starMaxOpacity, constellationCount, constellationLineOpacity]);

  // Animation function
  const animate = useCallback(() => {
    if (!ctxRef.current || !initialized) {
      animationFrameRef.current = requestAnimationFrame(animate);
      return;
    }

    const ctx = ctxRef.current;
    const now = performance.now();
    const { width, height } = dimensions;

    // Clear canvas
    ctx.clearRect(0, 0, width, height);

    // Draw test background to confirm rendering
    if (debugMode) {
      ctx.fillStyle = 'rgba(50, 50, 70, 0.1)';
      ctx.fillRect(0, 0, width, height);
    }

    // Calculate global movement
    const globalOffsetX = Math.sin(now * backgroundMotionSpeed) * width * 0.05;
    const globalOffsetY = Math.cos(now * backgroundMotionSpeed) * height * 0.05;

    // Draw stars
    starsRef.current.forEach(star => {
      const parallaxFactor = star.zDepth;

      // Update position with smooth motion
      const x = star.originalX + globalOffsetX * parallaxFactor;
      const y = star.originalY + globalOffsetY * parallaxFactor;

      // Pulse effect
      const pulseValue = Math.sin(now * 0.001 + star.pulsePhase) * 0.2 + 0.8;
      const currentSize = star.size * pulseValue;
      const currentOpacity = star.opacity * pulseValue;

      // Draw star
      ctx.beginPath();
      ctx.arc(x, y, currentSize, 0, Math.PI * 2);
      ctx.fillStyle = `rgba(255, 253, 227, ${currentOpacity})`;
      ctx.fill();
    });

    // Draw constellations
    constellationsRef.current.forEach(constellation => {
      if (constellation.points.length < 2) return;

      ctx.beginPath();

      // Move constellation center based on global movement
      const centerX = constellation.rotationCenter.x + globalOffsetX * 0.2;
      const centerY = constellation.rotationCenter.y + globalOffsetY * 0.2;

      // Calculate rotation based on time
      const rotationAmount = constellation.rotation * (now - lastFrameTimeRef.current);

      // Get starting point
      const firstPoint = constellation.points[0];
      const fx = centerX + (firstPoint.x - centerX) * Math.cos(rotationAmount) - (firstPoint.y - centerY) * Math.sin(rotationAmount);
      const fy = centerY + (firstPoint.x - centerX) * Math.sin(rotationAmount) + (firstPoint.y - centerY) * Math.cos(rotationAmount);

      ctx.moveTo(fx, fy);

      // Connect points
      for (let i = 1; i < constellation.points.length; i++) {
        const point = constellation.points[i];
        const px = centerX + (point.x - centerX) * Math.cos(rotationAmount) - (point.y - centerY) * Math.sin(rotationAmount);
        const py = centerY + (point.x - centerX) * Math.sin(rotationAmount) + (point.y - centerY) * Math.cos(rotationAmount);

        ctx.lineTo(px, py);
      }

      // Draw constellation lines
      ctx.strokeStyle = constellationLineColor;
      ctx.lineWidth = 0.7;
      ctx.stroke();
    });

    // Debug info
    if (debugMode) {
      ctx.fillStyle = 'rgba(255, 255, 255, 0.8)';
      ctx.font = '12px monospace';
      ctx.fillText(`Stars: ${starsRef.current.length}`, 10, 20);
      ctx.fillText(`Constellations: ${constellationsRef.current.length}`, 10, 40);
      ctx.fillText(`Dimensions: ${width}×${height}`, 10, 60);
      ctx.fillText(`Time: ${Math.floor(now)}`, 10, 80);
    }

    // Store time for next frame
    lastFrameTimeRef.current = now;

    // Request next frame
    animationFrameRef.current = requestAnimationFrame(animate);
  }, [dimensions, initialized, backgroundMotionSpeed, constellationLineColor, debugMode]);

  // Start animation loop
  useEffect(() => {
    if (!initialized) return;

    console.log("Starting animation loop");
    lastFrameTimeRef.current = performance.now();
    animationFrameRef.current = requestAnimationFrame(animate);

    // Add resize handler
    const handleResize = () => {
      if (!containerRef.current) return;

      const rect = containerRef.current.getBoundingClientRect();
      console.log("Window resized, new dimensions:", rect.width, rect.height);

      const canvas = canvasRef.current;
      const pixelRatio = window.devicePixelRatio || 1;

      canvas.width = rect.width * pixelRatio;
      canvas.height = rect.height * pixelRatio;
      canvas.style.width = `${rect.width}px`;
      canvas.style.height = `${rect.height}px`;

      if (ctxRef.current) {
        ctxRef.current.scale(pixelRatio, pixelRatio);
      }

      setDimensions({
        width: rect.width,
        height: rect.height
      });
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }
    };
  }, [initialized, animate]);

  // Render component
  return (
    <div
      ref={containerRef}
      style={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        overflow: 'hidden',
        zIndex,
        pointerEvents: 'none',
        backgroundColor: error ? 'rgba(20, 20, 30, 0.8)' : 'transparent',
      }}
      aria-hidden="true"
    >
      <canvas
        ref={canvasRef}
        style={{
          position: 'absolute',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          opacity: 1,
        }}
      />

      {/* Error message for debugging */}
      {error && (
        <div style={{ 
          position: 'absolute', 
          top: '10px', 
          left: '10px', 
          color: 'white', 
          background: 'rgba(255, 0, 0, 0.7)',
          padding: '10px',
          zIndex: 1000
        }}>
          Error: {error}
        </div>
      )}

      {/* Debug overlay with version information */}
      {debugMode && (
        <div style={{ 
          position: 'absolute', 
          bottom: '10px', 
          right: '10px', 
          color: 'white', 
          background: 'rgba(0, 0, 0, 0.5)',
          padding: '5px',
          zIndex: 1000,
          fontFamily: 'monospace',
          fontSize: '10px'
        }}>
          Background v1.0 | Canvas: {dimensions.width}×{dimensions.height} | 
          Stars: {starsRef.current.length} | Constellations: {constellationsRef.current.length}
        </div>
      )}
    </div>
  );
};

export default Background;
