          display: flex;
          justify-content: center;
          align-items: flex-start;
          font-family: 'Avenir', 'Avenir Next', sans-serif;
          -webkit-font-smoothing: antialiased;
        }

        .background-canvas {
          position: fixed;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          z-index: 1;
        }

        /* Intro Sequence Styles */
        .intro-sequence {
          position: fixed;
          top: 0;
          left: 0;
