          text-align: center;
          margin-bottom: 4rem; /* Increased spacing */
        }

        .main-title {
          font-size: 3rem;
          letter-spacing: 0.25em;
          margin-bottom: 0.5rem;
          color: #bfad7f;
          font-weight: 300;
          text-transform: uppercase;
        }

        .title-divider {
          height: 1px;
          width: 120px;
          background: linear-gradient(to right, rgba(160, 142, 97, 0), rgba(160, 142, 97, 0.6), rgba(160, 142, 97, 0));
          margin: 0.5rem auto;
        }

        .subtitle {
          font-size: 1rem;
          color: rgba(224, 224, 224, 0.6);
          font-family: 'Garamond', 'Adobe Caslon Pro', serif;
          font-style: italic;
          font-weight: 300;
          margin-top: 0.75rem;
          letter-spacing: 0.05em;
        }

        .sections-container {
          display: flex;
          flex-direction: column;
          gap: 2rem; /* Increased spacing between sections */
          margin-bottom: 3rem;
        }

        .section-wrapper {
          position: relative;
        }

        .section-row {
          position: relative;
          height: 6.5rem; /* Consistently taller rows for all sections */
          background-color: rgba(15, 15, 15, 0.5);
          border: 1px solid rgba(160, 142, 97, 0.2);
          border-radius: 3px;
          cursor: pointer;
          transition: all 0.4s ease;
          overflow: hidden;
          animation: fadeIn 0.8s ease backwards;
          display: flex;
          align-items: center;
          padding-right: 3rem; /* Space for diagonal cut */
        }

        .section-row::before {
          content: '';
          position: absolute;
          top: 0;
          left: 0;
          width: 0;
          height: 1px;
          background: linear-gradient(to right, rgba(160, 142, 97, 0.7), rgba(160, 142, 97, 0));
          transition: width 0.6s ease;
        }

        .section-row.active {
          background-color: rgba(20, 20, 20, 0.7);
          transform: translateX(5px);
          box-shadow: 0 5px 20px rgba(0, 0, 0, 0.3), 0 0 10px rgba(160, 142, 97, 0.15);
          border-color: rgba(160, 142, 97, 0.4);
        }

        .section-row.expanded {
          background-color: rgba(25, 25, 30, 0.8);
          transform: translateX(10px);
          box-shadow: 0 8px 25px rgba(0, 0, 0, 0.4), 0 0 15px rgba(160, 142, 97, 0.2);
          border-color: rgba(160, 142, 97, 0.5);
        }

        .section-row.active::before, .section-row.expanded::before {
          width: 100%;
        }

        .section-content {
          display: flex;
          align-items: center;
          width: 100%;
          height: 100%;
          position: relative;
          z-index: 2;
        }

        .section-icon-wrapper {
          width: 80px;
          display: flex;
          justify-content: center;
          align-items: center;
        }

        .icon-container {
          display: flex;
          justify-content: center;
          align-items: center;
          transition: all 0.3s ease;
        }

        .section-row.active .icon-container, .section-row.expanded .icon-container {
          transform: scale(1.15);
        }

        .section-text {
          flex: 1;
          padding-right: 1.5rem;
          display: flex;
          flex-direction: column;
        }

        .section-title {
          font-size: 1.25rem;
          margin-bottom: 0.25rem;
          color: #bfad7f;
          font-weight: 300;
          letter-spacing: 0.05em;
          transition: all 0.3s ease;
        }

        .section-row.active .section-title, .section-row.expanded .section-title {
          transform: translateY(-2px);
        }

        .section-description {
          font-size: 0.85rem;
          color: rgba(224, 224, 224, 0.6);
          line-height: 1.4;
          max-width: 90%;
          font-family: 'Garamond', 'Adobe Caslon Pro', serif;
          font-weight: 300;
          letter-spacing: 0.02em;
          margin-bottom: 0.5rem; /* Consistent margin for all sections */
        }

        .expand-hint {
          font-size: 0.75rem;
          color: rgba(160, 142, 97, 0.8);
          font-style: italic;
          opacity: 0.7;
          transition: opacity 0.3s ease;
          line-height: 1.2;
        }

        .section-row.active .expand-hint {
          opacity: 1;
        }

        .expand-indicator {
          display: flex;
          justify-content: center;
          align-items: center;
          margin-right: 1rem;
          transition: transform 0.3s ease;
          opacity: 0.7;
        }

        .expand-indicator.expanded {
          transform: rotate(180deg);
          opacity: 1;
        }

        .expand-indicator.small {
          margin-right: 0.5rem;
          transform: scale(0.8);
        }

        .expand-indicator.small.expanded {
          transform: scale(0.8) rotate(180deg);
        }

        .diagonal-cut {
          position: absolute;
          top: 0;
          right: 0;
          width: 40px;
          height: 100%;
          overflow: hidden;
          z-index: 1;
        }

        .diagonal-cut::before {
          content: '';
          position: absolute;
          top: 0;
          right: 0;
          width: 80px;
          height: 150%;
          background-color: #080808;
          transform: rotate(15deg) translateX(20px);
        }

        .diagonal-cut::after {
          content: '';
          position: absolute;
          top: 0;
          right: 0;
          width: 1px;
          height: 150%;
          background-color: rgba(160, 142, 97, 0.2);
          transform: rotate(15deg) translateX(20px);
        }

        /* Subsection Styles */
        .subsections-container {
          margin-top: 0.75rem;
          margin-left: 2rem;
          animation: fadeDown 0.3s ease;
        }

        .subsection-row {
          background-color: rgba(15, 15, 15, 0.4);
          border: 1px solid rgba(160, 142, 97, 0.15);
          border-radius: 3px;
          padding: 0.75rem 1rem;
          margin-bottom: 0.5rem;
          cursor: pointer;
          transition: all 0.3s ease;
        }

        .subsection-row.active,
        .subsection-row:hover {
          background-color: rgba(20, 20, 20, 0.6);
          border-color: rgba(160, 142, 97, 0.3);
          transform: translateX(5px);
        }

        .subsection-content {
          display: flex;
          flex-wrap: wrap;
          align-items: center;
        }

        .subsection-title {
          font-size: 1rem;
          color: #bfad7f;
          font-weight: 300;
          margin: 0;
          flex: 1;
        }

        .subsection-description {
          width: 100%;
          font-size: 0.8rem;
          color: rgba(224, 224, 224, 0.6);
          margin: 0;
          padding-top: 0.5rem;
          display: none;
        }

        .subsection-expand-hint {
          font-size: 0.7rem;
          color: rgba(160, 142, 97, 0.7);
          font-style: italic;
          margin-top: 0.25rem;
          display: none;
          width: 100%;
        }

        .subsection-row.active .subsection-description,
        .subsection-row:hover .subsection-description,
        .subsection-row.active .subsection-expand-hint,
        .subsection-row:hover .subsection-expand-hint {
          display: block;
        }

        /* Nested Subsection Styles */
        .nested-subsections-container {
          margin-top: 0.5rem;
          margin-left: 1.5rem;
          animation: fadeDown 0.3s ease;
        }

        .nested-subsection-row {
          background-color: rgba(15, 15, 15, 0.3);
          border: 1px solid rgba(160, 142, 97, 0.1);
          border-radius: 3px;
          padding: 0.6rem 0.75rem;
          margin-bottom: 0.4rem;
          cursor: pointer;
          transition: all 0.3s ease;
        }

        .nested-subsection-row:hover {
          background-color: rgba(20, 20, 20, 0.5);
          border-color: rgba(160, 142, 97, 0.25);
          transform: translateX(3px);
        }

        .nested-subsection-title {
          font-size: 0.9rem;
          color: rgba(224, 224, 224, 0.9);
          font-weight: 300;
          margin: 0;
        }

        .nested-subsection-description {
          display: none;
          font-size: 0.75rem;
          color: rgba(224, 224, 224, 0.6);
          margin: 0.4rem 0 0;
        }

        .nested-subsection-row:hover .nested-subsection-description {
          display: block;
        }

        /* Footer Moon Section */
        .footer-section {
          width: 100%;
          display: flex;
          flex-direction: column;
          align-items: center;
          padding: 2rem 0 4rem;
          position: relative;
        }

        .footer-moon {
          margin-bottom: 2rem;
        }

        .brand-mark {
          display: flex;
          flex-direction: column;
          align-items: center;
          opacity: 0.4;
          transition: opacity 0.3s ease;
        }

        .brand-mark:hover {
          opacity: 0.7;
        }

        .symbol {
          font-size: 1.2rem;
          color: #a08e61;
          margin-bottom: 0.25rem;
        }

        .year {
          font-size: 0.65rem;
          color: rgba(224, 224, 224, 0.4);
          letter-spacing: 0.2em;
          font-weight: 300;
        }

