import React, { useState, useEffect, useRef } from 'react';

const RefinedLandingPage = () => {
  const [activeSection, setActiveSection] = useState(null);
  const [expandedSection, setExpandedSection] = useState(null);
  const [expandedSubsection, setExpandedSubsection] = useState(null);
  const [cursorPosition, setCursorPosition] = useState({ x: 0, y: 0 });
  const [isLoaded, setIsLoaded] = useState(false);
  const [showIntro, setShowIntro] = useState(false);
  const [quoteVisible, setQuoteVisible] = useState(false);
  const [currentQuote, setCurrentQuote] = useState(null);
  const [moonDrawn, setMoonDrawn] = useState(0); // 0: not started, 1: drawing, 2: complete

  const canvasRef = useRef(null);
  const moonCanvasRef = useRef(null);
  const footerMoonRef = useRef(null);
  const particlesRef = useRef([]);
  const requestRef = useRef(null);
  const moonAnimationRef = useRef(null);

  // Collection of poetic quotes by women
  const quotes = [
    {
      text: "My candle burns at both ends;\nIt will not last the night;\nBut, ah my foes, and\noh my friend â€”\nit gives a\nlovely\nlight",
      author: "Edna St. Vincent Millay"
    },
    {
      text: "Out of the ash\nI rise with my red hair\nAnd I eat men like air.",
      author: "Sylvia Plath"
    },
    {
      text: "And he will come this, light insouciant lover,\nblowing his bugles to a rising morrow,\nto rend her veil, her wizened mind uncover,\nto leave her conquered by life's stinging sorrow",
      author: "Virginia McCormick"
    },
    {
      text: "Hope is the thing with feathers\nThat perches in the soul,\nAnd sings the tune without the words,\nAnd never stops at all.",
      author: "Emily Dickinson"
    },
    {
      text: "I have gone out, a possessed witch,\nhaunting the black air, braver at night;\ndreaming evil, I have done my hitch\nover the plain houses, light by light.",
      author: "Anne Sexton"
    }
  ];

  // Main sections - reduced to 3
  const mainSections = [
    {
      id: "cycles",
      title: "Cycles",
      description: "An intricate framework modeling the evolution of worlds through endless creation and destruction.",
      icon: "circles",
      path: "/cycles"
    },
    {
      id: "codex",
      title: "Codex",
      description: "text not decided yet.",
      icon: "triangle",
      path: "/codex",
      expandable: true,
      expandHint: "Click to explore repository sections",
      subsections: [
        {
          id: "creator",
          title: "Creator",
          description: "The mind behind the mysteries.",
          path: "/codex/creator"
        },
        {
          id: "visualizations",
          title: "Visualizations",
          description: "Complex systems revealed through dynamic visual representations.",
          path: "/codex/visualizations",
          expandable: true,
          expandHint: "Expand visualization options",
          subsections: [
            {
              id: "creature-traits",
              title: "Creature Trait Visualization",
              description: "Explore how creatures adapt and evolve in response to environmental pressures.",
              path: "/codex/visualizations/creature-traits"
            },
            {
              id: "environmental",
              title: "Environmental Visualization",
              description: "Witness the formation and transformation of worlds and their ecosystems.",
              path: "/codex/visualizations/environmental"
            }
          ]
        },
        {
          id: "basics",
          title: "Basics",
          description: "Fundamental concepts and essential knowledge for beginners.",
          path: "/codex/basics"
        },
        {
          id: "advanced",
          title: "Advanced",
          description: "Complex theories and elaborate mechanics for seasoned explorers.",
          path: "/codex/advanced"
        }
      ]
    },
    {
      id: "characters",
      title: "Characters",
      description: "Beings shaped by their environments, each with a unique story to tell.",
      icon: "targetCircle",
      path: "/characters"
    }
  ];

  // Check if user has visited before
  useEffect(() => {
    const hasVisited = localStorage.getItem('hasVisitedCrescent');
    const shouldShowIntro = !hasVisited;

    setShowIntro(shouldShowIntro);

    if (shouldShowIntro) {
      // Select a random quote
      const randomQuote = quotes[Math.floor(Math.random() * quotes.length)];
      setCurrentQuote(randomQuote);

      // Fast 3-second intro sequence
      setTimeout(() => setMoonDrawn(1), 200); // Start drawing moon quickly
      setTimeout(() => setQuoteVisible(true), 400); // Show quote almost immediately
      setTimeout(() => setMoonDrawn(2), 2500); // Complete moon animation at 2.5s
      setTimeout(() => {
        setQuoteVisible(false);
        // Start fade out of intro
        const introElement = document.querySelector('.intro-sequence');
        if (introElement) introElement.classList.add('fade-out');
      }, 2600);
      setTimeout(() => {
        setShowIntro(false);
        setIsLoaded(true);
      }, 3000); // Complete entire sequence at 3s

      // Save that user has visited
      localStorage.setItem('hasVisitedCrescent', 'true');
    } else {
      // Skip intro for returning visitors
      setIsLoaded(true);
    }
  }, [quotes]); // Added quotes to dependency array

  // Initialize particles
  useEffect(() => {
    particlesRef.current = Array(40).fill().map(() => ({
      x: Math.random() * window.innerWidth,
      y: Math.random() * window.innerHeight,
      size: Math.random() * 1.5 + 0.5, // Smaller particles
      speedX: (Math.random() - 0.5) * 0.15,
      speedY: (Math.random() - 0.5) * 0.15,
      opacity: Math.random() * 0.3 + 0.05 // More subtle
    }));

    return () => {
      if (requestRef.current) {
        cancelAnimationFrame(requestRef.current);
      }
      if (moonAnimationRef.current) {
        cancelAnimationFrame(moonAnimationRef.current);
      }
    };
  }, []);

  // Vector moon animation for intro - much faster
  useEffect(() => {
    if (!moonCanvasRef.current || moonDrawn === 0) return;

    const canvas = moonCanvasRef.current;
    const ctx = canvas.getContext('2d');
    const centerX = canvas.width / 2;
    const centerY = canvas.height / 2;
    const radius = 70;
    let angle = 0;
    let direction = 1; // 1 for drawing, -1 for undoing
    let lastTimestamp = 0;

    // Clear canvas
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    const drawMoonAnimation = (timestamp) => {
      if (!lastTimestamp) lastTimestamp = timestamp;
      const deltaTime = timestamp - lastTimestamp;
      lastTimestamp = timestamp;

      // Speed control - faster animation to complete in ~2.3 seconds
      const speed = 1.3 * deltaTime / 16.67; // Significantly faster

      angle += speed * direction;

      // Clear canvas
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Draw moon outline
      ctx.beginPath();
      ctx.arc(centerX, centerY, radius, 0, angle * Math.PI / 180, false);
      ctx.strokeStyle = 'rgba(191, 173, 127, 0.7)';
      ctx.lineWidth = 1.5;
      ctx.stroke();

      // Add subtle glow
      ctx.shadowColor = 'rgba(191, 173, 127, 0.3)';
      ctx.shadowBlur = 8;

      // Reverse direction when full circle is drawn
      if (angle >= 360 && direction === 1) {
        direction = -1; // Start undoing
      }

      // Stop when animation is complete or when we reach moonDrawn state 2
      if ((angle <= 0 && direction === -1) || moonDrawn === 2) {
        cancelAnimationFrame(moonAnimationRef.current);
        return;
      }

      moonAnimationRef.current = requestAnimationFrame(drawMoonAnimation);
    };

    if (moonDrawn === 1) {
      moonAnimationRef.current = requestAnimationFrame(drawMoonAnimation);
    }

    return () => {
      if (moonAnimationRef.current) {
        cancelAnimationFrame(moonAnimationRef.current);
      }
    };
  }, [moonDrawn]);

  // Static vector moon for footer
  useEffect(() => {
    if (!footerMoonRef.current) return;

    const canvas = footerMoonRef.current;
    const ctx = canvas.getContext('2d');

    const drawFooterMoon = () => {
      const centerX = canvas.width / 2;
      const centerY = canvas.height / 2;
      const radius = 70;

      // Clear canvas
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Draw thin circle
      ctx.beginPath();
      ctx.arc(centerX, centerY, radius, 0, 2 * Math.PI);
      ctx.strokeStyle = 'rgba(191, 173, 127, 0.7)';
      ctx.lineWidth = 1.5;

      // Add subtle glow
      ctx.shadowColor = 'rgba(191, 173, 127, 0.3)';
      ctx.shadowBlur = 10;

      ctx.stroke();

      // Draw a few thin lines across for texture (very subtle)
      ctx.beginPath();
      ctx.moveTo(centerX - radius * 0.7, centerY - radius * 0.3);
      ctx.lineTo(centerX + radius * 0.7, centerY + radius * 0.3);
      ctx.strokeStyle = 'rgba(191, 173, 127, 0.2)';
      ctx.lineWidth = 0.5;
      ctx.stroke();

      ctx.beginPath();
      ctx.moveTo(centerX - radius * 0.5, centerY + radius * 0.5);
      ctx.lineTo(centerX + radius * 0.5, centerY - radius * 0.5);
      ctx.stroke();
    };

    drawFooterMoon();

    window.addEventListener('resize', drawFooterMoon);
    return () => {
      window.removeEventListener('resize', drawFooterMoon);
    };
  }, []);

  // Canvas animation for background particles
  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Draw particles
      particlesRef.current.forEach(particle => {
        // Update position
        particle.x += particle.speedX;
        particle.y += particle.speedY;

        // Wrap around screen
        if (particle.x < 0) particle.x = canvas.width;
        if (particle.x > canvas.width) particle.x = 0;
        if (particle.y < 0) particle.y = canvas.height;
        if (particle.y > canvas.height) particle.y = 0;

        // Draw particle
        ctx.beginPath();
        ctx.arc(particle.x, particle.y, particle.size, 0, Math.PI * 2);
        ctx.fillStyle = `rgba(191, 173, 127, ${particle.opacity})`;
        ctx.fill();
      });

      // Draw cursor influence - very subtle glow
      if (cursorPosition.x && cursorPosition.y) {
        const gradient = ctx.createRadialGradient(
          cursorPosition.x, cursorPosition.y, 0,
          cursorPosition.x, cursorPosition.y, 80
        );
        gradient.addColorStop(0, 'rgba(160, 142, 97, 0.03)');
        gradient.addColorStop(1, 'rgba(160, 142, 97, 0)');

        ctx.beginPath();
        ctx.arc(cursorPosition.x, cursorPosition.y, 80, 0, Math.PI * 2);
        ctx.fillStyle = gradient;
        ctx.fill();
      }

      requestRef.current = requestAnimationFrame(animate);
    };

    animate();

    // Resize handler
    const handleResize = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };

    window.addEventListener('resize', handleResize);
    return () => {
      window.removeEventListener('resize', handleResize);
      cancelAnimationFrame(requestRef.current);
    };
  }, [cursorPosition]);

  // Track mouse movement
  const handleMouseMove = (e) => {
    setCursorPosition({ x: e.clientX, y: e.clientY });
  };

  // Skip intro
  const handleSkipIntro = () => {
    setShowIntro(false);
    setIsLoaded(true);
  };

  // Navigate to section
  const handleNavigate = (path, isExpandable = false, sectionId = null) => {
    // If section is expandable, toggle expansion instead of navigating
    if (isExpandable && sectionId) {
      handleSectionClick(sectionId);
      return;
    }

    // For demo purposes, just log the navigation
    console.log(`Navigating to: ${path}`);
    // In a real app, you'd use your routing library:
    // window.location.href = path; or history.push(path);
  };

  // Handle section hover
  const handleSectionHover = (sectionId) => {
    setActiveSection(sectionId);
  };

  // Handle section click for expansion
  const handleSectionClick = (sectionId) => {
    if (expandedSection === sectionId) {
      setExpandedSection(null);
      setExpandedSubsection(null);
    } else {
      setExpandedSection(sectionId);
      setExpandedSubsection(null);
    }
  };

  // Handle subsection click
  const handleSubsectionClick = (sectionId, event) => {
    event.stopPropagation();

    if (expandedSubsection === sectionId) {
      setExpandedSubsection(null);
    } else {
      setExpandedSubsection(sectionId);
    }
  };

  // Handle section leave
  const handleSectionLeave = () => {
    setActiveSection(null);
  };

  // Format quote with line breaks
  const formatQuote = (text) => {
    return text.split('\n').map((line, i) => (
      <span key={i} className="quote-line">
        {line}
        {i < text.split('\n').length - 1 && <br />}
      </span>
    ));
  };

  // Render SVG icons for main sections
  const renderIcon = (iconType) => {
    switch(iconType) {
      case 'circles':
        return (
          <div className="icon-container">
            <svg width="36" height="36" viewBox="0 0 36 36" fill="none" xmlns="http://www.w3.org/2000/svg">
              <circle cx="18" cy="18" r="12" stroke="#A08E61" strokeWidth="1.5" opacity="0.9" />
              <circle cx="14" cy="18" r="8" stroke="#A08E61" strokeWidth="1.5" opacity="0.7" />
              <circle cx="22" cy="18" r="8" stroke="#A08E61" strokeWidth="1.5" opacity="0.7" />
            </svg>
          </div>
        );
      case 'triangle':
        return (
          <div className="icon-container">
            <svg width="36" height="36" viewBox="0 0 36 36" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M18 6L30 27H6L18 6Z" stroke="#A08E61" strokeWidth="1.5" opacity="0.9" />
            </svg>
          </div>
        );
      case 'targetCircle':
        return (
          <div className="icon-container">
            <svg width="36" height="36" viewBox="0 0 36 36" fill="none" xmlns="http://www.w3.org/2000/svg">
              <circle cx="18" cy="18" r="12" stroke="#A08E61" strokeWidth="1.5" opacity="0.9" />
              <circle cx="18" cy="18" r="4" fill="#A08E61" opacity="0.7" />
            </svg>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div 
      className="landing-container"
      onMouseMove={handleMouseMove}
      onClick={showIntro ? handleSkipIntro : undefined}
    >
      <canvas ref={canvasRef} className="background-canvas" />

      {/* Intro Sequence */}
      {showIntro && (
        <div className="intro-sequence">
          <div className="moon-animation">
            <canvas 
              ref={moonCanvasRef} 
              width="200" 
              height="200"
              className="moon-canvas"
            />
          </div>

          <div className={`quote-container ${quoteVisible ? 'visible' : ''}`}>
            <div className="quote-text">
              {currentQuote && formatQuote(currentQuote.text)}
            </div>
            <div className="quote-author">
              {currentQuote && currentQuote.author}
            </div>
          </div>

          <div className="skip-intro">tap to skip</div>
        </div>
      )}

      <div className={`content-wrapper ${isLoaded ? 'loaded' : ''}`}>
        <div className="content-container">
          <div className="title-container">
            <h1 className="main-title">CRESCENT</h1>
            <div className="title-divider" />
            <p className="subtitle">A Dark Fantasy Experience</p>
          </div>

          <div className="sections-container">
            {mainSections.map((section, index) => (
              <div key={section.id} className="section-wrapper">
                <div 
                  className={`section-row ${activeSection === section.id ? 'active' : ''} ${expandedSection === section.id ? 'expanded' : ''}`}
                  onMouseEnter={() => handleSectionHover(section.id)}
                  onMouseLeave={handleSectionLeave}
                  onClick={() => section.expandable ? handleSectionClick(section.id) : handleNavigate(section.path)}
                  style={{ animationDelay: `${index * 0.15 + 0.3}s` }}
                >
                  <div className="section-content">
                    <div className="section-icon-wrapper">
                      {renderIcon(section.icon)}
                    </div>
                    <div className="section-text">
