          background-color: rgba(15, 15, 15, 0.3);
          border: 1px solid rgba(160, 142, 97, 0.1);
          border-radius: 3px;
          padding: 0.6rem 0.75rem;
          margin-bottom: 0.4rem;
          cursor: pointer;
          transition: all 0.3s ease;
        }

        .nested-subsection-row:hover {
          background-color: rgba(20, 20, 20, 0.5);
          border-color: rgba(160, 142, 97, 0.25);
          transform: translateX(3px);
        }

        .nested-subsection-title {
          font-size: 0.9rem;
          color: rgba(224, 224, 224, 0.9);
          font-weight: 300;
          margin: 0;
        }

        .nested-subsection-description {
          display: none;
          font-size: 0.75rem;
          color: rgba(224, 224, 224, 0.6);
          margin: 0.4rem 0 0;
        }

        .nested-subsection-row:hover .nested-subsection-description {
          display: block;
        }

        /* Footer Moon Section */
        .footer-section {
          width: 100%;
          display: flex;
          flex-direction: column;
          align-items: center;
          padding: 2rem 0 4rem;
          position: relative;
        }

        .footer-moon {
          margin-bottom: 2rem;
        }

        .brand-mark {
          display: flex;
          flex-direction: column;
          align-items: center;
          opacity: 0.4;
          transition: opacity 0.3s ease;
        }

        .brand-mark:hover {
          opacity: 0.7;
        }

        .symbol {
          font-size: 1.2rem;
          color: #a08e61;
          margin-bottom: 0.25rem;
        }

        .year {
          font-size: 0.65rem;
          color: rgba(224, 224, 224, 0.4);
          letter-spacing: 0.2em;
          font-weight: 300;
        }

        /* Animations */
        @keyframes fadeIn {
          from {
            opacity: 0;
            transform: translateX(-15px);
          }
          to {
            opacity: 1;
            transform: translateX(0);
          }
        }

        @keyframes fadeDown {
          from {
            opacity: 0;
            transform: translateY(-10px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        @keyframes pulse {
          0% {
            opacity: 0.15;
          }
          50% {
            opacity: 0.4;
          }
          100% {
            opacity: 0.15;
          }
        }

        /* Responsive Adjustments */
        @media (max-width: 768px) {
          .main-title {
            font-size: 2.25rem;
            letter-spacing: 0.2em;
          }

          .section-row {
            height: 6rem;
          }

          .section-icon-wrapper {
            width: 60px;
          }

          .section-description {
            font-size: 0.8rem;
            max-width: 95%;
          }

          .quote-text {
            font-size: 1rem;
          }

          .subsections-container {
            margin-left: 1rem;
          }

          .nested-subsections-container {
            margin-left: 1rem;
          }
        }

        @media (max-width: 480px) {
          .content-container {
            padding: 2rem 1rem 1rem;
          }

          .main-title {
            font-size: 1.75rem;
            letter-spacing: 0.15em;
          }

          .subtitle {
            font-size: 0.85rem;
          }

          .section-row {
            height: 5.5rem;
          }

          .section-icon-wrapper {
            width: 50px;
          }

          .icon-container {
            transform: scale(0.8);
          }

          .section-title {
            font-size: 1rem;
          }

          .expand-hint {
            font-size: 0.7rem;
          }

          .quote-text {
            font-size: 0.9rem;
            line-height: 1.6;
          }

          .quote-author {
            font-size: 0.8rem;
          }

          .subsections-container,
          .nested-subsections-container {
            margin-left: 0.5rem;
          }
        }
      `}</style>
    </div>
  );
};

export default RefinedLandingPage;
