import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { createUseStyles } from 'react-jss';

// Styles for the showcase navigation bar
const useStyles = createUseStyles({
  // Main container
  navContainer: {
    display: 'flex',
    flexDirection: 'column',
    width: '100%',
    backgroundColor: 'rgba(10, 10, 15, 0.9)',
    borderBottom: '1px solid rgba(191, 173, 127, 0.4)',
    boxShadow: '0 4px 15px rgba(0, 0, 0, 0.4)',
    zIndex: 100,
  },

  // Upper navigation section with primary controls
  upperNav: {
    display: 'flex',
    padding: '0.75rem 1.5rem',
    justifyContent: 'space-between',
    alignItems: 'center',
    borderBottom: '1px solid rgba(191, 173, 127, 0.2)',
  },

  // Logo and title section
  titleSection: {
    display: 'flex',
    alignItems: 'center',
    gap: '0.75rem',
  },
  logoIcon: {
    width: '24px',
    height: '24px',
    borderRadius: '50%',
    background: 'linear-gradient(135deg, #bfad7f 0%, #7D6B9E 100%)',
    boxShadow: '0 0 10px rgba(191, 173, 127, 0.5)',
  },
  title: {
    color: '#bfad7f',
    fontSize: '1.5rem',
    margin: 0,
    fontWeight: 300,
    letterSpacing: '0.05em',
    textShadow: '0 2px 5px rgba(0, 0, 0, 0.5)',
  },

  // Control buttons section
  controlsSection: {
    display: 'flex',
    gap: '1rem',
    alignItems: 'center',
  },

  // Body shape selector
  bodyShapeSelector: {
    position: 'relative',
  },
  selectorButton: {
    backgroundColor: 'rgba(30, 30, 40, 0.6)',
    border: '1px solid rgba(191, 173, 127, 0.3)',
    color: '#e0e0e0',
    padding: '0.5rem 1rem',
    borderRadius: '4px',
    fontFamily: 'inherit',
    fontSize: '0.9rem',
    cursor: 'pointer',
    display: 'flex',
    alignItems: 'center',
    gap: '0.5rem',
    transition: 'all 0.2s ease',
    '&:hover': {
      backgroundColor: 'rgba(191, 173, 127, 0.2)',
      borderColor: 'rgba(191, 173, 127, 0.6)',
    },
  },
  selectorMenu: {
    position: 'absolute',
    top: '100%',
    left: 0,
    backgroundColor: 'rgba(15, 15, 20, 0.95)',
    minWidth: '250px',
    maxHeight: '60vh',
    overflowY: 'auto',
    borderRadius: '4px',
    padding: '0.5rem',
    boxShadow: '0 5px 15px rgba(0, 0, 0, 0.5)',
    zIndex: 101,
    border: '1px solid rgba(191, 173, 127, 0.3)',
  },
  menuItem: {
    padding: '0.75rem',
    cursor: 'pointer',
    borderRadius: '3px',
    transition: 'all 0.15s ease',
    '&:hover': {
      backgroundColor: 'rgba(191, 173, 127, 0.2)',
    },
  },
  menuItemActive: {
    backgroundColor: 'rgba(191, 173, 127, 0.3)',
  },
  menuItemName: {
    fontSize: '0.95rem',
    color: '#bfad7f',
    fontWeight: 500,
    marginBottom: '0.25rem',
  },
  menuItemDesc: {
    fontSize: '0.8rem',
    color: 'rgba(224, 224, 224, 0.7)',
  },

  // Filter dropdown
  filterSelector: {
    position: 'relative',
  },

  // Main navigation tabs
  navTabs: {
    display: 'flex',
    marginLeft: 'auto',
  },
  navTab: {
    padding: '0.5rem 1rem',
    backgroundColor: 'transparent',
    border: 'none',
    borderBottom: '2px solid transparent',
    color: 'rgba(224, 224, 224, 0.7)',
    cursor: 'pointer',
    fontSize: '0.9rem',
    transition: 'all 0.2s ease',
    '&:hover': {
      color: '#bfad7f',
      borderBottomColor: 'rgba(191, 173, 127, 0.3)',
    },
  },
  navTabActive: {
    color: '#bfad7f',
    borderBottomColor: '#bfad7f',
  },

  // View control buttons
  viewControls: {
    display: 'flex',
    gap: '0.5rem',
    marginLeft: '1rem',
    alignItems: 'center',
  },
  viewButton: {
    backgroundColor: 'rgba(30, 30, 40, 0.6)',
    border: '1px solid rgba(191, 173, 127, 0.3)',
    color: '#e0e0e0',
    width: '28px',
    height: '28px',
    borderRadius: '4px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    fontSize: '0.9rem',
    cursor: 'pointer',
    transition: 'all 0.2s ease',
    '&:hover': {
      backgroundColor: 'rgba(191, 173, 127, 0.2)',
      borderColor: 'rgba(191, 173, 127, 0.6)',
    },
  },

  // Content panels section
  contentPanel: {
    padding: '1rem 1.5rem',
    overflow: 'hidden',
    transition: 'height 0.3s ease, opacity 0.3s ease',
  },

  // Network visualization controls panel
  visualizationPanel: {
    display: 'flex',
    justifyContent: 'space-between',
    borderBottom: '1px solid rgba(191, 173, 127, 0.2)',
    paddingBottom: '1rem',
    flexWrap: 'wrap',
    gap: '1.5rem',
  },
  sectionTitle: {
    color: 'rgba(191, 173, 127, 0.8)',
    fontSize: '1rem',
    margin: 0,
    marginBottom: '0.5rem',
  },

  // Visualization mode buttons
  visualizationButtons: {
    display: 'flex',
    gap: '0.5rem',
  },
  visualButton: {
    backgroundColor: 'rgba(15, 15, 20, 0.6)',
    border: '1px solid rgba(191, 173, 127, 0.4)',
    color: '#e0e0e0',
    padding: '0.5rem 1rem',
    borderRadius: '4px',
    fontFamily: 'inherit',
    fontSize: '0.9rem',
    cursor: 'pointer',
    transition: 'all 0.2s ease',
    '&:hover': {
      backgroundColor: 'rgba(191, 173, 127, 0.2)',
    },
  },
  visualButtonActive: {
    backgroundColor: 'rgba(191, 173, 127, 0.3)',
    borderColor: 'rgba(191, 173, 127, 0.7)',
  },

  // Trait probabilities section
  probabilitiesSection: {
    flex: 1,
    minWidth: '280px',
  },
  emergenceButton: {
    backgroundColor: 'rgba(125, 107, 158, 0.3)',
    border: '1px solid rgba(125, 107, 158, 0.6)',
    color: '#e0e0e0',
    padding: '0.5rem 1rem',
    borderRadius: '4px',
    fontFamily: 'inherit',
    fontSize: '0.9rem',
    cursor: 'pointer',
    transition: 'all 0.2s ease',
    '&:hover': {
      backgroundColor: 'rgba(125, 107, 158, 0.4)',
    },
  },

  // Tab content for traits
  traitsPanel: {
    marginTop: '1rem',
  },

  // Trait category section
  categorySection: {
    marginBottom: '1.5rem',
  },
  categoryHeading: {
    display: 'flex',
    alignItems: 'center',
    gap: '0.75rem',
    marginBottom: '0.75rem',
  },
  categoryIcon: {
    width: '16px',
    height: '16px',
    borderRadius: '50%',
  },
  categoryName: {
    color: '#e0e0e0',
    fontSize: '1rem',
    margin: 0,
  },

  // Trait cards container
  traitCards: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(200px, 1fr))',
    gap: '0.75rem',
  },
  traitCard: {
    backgroundColor: 'rgba(30, 30, 40, 0.6)',
    border: '1px solid rgba(191, 173, 127, 0.3)',
    borderRadius: '4px',
    padding: '0.75rem',
    cursor: 'pointer',
    transition: 'all 0.2s ease',
    '&:hover': {
      backgroundColor: 'rgba(191, 173, 127, 0.15)',
      borderColor: 'rgba(191, 173, 127, 0.5)',
    },
  },
  traitCardActive: {
    backgroundColor: 'rgba(191, 173, 127, 0.2)',
    borderColor: 'rgba(191, 173, 127, 0.6)',
    boxShadow: '0 0 10px rgba(191, 173, 127, 0.2)',
  },
  traitHeader: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: '0.5rem',
  },
  traitName: {
    color: '#bfad7f',
    fontSize: '0.95rem',
    margin: 0,
    fontWeight: 500,
  },
  traitTier: {
    backgroundColor: 'rgba(191, 173, 127, 0.2)',
    color: '#bfad7f',
    padding: '0.1rem 0.5rem',
    borderRadius: '2px',
    fontSize: '0.75rem',
  },
  traitDesc: {
    color: 'rgba(224, 224, 224, 0.7)',
    fontSize: '0.8rem',
    margin: 0,
    lineHeight: 1.4,
  },

  // Trait probabilities display
  traitProbabilities: {
    marginTop: '1rem',
  },
  probabilityChart: {
    width: '100%',
    background: 'rgba(15, 15, 20, 0.5)',
    borderRadius: '4px',
    padding: '1rem',
    border: '1px solid rgba(191, 173, 127, 0.2)',
  },
  probabilityBar: {
    display: 'flex',
    alignItems: 'center',
    marginBottom: '0.5rem',
  },
  probabilityLabel: {
    color: '#e0e0e0',
    fontSize: '0.85rem',
    width: '120px',
    overflow: 'hidden',
    textOverflow: 'ellipsis',
    whiteSpace: 'nowrap',
  },
  probabilityTrack: {
    flex: 1,
    height: '8px',
    backgroundColor: 'rgba(30, 30, 40, 0.6)',
    borderRadius: '4px',
    overflow: 'hidden',
    position: 'relative',
  },
  probabilityFill: {
    position: 'absolute',
    height: '100%',
    backgroundColor: '#7D6B9E',
    borderRadius: '4px',
    transition: 'width 0.3s ease',
  },
  probabilityValue: {
    color: '#bfad7f',
    fontSize: '0.85rem',
    marginLeft: '0.75rem',
    fontWeight: 'bold',
    width: '40px',
    textAlign: 'right',
  },

  // Environment panel
  environmentPanel: {
    marginTop: '1rem',
  },

  // Enhanced sliders grid
  sliderGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))',
    gap: '1.5rem',
    marginTop: '1rem',
  },
  sliderContainer: {
    display: 'flex',
    flexDirection: 'column',
  },
  sliderHeader: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: '0.5rem',
  },
  sliderLabel: {
    color: '#e0e0e0',
    fontSize: '0.95rem',
  },
  sliderValue: {
    fontWeight: 'bold',
    color: '#bfad7f',
    fontSize: '0.95rem',
  },
  sliderTrack: {
    width: '100%',
    height: '8px',
    backgroundColor: 'rgba(30, 30, 40, 0.6)',
    borderRadius: '4px',
    position: 'relative',
    margin: '0.5rem 0',
  },
  sliderFill: {
    position: 'absolute',
    height: '100%',
    borderRadius: '4px',
    transition: 'width 0.3s ease',
  },
  sliderThumb: {
    width: '18px',
    height: '18px',
    borderRadius: '50%',
    backgroundColor: '#bfad7f',
    position: 'absolute',
    top: '50%',
    transform: 'translate(-50%, -50%)',
    cursor: 'pointer',
    border: '2px solid rgba(15, 15, 20, 0.9)',
    boxShadow: '0 0 5px rgba(191, 173, 127, 0.5)',
    transition: 'transform 0.1s ease',
    '&:hover': {
      transform: 'translate(-50%, -50%) scale(1.1)',
    },
  },
  sliderInput: {
    position: 'absolute',
    width: '100%',
    height: '100%',
    opacity: 0,
    cursor: 'pointer',
  },

  // Slider scale labels
  sliderScale: {
    display: 'flex',
    justifyContent: 'space-between',
    fontSize: '0.8rem',
    color: 'rgba(224, 224, 224, 0.6)',
  },

  // Environmental stress indicators
  environmentalStressSection: {
    marginTop: '2rem',
    padding: '1rem',
    backgroundColor: 'rgba(15, 15, 20, 0.6)',
    borderRadius: '4px',
    border: '1px solid rgba(191, 173, 127, 0.2)',
  },
  stressHeader: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: '1rem',
  },
  stressTitle: {
    color: '#bfad7f',
    fontSize: '1rem',
    margin: 0,
  },
  stressValue: {
    fontWeight: 'bold',
    fontSize: '1.1rem',
  },
  adaptationBar: {
    width: '100%',
    height: '12px',
    backgroundColor: 'rgba(30, 30, 40, 0.6)',
    borderRadius: '6px',
    overflow: 'hidden',
    position: 'relative',
    marginBottom: '0.75rem',
  },
  adaptationFill: {
    height: '100%',
    transition: 'width 0.5s ease, background-color 0.5s ease',
  },
  adaptationLabel: {
    display: 'flex',
    justifyContent: 'space-between',
    fontSize: '0.85rem',
    color: 'rgba(224, 224, 224, 0.8)',
  },

  // Synthesis panel
  synthesisPanel: {
    marginTop: '1rem',
  },
  synthesisCard: {
    backgroundColor: 'rgba(30, 30, 40, 0.6)',
    border: '1px solid rgba(125, 107, 158, 0.4)',
    borderRadius: '4px',
    padding: '1rem',
    marginBottom: '1rem',
  },
  synthesisHeader: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: '0.75rem',
  },
  synthesisTitle: {
    color: '#7D6B9E',
    fontSize: '1.1rem',
    margin: 0,
  },
  synthesisProbability: {
    backgroundColor: 'rgba(125, 107, 158, 0.3)',
    color: '#e0e0e0',
    padding: '0.25rem 0.5rem',
    borderRadius: '4px',
    fontSize: '0.85rem',
  },
  synthesisDesc: {
    color: 'rgba(224, 224, 224, 0.8)',
    fontSize: '0.9rem',
    lineHeight: 1.5,
    margin: '0.5rem 0',
  },
  synthesisRequirements: {
    backgroundColor: 'rgba(15, 15, 20, 0.5)',
    padding: '0.75rem',
    borderRadius: '4px',
    marginTop: '0.75rem',
  },
  requirementTitle: {
    color: '#bfad7f',
    fontSize: '0.9rem',
    margin: '0 0 0.5rem 0',
  },
  requirementList: {
    margin: 0,
    padding: '0 0 0 1.5rem',
    color: 'rgba(224, 224, 224, 0.7)',
    fontSize: '0.85rem',
    '& li': {
      marginBottom: '0.25rem',
    },
  },

  // Media queries for responsiveness
  '@media (max-width: 768px)': {
    upperNav: {
      flexDirection: 'column',
      alignItems: 'flex-start',
      gap: '0.75rem',
    },
    controlsSection: {
      width: '100%',
      justifyContent: 'space-between',
    },
    navTabs: {
      marginTop: '0.5rem',
      width: '100%',
      justifyContent: 'space-between',
    },
    visualizationPanel: {
      flexDirection: 'column',
    },
    sliderGrid: {
      gridTemplateColumns: '1fr',
    },
  }
});

/**
 * EvoSimShowcaseBar - Enhanced navigation component for the Evolution Simulator
 * 
 * @param {Object} props
 * @param {string} props.selectedBodyShape - Currently selected body shape
 * @param {Function} props.onBodyShapeChange - Handler for body shape changes
 * @param {string} props.activeTab - Currently active main tab
 * @param {Function} props.onTabChange - Handler for tab changes
 * @param {string} props.visualizationMode - Current visualization mode
 * @param {Function} props.onVisualizationModeChange - Handler for visualization mode changes
 * @param {Object} props.environment - Current environmental conditions
 * @param {Function} props.onEnvironmentChange - Handler for environment changes
 * @param {boolean} props.showEmergence - Whether to show emergence probabilities
 * @param {Function} props.onToggleEmergence - Handler for toggling emergence probabilities
 * @param {Object} props.selectedTraits - Currently selected traits for each category
 * @param {Function} props.onTraitChange - Handler for trait changes
 * @param {number} props.zoom - Current zoom level
 * @param {Function} props.onZoomChange - Handler for zoom changes
 * @param {string} props.selectedCategory - Currently selected category filter
 * @param {Function} props.onCategoryFilterChange - Handler for category filter changes
 */
const EvoSimShowcaseBar = ({
  selectedBodyShape = 'quadrupedal',
  onBodyShapeChange,
  activeTab = 'traits',
  onTabChange,
  visualizationMode = 'direct',
  onVisualizationModeChange,
  environment = {},
  onEnvironmentChange,
  showEmergence = false,
  onToggleEmergence,
  selectedTraits = {},
  onTraitChange,
  zoom = 1,
  onZoomChange,
  selectedCategory = null,
  onCategoryFilterChange
}) => {
  const classes = useStyles();

  // Local state for dropdown menus
  const [showBodyShapeMenu, setShowBodyShapeMenu] = useState(false);
  const [showCategoryMenu, setShowCategoryMenu] = useState(false);
  const [showTraitMenus, setShowTraitMenus] = useState({});

  // State for calculated values
  const [totalEffectiveStress, setTotalEffectiveStress] = useState(0);
  const [adaptationProbability, setAdaptationProbability] = useState(0);

  // Define body shapes
  const bodyShapes = [
    { id: 'bipedal', name: 'Bipedal', description: 'Upright forms with manipulative upper limbs' },
    { id: 'quadrupedal', name: 'Quadrupedal', description: 'Four-limbed forms optimized for stability and movement' },
    { id: 'serpentine', name: 'Serpentine', description: 'Elongated, limbless forms specialized for flexibility' },
    { id: 'amorphous', name: 'Amorphous', description: 'Malleable forms without fixed structure' },
    { id: 'insectoid', name: 'Multi-limbed', description: 'Multi-limbed, segmented forms' },
    { id: 'avian', name: 'Avian', description: 'Forms optimized for flight and aerial movement' },
    { id: 'aquatic', name: 'Aquatic', description: 'Forms specialized for water environments' }
  ];

  // Define trait categories
  const traitCategories = [
    { id: 'locomotion', name: 'Locomotion', color: '#7D6B9E' },
    { id: 'metabolism', name: 'Metabolism', color: '#5C9D8B' },
    { id: 'sensory', name: 'Sensory Acuity', color: '#C99846' },
    { id: 'etheric', name: 'Etheric Adaptation', color: '#B54B4B' },
    { id: 'thermal', name: 'Thermal Regulation', color: '#A67C52' }
  ];

  // Define environmental pressures with more detailed info
  const environmentalPressures = [
    { 
      id: 'eldritch_influence', 
      name: 'Eldritch Influence', 
      default: 0.5,
      min: 0,
      max: 1,
      minLabel: 'None',
      maxLabel: 'Overwhelming',
      color: '#7D6B9E',
      description: 'The presence of otherworldly, reality-bending forces'
    },
    { 
      id: 'corruption_level', 
      name: 'Corruption Level', 
      default: 0.5,
      min: 0,
      max: 1,
      minLabel: 'Pure',
      maxLabel: 'Corrupted',
      color: '#B54B4B',
      description: 'The degree to which the environment has been tainted by unnatural forces'
    },
    { 
      id: 'resource_scarcity', 
      name: 'Resource Scarcity', 
      default: 0.5,
      min: 0,
      max: 1,
      minLabel: 'Abundant',
      maxLabel: 'Barren',
      color: '#5C9D8B',
      description: 'The availability of essential resources for survival'
    },
    { 
      id: 'ambient_magic', 
      name: 'Ambient Magic', 
      default: 0.5,
      min: 0,
      max: 1,
      minLabel: 'Mundane',
      maxLabel: 'Saturated',
      color: '#bfad7f',
      description: 'The concentration of magical energies in the environment'
    },
    { 
      id: 'darkness', 
      name: 'Darkness', 
      default: 0.5,
      min: 0,
      max: 1,
      minLabel: 'Light',
      maxLabel: 'Dark',
      color: '#333333',
      description: 'The level of light available in the environment'
    }
  ];

  // Sample trait data (would be provided by props)
  const traitsByCategory = {
    locomotion: [
      { id: 'running', name: 'Swift Limbs', tier: 1, description: 'Limbs designed for quick locomotion across varied terrain.', probability: 0.82 },
      { id: 'climbing', name: 'Grasping Appendages', tier: 1, description: 'Specialized limb structures with superior grip for vertical movement.', probability: 0.65 },
      { id: 'webbed', name: 'Webbed Appendages', tier: 1, description: 'Limbs with membrane structures optimized for aquatic movement.', probability: 0.43 }
    ],
    metabolism: [
      { id: 'carnivore', name: 'Carnivore Digestion', tier: 1, description: 'Specialized digestive system optimized for meat processing.', probability: 0.76 },
      { id: 'omnivore', name: 'Omnivore Adaptation', tier: 1, description: 'Versatile digestive system capable of processing diverse food sources.', probability: 0.59 }
    ],
    sensory: [
      { id: 'acute_vision', name: 'Acute Vision', tier: 1, description: 'Highly developed visual organs with exceptional acuity.', probability: 0.68 },
      { id: 'sonar_mapping', name: 'Echolocation', tier: 1, description: 'Ability to emit and detect sound waves for environmental mapping.', probability: 0.52 }
    ],
    etheric: [
      { id: 'ether_sensing', name: 'Ether Sensing', tier: 1, description: 'Specialized organs that can detect and interpret etheric energy patterns.', probability: 0.74 },
      { id: 'ether_channeling', name: 'Ether Channeling', tier: 1, description: 'Biological structures capable of directing and manipulating etheric energies.', probability: 0.47 }
    ],
    thermal: [
      { id: 'thick_blubber', name: 'Thick Blubber', tier: 1, description: 'Dense fatty tissue layer providing insulation in cold environments.', probability: 0.39 },
      { id: 'reflective_scales', name: 'Reflective Scales', tier: 1, description: 'Light-reflecting scales that help regulate heat absorption.', probability: 0.85 }
    ]
  };

  // Sample synthesis options
  const synthesisOptions = [
    {
      id: 'dimensional_sight',
      name: 'Dimensional Sight',
      description: 'Advanced visual organs that can perceive multiple planes of reality simultaneously, revealing hidden truths and dangers.',
      probability: 0.37,
      requirements: [
        'Requires Eldritch Influence > 0.7',
        'Requires Acute Vision trait',
        'Requires Ether Sensing trait'
      ]
    },
    {
      id: 'void_communion',
      name: 'Void Communion',
      description: 'Specialized neural structures that establish a symbiotic relationship with eldritch entities, granting access to forbidden knowledge.',
      probability: 0.19,
      requirements: [
        'Requires Corruption Level > 0.6',
        'Requires Ether Channeling trait',
        'Requires prolonged exposure to high Eldritch Influence'
      ]
    }
  ];

  // Calculate effective stress and adaptation probability
  useEffect(() => {
    // Simplified calculation for this example
    const stressValues = environmentalPressures.map(pressure => 
      environment[pressure.id] !== undefined ? environment[pressure.id] : pressure.default
    );

    const avgStress = stressValues.reduce((sum, value) => sum + value, 0) / stressValues.length;
    setTotalEffectiveStress(avgStress);

    // Simplified adaptation probability based on sigmoid function
    const adaptProb = 1 / (1 + Math.exp(-10 * (avgStress - 0.5)));
    setAdaptationProbability(adaptProb);
  }, [environment]);

  // Handle body shape selection
  const handleSelectBodyShape = (shapeId) => {
    onBodyShapeChange(shapeId);
    setShowBodyShapeMenu(false);
  };

  // Handle category filter selection
  const handleSelectCategory = (categoryId) => {
    onCategoryFilterChange(categoryId);
    setShowCategoryMenu(false);
  };

  // Handle trait selection
  const handleSelectTrait = (categoryId, traitId) => {
    onTraitChange(categoryId, traitId);
  };

  // Handle trait category dropdown toggle
  const handleToggleTraitMenu = (categoryId) => {
    setShowTraitMenus(prev => ({
      ...prev,
      [categoryId]: !prev[categoryId]
    }));

    // Close other menus
    setShowBodyShapeMenu(false);
    setShowCategoryMenu(false);
    Object.keys(showTraitMenus).forEach(key => {
      if (key !== categoryId) {
        setShowTraitMenus(prev => ({
          ...prev,
          [key]: false
        }));
      }
    });
  };

  // Handle environment pressure change
  const handleEnvironmentChange = (pressureId, value) => {
    onEnvironmentChange({ 
      ...environment, 
      [pressureId]: parseFloat(value) 
    });
  };

  // Get zoom controls
  const handleZoom = (direction) => {
    const newZoom = direction === 'in' 
      ? Math.min(zoom + 0.1, 3)
      : Math.max(zoom - 0.1, 0.5);
    onZoomChange(newZoom);
  };

  // Get selected body shape name
  const getSelectedBodyShapeName = () => {
    const shape = bodyShapes.find(shape => shape.id === selectedBodyShape);
    return shape ? shape.name : 'Select Body Shape';
  };

  // Get selected category name
  const getSelectedCategoryName = () => {
    if (!selectedCategory) return 'All Categories';
    const category = traitCategories.find(cat => cat.id === selectedCategory);
    return category ? category.name : 'All Categories';
  };

  // Render environmental stress bars with appropriate colors
  const getStressColor = (value) => {
    if (value < 0.4) return '#5C9D8B';
    if (value < 0.7) return '#C99846';
    return '#B54B4B';
  };

  // Render environmental pressure sliders
  const renderEnvironmentalPressure = (pressure) => {
    const value = environment[pressure.id] !== undefined 
      ? environment[pressure.id] 
      : pressure.default;

    const percentage = value * 100;

    return (
      <div className={classes.sliderContainer} key={pressure.id}>
        <div className={classes.sliderHeader}>
          <span className={classes.sliderLabel}>{pressure.name}</span>
          <span className={classes.sliderValue}>{value.toFixed(1)}</span>
        </div>

        <div className={classes.sliderTrack}>
          <div 
            className={classes.sliderFill}
            style={{ 
              width: `${percentage}%`,
              backgroundColor: pressure.color
            }}
          />
          <input
            type="range"
            min={pressure.min}
            max={pressure.max}
            step="0.1"
            value={value}
            onChange={(e) => handleEnvironmentChange(pressure.id, e.target.value)}
            className={classes.sliderInput}
          />
          <div 
            className={classes.sliderThumb}
            style={{ left: `${percentage}%` }}
          />
        </div>

        <div className={classes.sliderScale}>
          <span>{pressure.minLabel}</span>
          <span>{pressure.maxLabel}</span>
        </div>

        <div style={{ fontSize: '0.8rem', color: 'rgba(224, 224, 224, 0.6)', marginTop: '0.25rem' }}>
          {pressure.description}
        </div>
      </div>
    );
  };

  // Render trait cards
  const renderTraitCards = (categoryId) => {
    const traits = traitsByCategory[categoryId] || [];

    return (
      <div className={classes.traitCards}>
        {traits.map(trait => (
          <div 
            key={trait.id}
            className={`${classes.traitCard} ${selectedTraits[categoryId] === trait.id ? classes.traitCardActive : ''}`}
            onClick={() => handleSelectTrait(categoryId, trait.id)}
          >
            <div className={classes.traitHeader}>
              <h4 className={classes.traitName}>{trait.name}</h4>
              <span className={classes.traitTier}>Tier {trait.tier}</span>
            </div>
            <p className={classes.traitDesc}>{trait.description}</p>
          </div>
        ))}
      </div>
    );
  };

  // Render trait category sections
  const renderTraitCategories = () => {
    return traitCategories.map(category => (
      <div className={classes.categorySection} key={category.id}>
        <div className={classes.categoryHeading}>
          <div 
            className={classes.categoryIcon}
            style={{ backgroundColor: category.color }}
          />
          <h3 className={classes.categoryName}>{category.name}</h3>
        </div>
        {renderTraitCards(category.id)}
      </div>
    ));
  };

  // Render trait probabilities chart
  const renderProbabilitiesChart = () => {
    // Flatten all traits into a single array and sort by probability
    const allTraits = Object.values(traitsByCategory)
      .flat()
      .sort((a, b) => b.probability - a.probability)
      .slice(0, 5); // Show top 5

    return (
      <div className={classes.probabilityChart}>
        <h4 style={{ color: '#bfad7f', margin: '0 0 1rem 0' }}>Highest Adaptation Probabilities</h4>
        {allTraits.map(trait => (
          <div className={classes.probabilityBar} key={trait.id}>
            <div className={classes.probabilityLabel}>{trait.name}</div>
            <div className={classes.probabilityTrack}>
              <div 
                className={classes.probabilityFill}
                style={{ width: `${trait.probability * 100}%` }}
              />
            </div>
            <div className={classes.probabilityValue}>{(trait.probability * 100).toFixed(0)}%</div>
          </div>
        ))}
      </div>
    );
  };

  // Render synthesis options
  const renderSynthesisOptions = () => {
    return synthesisOptions.map(option => (
      <div className={classes.synthesisCard} key={option.id}>
        <div className={classes.synthesisHeader}>
          <h3 className={classes.synthesisTitle}>{option.name}</h3>
          <span className={classes.synthesisProbability}>{(option.probability * 100).toFixed(0)}%</span>
        </div>
        <p className={classes.synthesisDesc}>{option.description}</p>
        <div className={classes.synthesisRequirements}>
          <h4 className={classes.requirementTitle}>Requirements</h4>
          <ul className={classes.requirementList}>
            {option.requirements.map((req, index) => (
              <li key={index}>{req}</li>
            ))}
          </ul>
        </div>
      </div>
    ));
  };

  return (
    <div className={classes.navContainer}>
      {/* Upper navigation bar with title and main controls */}
      <div className={classes.upperNav}>
        <div className={classes.titleSection}>
          <div className={classes.logoIcon}></div>
          <h1 className={classes.title}>Crescent Evolution System</h1>
        </div>

        <div className={classes.controlsSection}>
          {/* Body shape selector */}
          <div className={classes.bodyShapeSelector}>
            <button 
              className={classes.selectorButton}
              onClick={() => {
                setShowBodyShapeMenu(!showBodyShapeMenu);
                setShowCategoryMenu(false);
                setShowTraitMenus({});
              }}
            >
              <span>Body Shape:</span>
              <strong>{getSelectedBodyShapeName()}</strong>
              <span style={{ fontSize: '0.7rem', marginLeft: '0.25rem' }}></span>
            </button>

            <AnimatePresence>
              {showBodyShapeMenu && (
                <motion.div
                  className={classes.selectorMenu}
                  initial={{ opacity: 0, y: -10 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -10 }}
                  transition={{ duration: 0.2 }}
                >
                  {bodyShapes.map(shape => (
                    <div 
                      key={shape.id}
                      className={`${classes.menuItem} ${selectedBodyShape === shape.id ? classes.menuItemActive : ''}`}
                      onClick={() => handleSelectBodyShape(shape.id)}
                    >
                      <div className={classes.menuItemName}>{shape.name}</div>
                      <div className={classes.menuItemDesc}>{shape.description}</div>
                    </div>
                  ))}
                </motion.div>
              )}
            </AnimatePresence>
          </div>

          {/* Category filter */}
          <div className={classes.filterSelector}>
            <button 
              className={classes.selectorButton}
              onClick={() => {
                setShowCategoryMenu(!showCategoryMenu);
                setShowBodyShapeMenu(false);
                setShowTraitMenus({});
              }}
            >
              <span>Filter:</span>
              <strong>{getSelectedCategoryName()}</strong>
              <span style={{ fontSize: '0.7rem', marginLeft: '0.25rem' }}></span>
            </button>

            <AnimatePresence>
              {showCategoryMenu && (
                <motion.div
                  className={classes.selectorMenu}
                  initial={{ opacity: 0, y: -10 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -10 }}
                  transition={{ duration: 0.2 }}
                >
                  <div 
                    className={`${classes.menuItem} ${!selectedCategory ? classes.menuItemActive : ''}`}
                    onClick={() => handleSelectCategory(null)}
                  >
                    <div className={classes.menuItemName}>All Categories</div>
                  </div>

                  {traitCategories.map(category => (
                    <div 
                      key={category.id}
                      className={`${classes.menuItem} ${selectedCategory === category.id ? classes.menuItemActive : ''}`}
                      onClick={() => handleSelectCategory(category.id)}
                    >
                      <div className={classes.menuItemName}>{category.name}</div>
                    </div>
                  ))}
                </motion.div>
              )}
            </AnimatePresence>
          </div>

          {/* Main navigation tabs */}
          <div className={classes.navTabs}>
            <button 
              className={`${classes.navTab} ${activeTab === 'traits' ? classes.navTabActive : ''}`}
              onClick={() => onTabChange('traits')}
            >
              Traits
            </button>
            <button 
              className={`${classes.navTab} ${activeTab === 'environment' ? classes.navTabActive : ''}`}
              onClick={() => onTabChange('environment')}
            >
              Environment
            </button>
            <button 
              className={`${classes.navTab} ${activeTab === 'synthesis' ? classes.navTabActive : ''}`}
              onClick={() => onTabChange('synthesis')}
            >
              Synthesis
            </button>
          </div>

          {/* View controls */}
          <div className={classes.viewControls}>
            <button 
              className={classes.viewButton}
              onClick={() => handleZoom('in')}
              title="Zoom In"
            >
              +
            </button>
            <button 
              className={classes.viewButton}
              onClick={() => handleZoom('out')}
              title="Zoom Out"
            >
              -
            </button>
          </div>
        </div>
      </div>

      {/* Network visualization controls */}
      <div className={classes.visualizationPanel}>
        <div>
          <h3 className={classes.sectionTitle}>Network Visualization</h3>
          <div className={classes.visualizationButtons}>
            <button 
              className={`${classes.visualButton} ${visualizationMode === 'direct' ? classes.visualButtonActive : ''}`}
              onClick={() => onVisualizationModeChange('direct')}
            >
              Direct Connections
            </button>
            <button 
              className={`${classes.visualButton} ${visualizationMode === 'tree' ? classes.visualButtonActive : ''}`}
              onClick={() => onVisualizationModeChange('tree')}
            >
              Evolutionary Tree
            </button>
            <button 
              className={`${classes.visualButton} ${visualizationMode === 'category' ? classes.visualButtonActive : ''}`}
              onClick={() => onVisualizationModeChange('category')}
            >
              Trait Category
            </button>
          </div>
        </div>

        {/* Trait probabilities section */}
        <div className={classes.probabilitiesSection}>
          <h3 className={classes.sectionTitle}>Trait Probabilities</h3>
          <button 
            className={classes.emergenceButton}
            onClick={onToggleEmergence}
          >
            {showEmergence ? 'Hide' : 'Show'} Emergence Probabilities
          </button>
        </div>
      </div>

      {/* Tab content panels */}
      <div className={classes.contentPanel}>
        {/* Traits panel */}
        {activeTab === 'traits' && (
          <div className={classes.traitsPanel}>
            <div style={{ display: 'flex', gap: '2rem' }}>
              <div style={{ flex: 1 }}>
                {renderTraitCategories()}
              </div>
              <div style={{ width: '300px' }}>
                <div className={classes.traitProbabilities}>
                  {renderProbabilitiesChart()}
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Environment panel */}
        {activeTab === 'environment' && (
          <div className={classes.environmentPanel}>
            <div className={classes.sliderGrid}>
              {environmentalPressures.map(pressure => renderEnvironmentalPressure(pressure))}
            </div>

            <div className={classes.environmentalStressSection}>
              <div className={classes.stressHeader}>
                <h3 className={classes.stressTitle}>Environmental Stress Impact</h3>
                <span 
                  className={classes.stressValue}
                  style={{ color: getStressColor(totalEffectiveStress) }}
                >
                  {(totalEffectiveStress * 100).toFixed(0)}%
                </span>
              </div>

              <div className={classes.adaptationBar}>
                <div 
                  className={classes.adaptationFill}
                  style={{ 
                    width: `${totalEffectiveStress * 100}%`,
                    backgroundColor: getStressColor(totalEffectiveStress)
                  }}
                />
              </div>

              <div className={classes.stressHeader}>
                <h3 className={classes.stressTitle}>Adaptation Probability</h3>
                <span 
                  className={classes.stressValue}
                  style={{ color: getStressColor(adaptationProbability) }}
                >
                  {(adaptationProbability * 100).toFixed(0)}%
                </span>
              </div>

              <div className={classes.adaptationBar}>
                <div 
                  className={classes.adaptationFill}
                  style={{ 
                    width: `${adaptationProbability * 100}%`,
                    backgroundColor: getStressColor(adaptationProbability)
                  }}
                />
              </div>

              <div className={classes.adaptationLabel}>
                <span>Stable</span>
                <span>Adaptation Likely</span>
              </div>
            </div>
          </div>
        )}

        {/* Synthesis panel */}
        {activeTab === 'synthesis' && (
          <div className={classes.synthesisPanel}>
            {renderSynthesisOptions()}
          </div>
        )}
      </div>
    </div>
  );
};

export default EvoSimShowcaseBar;
