import React, { useState, useEffect, useRef } from 'react';
import { createUseStyles } from 'react-jss';

// CSS-in-JS styles
const useStyles = createUseStyles({
  introSequence: {
    position: 'fixed',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    zIndex: 1000,
    backgroundColor: '#080808',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    cursor: 'pointer',
    transition: 'opacity 0.4s ease'
  },
  fadeOut: {
    opacity: 0
  },
  moonAnimation: {
    position: 'relative',
    marginBottom: '3rem'
  },
  cometCanvas: {
    display: 'block'
  },
  quoteContainer: {
    maxWidth: 500,
    textAlign: 'center',
    opacity: 0,
    transform: 'translateY(10px)',
    transition: 'opacity 0.6s ease, transform 0.6s ease',
    padding: '0 2rem'
  },
  visible: {
    opacity: 1,
    transform: 'translateY(0)'
  },
  quoteText: {
    fontFamily: "'Garamond', 'Adobe Caslon Pro', serif",
    fontSize: '1.2rem',
    lineHeight: 1.7,
    color: 'rgba(191, 173, 127, 0.7)',
    marginBottom: '1.5rem',
    fontWeight: 300,
    letterSpacing: '0.02em'
  },
  quoteLine: {
    display: 'inline-block'
  },
  quoteAuthor: {
    fontSize: '0.9rem',
    color: 'rgba(224, 224, 224, 0.5)',
    letterSpacing: '0.1em',
    textAlign: 'right',
    paddingRight: '2rem',
    fontWeight: 300
  },
  skipIntro: {
    position: 'absolute',
    bottom: '2rem',
    fontSize: '0.7rem',
    color: 'rgba(224, 224, 224, 0.3)',
    letterSpacing: '0.15em',
    animation: '$pulse 2s infinite',
    fontWeight: 300,
    textTransform: 'uppercase'
  },
  '@keyframes pulse': {
    '0%': { opacity: 0.15 },
    '50%': { opacity: 0.4 },
    '100%': { opacity: 0.15 }
  }
});

// CometAnimation Component
const CometAnimation = (props) => {
  const {
    width = 200,
    height = 200,
    radius = 70,
    speed = 0.12,
    trailLength = 120,
    trailSegments = 20,
    cometSize = 4,
    coreColor = 'rgba(255, 255, 255, 1)',
    glowColor = 'rgba(255, 253, 227, 0.9)',
    trailColor = 'rgba(191, 173, 127, 0.8)',
    isActive = true
  } = props;

  const classes = useStyles();
  const canvasRef = useRef(null);
  const requestRef = useRef(null);
  const angleRef = useRef(0);
  const prevTimeRef = useRef(0);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    canvas.width = width;
    canvas.height = height;

    const animate = (time) => {
      if (!isActive) {
        return;
      }

      // Calculate time delta
      const deltaTime = prevTimeRef.current ? Math.min(time - prevTimeRef.current, 50) : 16;
      prevTimeRef.current = time;

      // Update angle
      angleRef.current += speed * (deltaTime / 16);
      if (angleRef.current >= 360) angleRef.current -= 360;

      // Clear canvas
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      const centerX = canvas.width / 2;
      const centerY = canvas.height / 2;

      // Draw trail
      for (let i = 0; i < trailSegments; i++) {
        const tailAngle = angleRef.current - (i * (trailLength / trailSegments));
        const rad = tailAngle * Math.PI / 180;

        const x = centerX + radius * Math.cos(rad);
        const y = centerY + radius * Math.sin(rad);

        const nextAngle = angleRef.current - ((i + 1) * (trailLength / trailSegments));
        const nextRad = nextAngle * Math.PI / 180;

        const nextX = centerX + radius * Math.cos(nextRad);
        const nextY = centerY + radius * Math.sin(nextRad);

        const opacity = 0.8 * (1 - (i / trailSegments));

        ctx.beginPath();
        ctx.moveTo(x, y);
        ctx.lineTo(nextX, nextY);
        ctx.strokeStyle = trailColor.replace(/[\d.]+\)$/, opacity + ')');
        ctx.lineWidth = 2 * (1 - (i / trailSegments)) + 0.5;
        ctx.lineCap = 'round';
        ctx.shadowColor = glowColor;
        ctx.shadowBlur = 5;
        ctx.stroke();
      }

      // Draw comet head
      const headRad = angleRef.current * Math.PI / 180;
      const headX = centerX + radius * Math.cos(headRad);
      const headY = centerY + radius * Math.sin(headRad);

      ctx.beginPath();
      ctx.arc(headX, headY, cometSize, 0, Math.PI * 2);
      ctx.fillStyle = coreColor;
      ctx.shadowColor = glowColor;
      ctx.shadowBlur = 15;
      ctx.fill();

      requestRef.current = requestAnimationFrame(animate);
    };

    if (isActive) {
      requestRef.current = requestAnimationFrame(animate);
    }

    return () => {
      if (requestRef.current) {
        cancelAnimationFrame(requestRef.current);
      }
    };
  }, [
    width, height, radius, speed, trailLength, trailSegments, 
    cometSize, coreColor, glowColor, trailColor, isActive
  ]);

  return (
    <canvas
      ref={canvasRef}
      width={width}
      height={height}
      className={classes.cometCanvas}
    />
  );
};

// Main IntroSequence Component
const IntroSequence = ({ showIntro, setShowIntro, setIsLoaded }) => {
  const classes = useStyles();
  const [quoteVisible, setQuoteVisible] = useState(false);
  const [currentQuote, setCurrentQuote] = useState(null);
  const [animationActive, setAnimationActive] = useState(false);
  const [fadeOut, setFadeOut] = useState(false);
  const initialized = useRef(false);
  const timeouts = useRef([]);

  // Quotes data
  const quotes = [
    {
      text: "My candle burns at both ends;\nIt will not last the night;\nBut, ah my foes, and\noh my friend —\nit gives a\nlovely\nlight",
      author: "Edna St. Vincent Millay"
    },
    {
      text: "Though my soul may set in darkness,\nit will rise in perfect light;\nI have loved the stars too fondly\nto be fearful of the night.",
      author: "Sarah Williams"
    },
    {
      text: "The Moon is a loyal companion.\nIt never leaves. It's always there, watching, steadfast,\nknowing us in our light and dark moments,\nchanging forever just as we do.",
      author: "Tahereh Mafi"
    },
    {
      text: "I like the night. Without the dark,\nwe'd never see the stars.",
      author: "Stephenie Meyer"
    },
    {
      text: "In a dark time, the eye begins to see.",
      author: "Adrienne Rich"
    },
  ];

  // Clear all timeouts helper
  const clearAllTimeouts = () => {
    timeouts.current.forEach(id => clearTimeout(id));
    timeouts.current = [];
  };

  // Setup intro sequence
  useEffect(() => {
    if (!showIntro || initialized.current) return;

    // Mark as initialized to prevent multiple runs
    initialized.current = true;

    // Select a random quote
    const randomQuote = quotes[Math.floor(Math.random() * quotes.length)];
    setCurrentQuote(randomQuote);

    // Start animation sequence
    timeouts.current.push(setTimeout(() => setAnimationActive(true), 150));
    timeouts.current.push(setTimeout(() => setQuoteVisible(true), 350));

    // End animation sequence
    timeouts.current.push(setTimeout(() => {
      setAnimationActive(false);
      setQuoteVisible(false);
      setFadeOut(true);
    }, 4000));

    timeouts.current.push(setTimeout(() => {
      setShowIntro(false);
      setIsLoaded(true);
      initialized.current = false; // Reset for potential reuse
    }, 4500));

    // Cleanup on unmount
    return clearAllTimeouts;
  }, [showIntro, setShowIntro, setIsLoaded, quotes]);

  // Skip intro handler
  const handleSkipIntro = () => {
    clearAllTimeouts();
    setFadeOut(true);

    // Small delay to allow fade animation
    setTimeout(() => {
      setShowIntro(false);
      setIsLoaded(true);
      initialized.current = false;
    }, 400);
  };

  // Format quote with line breaks
  const formatQuote = (text) => {
    return text.split('\n').map((line, i) => (
      <span key={i} className={classes.quoteLine}>
        {line}
        {i < text.split('\n').length - 1 && <br />}
      </span>
    ));
  };

  if (!showIntro) return null;

  return (
    <div
      className={`${classes.introSequence} ${fadeOut ? classes.fadeOut : ''}`}
      onClick={handleSkipIntro}
    >
      <div className={classes.moonAnimation}>
        <CometAnimation
          width={200}
          height={200}
          radius={70}
          speed={0.12}
          isActive={animationActive}
        />
      </div>

      <div className={`${classes.quoteContainer} ${quoteVisible ? classes.visible : ''}`}>
        <div className={classes.quoteText}>
          {currentQuote && formatQuote(currentQuote.text)}
        </div>
        <div className={classes.quoteAuthor}>
          {currentQuote && currentQuote.author}
        </div>
      </div>

      <div className={classes.skipIntro}>tap to skip</div>
    </div>
  );
};

export default IntroSequence;
