import React, { useState, useEffect, useRef } from 'react';
import { motion, useScroll, useTransform, useSpring, useInView } from 'framer-motion';
import Background from '../../../core/Background';
import './creatorsPage.css';

// Text reveal animation component
const RevealText = ({ children, threshold = 0.5, delay = 0 }) => {
  const ref = useRef(null);
  const isInView = useInView(ref, { once: false, threshold });

  return (
    <div ref={ref} className="crescent-creators-page__reveal-container">
      <div
        className={`crescent-creators-page__reveal-text ${isInView ? 'crescent-creators-page__reveal-text--revealed' : ''}`}
        style={{ transitionDelay: `${delay}ms` }}
      >
        {children}
      </div>
    </div>
  );
};

// Project card component
const ProjectCard = ({ project, index }) => {
  const cardRef = useRef(null);
  const isInView = useInView(cardRef, { once: true, threshold: 0.2 });

  return (
    <motion.div 
      ref={cardRef}
      className="crescent-creators-page__project-card"
      initial={{ opacity: 0, y: 30 }}
      animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 30 }}
      transition={{ 
        duration: 0.8, 
        ease: "easeOut",
        delay: index * 0.15
      }}
    >
      <div className="crescent-creators-page__project-image">
        <img src={project.image} alt={project.title} />
      </div>
      <div className="crescent-creators-page__project-details">
        <h3 className="crescent-creators-page__project-title">{project.title}</h3>
        <p className="crescent-creators-page__project-description">{project.description}</p>
        <div className="crescent-creators-page__project-tags">
          {project.tags.map((tag, i) => (
            <span key={i} className="crescent-creators-page__project-tag">{tag}</span>
          ))}
        </div>
      </div>
    </motion.div>
  );
};

// Main component
const CreatorsPage = () => {
  const [cursorPosition, setCursorPosition] = useState({ x: 0, y: 0 });
  const [isLoaded, setIsLoaded] = useState(false);
  const [titleReachedTarget, setTitleReachedTarget] = useState(false);

  // Scroll progress refs and values
  const { scrollY } = useScroll();
  const mainContainerRef = useRef(null);
  const titleRef = useRef(null);
  const quoteRef = useRef(null);
  const creatorProfileRef = useRef(null);

  // Calculate dynamic stop position based on quote position
  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY;
      const moonMeltMask = document.querySelector('.crescent-creators-page__moon-melt-mask');

      if (moonMeltMask) {
        // Calculate mask position based on scroll (0 to -300px)
        const maskPosition = Math.min(scrollPosition / 2, 300);
        moonMeltMask.style.maskPosition = `0 -${maskPosition}px`;
        moonMeltMask.style.webkitMaskPosition = `0 -${maskPosition}px`;
      }

      // Get the position of the quote
      if (quoteRef.current) {
        // Get quote position and offset to position title above it
        const quotePosition = quoteRef.current.getBoundingClientRect().top + window.scrollY;
        const offset = 100; // Adjust this value to control space between title and quote
        const stopPosition = quotePosition - offset;

        if (scrollPosition >= stopPosition && !titleReachedTarget) {
          setTitleReachedTarget(true);
        } else if (scrollPosition < stopPosition && titleReachedTarget) {
          setTitleReachedTarget(false);
        }
      }
    };

    // Run once after component mounts to set initial state
    handleScroll();

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [titleReachedTarget]);

  // Transform values for true melting moon effect
  const moonY = useTransform(scrollY, [0, 600], [0, 150]); // More vertical movement to create melting impression
  const moonScale = useTransform(scrollY, [0, 400], [1, 1.1]);
  // We'll rely on the clip-path and mask effects in CSS rather than opacity changes

  // Title parallax effect that stops at the target position
  const headerY = useTransform(scrollY, [0, 400], [0, -150]);
  const titleY = useTransform(scrollY, [0, 400], [0, 100]);
  const titleScale = useTransform(scrollY, [0, 300], [1, 1.2]);

  // Spring physics for smoother parallax
  const springHeaderY = useSpring(headerY, { stiffness: 100, damping: 30 });
  const springTitleY = useSpring(titleY, { stiffness: 100, damping: 30 });
  const springMoonY = useSpring(moonY, { stiffness: 80, damping: 20 });

  // Track mouse movement for Background component
  const handleMouseMove = (e) => {
    setCursorPosition({ x: e.clientX, y: e.clientY });
  };

  // Check if creator profile is in view
  const isProfileInView = useInView(creatorProfileRef, { once: true, threshold: 0.2 });

  // Project data
  const projects = [
    {
      id: "crescent",
      title: "Crescent TTRPG",
      description: "A dark fantasy tabletop roleplaying game centered around cycles of creation, evolution, and destruction.",
      image: "/images/projects/crescent.jpg",
      tags: ["Game Design", "World Building", "System Design"]
    },
    {
      id: "crescent-website",
      title: "Crucible Platform",
      description: "A web platform for managing and visualizing game worlds, character creation, and rule systems.",
      image: "/images/projects/crucible.jpg",
      tags: ["React", "UI/UX", "Interactive Design", "D3 visualization"]
    },
    {
      id: "crucible",
      title: "Evolution Simulator",
      description: "A sophisticated simulation system modeling environment formation and creature adaptation within dark fantasy settings.",
      image: "/images/projects/evolution.jpg",
      tags: ["C++", "Procedural Generation", "Complex Systems"]
    }
  ];

  // Simulate initial loading
  useEffect(() => {
    // Set loaded state after a brief delay
    const loadTimer = setTimeout(() => {
      setIsLoaded(true);
    }, 300);

    return () => {
      clearTimeout(loadTimer);
    };
  }, []);

  return (
    <div 
      className="crescent-creators-page"
      onMouseMove={handleMouseMove}
      ref={mainContainerRef}
    >
      {/* Persistent background layer */}
      <div className="crescent-creators-page__background-layer"></div>

      {/* Melting Moon Background with overlay effect */}
      <div className="crescent-creators-page__moon-container">
        <motion.div 
          className="crescent-creators-page__moon-background"
          style={{ 
            y: springMoonY,
            scale: moonScale,
            opacity: moonOpacity
          }}
        >
          <div className="crescent-creators-page__moon-image-container">
            <img 
              src="/assets/images/backgrounds/Melting_Moon.png" 
              alt="Melting Moon Background" 
              className="crescent-creators-page__moon-image" 
            />
          </div>
          <div 
            className="crescent-creators-page__moon-melt-overlay"
            style={{
              opacity: moonMeltProgress
            }}
          ></div>
        </motion.div>
        <div className="crescent-creators-page__moon-bottom-fade"></div>
      </div>

      {/* Background component with stars */}
      <Background 
        cursorPosition={cursorPosition} 
        setCursorPosition={setCursorPosition} 
      />

      {/* Parallax title section that stops at target position */}
      <div 
        className={`crescent-creators-page__title-wrapper ${titleReachedTarget ? 'crescent-creators-page__title-wrapper--fixed' : ''}`}
        ref={titleRef}
      >
        <motion.div 
          className="crescent-creators-page__parallax-header"
          style={{ 
            y: titleReachedTarget ? 0 : springHeaderY,
            opacity: 1, // Always maintain full opacity
            zIndex: 4
          }}
        >
          <motion.div 
            className="crescent-creators-page__title-container"
            style={{ 
              y: titleReachedTarget ? 0 : springTitleY,
              scale: titleScale
            }}
          >
            <h1 className="crescent-creators-page__main-title">Creator of Crescent</h1>
            <div className="crescent-creators-page__title-divider"></div>
          </motion.div>
        </motion.div>
      </div>

      <div className={`crescent-creators-page__scrollable-content ${isLoaded ? 'crescent-creators-page__scrollable-content--loaded' : ''}`}>

        {/* Introduction section with reveal animations */}
        <section className="crescent-creators-page__section crescent-creators-page__intro" ref={quoteRef}>
          <RevealText threshold={0.2}>
            <p className="crescent-creators-page__intro-quote">
              "Though my soul may set in darkness,<br />
              it will rise in perfect light;<br />
              I have loved the stars too fondly<br />
              to be fearful of the night."
            </p>
            <p className="crescent-creators-page__quote-author">— Sarah Williams, "The Old Astronomer to His Pupil"</p>
          </RevealText>

          <RevealText delay={300}>
            <p className="crescent-creators-page__subtitle">
              Crescent was born from a fascination with cycles of rebirth, evolutionary pressure, and the eternal dance between light and darkness. As its creator, I've woven together systems design, narrative structure, and visual aesthetics to craft an immersive dark fantasy world.
            </p>
          </RevealText>
        </section>

        {/* Creator profile section */}
        <section className="crescent-creators-page__section">
          <motion.div 
            ref={creatorProfileRef}
            className={`crescent-creators-page__profile ${isProfileInView ? 'crescent-creators-page__profile--in-view' : ''}`}
            initial={{ opacity: 0, y: 50 }}
            animate={isProfileInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 50 }}
            transition={{ duration: 0.8, ease: "easeOut" }}
          >
            <div className="crescent-creators-page__profile-image">
              <img src="/assets/images/creators/rosalia.png" alt="Rosalia Valeria Fialkova" />
            </div>

            <div className="crescent-creators-page__profile-details">
              <h2 className="crescent-creators-page__profile-name">Rosalia Valeria Fialkova</h2>
              <p className="crescent-creators-page__profile-role">Game Designer & World Builder</p>

              <p className="crescent-creators-page__profile-bio">
                Architect of the Crescent world, where cycles of creation and destruction
                shape endless possibilities. With a passion for dark fantasy and intricate
                systems, I craft universes where player choices and environmental
                pressures create unique, evolving narratives.
              </p>

              <p className="crescent-creators-page__profile-bio">
                My work merges scientific principles of evolution and adaptation
                with mythological themes of rebirth and renewal. I create systems
                that are both mechanically sound and narratively compelling, 
                allowing players to explore complex themes through immersive gameplay.
              </p>

              <div className="crescent-creators-page__profile-stats">
                <div className="crescent-creators-page__stat">
                  <div className="crescent-creators-page__stat-value">8</div>
                  <div className="crescent-creators-page__stat-label">Environment Types</div>
                </div>
                <div className="crescent-creators-page__stat">
                  <div className="crescent-creators-page__stat-value">3</div>
                  <div className="crescent-creators-page__stat-label">Metropoles</div>
                </div>
                <div className="crescent-creators-page__stat">
                  <div className="crescent-creators-page__stat-value">∞</div>
                  <div className="crescent-creators-page__stat-label">Cycles</div>
                </div>
              </div>
            </div>
          </motion.div>
        </section>

        {/* Projects section */}
        <section className="crescent-creators-page__section crescent-creators-page__projects">
          <RevealText>
            <h2 className="crescent-creators-page__section-title">Celestial Works</h2>
            <div className="crescent-creators-page__title-divider"></div>
          </RevealText>

          <div className="crescent-creators-page__projects-grid">
            {projects.map((project, index) => (
              <ProjectCard key={project.id} project={project} index={index} />
            ))}
          </div>
        </section>

        {/* Inspiration section */}
        <section className="crescent-creators-page__section crescent-creators-page__inspiration">
          <div className="crescent-creators-page__inspiration-bg"></div>

          <RevealText>
            <h2 className="crescent-creators-page__section-title">Celestial Inspirations</h2>
            <div className="crescent-creators-page__title-divider"></div>
          </RevealText>

          <div className="crescent-creators-page__inspiration-categories">
            <div className="crescent-creators-page__inspiration-category">
              <RevealText delay={200}>
                <h3 className="crescent-creators-page__inspiration-heading">Literary & Media</h3>
                <ul className="crescent-creators-page__inspiration-list">
                  <li className="crescent-creators-page__inspiration-item">Berserk by Kentaro Miura</li>
                  <li className="crescent-creators-page__inspiration-item">Made in Abyss by Akihito Tsukushi</li>
                  <li className="crescent-creators-page__inspiration-item">Bloodborne by FromSoft</li>
                  <li className="crescent-creators-page__inspiration-item">Scavenger's Reign by Joseph Bennett & Christopher Huettner</li>
                </ul>
              </RevealText>
            </div>

            <div className="crescent-creators-page__inspiration-category">
              <RevealText delay={400}>
                <h3 className="crescent-creators-page__inspiration-heading">Tabletop RPG</h3>
                <ul className="crescent-creators-page__inspiration-list">
                  <li className="crescent-creators-page__inspiration-item">Blades in the Dark by John Harper</li>
                  <li className="crescent-creators-page__inspiration-item">Mouse Guard by Luke Crane</li>
                  <li className="crescent-creators-page__inspiration-item">Ironsworn by Shaun Tomkin</li>
                  <li className="crescent-creators-page__inspiration-item">Heart by Grant Howitt & Christopher Taylor</li>
                </ul>
              </RevealText>
            </div>
          </div>
        </section>

        {/* Final cosmic message */}
        <section className="crescent-creators-page__section crescent-creators-page__final">
          <RevealText>
            <p className="crescent-creators-page__final-text">
              In each beginning, the Moon gives birth to the skies and earth. 
              Ether, the life-sustaining substance of the Moon, swells across the newborn world.
              With ether comes life. With that life comes profound light and shadow.
            </p>
          </RevealText>
        </section>
      </div>

      {/* Accessibility skip link (hidden visually) */}
      <a href="#content" className="crescent-creators-page__skip-link">
        Skip to main content
      </a>
    </div>
  );
};

export default CreatorsPage;
