import React, { useState, useEffect, useRef } from 'react';

const CreatorSection = () => {
  const [isLoaded, setIsLoaded] = useState(false);
  const [activeAccomplishment, setActiveAccomplishment] = useState(null);
  const [showMoreBio, setShowMoreBio] = useState(false);
  const creatorRef = useRef(null);
  const portraitCanvasRef = useRef(null);
  const starfieldCanvasRef = useRef(null);
  const requestRef = useRef(null);

  // Accomplishments data - based on your resume but rephrased for impact
  const accomplishments = [
    {
      id: "rpg-system",
      title: "Project Leadership",
