  ];

  // Animated portrait creation
  useEffect(() => {
    if (!portraitCanvasRef.current) return;

    const canvas = portraitCanvasRef.current;
    const ctx = canvas.getContext('2d');

    // Canvas dimensions
    const width = canvas.width;
    const height = canvas.height;

    const drawPortrait = () => {
      // Clear canvas
      ctx.clearRect(0, 0, width, height);

      // Set up glowing effect
      ctx.shadowColor = 'rgba(191, 173, 127, 0.6)';
      ctx.shadowBlur = 15;
      ctx.strokeStyle = 'rgba(191, 173, 127, 0.7)';
      ctx.lineWidth = 1.5;

      // Draw face outline
      ctx.beginPath();
      ctx.ellipse(width/2, height/2 - 10, 60, 75, 0, 0, Math.PI * 2);
      ctx.stroke();

      // Draw flowing hair
      ctx.beginPath();
      ctx.moveTo(width/2 - 60, height/2 - 30);

      // Left side hair waves
      ctx.bezierCurveTo(
        width/2 - 100, height/2,
        width/2 - 110, height/2 + 80,
        width/2 - 70, height/2 + 120
      );

      // Bottom hair waves
      ctx.bezierCurveTo(
        width/2 - 20, height/2 + 150,
        width/2 + 20, height/2 + 150,
        width/2 + 70, height/2 + 120
      );

      // Right side hair waves
      ctx.bezierCurveTo(
        width/2 + 110, height/2 + 80,
        width/2 + 100, height/2,
        width/2 + 60, height/2 - 30
      );

      ctx.stroke();

      // Draw eyes (hazel colored)
      ctx.shadowBlur = 5;
      ctx.beginPath();
      ctx.ellipse(width/2 - 20, height/2 - 20, 8, 5, 0, 0, Math.PI * 2);
      ctx.ellipse(width/2 + 20, height/2 - 20, 8, 5, 0, 0, Math.PI * 2);
      ctx.stroke();

      // Draw eyebrows
      ctx.beginPath();
      ctx.moveTo(width/2 - 30, height/2 - 35);
      ctx.bezierCurveTo(
        width/2 - 25, height/2 - 38,
        width/2 - 15, height/2 - 38,
        width/2 - 10, height/2 - 35
      );
      ctx.moveTo(width/2 + 30, height/2 - 35);
      ctx.bezierCurveTo(
        width/2 + 25, height/2 - 38,
        width/2 + 15, height/2 - 38,
        width/2 + 10, height/2 - 35
      );
      ctx.stroke();

      // Draw nose
      ctx.beginPath();
      ctx.moveTo(width/2, height/2 - 15);
      ctx.bezierCurveTo(
        width/2 - 5, height/2,
        width/2 - 5, height/2 + 5,
        width/2, height/2 + 5
      );
      ctx.stroke();

      // Draw lips
      ctx.beginPath();
      ctx.moveTo(width/2 - 15, height/2 + 20);
      ctx.bezierCurveTo(
        width/2 - 5, height/2 + 25,
        width/2 + 5, height/2 + 25,
        width/2 + 15, height/2 + 20
      );
      ctx.bezierCurveTo(
        width/2 + 5, height/2 + 15,
        width/2 - 5, height/2 + 15,
        width/2 - 15, height/2 + 20
      );
      ctx.stroke();

      // Add astrological elements - small stars around the portrait
      ctx.fillStyle = 'rgba(191, 173, 127, 0.7)';

      // Star at top
      drawStar(width/2, height/2 - 120, 5, 10, 5);

      // Constellation pattern
      drawStar(width/2 - 100, height/2 - 80, 3, 6, 5);
      drawStar(width/2 + 100, height/2 - 70, 4, 8, 5);
      drawStar(width/2 - 110, height/2 + 50, 3, 6, 5);
      drawStar(width/2 + 90, height/2 + 40, 4, 8, 5);

      // Connect constellation with subtle lines
      ctx.strokeStyle = 'rgba(191, 173, 127, 0.3)';
      ctx.lineWidth = 0.75;
      ctx.beginPath();
      ctx.moveTo(width/2, height/2 - 120);
      ctx.lineTo(width/2 - 100, height/2 - 80);
      ctx.lineTo(width/2 - 110, height/2 + 50);
      ctx.moveTo(width/2, height/2 - 120);
      ctx.lineTo(width/2 + 100, height/2 - 70);
      ctx.lineTo(width/2 + 90, height/2 + 40);
      ctx.stroke();
    };

    // Helper function to draw stars
    function drawStar(cx, cy, spikes, outerRadius, innerRadius) {
      let rot = Math.PI / 2 * 3;
      let x = cx;
      let y = cy;
      let step = Math.PI / spikes;

      ctx.beginPath();
      ctx.moveTo(cx, cy - outerRadius);

      for (let i = 0; i < spikes; i++) {
        x = cx + Math.cos(rot) * outerRadius;
        y = cy + Math.sin(rot) * outerRadius;
        ctx.lineTo(x, y);
        rot += step;

        x = cx + Math.cos(rot) * innerRadius;
        y = cy + Math.sin(rot) * innerRadius;
        ctx.lineTo(x, y);
        rot += step;
      }

      ctx.lineTo(cx, cy - outerRadius);
      ctx.closePath();
      ctx.fill();
    }

    drawPortrait();

    // Handle resize
    const handleResize = () => {
      drawPortrait();
    };

    window.addEventListener('resize', handleResize);
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  // Animate starfield background
  useEffect(() => {
    if (!starfieldCanvasRef.current) return;

    const canvas = starfieldCanvasRef.current;
    const ctx = canvas.getContext('2d');

    canvas.width = canvas.offsetWidth;
    canvas.height = canvas.offsetHeight;

    // Create stars
    const stars = Array(100).fill().map(() => ({
      x: Math.random() * canvas.width,
      y: Math.random() * canvas.height,
      size: Math.random() * 1.5 + 0.5,
      speed: Math.random() * 0.05 + 0.02,
      opacity: Math.random() * 0.5 + 0.3,
      twinkleSpeed: Math.random() * 0.02 + 0.01,
      twinkleDirection: Math.random() > 0.5 ? 1 : -1
    }));

    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Draw stars
      stars.forEach(star => {
        // Update twinkle effect
        star.opacity += star.twinkleSpeed * star.twinkleDirection;

        // Reverse direction if reaching opacity limits
        if (star.opacity > 0.8 || star.opacity < 0.2) {
          star.twinkleDirection *= -1;
        }

        // Move stars slowly
        star.y += star.speed;

        // Reset if star moves off canvas
        if (star.y > canvas.height) {
          star.y = 0;
          star.x = Math.random() * canvas.width;
        }

        // Draw star
        ctx.beginPath();
        ctx.arc(star.x, star.y, star.size, 0, Math.PI * 2);
        ctx.fillStyle = `rgba(191, 173, 127, ${star.opacity})`;
        ctx.fill();
      });

      requestRef.current = requestAnimationFrame(animate);
    };

    animate();

    // Handle resize
    const handleResize = () => {
      canvas.width = canvas.offsetWidth;
      canvas.height = canvas.offsetHeight;
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
      cancelAnimationFrame(requestRef.current);
    };
  }, []);

