  useEffect(() => {
    if (!creatorRef.current) return;

    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setIsLoaded(true);
          observer.disconnect();
        }
      },
      { threshold: 0.1 }
    );

    observer.observe(creatorRef.current);

    return () => {
      observer.disconnect();
    };
  }, []);

