import React, { useRef, useState, useCallback, useMemo, useEffect } from 'react';
import { motion, useInView } from 'framer-motion';
import { createUseStyles } from 'react-jss';
import CometBorderEffect from '../bordereffects/CometBorderEffect';

// Enhanced styles with split layout and sticky sidebar
const useStyles = createUseStyles({
  container: {
    display: 'flex',
    flexDirection: 'row',
    width: '100%',
    maxWidth: '1300px',
    margin: '0 auto',
    minHeight: '70vh',
    position: 'relative',
    overflow: 'visible',
    '@media (max-width: 768px)': {
      flexDirection: 'column',
    }
  },

  // Left sidebar section with sticky positioning
  profileSidebar: {
    width: '40%',
    padding: '3rem 1rem 2rem 0',
    display: 'flex',
    flexDirection: 'column',
    position: 'sticky',
    top: '2rem',
    height: 'fit-content',
    alignSelf: 'flex-start',
    '@media (max-width: 768px)': {
      width: '100%',
      padding: '2rem 1rem',
      alignItems: 'center',
      textAlign: 'center',
      position: 'relative',
      top: 0
    }
  },

  // Right content section
  contentSection: {
    width: '60%',
    padding: '3rem 0 2rem 2rem',
    '@media (max-width: 768px)': {
      width: '100%',
      padding: '2rem 1rem'
    }
  },

  // Profile image container with circular shape
  profileImageContainer: {
    position: 'relative',
    width: '240px',
    height: '240px',
    borderRadius: '50%',
    boxShadow: '0 5px 15px rgba(0, 0, 0, 0.3)',
    transition: 'box-shadow 0.3s ease',
    overflow: 'hidden',
    boxSizing: 'border-box',
    marginBottom: '2rem',
    border: '1px solid rgba(191, 173, 127, 0.2)',
    '&:hover': {
      boxShadow: '0 5px 20px rgba(191, 173, 127, 0.4)'
    },
    '@media (max-width: 768px)': {
      width: '200px',
      height: '200px',
    },
  },

  profileImage: {
    position: 'absolute',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    borderRadius: '50%',
    overflow: 'hidden',
    '&::after': {
      content: '""',
      position: 'absolute',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      boxShadow: 'inset 0 0 20px rgba(191, 173, 127, 0.3)',
      borderRadius: '50%',
      pointerEvents: 'none'
    }
  },

  imageWrapper: {
    position: 'absolute',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    overflow: 'hidden',
