/**
 * PersonProfileCard Component with Split Layout and Sticky Sidebar
 * 
 * A highly configurable profile card with a sticky sidebar, navigation links,
 * and content sections. Features smooth scrolling and an orbiting comet effect.
 * 
 * @param {Object} props - Component props
 * @param {Object} props.person - Person data object
 * @param {string} props.person.name - Person's name
 * @param {string} props.person.role - Person's role or title
 * @param {string} props.person.image - URL to person's image
 * @param {string} props.person.tagline - Short tagline about the person
 * @param {string[]} props.person.bio - Array of bio paragraphs for content area
 * @param {Object[]} props.person.stats - Array of statistic objects
 * @param {Object[]} props.person.experience - Array of experience objects
 * @param {Object[]} props.person.projects - Array of project objects
 * @param {Object} props.animationConfig - Animation configuration
 * @param {Object} props.cometConfig - Comet animation configuration
 * @param {Object} props.imagePosition - Image position configuration
 * @param {Array} props.additionalSections - Additional content sections
 * @param {Function} props.onSectionChange - Callback when active section changes
 */
const PersonProfileCard = ({ 
  person,
  animationConfig = {
    threshold: 0.2,
    once: true,
    initialY: 30,
    duration: 0.8
  },
  cometConfig = {
    size: 1.5,
    trailLength: 100,
    speed: 0.001,
    targetFPS: 30,
    respectReducedMotion: true
  },
  imagePosition = {
    x: 50,
    y: 50,
    scale: 1
  },
  additionalSections = [],
  onSectionChange = null
}) => {
  const classes = useStyles();
  const [isHovered, setIsHovered] = useState(false);
  const [activeSection, setActiveSection] = useState('about');

  // Refs for sections to enable scrolling
  const aboutRef = useRef(null);
  const experienceRef = useRef(null);
  const projectsRef = useRef(null);
  const sectionRefs = {
    about: aboutRef,
    experience: experienceRef,
    projects: projectsRef
  };

  // Set up ref and inView detection
  const cardRef = useRef(null);
  const isInView = useInView(cardRef, {
    threshold: animationConfig?.threshold ?? 0.2,
    once: animationConfig?.once ?? true
  });

  // Handle mouse events
  const handleMouseEnter = useCallback(() => setIsHovered(true), []);
  const handleMouseLeave = useCallback(() => setIsHovered(false), []);

  // Precompute image style
  const imageStyle = useMemo(() => ({
    objectPosition: `${imagePosition?.x ?? 50}% ${imagePosition?.y ?? 50}%`,
    transform: `scale(${imagePosition?.scale ?? 1})`
  }), [imagePosition?.x, imagePosition?.y, imagePosition?.scale]);

  // Scroll to section function
  const scrollToSection = useCallback((sectionId) => {
    setActiveSection(sectionId);

    // Notify parent component if callback provided
    if (onSectionChange) {
      onSectionChange(sectionId);
    }

    // Scroll to section with smooth behavior
    const sectionRef = sectionRefs[sectionId];
    if (sectionRef?.current) {
      sectionRef.current.scrollIntoView({ 
        behavior: 'smooth',
        block: 'start'
      });
    }
  }, [onSectionChange, sectionRefs]);

  // Set up intersection observer to detect which section is in view
  useEffect(() => {
    if (!aboutRef.current || !experienceRef.current || !projectsRef.current) return;

    const options = {
      root: null,
      rootMargin: '-10% 0px -70% 0px', // Consider element in view when in the top 20% of viewport
      threshold: 0
    };

    const callback = (entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          const sectionId = entry.target.getAttribute('data-section');
          if (sectionId && sectionId !== activeSection) {
            setActiveSection(sectionId);

            // Notify parent component if callback provided
            if (onSectionChange) {
              onSectionChange(sectionId);
            }
          }
        }
      });
    };

    const observer = new IntersectionObserver(callback, options);

    // Observe all section refs
    if (aboutRef.current) observer.observe(aboutRef.current);
    if (experienceRef.current) observer.observe(experienceRef.current);
    if (projectsRef.current) observer.observe(projectsRef.current);

    return () => {
      observer.disconnect();
    };
  }, [activeSection, onSectionChange]);

  return (
    <motion.div 
      ref={cardRef}
      className={classes.container}
      initial={{ opacity: 0, y: animationConfig?.initialY ?? 30 }}
      animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: animationConfig?.initialY ?? 30 }}
      transition={{ 
        duration: animationConfig?.duration ?? 0.8, 
        ease: "easeOut" 
      }}
    >
      {/* Left sidebar with profile image and basic info - sticky positioned */}
      <div className={classes.profileSidebar}>
        {/* Profile image with comet animation */}
        <div 
          className={classes.profileImageContainer}
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
        >
          {/* Comet animation around the border using standalone component */}
          <CometBorderEffect 
            isHovered={isHovered}
            size={cometConfig.size}
            trailLength={cometConfig.trailLength}
            speed={cometConfig.speed}
            hoverSpeedMultiplier={cometConfig.hoverSpeedMultiplier || 2}
            trailSegments={cometConfig.trailSegments || 25}
            glowIntensity={cometConfig.glowIntensity || 0.8}
            targetFPS={cometConfig.targetFPS || 30}
            respectReducedMotion={cometConfig.respectReducedMotion || true}
            paddingTop={20}
            paddingRight={20}
            paddingBottom={20}
            paddingLeft={20}
          />

          <div className={classes.profileImage}>
            <div className={classes.imageWrapper}>
              <img 
                src={person.image} 
                alt={person.name} 
                className={classes.image}
                style={imageStyle}
                loading="lazy"
              />
            </div>
          </div>
        </div>

        {/* Profile information - short and to the point */}
        <div className={classes.profileInfo}>
          <h1 className={classes.profileName}>{person.name}</h1>
          <h2 className={classes.profileRole}>{person.role}</h2>

          {/* Short tagline instead of full paragraph */}
          {person.tagline && (
            <p className={classes.profileTagline}>{person.tagline}</p>
          )}
        </div>

        {/* Navigation links - enhanced with better hover effects and larger lines */}
        <div className={classes.navLinks}>
          <a 
            className={`${classes.navLink} ${activeSection === 'about' ? classes.activeLink : ''}`}
            onClick={() => scrollToSection('about')}
          >
            ABOUT
          </a>
          <a 
            className={`${classes.navLink} ${activeSection === 'experience' ? classes.activeLink : ''}`}
            onClick={() => scrollToSection('experience')}
          >
            EXPERIENCE
          </a>
          <a 
            className={`${classes.navLink} ${activeSection === 'projects' ? classes.activeLink : ''}`}
            onClick={() => scrollToSection('projects')}
          >
            PROJECTS
          </a>
        </div>
      </div>

      {/* Right content section - scrollable with visible sections */}
      <div className={classes.contentSection}>
        {/* About section */}
        <div 
          ref={aboutRef} 
          data-section="about"
          className={classes.section}
          id="about"
        >
          {person.bio && person.bio.map((paragraph, index) => (
            <p key={index} className={classes.sectionContent}>
              {paragraph}
            </p>
          ))}

          {/* Stats display */}
          {person.stats && person.stats.length > 0 && (
            <div className={classes.statsContainer}>
              {person.stats.map((stat, index) => (
                <div key={index} className={classes.stat}>
                  <div className={classes.statValue}>{stat.value}</div>
                  <div className={classes.statLabel}>{stat.label}</div>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Experience section */}
        <div 
          ref={experienceRef}
          data-section="experience"
          className={classes.section}
          id="experience"
        >
          {person.experience ? (
            person.experience.map((item, index) => (
              <p key={index} className={classes.sectionContent}>
                {item.content}
              </p>
            ))
          ) : (
            <p className={classes.sectionContent}>
              Throughout my career, I've specialized in developing software systems that seamlessly 
              integrate front-end experiences with robust back-end architectures. My experience spans 
              various domains, from interactive media to data visualization systems.
            </p>
          )}
        </div>

        {/* Projects section */}
        <div 
          ref={projectsRef}
          data-section="projects"
          className={classes.section}
          id="projects"
        >
          {person.projects ? (
            person.projects.map((item, index) => (
              <p key={index} className={classes.sectionContent}>
                {item.content}
              </p>
            ))
          ) : (
            <p className={classes.sectionContent}>
              My personal projects merge the scientific principles of evolution and adaptation with 
              mythological themes of rebirth and renewal. I create 'things' that are both mechanically 
              sound and narratively compelling, allowing users to explore complex themes through 
              immersive experiences.
            </p>
          )}
        </div>

        {/* Dynamic sections from props */}
        {additionalSections.length > 0 && (
          additionalSections.map((section, index) => (
            <div key={index} className={classes.section}>
              {section.content && (
                typeof section.content === 'string' 
                  ? <p className={classes.sectionContent}>{section.content}</p>
                  : section.content
              )}
            </div>
          ))
        )}
      </div>
    </motion.div>
  );
};

export default PersonProfileCard;
