import React, { useState, useRef, useCallback } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import './landingNavigation.css';

const LandingNavigation = ({ mainSections }) => {
  const [activeSection, setActiveSection] = useState(null);
  const [expandedSection, setExpandedSection] = useState(null);
  const [expandedSubsection, setExpandedSubsection] = useState(null);
  const [animatingElements, setAnimatingElements] = useState(new Set());
  const animationTimeoutRef = useRef({});

  // Clear specific animation timeouts
  const clearAnimationTimeout = useCallback((elementId) => {
    if (animationTimeoutRef.current[elementId]) {
      clearTimeout(animationTimeoutRef.current[elementId]);
      delete animationTimeoutRef.current[elementId];
    }
  }, []);

  // Add animating element
  const addAnimatingElement = useCallback((elementId) => {
    setAnimatingElements(prev => {
      const updated = new Set(prev);
      updated.add(elementId);
      return updated;
    });
  }, []);

  // Remove animating element
  const removeAnimatingElement = useCallback((elementId) => {
    setAnimatingElements(prev => {
      const updated = new Set(prev);
      updated.delete(elementId);
      return updated;
    });
  }, []);

  // Check if a specific element is animating
  const isElementAnimating = useCallback((elementId) => {
    return animatingElements.has(elementId);
  }, [animatingElements]);

  // Navigate to section
  const handleNavigate = (path, event) => {
    event.stopPropagation();
    console.log(`Navigating to: ${path}`);
    // In a real app, you'd use your routing library
  };

  // Standardized expand/collapse handler for any navigation level
  const handleExpandCollapse = useCallback((
    elementId, 
    event, 
    isExpandable, 
    currentExpandedState, 
    setExpandedState, 
    childExpandedState = null, 
    setChildExpandedState = null
  ) => {
    event.stopPropagation();

    if (!isExpandable || isElementAnimating(elementId)) {
      return;
    }

    // Clear any existing animation timeout for this element
    clearAnimationTimeout(elementId);

    // Mark this element as animating
    addAnimatingElement(elementId);

    const isExpanding = currentExpandedState !== elementId;

    // Handle expansion/collapse
    if (isExpanding) {
      // Expand this element
      setExpandedState(elementId);

      // If this element has children that can be expanded, collapse them
      if (setChildExpandedState) {
        setChildExpandedState(null);
      }

      // Set timeout to remove animating flag after expansion completes
      animationTimeoutRef.current[elementId] = setTimeout(() => {
        removeAnimatingElement(elementId);
      }, 450); // Match your animation duration
    } else {
      // Collapse logic
      setTimeout(() => {
        setExpandedState(null);

        // If this element has children, also collapse them
        if (setChildExpandedState) {
          setChildExpandedState(null);
        }

        // Set timeout to remove animating flag after collapse completes
        animationTimeoutRef.current[elementId] = setTimeout(() => {
          removeAnimatingElement(elementId);
        }, 450); // Match your animation duration
      }, 10);
    }
  }, [isElementAnimating, addAnimatingElement, removeAnimatingElement, clearAnimationTimeout]);

  // Specific handlers that use the standardized function
  const handleSectionClick = useCallback((sectionId, event, isExpandable) => {
    handleExpandCollapse(
      sectionId,
      event,
      isExpandable,
      expandedSection,
      setExpandedSection,
      expandedSubsection,
      setExpandedSubsection
    );
  }, [expandedSection, expandedSubsection, handleExpandCollapse]);

  const handleSubsectionClick = useCallback((subsectionId, event, isExpandable) => {
    handleExpandCollapse(
      subsectionId,
      event,
      isExpandable,
      expandedSubsection,
      setExpandedSubsection
    );
  }, [expandedSubsection, handleExpandCollapse]);

  // Render SVG icons for main sections
  const renderIcon = (iconType) => {
    switch(iconType) {
      case 'circles':
        return (
          <div className="icon-container">
            <svg width="42" height="42" viewBox="0 0 42 42" fill="none" xmlns="http://www.w3.org/2000/svg">
              <circle cx="21" cy="21" r="14" stroke="#A08E61" strokeWidth="1.5" opacity="0.9" />
              <circle cx="16" cy="21" r="9" stroke="#A08E61" strokeWidth="1.5" opacity="0.7" />
              <circle cx="26" cy="21" r="9" stroke="#A08E61" strokeWidth="1.5" opacity="0.7" />
            </svg>
          </div>
        );
      case 'triangle':
        return (
          <div className="icon-container">
            <svg width="42" height="42" viewBox="0 0 42 42" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M21 7L35 32H7L21 7Z" stroke="#A08E61" strokeWidth="1.5" opacity="0.9" />
            </svg>
          </div>
        );
      case 'targetCircle':
        return (
          <div className="icon-container">
            <svg width="42" height="42" viewBox="0 0 42 42" fill="none" xmlns="http://www.w3.org/2000/svg">
              <circle cx="21" cy="21" r="14" stroke="#A08E61" strokeWidth="1.5" opacity="0.9" />
              <circle cx="21" cy="21" r="5" fill="#A08E61" opacity="0.7" />
            </svg>
          </div>
        );
      default:
        return null;
    }
  };

  // Standardized animation variants
  const containerVariants = {
    hidden: { 
      opacity: 0, 
      height: 0, 
      overflow: 'hidden' 
    },
    visible: { 
      opacity: 1, 
      height: 'auto', 
      overflow: 'visible',
      transition: {
        height: { duration: 0.3, ease: "easeOut" },
        opacity: { duration: 0.3, ease: "easeOut" },
        overflow: { delay: 0.25 },
        staggerChildren: 0.07,
        delayChildren: 0.05
      }
    },
    exit: { 
      opacity: 0, 
      height: 0, 
      overflow: 'hidden',
      transition: {
        height: { duration: 0.2, ease: "easeIn" },
        opacity: { duration: 0.2, ease: "easeIn" }
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, x: -10 },
    visible: { 
      opacity: 1, 
      x: 0,
      transition: { duration: 0.25, ease: "easeOut" }
    }
  };

  // Nested subsection container variants - slightly delayed
  const nestedContainerVariants = {
    ...containerVariants,
    visible: {
      ...containerVariants.visible,
      transition: {
        ...containerVariants.visible.transition,
        delayChildren: 0.1, // Slightly more delay for nested items
      }
    }
  };

  return (
    <div className="sections-container">
      {mainSections.map((section, index) => (
        <motion.div 
          key={section.id} 
          className={`section-wrapper ${expandedSection === section.id ? 'expanded-section' : ''}`}
          initial={{ opacity: 0, x: -15 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ 
            duration: 0.4, 
            ease: "easeInOut",
            delay: index * 0.15 + 0.3
          }}
        >
          <div 
            className={`section-row ${activeSection === section.id ? 'active' : ''} ${expandedSection === section.id ? 'expanded' : ''}`}
            onMouseEnter={() => setActiveSection(section.id)}
            onMouseLeave={() => setActiveSection(null)}
            onClick={(e) => {
              if (section.expandable) {
                handleSectionClick(section.id, e, section.expandable);
              } else {
                handleNavigate(section.path, e);
              }
            }}
            style={{ animationDelay: `${index * 0.15 + 0.3}s` }}
          >
            <div className="section-content">
              <div className="section-icon-wrapper">
                {renderIcon(section.icon)}
              </div>
              <div className="section-text">
                <h2 className="section-title">{section.title}</h2>
                <p className="section-description">{section.description}</p>
              </div>
              {section.expandable && (
                <div className={`expand-indicator ${expandedSection === section.id ? 'expanded' : ''}`}>
                  <svg width="16" height="16" viewBox="0 0 16 16" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <path d="M4 6L8 10L12 6" stroke="#A08E61" strokeWidth="1.5" strokeLinecap="round" />
                  </svg>
                </div>
              )}
            </div>
            <div className="diagonal-cut" />
          </div>

          {/* Expandable Subsections */}
          {section.expandable && (
            <AnimatePresence mode="wait">
              {expandedSection === section.id && (
                <motion.div 
                  className="subsection-animation-wrapper"
                  key={`section-${section.id}-subsections`}
                  variants={containerVariants}
                  initial="hidden"
                  animate="visible"
                  exit="exit"
                >
                  <div className="subsections-container">
                    {section.subsections.map((subsection, subIndex) => (
                      <motion.div 
                        key={subsection.id} 
                        className={`subsection-wrapper ${expandedSubsection === subsection.id ? 'expanded-subsection' : ''}`}
                        variants={itemVariants}
                      >
                        <div 
                          className={`subsection-row ${expandedSubsection === subsection.id ? 'active' : ''}`}
                          onClick={(e) => {
                            if (subsection.expandable) {
                              handleSubsectionClick(subsection.id, e, subsection.expandable);
                            } else {
                              handleNavigate(subsection.path, e);
                            }
                          }}
                        >
                          <div className="subsection-content">
                            <h3 className="subsection-title">{subsection.title}</h3>
                            <p className="subsection-description">{subsection.description}</p>
                            {subsection.expandable && (
                              <div 
                                className={`expand-indicator small ${expandedSubsection === subsection.id ? 'expanded' : ''}`}
                              >
                                <svg width="12" height="12" viewBox="0 0 16 16" fill="none" xmlns="http://www.w3.org/2000/svg">
                                  <path d="M4 6L8 10L12 6" stroke="#A08E61" strokeWidth="1.5" strokeLinecap="round" />
                                </svg>
                              </div>
                            )}
                          </div>
                        </div>

                        {/* Nested Subsections - Using standardized animation approach */}
                        {subsection.expandable && (
                          <AnimatePresence mode="wait">
                            {expandedSubsection === subsection.id && (
                              <motion.div 
                                className="nested-animation-wrapper"
                                key={`subsection-${subsection.id}-nested`}
                                variants={nestedContainerVariants}
                                initial="hidden"
                                animate="visible"
                                exit="exit"
                              >
                                <div className="nested-subsections-container">
                                  {subsection.subsections.map((nestedSubsection, nestedIndex) => (
                                    <motion.div 
                                      key={nestedSubsection.id}
                                      className="nested-subsection-row"
                                      onClick={(e) => handleNavigate(nestedSubsection.path, e)}
                                      variants={itemVariants}
                                    >
                                      <div className="nested-subsection-content">
                                        <h4 className="nested-subsection-title">{nestedSubsection.title}</h4>
                                        <p className="nested-subsection-description">{nestedSubsection.description}</p>
                                      </div>
                                    </motion.div>
                                  ))}
                                </div>
                              </motion.div>
                            )}
                          </AnimatePresence>
                        )}
                      </motion.div>
                    ))}
                  </div>
                </motion.div>
              )}
            </AnimatePresence>
          )}
        </motion.div>
      ))}
    </div>
  );
};

export default LandingNavigation;
