import React, { useRef, useState, useCallback, useMemo, useEffect } from 'react';
import { motion, useInView } from 'framer-motion';
import { createUseStyles } from 'react-jss';
import CometBorderEffect from '../bordereffects/CometBorderEffect';

// Enhanced styles with proper scroll-triggered positioning
const useStyles = createUseStyles({
  container: {
    display: 'flex',
    flexDirection: 'row',
    width: '100%',
    maxWidth: '1300px',
    margin: '0 auto',
    minHeight: '70vh',
    position: 'relative',
    '@media (max-width: 768px)': {
      flexDirection: 'column',
    }
  },

  // Left sidebar wrapper with relative positioning
  sidebarWrapper: {
    width: '40%',
    position: 'relative',
    '@media (max-width: 768px)': {
      width: '100%',
    }
  },

  // The sidebar that will dynamically change positioning
  profileSidebar: {
    padding: '3rem 2rem 2rem 0',
    display: 'flex',
    flexDirection: 'column',
    position: 'absolute', // Start as absolute, not fixed
    top: 0, // Initially aligned with wrapper
    width: 'inherit',
    maxWidth: 'inherit',
    transition: 'transform 0.3s ease',
    '@media (max-width: 768px)': {
      position: 'relative',
      padding: '2rem 1rem',
      alignItems: 'center',
      textAlign: 'center',
      width: '100%'
    }
  },

  // Applied when sidebar should be fixed in viewport
  sidebarFixed: {
    position: 'fixed',
  },

  // Applied when sidebar should stop scrolling at the bottom
  sidebarBottom: {
    position: 'absolute',
    top: 'auto',
    bottom: 0,
  },

  // Content section
  contentSection: {
    width: '60%',
    padding: '3rem 0 2rem 2rem',
    marginLeft: 'auto',
    '@media (max-width: 768px)': {
      width: '100%',
      padding: '2rem 1rem',
      marginLeft: 0
    }
  },

  // Profile image container with circular shape
  profileImageContainer: {
    position: 'relative',
    width: '240px',
    height: '240px',
    borderRadius: '50%',
    boxShadow: '0 5px 15px rgba(0, 0, 0, 0.3)',
    transition: 'box-shadow 0.3s ease',
    overflow: 'hidden',
    boxSizing: 'border-box',
    marginBottom: '2rem',
    border: '1px solid rgba(191, 173, 127, 0.2)',
    '&:hover': {
      boxShadow: '0 5px 20px rgba(191, 173, 127, 0.4)'
    },
    '@media (max-width: 768px)': {
      width: '200px',
      height: '200px',
    },
  },

  profileImage: {
    position: 'absolute',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    borderRadius: '50%',
    overflow: 'hidden',
    '&::after': {
      content: '""',
      position: 'absolute',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      boxShadow: 'inset 0 0 20px rgba(191, 173, 127, 0.3)',
      borderRadius: '50%',
      pointerEvents: 'none'
    }
  },

  imageWrapper: {
    position: 'absolute',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    overflow: 'hidden',
    borderRadius: '50%'
  },

  image: {
    width: '100%',
    height: '100%',
    objectFit: 'cover',
    transition: 'transform 0.6s ease, object-position 0.6s ease',
    display: 'block'
  },

  // Profile information styling
  profileInfo: {
    marginBottom: '2rem',
    paddingLeft: '5px',
  },

  profileName: {
    fontSize: '2.8rem',
    fontWeight: '300',
    color: '#bfad7f',
    letterSpacing: '0.05em',
    marginBottom: '0.5rem',
    '@media (max-width: 768px)': {
      fontSize: '2.2rem'
    }
  },

  profileRole: {
    fontSize: '1.2rem',
    fontWeight: '300',
    marginBottom: '2rem',
    color: 'rgba(191, 173, 127, 0.7)',
    letterSpacing: '0.05em',
    fontFamily: '"Garamond", "Adobe Caslon Pro", serif',
    fontStyle: 'italic',
  },

  profileTagline: {
    fontSize: '1.1rem',
    lineHeight: '1.6',
    marginBottom: '3rem',
    maxWidth: '90%',
    color: 'rgba(224, 224, 224, 0.7)',
    fontFamily: '"Garamond", "Adobe Caslon Pro", serif',
    fontWeight: '300',
    '@media (max-width: 768px)': {
      maxWidth: '100%',
      textAlign: 'center'
    }
  },

  // Content sections
  section: {
    marginBottom: '3rem',
    scrollMarginTop: '2rem',
  },

  sectionContent: {
    fontSize: '1rem',
    lineHeight: '1.8',
    marginBottom: '1.5rem',
    color: 'rgba(224, 224, 224, 0.7)',
    fontFamily: '"Garamond", "Adobe Caslon Pro", serif',
    fontWeight: '300',
  },

  // Stats section
  statsContainer: {
    display: 'flex',
    justifyContent: 'space-between',
    flexWrap: 'wrap',
    marginTop: '3rem',
    width: '100%',
    '@media (max-width: 768px)': {
      justifyContent: 'center',
      gap: '2rem'
    },
    '@media (max-width: 480px)': {
      flexDirection: 'column',
      alignItems: 'center',
      gap: '1.5rem'
    }
  },

  stat: {
    flex: '1',
    textAlign: 'center',
    padding: '0 1rem',
    minWidth: '100px',
    '@media (max-width: 480px)': {
      width: '100%',
      maxWidth: '180px'
    }
  },

  statValue: {
    fontSize: '2.5rem',
    fontWeight: '100',
    color: 'rgba(191, 173, 127, 0.9)',
    marginBottom: '0.5rem'
  },

  statLabel: {
    fontSize: '0.85rem',
    color: 'rgba(224, 224, 224, 0.5)',
    textTransform: 'uppercase',
    letterSpacing: '0.1em'
  },

  // Nav links with initial visible lines
  navLinks: {
    marginTop: '2rem',
  },

  navLinkContainer: {
    position: 'relative',
    marginBottom: '1rem',
    display: 'flex',
    alignItems: 'center',
  },

  // Line indicator with initial width
  navLine: {
    position: 'absolute',
    left: '0',
    top: '50%',
    transform: 'translateY(-50%)',
    width: '10px', // Initial width is visible
    height: '2px',
    backgroundColor: 'rgba(191, 173, 127, 0.5)',
    transition: 'width 0.3s ease, background-color 0.3s ease',
  },

  navLink: {
    position: 'relative',
    display: 'block',
    padding: '0.5rem 0 0.5rem 25px', // Padding ensures text doesn't overlap line
    fontSize: '0.85rem',
    letterSpacing: '0.1em',
    transition: 'color 0.3s, transform 0.3s',
    textDecoration: 'none',
    color: 'rgba(224, 224, 224, 0.7)',
    cursor: 'pointer',
    '&:hover': {
      color: '#bfad7f',
      transform: 'translateX(3px)',
      '& + $navLine': { // Target the sibling line element
        width: '20px', // Extended on hover
        backgroundColor: 'rgba(191, 173, 127, 0.9)' // Brighter color on hover
      }
    },
    '@media (max-width: 768px)': {
      display: 'inline-block',
      margin: '0 1rem',
    }
  },

  // Style for the active navigation link
  activeLinkContainer: {
    '& $navLine': {
      width: '20px', // Extended width for active section
      backgroundColor: 'rgba(191, 173, 127, 0.9)' // Brighter color for active section
    },
    '& $navLink': {
      color: '#bfad7f' // Highlight text color for active section
    }
  }
});

/**
 * PersonProfileCard Component with sophisticated scroll behavior
 * 
 * A highly configurable profile card with a sidebar that follows specific scroll
 * behavior: normal flow at first, fixed when in view, and returns to normal 
 * flow after scrolling past the component.
 * 
 * @param {Object} props - Component props
 * @param {Object} props.person - Person data object
 * @param {string} props.person.name - Person's name
 * @param {string} props.person.role - Person's role or title
 * @param {string} props.person.image - URL to person's image
 * @param {string} props.person.tagline - Short tagline about the person
 * @param {string[]} props.person.bio - Array of bio paragraphs for content area
 * @param {Object[]} props.person.stats - Array of statistic objects
 * @param {Object[]} props.person.experience - Array of experience objects
 * @param {Object[]} props.person.projects - Array of project objects
 * @param {Object} props.animationConfig - Animation configuration
 * @param {Object} props.cometConfig - Comet animation configuration
 * @param {Object} props.imagePosition - Image position configuration
 * @param {Array} props.additionalSections - Additional content sections
 * @param {Function} props.onSectionChange - Callback when active section changes
 * @param {number} props.topOffset - Offset from top when sidebar becomes fixed (default: 100px)
 */
const PersonProfileCard = ({ 
  person,
  animationConfig = {
    threshold: 0.2,
    once: true,
    initialY: 30,
    duration: 0.8
  },
  cometConfig = {
    size: 1.5,
    trailLength: 100,
    speed: 0.001,
    targetFPS: 30,
    respectReducedMotion: true
  },
  imagePosition = {
    x: 50,
    y: 50,
    scale: 1
  },
  additionalSections = [],
  onSectionChange = null,
  topOffset = 100 // Default offset from top
}) => {
  const classes = useStyles();
  const [isHovered, setIsHovered] = useState(false);
  const [activeSection, setActiveSection] = useState('about');
  const [sidebarPosition, setSidebarPosition] = useState('normal'); // 'normal', 'fixed', or 'bottom'

  // Refs for sections to enable scrolling
  const aboutRef = useRef(null);
  const experienceRef = useRef(null);
  const projectsRef = useRef(null);
  const containerRef = useRef(null);
  const sidebarRef = useRef(null);
  const wrapperRef = useRef(null);

  const sectionRefs = {
    about: aboutRef,
    experience: experienceRef,
    projects: projectsRef
  };

  // Set up ref and inView detection
  const isInView = useInView(containerRef, {
    threshold: animationConfig?.threshold ?? 0.2,
    once: animationConfig?.once ?? true
  });

  // Handle mouse events
  const handleMouseEnter = useCallback(() => setIsHovered(true), []);
  const handleMouseLeave = useCallback(() => setIsHovered(false), []);

  // Precompute image style
  const imageStyle = useMemo(() => ({
    objectPosition: `${imagePosition?.x ?? 50}% ${imagePosition?.y ?? 50}%`,
    transform: `scale(${imagePosition?.scale ?? 1})`
  }), [imagePosition?.x, imagePosition?.y, imagePosition?.scale]);

  // Advanced scroll handling for sidebar positioning
  useEffect(() => {
    if (!containerRef.current || !wrapperRef.current || !sidebarRef.current) return;

    // Skip this behavior on mobile
    if (window.innerWidth <= 768) {
      setSidebarPosition('normal');
      return;
    }

    const handleScroll = () => {
      // Get element positions
      const containerRect = containerRef.current.getBoundingClientRect();
      const wrapperRect = wrapperRef.current.getBoundingClientRect();
      const sidebarHeight = sidebarRef.current.offsetHeight;
      const windowHeight = window.innerHeight;

      // Calculate when to start fixing the sidebar
      const startFixPoint = wrapperRect.top <= topOffset;

      // Calculate when to stop fixing the sidebar (when container bottom reaches sidebar bottom)
      const stopFixPoint = containerRect.bottom <= sidebarHeight + topOffset;

      // Update sidebar position based on scroll position
      if (!startFixPoint) {
        // Before the start point - normal position
        setSidebarPosition('normal');
      } else if (startFixPoint && !stopFixPoint) {
        // Between start and stop points - fixed position
        setSidebarPosition('fixed');
      } else if (stopFixPoint) {
        // After the stop point - bottom position
        setSidebarPosition('bottom');
      }
    };

    window.addEventListener('scroll', handleScroll);

    // Initial check
    handleScroll();

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [topOffset]);

  // Scroll to section function
  const scrollToSection = useCallback((sectionId) => {
    setActiveSection(sectionId);

    // Notify parent component if callback provided
    if (onSectionChange) {
      onSectionChange(sectionId);
    }

    // Scroll to section with smooth behavior
    const sectionRef = sectionRefs[sectionId];
    if (sectionRef?.current) {
      sectionRef.current.scrollIntoView({ 
        behavior: 'smooth',
        block: 'start'
      });
    }
  }, [onSectionChange, sectionRefs]);

  // Set up intersection observer to detect which section is in view
  useEffect(() => {
    if (!aboutRef.current || !experienceRef.current || !projectsRef.current) return;

    const options = {
      root: null,
      rootMargin: '-10% 0px -70% 0px', // Consider element in view when in the top 30% of viewport
      threshold: 0
    };

    const callback = (entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          const sectionId = entry.target.getAttribute('data-section');
          if (sectionId && sectionId !== activeSection) {
            setActiveSection(sectionId);

            // Notify parent component if callback provided
            if (onSectionChange) {
              onSectionChange(sectionId);
            }
          }
        }
      });
    };

    const observer = new IntersectionObserver(callback, options);

    // Observe all section refs
    if (aboutRef.current) observer.observe(aboutRef.current);
    if (experienceRef.current) observer.observe(experienceRef.current);
    if (projectsRef.current) observer.observe(projectsRef.current);

    return () => {
      observer.disconnect();
    };
  }, [activeSection, onSectionChange]);

  // Get the appropriate sidebar class based on position state
  const getSidebarClass = () => {
    switch(sidebarPosition) {
      case 'fixed':
        return classes.sidebarFixed;
      case 'bottom':
        return classes.sidebarBottom;
      default:
        return '';
    }
  };

  return (
    <motion.div 
      ref={containerRef}
      className={classes.container}
      initial={{ opacity: 0, y: animationConfig?.initialY ?? 30 }}
      animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: animationConfig?.initialY ?? 30 }}
      transition={{ 
        duration: animationConfig?.duration ?? 0.8, 
        ease: "easeOut" 
      }}
    >
      {/* Left sidebar wrapper maintains space */}
      <div ref={wrapperRef} className={classes.sidebarWrapper}>
        {/* Sidebar with dynamic positioning */}
        <div 
          ref={sidebarRef}
          className={`${classes.profileSidebar} ${getSidebarClass()}`}
          style={{ top: sidebarPosition === 'fixed' ? `${topOffset}px` : 0 }}
        >
          {/* Profile image with comet animation */}
          <div 
            className={classes.profileImageContainer}
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
          >
            {/* Comet animation around the border using standalone component */}
            <CometBorderEffect 
              isHovered={isHovered}
              size={cometConfig.size}
              trailLength={cometConfig.trailLength}
              speed={cometConfig.speed}
              hoverSpeedMultiplier={cometConfig.hoverSpeedMultiplier || 2}
              trailSegments={cometConfig.trailSegments || 25}
              glowIntensity={cometConfig.glowIntensity || 0.8}
              targetFPS={cometConfig.targetFPS || 30}
              respectReducedMotion={cometConfig.respectReducedMotion || true}
              paddingTop={20}
              paddingRight={20}
              paddingBottom={20}
              paddingLeft={20}
            />

            <div className={classes.profileImage}>
              <div className={classes.imageWrapper}>
                <img 
                  src={person.image} 
                  alt={person.name} 
                  className={classes.image}
                  style={imageStyle}
                  loading="lazy"
                />
              </div>
            </div>
          </div>

          {/* Profile information */}
          <div className={classes.profileInfo}>
            <h1 className={classes.profileName}>{person.name}</h1>
            <h2 className={classes.profileRole}>{person.role}</h2>

            {/* Short tagline */}
            {person.tagline && (
              <p className={classes.profileTagline}>{person.tagline}</p>
            )}
          </div>

          {/* Navigation links with persistent line indicators */}
          <div className={classes.navLinks}>
            <div className={`${classes.navLinkContainer} ${activeSection === 'about' ? classes.activeLinkContainer : ''}`}>
              <a 
                className={classes.navLink}
                onClick={() => scrollToSection('about')}
              >
                ABOUT
              </a>
              <div className={classes.navLine}></div>
            </div>

            <div className={`${classes.navLinkContainer} ${activeSection === 'experience' ? classes.activeLinkContainer : ''}`}>
              <a 
                className={classes.navLink}
                onClick={() => scrollToSection('experience')}
              >
                EXPERIENCE
              </a>
              <div className={classes.navLine}></div>
            </div>

            <div className={`${classes.navLinkContainer} ${activeSection === 'projects' ? classes.activeLinkContainer : ''}`}>
              <a 
                className={classes.navLink}
                onClick={() => scrollToSection('projects')}
              >
                PROJECTS
              </a>
              <div className={classes.navLine}></div>
            </div>
          </div>
        </div>
      </div>

      {/* Right content section - scrollable with sections */}
      <div className={classes.contentSection}>
        {/* About section */}
        <div 
          ref={aboutRef} 
          data-section="about"
          className={classes.section}
          id="about"
        >
          {person.bio && person.bio.map((paragraph, index) => (
            <p key={index} className={classes.sectionContent}>
              {paragraph}
            </p>
          ))}

          {/* Stats display */}
          {person.stats && person.stats.length > 0 && (
            <div className={classes.statsContainer}>
              {person.stats.map((stat, index) => (
                <div key={index} className={classes.stat}>
                  <div className={classes.statValue}>{stat.value}</div>
                  <div className={classes.statLabel}>{stat.label}</div>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Experience section */}
        <div 
          ref={experienceRef}
          data-section="experience"
          className={classes.section}
          id="experience"
        >
          {person.experience ? (
            person.experience.map((item, index) => (
              <p key={index} className={classes.sectionContent}>
                {item.content}
              </p>
            ))
          ) : (
            <p className={classes.sectionContent}>
              Throughout my career, I've specialized in developing software systems that seamlessly 
              integrate front-end experiences with robust back-end architectures. My experience spans 
              various domains, from interactive media to data visualization systems.
            </p>
          )}
        </div>

        {/* Projects section */}
        <div 
          ref={projectsRef}
          data-section="projects"
          className={classes.section}
          id="projects"
        >
          {person.projects ? (
            person.projects.map((item, index) => (
              <p key={index} className={classes.sectionContent}>
                {item.content}
              </p>
            ))
          ) : (
            <p className={classes.sectionContent}>
              My personal projects merge the scientific principles of evolution and adaptation with 
              mythological themes of rebirth and renewal. I create 'things' that are both mechanically 
              sound and narratively compelling, allowing users to explore complex themes through 
              immersive experiences.
            </p>
          )}
        </div>

        {/* Dynamic sections from props */}
        {additionalSections.length > 0 && (
          additionalSections.map((section, index) => (
            <div key={index} className={classes.section}>
              {section.content && (
                typeof section.content === 'string' 
                  ? <p className={classes.sectionContent}>{section.content}</p>
                  : section.content
              )}
            </div>
          ))
        )}
      </div>
    </motion.div>
  );
};

export default PersonProfileCard;
