import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';

const HeaderContainer = styled.header`
  padding: 1.5rem;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const Logo = styled.div`
  font-weight: bold;
  font-size: 1.5rem;
`;

const Nav = styled.nav`
  display: flex;
  gap: 2rem;
`;

const NavLink = styled(Link)`
  text-decoration: none;
  position: relative;

  &:after {
    content: '';
    position: absolute;
    bottom: -4px;
    left: 0;
    width: 0;
    height: 2px;
    background-color: currentColor;
    transition: width 0.3s ease;
  }

  &:hover:after, &.active:after {
    width: 100%;
  }
`;

const Header = () => {
  return (
    <HeaderContainer>
      <Logo>Rosalia Fialkova</Logo>
      <Nav>
        <NavLink to="/" className={window.location.pathname === '/' ? 'active' : ''}>
          Creator
        </NavLink>
        <NavLink to="/cycles" className={window.location.pathname.includes('/cycles') ? 'active' : ''}>
          Cycles
        </NavLink>
        <NavLink to="/characters" className={window.location.pathname.includes('/characters') ? 'active' : ''}>
          Characters
        </NavLink>
        <NavLink to="/codex" className={window.location.pathname.includes('/codex') ? 'active' : ''}>
          Codex
        </NavLink>
      </Nav>
    </HeaderContainer>
  );
};

export default Header;
