import React, { useState, useEffect, useRef } from 'react';

const RefinedLandingPage = () => {
  const [activeSection, setActiveSection] = useState(null);
  const [cursorPosition, setCursorPosition] = useState({ x: 0, y: 0 });
  const [isLoaded, setIsLoaded] = useState(false);
  const [introState, setIntroState] = useState('initial'); // 'initial', 'quote', 'transition', 'complete'
  const [currentQuote, setCurrentQuote] = useState(null);

  const canvasRef = useRef(null);
  const moonCanvasRef = useRef(null);
  const footerMoonRef = useRef(null);
  const particlesRef = useRef([]);
  const requestRef = useRef(null);
  const animationPhaseRef = useRef(0);

  // Collection of poetic quotes by women
  const quotes = [
    {
      text: "My candle burns at both ends;\nIt will not last the night;\nBut, ah my foes, and\noh my friend —\nit gives a\nlovely\nlight",
      author: "Edna St. Vincent Millay"
    },
    {
      text: "Out of the ash\nI rise with my red hair\nAnd I eat men like air.",
      author: "Sylvia Plath"
    },
    {
      text: "And he will come this, light insouciant lover,\nblowing his bugles to a rising morrow,\nto rend her veil, her wizened mind uncover,\nto leave her conquered by life's stinging sorrow",
      author: "Virginia McCormick"
    },
    {
      text: "Hope is the thing with feathers\nThat perches in the soul,\nAnd sings the tune without the words,\nAnd never stops at all.",
      author: "Emily Dickinson"
    },
    {
      text: "I have gone out, a possessed witch,\nhaunting the black air, braver at night;\ndreaming evil, I have done my hitch\nover the plain houses, light by light.",
      author: "Anne Sexton"
    }
  ];

  const sections = [
    {
      id: "creator",
      title: "Creator",
      description: "The mind behind the mysteries, shaping worlds from shadow and light.",
      icon: "◉", // Circle symbol
      path: "/creator"
    },
    {
      id: "creativity",
      title: "Creativity",
      description: "Visualizations of complex systems, revealing patterns beneath the chaos.",
      icon: "△", // Triangle symbol
      path: "/creativity"
    },
    {
      id: "cycles",
      title: "Cycles",
      description: "An intricate framework modeling the evolution of worlds through endless creation and destruction.",
      icon: "⌁", // Lightning symbol
      path: "/cycles"
    },
    {
      id: "codex",
      title: "Codex",
      description: "Repository of arcane knowledge, rules, and lore from across the multiverse.",
      icon: "⊕", // Circled plus symbol
      path: "/codex"
    },
    {
      id: "characters",
      title: "Characters",
      description: "Beings shaped by their environments, each with a unique story to tell.",
      icon: "✧", // Star symbol
      path: "/characters"
    }
  ];

  // Initialize and set up intro sequence
  useEffect(() => {
    // Select a random quote
    const randomQuote = quotes[Math.floor(Math.random() * quotes.length)];
    setCurrentQuote(randomQuote);

    // Initialize particles
    particlesRef.current = Array(40).fill().map(() => ({
      x: Math.random() * window.innerWidth,
      y: Math.random() * window.innerHeight,
      size: Math.random() * 2 + 1,
      speedX: (Math.random() - 0.5) * 0.2,
      speedY: (Math.random() - 0.5) * 0.2,
      opacity: Math.random() * 0.4 + 0.1
    }));

    // Start sequence and timing
    const timing = {
      quote: 500,        // Quote appears
      transition: 3000,  // Start transition
      complete: 4000     // Complete transition
    };

    const timers = [];
    timers.push(setTimeout(() => setIntroState('quote'), timing.quote));
    timers.push(setTimeout(() => setIntroState('transition'), timing.transition));
    timers.push(setTimeout(() => {
      setIntroState('complete');
      setIsLoaded(true);
    }, timing.complete));

    // Cleanup timers
    return () => {
      timers.forEach(timer => clearTimeout(timer));
      if (requestRef.current) {
        cancelAnimationFrame(requestRef.current);
      }
    };
  }, []);

  // Refined vector moon animation
  useEffect(() => {
    if (!moonCanvasRef.current || introState === 'complete') return;

    const canvas = moonCanvasRef.current;
    const ctx = canvas.getContext('2d');

    let startTime;
    const totalDuration = 4000; // 4 seconds total animation

    // Animation function
    const animate = (timestamp) => {
      if (!startTime) startTime = timestamp;
      const elapsed = timestamp - startTime;
      const progress = Math.min(elapsed / totalDuration, 1);

      // Clear canvas
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      const centerX = canvas.width / 2;
      const centerY = canvas.height / 2;
      const radius = 40;

      // Drawing phases
      const phase1Duration = 0.3; // Line forming into circle (0-30%)
      const phase2Duration = 0.2; // Circle complete (30-50%)
      const phase3Duration = 0.3; // Details appear (50-80%)
      const phase4Duration = 0.2; // Fade out (80-100%)

      if (progress < phase1Duration) {
        // Phase 1: Arc draws to form circle
        const arcProgress = progress / phase1Duration;
        ctx.beginPath();
        ctx.arc(centerX, centerY, radius, 0, Math.PI * 2 * arcProgress);
        ctx.strokeStyle = '#bfad7f';
        ctx.lineWidth = 2;
        ctx.stroke();
      } 
      else if (progress < phase1Duration + phase2Duration) {
        // Phase 2: Complete circle with glow
        const phase2Progress = (progress - phase1Duration) / phase2Duration;

        // Draw circle
        ctx.beginPath();
        ctx.arc(centerX, centerY, radius, 0, Math.PI * 2);
        ctx.strokeStyle = '#bfad7f';
        ctx.lineWidth = 2;
        ctx.stroke();

        // Add subtle glow
        const glowRadius = radius + 10 * phase2Progress;
        const gradient = ctx.createRadialGradient(
          centerX, centerY, radius,
          centerX, centerY, glowRadius
        );
        gradient.addColorStop(0, 'rgba(191, 173, 127, 0.3)');
        gradient.addColorStop(1, 'rgba(191, 173, 127, 0)');

        ctx.beginPath();
        ctx.arc(centerX, centerY, glowRadius, 0, Math.PI * 2);
        ctx.fillStyle = gradient;
        ctx.fill();
      } 
      else if (progress < phase1Duration + phase2Duration + phase3Duration) {
        // Phase 3: Draw interior details
        const phase3Progress = (progress - phase1Duration - phase2Duration) / phase3Duration;

        // Draw circle and glow
        ctx.beginPath();
        ctx.arc(centerX, centerY, radius, 0, Math.PI * 2);
        ctx.strokeStyle = '#bfad7f';
        ctx.lineWidth = 2;
        ctx.stroke();

        const glowRadius = radius + 10;
        const gradient = ctx.createRadialGradient(
          centerX, centerY, radius,
          centerX, centerY, glowRadius
        );
        gradient.addColorStop(0, 'rgba(191, 173, 127, 0.3)');
        gradient.addColorStop(1, 'rgba(191, 173, 127, 0)');

        ctx.beginPath();
        ctx.arc(centerX, centerY, glowRadius, 0, Math.PI * 2);
        ctx.fillStyle = gradient;
        ctx.fill();

        // Draw interior details - subtle curves representing moon's surface
        const detailOpacity = phase3Progress * 0.6;

        // Crescent shadow
        ctx.beginPath();
        ctx.arc(centerX + radius * 0.5, centerY, radius * 0.8, -Math.PI * 0.7, Math.PI * 0.7, true);
        ctx.strokeStyle = `rgba(134, 120, 87, ${detailOpacity})`;
        ctx.lineWidth = 1;
        ctx.stroke();

        // Surface lines
        const lineCount = 3;
        for (let i = 0; i < lineCount; i++) {
          const lineProgress = Math.min(1, phase3Progress * 3 - i * 0.3);
          if (lineProgress <= 0) continue;

          const y = centerY - radius * 0.5 + radius * i / (lineCount - 1);
          const amplitude = radius * 0.1;
          const frequency = Math.PI / radius;

          ctx.beginPath();
          ctx.moveTo(centerX - radius + 5, y);

          for (let x = -radius + 5; x <= radius - 5; x += 2) {
            const relativeX = x / radius;
            const waveY = Math.sin(relativeX * 5) * amplitude;
            ctx.lineTo(centerX + x, y + waveY);
          }

          ctx.strokeStyle = `rgba(134, 120, 87, ${lineProgress * detailOpacity})`;
          ctx.lineWidth = 0.5;
          ctx.stroke();
        }
      } 
      else {
        // Phase 4: Fade out
        const phase4Progress = (progress - phase1Duration - phase2Duration - phase3Duration) / phase4Duration;
        const fadeOpacity = 1 - phase4Progress;

        // Draw everything with fading opacity
        ctx.globalAlpha = fadeOpacity;

        // Draw circle
        ctx.beginPath();
        ctx.arc(centerX, centerY, radius, 0, Math.PI * 2);
        ctx.strokeStyle = '#bfad7f';
        ctx.lineWidth = 2;
        ctx.stroke();

        // Draw glow
        const glowRadius = radius + 10 + phase4Progress * 20; // Expanding glow as it fades
        const gradient = ctx.createRadialGradient(
          centerX, centerY, radius,
          centerX, centerY, glowRadius
        );
        gradient.addColorStop(0, 'rgba(191, 173, 127, 0.3)');
        gradient.addColorStop(1, 'rgba(191, 173, 127, 0)');

        ctx.beginPath();
        ctx.arc(centerX, centerY, glowRadius, 0, Math.PI * 2);
        ctx.fillStyle = gradient;
        ctx.fill();

        // Restore global alpha
        ctx.globalAlpha = 1;
      }

      // Continue animation if not complete
      if (introState !== 'complete' && progress < 1) {
        requestRef.current = requestAnimationFrame(animate);
      }
    };

    // Start animation
    requestRef.current = requestAnimationFrame(animate);

    return () => {
      if (requestRef.current) {
        cancelAnimationFrame(requestRef.current);
      }
    };
  }, [introState]);

  // Background canvas animation
  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Draw particles
      particlesRef.current.forEach(particle => {
        // Update position
        particle.x += particle.speedX;
        particle.y += particle.speedY;

        // Wrap around screen
        if (particle.x < 0) particle.x = canvas.width;
        if (particle.x > canvas.width) particle.x = 0;
        if (particle.y < 0) particle.y = canvas.height;
        if (particle.y > canvas.height) particle.y = 0;

        // Draw particle
        ctx.beginPath();
        ctx.arc(particle.x, particle.y, particle.size, 0, Math.PI * 2);
        ctx.fillStyle = `rgba(191, 173, 127, ${particle.opacity})`;
        ctx.fill();
      });

      // Draw cursor influence - very subtle
      if (cursorPosition.x && cursorPosition.y) {
        const gradient = ctx.createRadialGradient(
          cursorPosition.x, cursorPosition.y, 0,
          cursorPosition.x, cursorPosition.y, 80
        );
        gradient.addColorStop(0, 'rgba(160, 142, 97, 0.03)');
        gradient.addColorStop(1, 'rgba(160, 142, 97, 0)');

        ctx.beginPath();
        ctx.arc(cursorPosition.x, cursorPosition.y, 80, 0, Math.PI * 2);
        ctx.fillStyle = gradient;
        ctx.fill();
      }

      requestRef.current = requestAnimationFrame(animate);
    };

    animate();

    // Resize handler
    const handleResize = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };

    window.addEventListener('resize', handleResize);
    return () => {
      window.removeEventListener('resize', handleResize);
      cancelAnimationFrame(requestRef.current);
    };
  }, [cursorPosition]);

  // Footer moon animation
  useEffect(() => {
    if (!footerMoonRef.current) return;

    const canvas = footerMoonRef.current;
    const ctx = canvas.getContext('2d');

    const drawMoon = () => {
      canvas.width = 300;
      canvas.height = 300;

      const centerX = 150;
      const centerY = 150;
      const radius = 65;

      // Clear canvas
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Create glow
      const outerGlow = ctx.createRadialGradient(
        centerX, centerY, radius,
        centerX, centerY, radius * 2
      );
      outerGlow.addColorStop(0, 'rgba(191, 173, 127, 0.2)');
      outerGlow.addColorStop(0.5, 'rgba(191, 173, 127, 0.05)');
      outerGlow.addColorStop(1, 'rgba(191, 173, 127, 0)');

      ctx.beginPath();
      ctx.arc(centerX, centerY, radius * 2, 0, Math.PI * 2);
      ctx.fillStyle = outerGlow;
      ctx.fill();

      // Draw moon base
      const moonGradient = ctx.createRadialGradient(
        centerX - radius * 0.2, centerY - radius * 0.2, 0,
        centerX, centerY, radius
      );
      moonGradient.addColorStop(0, '#d0c293');
      moonGradient.addColorStop(0.8, '#a08e61');
      moonGradient.addColorStop(1, '#8a7952');

      ctx.beginPath();
      ctx.arc(centerX, centerY, radius, 0, Math.PI * 2);
      ctx.fillStyle = moonGradient;
      ctx.fill();

      // Draw subtle texture
      ctx.globalAlpha = 0.15;
      ctx.beginPath();

      // Draw several arcs for surface features
      const features = [
        { x: centerX - radius * 0.3, y: centerY - radius * 0.2, r: radius * 0.6, start: 0.8, end: 1.8 },
        { x: centerX + radius * 0.4, y: centerY + radius * 0.1, r: radius * 0.5, start: 3.8, end: 5.2 },
        { x: centerX - radius * 0.1, y: centerY + radius * 0.4, r: radius * 0.4, start: 2.4, end: 3.6 }
      ];

      features.forEach(f => {
        ctx.beginPath();
        ctx.arc(f.x, f.y, f.r, f.start, f.end);
        ctx.strokeStyle = '#786b49';
        ctx.lineWidth = 0.8;
        ctx.stroke();
      });

      // Restore alpha
      ctx.globalAlpha = 1;

      // Draw subtle rim highlight
      ctx.beginPath();
      ctx.arc(centerX, centerY, radius, 0, Math.PI * 2);
      ctx.strokeStyle = 'rgba(224, 224, 224, 0.15)';
      ctx.lineWidth = 0.5;
      ctx.stroke();
    };

    drawMoon();

    window.addEventListener('resize', drawMoon);
    return () => {
      window.removeEventListener('resize', drawMoon);
    };
  }, [isLoaded]);

  // Track mouse movement
  const handleMouseMove = (e) => {
    setCursorPosition({ x: e.clientX, y: e.clientY });
  };

  // Skip intro
  const handleSkipIntro = () => {
    if (introState !== 'complete') {
      setIntroState('complete');
      setIsLoaded(true);
    }
  };

  // Navigate to section
  const handleNavigate = (path) => {
    // For demo purposes, just log the navigation
    console.log(`Navigating to: ${path}`);
    // In a real app, you'd use your routing library:
    // window.location.href = path; or history.push(path);
  };

  // Format quote with line breaks
  const formatQuote = (text) => {
    return text.split('\n').map((line, i) => (
      <span key={i} className="quote-line">
        {line}
        {i < text.split('\n').length - 1 && <br />}
      </span>
    ));
  };

  return (
    <div 
      className="landing-container"
      onMouseMove={handleMouseMove}
      onClick={introState !== 'complete' ? handleSkipIntro : undefined}
    >
      <canvas ref={canvasRef} className="background-canvas" />

      {/* Intro Sequence */}
      {introState !== 'complete' && (
        <div className={`intro-sequence ${introState === 'transition' ? 'fading' : ''}`}>
          <div className="moon-container">
            <canvas 
              ref={moonCanvasRef} 
              width="120" 
              height="120" 
              className="moon-canvas"
            />
          </div>

          <div className={`quote-container ${introState === 'quote' || introState === 'transition' ? 'visible' : ''}`}>
            <div className="quote-text">
              {currentQuote && formatQuote(currentQuote.text)}
            </div>
            <div className="quote-author">
              {currentQuote && currentQuote.author}
            </div>
          </div>

          <div className="skip-intro">Tap to continue</div>
        </div>
      )}

      <div className={`content-wrapper ${isLoaded ? 'loaded' : ''}`}>
        <div className="content-container">
          <div className="title-container">
            <h1 className="main-title">CRESCENT</h1>
            <div className="title-divider" />
            <p className="subtitle">A Dark Fantasy Experience</p>
          </div>

          <div className="sections-container">
            {sections.map((section, index) => (
              <div 
                key={section.id}
                className={`section-row ${activeSection === section.id ? 'active' : ''}`}
                onMouseEnter={() => setActiveSection(section.id)}
                onMouseLeave={() => setActiveSection(null)}
                onClick={() => handleNavigate(section.path)}
                style={{ animationDelay: `${index * 0.12 + 0.2}s` }}
              >
                <div className="section-content">
                  <div className="section-icon-wrapper">
                    <div className="section-icon">{section.icon}</div>
                  </div>
                  <div className="section-text">
                    <h2 className="section-title">{section.title}</h2>
