import React, { useState, useEffect, useRef } from 'react';
import { motion, useScroll, useTransform, useSpring } from 'framer-motion';
import './CreatorsPage.css';

// Import the meteor animation component
import CreatorsMeteorField from './CreatorsMeteorField';

const CreatorsPage = () => {
  // State management
  const [activeCreator, setActiveCreator] = useState(null);
  const [cursorPosition, setCursorPosition] = useState({ x: 0, y: 0 });
  const [isLoaded, setIsLoaded] = useState(false);
  const [uploadedImage, setUploadedImage] = useState(null);
  const [imageOpacity, setImageOpacity] = useState(0.7);

  // Refs for scroll animations and parallax
  const pageRef = useRef(null);
  const titleRef = useRef(null);
  const meteorFieldRef = useRef(null);
  const creatorsSectionRef = useRef(null);
  const inspirationSectionRef = useRef(null);

  // Scroll animation values
  const { scrollYProgress } = useScroll({
    target: pageRef,
    offset: ["start start", "end end"]
  });

  // Smooth scrollY progress for smoother animations
  const smoothScrollProgress = useSpring(scrollYProgress, {
    stiffness: 100,
    damping: 30,
    restDelta: 0.001
  });

  // Parallax transforms for different elements
  const titleParallax = useTransform(smoothScrollProgress, [0, 0.2], [0, -100]);
  const meteorFieldParallax = useTransform(smoothScrollProgress, [0, 0.5], [0, -150]);
  const creatorsOpacity = useTransform(smoothScrollProgress, [0.1, 0.2, 0.8, 0.9], [0, 1, 1, 0]);
  const inspirationOpacity = useTransform(smoothScrollProgress, [0.7, 0.8, 0.9, 1], [0, 1, 1, 0]);

  // Mouse movement tracking for interactive effects
  const handleMouseMove = (e) => {
    setCursorPosition({ x: e.clientX, y: e.clientY });
  };

  // Image upload handler
  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file && file.type.match('image.*')) {
      const reader = new FileReader();
      reader.onload = (event) => {
        setUploadedImage(event.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  // Opacity adjustment handler
  const handleOpacityChange = (e) => {
    setImageOpacity(parseFloat(e.target.value));
  };

  // Featured first creator (the user)
  const featuredCreator = {
    id: "featured",
    name: "Your Name",
    role: "Lead Designer & Visionary",
    bio: "The creative force behind Crescent, bringing together a unique blend of narrative design and mechanical innovation. Your vision has shaped this world of fading moonlight into a compelling journey that challenges players to confront their deepest motivations.",
    image: uploadedImage || "/images/creators/featured.jpg"
  };

  // Other creators
  const creators = [
    {
      id: "rosalia",
      name: "Rosalia Valeria Fialkova",
      role: "Game Design, Writing, Layout, and Graphic Design",
      bio: "Rosalia's visionary approach to game design merges traditional tabletop mechanics with innovative narrative structures. Her work on Crescent brings together years of experience in world-building and system design.",
      image: "/images/creators/rosalia.jpg"
    },
    {
      id: "daniel",
      name: "Daniel Fialkov",
      role: "Game Design, Inspiration, Writing, and Editing",
      bio: "Daniel's contributions to game theory and mechanics development have shaped numerous systems. For Crescent, he focused on creating balanced yet emotionally resonant gameplay experiences.",
      image: "/images/creators/daniel.jpg"
    },
    {
      id: "patrick",
      name: "Patrick Conner Purciful",
      role: "Developmental and Line Editing",
      bio: "Patrick's keen editorial insight has refined the game's systems and narrative voice. His meticulous attention to detail ensures consistency and clarity throughout the rules.",
      image: "/images/creators/patrick.jpg"
    },
    {
      id: "samantha",
      name: "Samantha Keeler",
      role: "Graphic Design Consultant",
      bio: "Samantha's artistic vision brings visual cohesion to Crescent's complex world. Her design philosophy emphasizes atmosphere and immersion through careful visual storytelling.",
      image: "/images/creators/samantha.jpg"
    }
  ];

  // Text animation variants for "Visionaries" text
  const titleTextVariants = {
    hidden: {
      opacity: 0
    },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const letterVariants = {
    hidden: { 
      opacity: 0,
      y: 20,
      rotateX: 90
    },
    visible: { 
      opacity: 1,
      y: 0,
      rotateX: 0,
      transition: {
        type: "spring",
        stiffness: 100,
        damping: 10
      }
    }
  };

  // Split title text for letter-by-letter animation
  const titleText = "VISIONARIES";
  const titleLetters = titleText.split("");

  // Card animation variants
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: { 
      opacity: 1,
      transition: {
        staggerChildren: 0.15,
        delayChildren: 0.3
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 50 },
    visible: { 
      opacity: 1, 
      y: 0,
      transition: { 
        type: "spring",
        stiffness: 100,
        damping: 15
      }
    }
  };

  // Initialize and set loaded state
  useEffect(() => {
    const timer = setTimeout(() => {
      setIsLoaded(true);
    }, 500);

    return () => clearTimeout(timer);
  }, []);

  return (
    <div 
      className="creators-page"
      onMouseMove={handleMouseMove}
      ref={pageRef}
    >
      {/* Background meteor field with custom parallax */}
      <motion.div 
        className="meteor-field-container"
        style={{ y: meteorFieldParallax }}
        ref={meteorFieldRef}
      >
        <CreatorsMeteorField 
          cursorPosition={cursorPosition}
          width={window.innerWidth}
          height={window.innerHeight * 1.5}
          uploadedImage={uploadedImage}
          imageOpacity={imageOpacity}
        />

        {/* Animated title overlay on meteor field */}
        <motion.div 
          className="title-overlay"
          style={{ y: titleParallax }}
          ref={titleRef}
          initial="hidden"
          animate={isLoaded ? "visible" : "hidden"}
          variants={titleTextVariants}
        >
          {titleLetters.map((letter, index) => (
            <motion.span
              key={index}
              className="title-letter"
              variants={letterVariants}
            >
              {letter}
            </motion.span>
          ))}
        </motion.div>

        {/* Image upload controls */}
        <div className="image-controls">
          <label htmlFor="image-upload" className="upload-button">
            Upload Background Image
          </label>
          <input 
            id="image-upload" 
            type="file" 
            accept="image/*" 
            onChange={handleImageUpload} 
            style={{ display: 'none' }}
          />

          {uploadedImage && (
            <div className="opacity-control">
              <label htmlFor="opacity-slider">Image Opacity: {imageOpacity}</label>
              <input
                id="opacity-slider"
                type="range"
                min="0"
                max="1"
                step="0.05"
                value={imageOpacity}
                onChange={handleOpacityChange}
              />
            </div>
          )}
        </div>
      </motion.div>

      {/* Scrollable content with parallax effects */}
      <div className="scrollable-content">
        {/* Featured Creator Section */}
        <motion.div 
          className="featured-creator-section"
          style={{ opacity: creatorsOpacity }}
          ref={creatorsSectionRef}
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 1, delay: 1 }}
        >
          <div className="section-header">
            <h2 className="section-title">The Visionary</h2>
            <div className="title-divider" />
          </div>

          <div className="featured-creator-container">
            <div className="featured-creator-portrait">
              <div 
                className="portrait-image"
                style={{ 
                  backgroundImage: featuredCreator.image ? `url(${featuredCreator.image})` : 'none',
                  opacity: imageOpacity
                }}
              />
              <div className="portrait-frame" />
              <div className="portrait-glow" />
            </div>

            <div className="featured-creator-info">
              <h1 className="featured-creator-name">{featuredCreator.name}</h1>
              <h3 className="featured-creator-role">{featuredCreator.role}</h3>
              <p className="featured-creator-bio">{featuredCreator.bio}</p>
            </div>
          </div>
        </motion.div>

        {/* Team Section with Staggered Cards */}
        <motion.div 
          className="team-section"
          style={{ opacity: creatorsOpacity }}
          variants={containerVariants}
          initial="hidden"
          animate={isLoaded ? "visible" : "hidden"}
        >
          <div className="section-header">
            <h2 className="section-title">The Team</h2>
            <div className="title-divider" />
          </div>

          <div className="creators-grid">
            {creators.map((creator, index) => (
              <motion.div 
                key={creator.id}
                className={`creator-card ${activeCreator === creator.id ? 'active' : ''}`}
                variants={itemVariants}
                onMouseEnter={() => setActiveCreator(creator.id)}
                onMouseLeave={() => setActiveCreator(null)}
              >
                <div className="creator-portrait">
                  <div className="portrait-container">
                    <div 
                      className="portrait-placeholder"
                      style={{ 
                        backgroundImage: creator.image ? `url(${creator.image})` : 'none'
                      }}
                    />
                  </div>
                  <div className="portrait-frame" />
                </div>
                <div className="creator-info">
                  <h2 className="creator-name">{creator.name}</h2>
                  <h3 className="creator-role">{creator.role}</h3>
                  <p className="creator-bio">{creator.bio}</p>
                </div>
                <div className="diagonal-cut" />
              </motion.div>
            ))}
          </div>
        </motion.div>

        {/* Inspiration Section */}
        <motion.div 
          className="inspiration-section"
          style={{ opacity: inspirationOpacity }}
          ref={inspirationSectionRef}
        >
          <div className="section-header">
            <h2 className="section-title">Inspirations</h2>
            <div className="title-divider" />
          </div>

          <div className="inspiration-categories">
            <div className="inspiration-category">
              <h3>Literary & Media</h3>
              <ul className="inspiration-list">
                <li>Berserk by Kentaro Miura</li>
                <li>Made in Abyss by Akihito Tsukushi</li>
                <li>Bloodborne by FromSoft</li>
                <li>Scavenger's Reign by Joseph Bennett & Christopher Huettner</li>
              </ul>
            </div>
            <div className="inspiration-category">
              <h3>Tabletop RPG</h3>
              <ul className="inspiration-list">
                <li>Blades in the Dark by John Harper</li>
                <li>Mouse Guard by Luke Crane</li>
                <li>Ironsworn by Shaun Tomkin</li>
                <li>Heart by Grant Howitt & Christopher Taylor</li>
              </ul>
            </div>
          </div>
        </motion.div>

        {/* Credits */}
        <motion.div 
          className="credits-section"
          style={{ opacity: inspirationOpacity }}
        >
          <p className="image-credits">
            Artwork utilizes elements from Shutterstock.com and Adobe Stock images.
          </p>
        </motion.div>
      </div>
    </div>
  );
};

export default CreatorsPage;
