// Optimized transform template for GPU rendering
const optimizedTransformTemplate = useCallback((_, transform) => {
  return `${transform} translateZ(0)`;
}, []);import React, { useRef, useState, useEffect, useCallback, useMemo } from 'react';
import { 
motion, 
useInView, 
useAnimation, 
useScroll, 
useTransform, 
useSpring, 
useReducedMotion 
} from 'framer-motion';

/**
* PersonProfileCard Component
* 
* Enhanced with smooth scrolling animations for the navbar
* Integrates performance-optimized animations with three-phase scroll behavior
*/
const PersonProfileCard = ({
person = {}, // Provide default empty object
animationConfig = {
  threshold: 0.2,
  once: true,
  initialY: 30,
  duration: 0.8
},
additionalSections = [],
onSectionChange = null,
topOffset = 100,
showStats = true,
highlightColor = '#bfad7f',
textColor = 'rgba(224, 224, 224, 0.7)',
minLineWidth = 10,
maxLineWidth = 40,
fontFamily = '"Garamond", "Adobe Caslon Pro", serif',
navigationItems = [
  { id: 'about', label: 'ABOUT', content: null },
  { id: 'experience', label: 'EXPERIENCE', content: null },
  { id: 'projects', label: 'PROJECTS', content: null }
],
contentCompression = 0,
navAnimationConfig = {
  staggerDelay: 0.05,         // Delay between each nav item animation
  startPosition: 0,           // Scroll position to start animation
  endPosition: 300,           // Scroll position to end animation
  initialY: 20,               // Initial Y offset
  finalY: 0,                  // Final Y position
  initialOpacity: 0.6,        // Initial opacity
  finalOpacity: 1,            // Final opacity
  maxVelocity: 2000,          // Maximum velocity cap (pixels per second)
  reverseDirection: false,    // Whether to reverse the animation direction
  clampValues: true,          // Whether to clamp animation values
  springConfig: {             // Spring physics configuration for smooth motion
    stiffness: 50,            // Lower stiffness for smoother motion
    damping: 25,              // Balanced damping 
    mass: 0.5,                // Slightly higher mass for momentum
    restDelta: 0.001,         // Higher precision for position
    restSpeed: 0.001,         // Higher precision for velocity
  },
  disableOnMobile: false      // Whether to disable on mobile devices
}
}) => {
// =====================================================
// STATE AND REFS
// =====================================================
const [activeSection, setActiveSection] = useState(navigationItems[0]?.id || 'about');
const [expandedNavItem, setExpandedNavItem] = useState(null);
const [sidebarMode, setSidebarMode] = useState('normal'); // 'normal', 'fixed', or 'end'
const [isInitialized, setIsInitialized] = useState(false);
const [navItemsVisible, setNavItemsVisible] = useState(false);

// Calculate widths based on contentCompression (0-10 scale)
const compressionFactor = Math.min(Math.max(contentCompression, 0), 10) / 10;
const sidebarWidth = 45 - (compressionFactor * 5); // 35-40% range
const contentWidth = 55 - (compressionFactor * 5); // 55-60% range
const contentPadding = 2 + (compressionFactor * 2); // 2-4rem range

// Refs for DOM elements
const containerRef = useRef(null);
const sidebarRef = useRef(null);
const contentRef = useRef(null);
const sidebarWrapperRef = useRef(null);
const navRef = useRef(null);

// Animation controls
const controls = useAnimation();

// Check for reduced motion preference
const prefersReducedMotion = useReducedMotion();

// Detect if on mobile device (using ref to avoid rerenders)
const isMobileRef = useRef(false);

// =====================================================
// SECTION REFS MANAGEMENT
// =====================================================
// Create individual section refs at the top level
const section0Ref = useRef(null);
const section1Ref = useRef(null);
const section2Ref = useRef(null);
const section3Ref = useRef(null);
const section4Ref = useRef(null);
const section5Ref = useRef(null);
const section6Ref = useRef(null);
const section7Ref = useRef(null);

// Memoize the array of refs so it's stable across renders
const sectionRefs = useMemo(() => [
  section0Ref, section1Ref, section2Ref, section3Ref,
  section4Ref, section5Ref, section6Ref, section7Ref
], []);

// Map section IDs to refs
const sectionRefsMap = useMemo(() => {
  const refsMap = {};
  navigationItems.forEach((item, index) => {
    if (index < sectionRefs.length) {
      refsMap[item.id] = sectionRefs[index];
    }
  });
  return refsMap;
}, [navigationItems, sectionRefs]);

// =====================================================
// ANIMATION AND SCROLL CONFIGURATION
// =====================================================
// Animation inView detection
const isInView = useInView(containerRef, {
  threshold: animationConfig?.threshold ?? 0.2,
  once: animationConfig?.once ?? true
});

// Navigation menu in-view detection with larger margin
const isNavInView = useInView(navRef, {
  threshold: 0.1,
  rootMargin: "100px 0px",
  once: false
});

// Scroll position tracking with smooth scrolling
const { scrollY } = useScroll({
  smooth: 0.05  // Smoother scrolling for high-framerate animations
});

// Create transformed Y values for nav items with direction control
const navTranslateY = useTransform(
  scrollY,
  [navAnimationConfig.startPosition, navAnimationConfig.endPosition],
  navAnimationConfig.reverseDirection ? 
    [navAnimationConfig.finalY, navAnimationConfig.initialY] : 
    [navAnimationConfig.initialY, navAnimationConfig.finalY],
  { clamp: navAnimationConfig.clampValues }
);

// Apply spring physics for smoother motion with velocity limiting
const smoothNavY = useSpring(navTranslateY, {
  ...navAnimationConfig.springConfig,
  // Add velocity limiter function to cap maximum speed
  velocity: current => Math.min(Math.max(current, -navAnimationConfig.maxVelocity), navAnimationConfig.maxVelocity)
});

// Create opacity transform based on scroll with enhanced control
const navOpacity = useTransform(
  scrollY,
  [navAnimationConfig.startPosition, navAnimationConfig.endPosition],
  [navAnimationConfig.initialOpacity, navAnimationConfig.finalOpacity],
  { clamp: navAnimationConfig.clampValues }
);

// Apply spring physics to opacity with same configuration for consistency
const smoothNavOpacity = useSpring(navOpacity, {
  ...navAnimationConfig.springConfig,
  // Add velocity limiter for opacity changes too
  velocity: current => Math.min(Math.max(current, -navAnimationConfig.maxVelocity), navAnimationConfig.maxVelocity)
});

// Optimized transform template for GPU rendering
const optimizedTransformTemplate = useCallback((_, transform) => {
  return `${transform} translateZ(0)`;
}, []);

// =====================================================
// HANDLERS AND CALLBACKS
// =====================================================
// Navigation item hover handlers
const handleNavItemHover = useCallback((section) => {
  setExpandedNavItem(section);
}, []);

const handleNavItemLeave = useCallback(() => {
  setExpandedNavItem(null);
}, []);

// Scroll to section handler
const scrollToSection = useCallback((sectionId) => {
  setActiveSection(sectionId);

  if (onSectionChange) {
    onSectionChange(sectionId);
  }

  const sectionRef = sectionRefsMap[sectionId];
  if (sectionRef?.current) {
    sectionRef.current.scrollIntoView({ 
      behavior: 'smooth',
      block: 'start'
    });
  }
}, [onSectionChange, sectionRefsMap]);

// =====================================================
// EFFECTS
// =====================================================
// Initialize and update mobile detection
useEffect(() => {
  if (typeof window === 'undefined') return;

  const checkMobile = () => {
    isMobileRef.current = 
      window.innerWidth <= 768 || 
      /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent);
  };

  checkMobile();

  const handleResize = () => {
    checkMobile();
  };

  window.addEventListener('resize', handleResize);

  return () => {
    window.removeEventListener('resize', handleResize);
  };
}, []);

// Initial animation
useEffect(() => {
  if (isInView) {
    controls.start({ 
      opacity: 1, 
      y: 0,
      transition: { 
        duration: animationConfig?.duration ?? 0.8,
        ease: "easeOut",
        delay: 0.1 // Small delay to ensure DOM is settled
      }
    });

    // Mark as initialized after animation completes
    const timeout = setTimeout(() => {
      setIsInitialized(true);
    }, (animationConfig?.duration ?? 0.8) * 1000 + 100);

    return () => clearTimeout(timeout);
  }
}, [isInView, controls, animationConfig?.duration]);

// Navigation menu animation
useEffect(() => {
  if (isNavInView && isInitialized) {
    setNavItemsVisible(true);
  }
}, [isNavInView, isInitialized]);

// Set up intersection observer to detect which section is in view
useEffect(() => {
  const sectionElements = Object.values(sectionRefsMap)
    .map(ref => ref.current)
    .filter(Boolean);

  if (sectionElements.length === 0) return;

  const options = {
    root: null,
    rootMargin: '-10% 0px -70% 0px', // Consider element in view when in the top 30% of viewport
    threshold: 0
  };

  const callback = (entries) => {
    entries.forEach(entry => {
      if (entry.isIntersecting) {
        const sectionId = entry.target.getAttribute('data-section');
        if (sectionId && sectionId !== activeSection) {
          setActiveSection(sectionId);

          if (onSectionChange) {
            onSectionChange(sectionId);
          }
        }
      }
    });
  };

  const observer = new IntersectionObserver(callback, options);
  sectionElements.forEach(element => observer.observe(element));

  return () => observer.disconnect();
}, [activeSection, onSectionChange, sectionRefsMap]);

// Implement scroll behavior
useEffect(() => {
  if (!isInitialized || !containerRef.current || !sidebarRef.current || !sidebarWrapperRef.current) {
    return;
  }

  // Skip on mobile
  const isMobile = window.innerWidth <= 768;
  if (isMobile) {
    return;
  }

  const container = containerRef.current;
  const sidebar = sidebarRef.current;
  const sidebarWrapper = sidebarWrapperRef.current;

  let containerRect;
  let wrapperRect;
  let sidebarHeight;

  // Update measurements
  const updateMeasurements = () => {
    containerRect = container.getBoundingClientRect();
    wrapperRect = sidebarWrapper.getBoundingClientRect();
    sidebarHeight = sidebar.offsetHeight;
  };

  // First, get accurate measurements
  updateMeasurements();

  const handleScroll = () => {
    // Skip on mobile
    if (window.innerWidth <= 768) return;

    // Update measurements if necessary
    updateMeasurements();

    // Calculate phase transition points
    const startFixPoint = containerRect.top <= topOffset;
    const endFixPoint = containerRect.bottom <= (sidebarHeight + topOffset);

    // Determine the current scroll phase
    let newMode;
    if (!startFixPoint) {
      newMode = 'normal';
    } else if (startFixPoint && !endFixPoint) {
      newMode = 'fixed';
    } else {
      newMode = 'end';
    }

    // Only update DOM if the mode changes
    if (sidebarMode !== newMode) {
      setSidebarMode(newMode);

      if (newMode === 'normal') {
        // Phase 1: Normal flow
        sidebar.style.position = 'relative';
        sidebar.style.top = '0';
        sidebar.style.left = '0';
        sidebar.style.width = '';
        sidebar.style.bottom = '';
      } 
      else if (newMode === 'fixed') {
        // Phase 2: Fixed position
        sidebar.style.position = 'fixed';
        sidebar.style.top = `${topOffset}px`;
        sidebar.style.width = `${wrapperRect.width}px`;
        sidebar.style.left = `${wrapperRect.left}px`;
        sidebar.style.bottom = '';
      } 
      else if (newMode === 'end') {
        // Phase 3: End position
        sidebar.style.position = 'absolute';
        sidebar.style.top = 'auto';
        sidebar.style.bottom = '0';
        sidebar.style.left = '0';
        sidebar.style.width = '';
      }
    }
  };

  // Add scroll event listener
  let ticking = false;
  const scrollListener = () => {
    if (!ticking) {
      window.requestAnimationFrame(() => {
        handleScroll();
        ticking = false;
      });
      ticking = true;
    }
  };

  window.addEventListener('scroll', scrollListener, { passive: true });

  // Handle window resize
  const handleResize = () => {
    // Update measurements
    updateMeasurements();

    const isMobile = window.innerWidth <= 768;
    if (isMobile) {
      // Reset styles for mobile
      sidebar.style.position = '';
      sidebar.style.top = '';
      sidebar.style.bottom = '';
      sidebar.style.left = '';
      sidebar.style.width = '';
      setSidebarMode('normal');
    } else {
      // Apply current mode
      handleScroll();
    }
  };

  window.addEventListener('resize', handleResize);

  // Initial setup
  handleResize();
  handleScroll();

  return () => {
    window.removeEventListener('scroll', scrollListener);
    window.removeEventListener('resize', handleResize);
  };
}, [isInitialized, topOffset, sidebarMode]);

// =====================================================
// RENDER PROFILE INFO
// =====================================================
const renderProfileInfo = () => (
  <div 
    className="profile-info"
    style={{
      marginBottom: '2rem',
      paddingLeft: '5px',
    }}
  >
    <h1 
      className="profile-name"
      style={{
        fontSize: '2.8rem',
        fontWeight: '300',
        color: highlightColor,
        letterSpacing: '0.05em',
        marginBottom: '0.5rem',
      }}
    >
      {person?.name || 'Profile Name'}
    </h1>

    <h2 
      className="profile-role"
      style={{
        fontSize: '1.2rem',
        fontWeight: '300',
        marginBottom: '2rem',
        color: `${highlightColor}B3`, // 70% opacity
        letterSpacing: '0.05em',
        fontFamily: fontFamily,
        fontStyle: 'italic',
      }}
    >
      {person?.role || 'Role'}
    </h2>

    {person?.tagline && (
      <p 
        className="profile-tagline"
        style={{
          fontSize: '1.1rem',
          lineHeight: '1.6',
          marginBottom: '3rem',
          maxWidth: '90%',
          color: textColor,
          fontFamily: fontFamily,
          fontWeight: '300',
        }}
      >
        {person.tagline}
      </p>
    )}
  </div>
);

// =====================================================
// RENDER NAVIGATION LINKS
// =====================================================
const renderNavLinks = () => (
  <div 
    ref={navRef}
    className="nav-links" 
    style={{ 
      marginTop: '3rem',
      willChange: 'transform, opacity',
      transform: 'translate3d(0,0,0)',
      backfaceVisibility: 'hidden',
      '-webkit-font-smoothing': 'antialiased',
      perspective: '1000px',
    }}
  >
    {navigationItems.map((navItem, index) => {
      // Check if we should use simplified animations
      const shouldUseSimpleAnimation = prefersReducedMotion || 
        (navAnimationConfig.disableOnMobile && isMobileRef.current);

      // Calculate staggered animation delay
      const animationDelay = navAnimationConfig.staggerDelay * index;

      return (
        <motion.div 
          key={navItem.id}
          className="nav-link-container"
          style={{
            position: 'relative',
            marginBottom: '1rem',
            display: 'flex',
            alignItems: 'center',
            willChange: 'transform, opacity',
            transformOrigin: 'left center',
            transform: 'translate3d(0,0,0)',
            backfaceVisibility: 'hidden',
            '-webkit-font-smoothing': 'antialiased',
          }}
          initial={shouldUseSimpleAnimation ? 
            { opacity: 1, y: 0 } : 
            { opacity: navAnimationConfig.initialOpacity, y: navAnimationConfig.initialY }
          }
          animate={navItemsVisible ? {
            opacity: shouldUseSimpleAnimation ? 1 : smoothNavOpacity,
            y: shouldUseSimpleAnimation ? 0 : smoothNavY,
          } : {
            opacity: navAnimationConfig.initialOpacity,
            y: navAnimationConfig.initialY
          }}
          transition={shouldUseSimpleAnimation ? {
            duration: 0.5,
            delay: animationDelay,
            ease: [0.25, 0.1, 0.25, 1.0]
          } : {
            delay: animationDelay,
          }}
          onMouseEnter={() => handleNavItemHover(navItem.id)}
          onMouseLeave={handleNavItemLeave}
          transformTemplate={optimizedTransformTemplate}
        >
          <div
            className="nav-line"
            style={{
              position: 'absolute',
              left: '0',
              top: '50%',
              transform: 'translateY(-50%)',
              width: activeSection === navItem.id || expandedNavItem === navItem.id 
                ? `${maxLineWidth}px` 
                : `${minLineWidth}px`,
              height: '0.75px',
              backgroundColor: activeSection === navItem.id || expandedNavItem === navItem.id
                ? `${highlightColor}E6` // 90% opacity 
                : `${highlightColor}80`, // 50% opacity
              transition: 'width 0.3s ease, background-color 0.3s ease',
            }}
          ></div>
          <button 
            className="nav-link"
            onClick={() => scrollToSection(navItem.id)}
            style={{
              position: 'relative',
              display: 'block',
              padding: '0.5rem 0 0.5rem 40px',
              fontSize: '0.85rem',
              letterSpacing: '0.1em',
              background: 'transparent',
              border: 'none',
              textAlign: 'left',
              outline: 'none',
              boxShadow: 'none',
              transition: 'color 0.5s ease, transform 0.5s ease',
              color: activeSection === navItem.id || expandedNavItem === navItem.id 
                ? highlightColor 
                : textColor,
              cursor: 'pointer',
              transform: activeSection === navItem.id || expandedNavItem === navItem.id 
                ? 'translateX(10px)' 
                : 'none',
              width: 'fit-content',
            }}
          >
            {navItem.label}
          </button>
        </motion.div>
      );
    })}
  </div>
);

// =====================================================
// RENDER STATS SECTION
// =====================================================
const renderStats = () => {
  if (!showStats || !person.stats || person.stats.length === 0) return null;

  return (
    <div 
      className="stats-container"
      style={{
        display: 'flex',
        justifyContent: 'space-between',
        flexWrap: 'wrap',
        marginTop: '3rem',
        width: '100%',
      }}
    >
      {person.stats.map((stat, statIdx) => (
        <div 
          key={statIdx} 
          className="stat"
          style={{
            flex: '1',
            textAlign: 'center',
            padding: '0 1rem',
            minWidth: '100px',
            transition: 'transform 0.3s ease',
          }}
          onMouseEnter={(e) => {
            e.currentTarget.style.transform = 'translateY(-5px)';
          }}
          onMouseLeave={(e) => {
            e.currentTarget.style.transform = 'none';
          }}
        >
          <div 
            className="stat-value"
            style={{
              fontSize: '2.5rem',
              fontWeight: '100',
              color: highlightColor,
              marginBottom: '0.5rem'
            }}
          >
            {stat.value}
          </div>
          <div 
            className="stat-label"
            style={{
              fontSize: '0.85rem',
              color: `${textColor}CC`, // 80% opacity
              textTransform: 'uppercase',
              letterSpacing: '0.1em'
            }}
          >
            {stat.label}
          </div>
        </div>
      ))}
    </div>
  );
};

// =====================================================
// RENDER CONTENT SECTIONS
// =====================================================
// Render section content
const renderSectionContent = (content) => {
  if (!content) return null;

  if (typeof content === 'string') {
    return (
      <p 
        className="section-content"
        style={{
          fontSize: '1rem',
          lineHeight: '1.8',
          marginBottom: '1.5rem',
          color: textColor,
          fontFamily: fontFamily,
          fontWeight: '300',
        }}
      >
        {content}
      </p>
    );
  }

  return content;
};

// Render about section
const renderAboutSection = (section) => {
  return (
    <>
      {person?.bio && person.bio.map((paragraph, idx) => (
        <p 
          key={idx} 
          className="section-content"
          style={{
            fontSize: '1rem',
            lineHeight: '1.8',
            marginBottom: '1.5rem',
            color: textColor,
            fontFamily: fontFamily,
            fontWeight: '300',
          }}
        >
          {paragraph}
        </p>
      ))}

      {renderStats()}
    </>
  );
};

// Render experience section
const renderExperienceSection = (section) => {
  if (person?.experience) {
    return person.experience.map((item, expIdx) => (
      <React.Fragment key={expIdx}>
        {renderSectionContent(item.content)}
      </React.Fragment>
    ));
  }

  return renderSectionContent(
    "Throughout my career, I've specialized in developing software systems that seamlessly " +
    "integrate front-end experiences with robust back-end architectures. My experience spans " +
    "various domains, from interactive media to data visualization systems."
  );
};

// Render projects section
const renderProjectsSection = (section) => {
  if (person?.projects) {
    return person.projects.map((item, projIdx) => (
      <React.Fragment key={projIdx}>
        {renderSectionContent(item.content)}
      </React.Fragment>
    ));
  }

  return null;
};

// Render sections based on section ID
const renderSection = (section) => {
  if (section.content) {
    return renderSectionContent(section.content);
  }

  switch (section.id) {
    case 'about':
      return renderAboutSection(section);
    case 'experience':
      return renderExperienceSection(section);
    case 'projects':
      return renderProjectsSection(section);
    default:
      return null;
  }
};

// =====================================================
// MAIN COMPONENT RENDER
// =====================================================
return (
  <motion.div 
    ref={containerRef}
    className="profile-container"
    initial={{ opacity: 0, y: animationConfig?.initialY ?? 30 }}
    animate={controls}
    style={{
      display: 'flex',
      flexDirection: 'row',
      width: '100%',
      maxWidth: '1300px',
      margin: '0 auto',
      minHeight: '70vh',
      position: 'relative',
    }}
  >
    {/* Left sidebar wrapper */}
    <div 
      ref={sidebarWrapperRef}
      className="sidebar-wrapper"
      style={{
        width: `${sidebarWidth}%`,
        position: 'relative',
      }}
    >
      {/* Profile sidebar */}
      <div 
        ref={sidebarRef}
        className="profile-sidebar"
        style={{
          padding: '3rem 2rem 2rem 0',
          display: 'flex',
          flexDirection: 'column',
          width: '100%',
          position: 'relative',
          top: 0,
        }}
      >
        {renderProfileInfo()}
        {renderNavLinks()}
      </div>
    </div>

    {/* Right content section */}
    <div 
      ref={contentRef}
      className="content-section"
      style={{
        width: `${contentWidth}%`,
        padding: `3rem 0 2rem ${contentPadding}rem`,
        marginLeft: 'auto',
      }}
    >
      {/* Render sections based on navigationItems */}
      {navigationItems.map((section, index) => {
        if (index >= sectionRefs.length) return null;

        return (
          <div 
            key={section.id}
            ref={sectionRefs[index]}
            data-section={section.id}
            className="section"
            id={section.id}
            style={{
              marginBottom: '3rem',
              scrollMarginTop: '2rem',
            }}
          >
            {renderSection(section)}
          </div>
        );
      })}

      {/* Additional sections */}
      {additionalSections.map((section, index) => (
        <div 
          key={`additional-${index}`}
          className="section"
          style={{
            marginBottom: '3rem',
            scrollMarginTop: '2rem',
          }}
        >
          {section.title && (
            <h3 style={{
              fontSize: '1.4rem',
              color: highlightColor,
              marginBottom: '1rem',
              fontWeight: '300',
            }}>
              {section.title}
            </h3>
          )}
          {renderSectionContent(section.content)}
        </div>
      ))}
    </div>

    {/* Media query styles for mobile */}
    <style jsx>{`
      @media (max-width: 768px) {
        .profile-container {
          flex-direction: column;
        }

        .sidebar-wrapper {
          width: 100% !important;
        }

        .profile-sidebar {
          position: relative !important;
          padding: 2rem 1rem !important;
          align-items: center;
          text-align: center;
          top: 0 !important;
          bottom: auto !important;
        }

        .profile-name {
          font-size: 2.2rem !important;
        }

        .profile-tagline {
          max-width: 100% !important;
          text-align: center;
        }

        .content-section {
          width: 100% !important;
          padding: 2rem 1rem !important;
