// Comet Animation Component
const CometOrbit = ({
  containerRef,
  isActive = true,
  isHovering = false
}) => {
  const canvasRef = useRef(null);
  const animationRef = useRef(null);
  const rotationRef = useRef(0);
  const lastTimestampRef = useRef(0);

  useEffect(() => {
    if (!canvasRef.current || !containerRef.current) return;

    const updateCanvasSize = () => {
      const container = containerRef.current;
      if (!container) return;

      const rect = container.getBoundingClientRect();
      const canvas = canvasRef.current;

      // Set canvas size with padding for orbit beyond the container
      canvas.width = rect.width + 80;
      canvas.height = rect.height + 80;
    };

    // Initial size update
    updateCanvasSize();

    // Setup resize observer
    const resizeObserver = new ResizeObserver(() => {
      updateCanvasSize();
    });

    if (containerRef.current) {
      resizeObserver.observe(containerRef.current);
    }

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    const drawComet = (timestamp) => {
      if (!canvas || !ctx) return;

      // Initialize timestamp on first run
      if (lastTimestampRef.current === 0) {
        lastTimestampRef.current = timestamp;
      }

      // Calculate time delta
      const deltaTime = Math.min(timestamp - lastTimestampRef.current, 100);
      lastTimestampRef.current = timestamp;

      // Clear canvas
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Calculate rectangle path (border of the card)
      const padding = 15; // Padding from the actual border
      const rectX = padding;
      const rectY = padding;
      const rectWidth = canvas.width - (padding * 2);
      const rectHeight = canvas.height - (padding * 2);

      // Update position progress
      const baseSpeed = 0.0004; // Slower to trace the border more visibly
      const speedFactor = isHovering ? 2.5 : 1;
      const progressDelta = baseSpeed * speedFactor * deltaTime;
      rotationRef.current = (rotationRef.current + progressDelta) % 1; // Use 0-1 range for progress

      // Determine which side of the rectangle the comet is on based on progress
      let headX, headY;
      const progress = rotationRef.current;

      if (progress < 0.25) {
        // Top edge: left to right
        headX = rectX + (progress * 4 * rectWidth);
        headY = rectY;
      } else if (progress < 0.5) {
        // Right edge: top to bottom
        headX = rectX + rectWidth;
        headY = rectY + ((progress - 0.25) * 4 * rectHeight);
      } else if (progress < 0.75) {
        // Bottom edge: right to left
        headX = rectX + rectWidth - ((progress - 0.5) * 4 * rectWidth);
        headY = rectY + rectHeight;
      } else {
        // Left edge: bottom to top
        headX = rectX;
        headY = rectY + rectHeight - ((progress - 0.75) * 4 * rectHeight);
      }

      // Trail segments
      const trailSegments = 20;
      const trailLength = 0.15; // 15% of the total border length
      const trailColor = 'rgba(191, 173, 127, 0.8)';
      const glowColor = 'rgba(255, 253, 227, 0.9)';
      const coreColor = 'rgba(255, 255, 255, 1)';
      const cometSize = isHovering ? 5 : 3.5;

      // Draw trail segments with decreasing opacity
      for (let i = 0; i < trailSegments; i++) {
        // Calculate progress for this segment
        const segmentProgress = (rotationRef.current - (i * (trailLength / trailSegments)));
        // Ensure progress is between 0 and 1 with looping
        const normalizedProgress = (segmentProgress < 0 ? segmentProgress + 1 : segmentProgress) % 1;

        // Calculate position for this segment based on the border path
        let segmentX, segmentY;
        if (normalizedProgress < 0.25) {
          // Top edge: left to right
          segmentX = rectX + (normalizedProgress * 4 * rectWidth);
          segmentY = rectY;
        } else if (normalizedProgress < 0.5) {
          // Right edge: top to bottom
          segmentX = rectX + rectWidth;
          segmentY = rectY + ((normalizedProgress - 0.25) * 4 * rectHeight);
        } else if (normalizedProgress < 0.75) {
          // Bottom edge: right to left
          segmentX = rectX + rectWidth - ((normalizedProgress - 0.5) * 4 * rectWidth);
          segmentY = rectY + rectHeight;
        } else {
          // Left edge: bottom to top
          segmentX = rectX;
          segmentY = rectY + rectHeight - ((normalizedProgress - 0.75) * 4 * rectHeight);
        }

        // Calculate progress for next segment
        const nextSegmentProgress = (rotationRef.current - ((i + 1) * (trailLength / trailSegments)));
        const nextNormalizedProgress = (nextSegmentProgress < 0 ? nextSegmentProgress + 1 : nextSegmentProgress) % 1;

        // Calculate next segment position
        let nextSegmentX, nextSegmentY;
        if (nextNormalizedProgress < 0.25) {
          nextSegmentX = rectX + (nextNormalizedProgress * 4 * rectWidth);
          nextSegmentY = rectY;
        } else if (nextNormalizedProgress < 0.5) {
          nextSegmentX = rectX + rectWidth;
          nextSegmentY = rectY + ((nextNormalizedProgress - 0.25) * 4 * rectHeight);
        } else if (nextNormalizedProgress < 0.75) {
          nextSegmentX = rectX + rectWidth - ((nextNormalizedProgress - 0.5) * 4 * rectWidth);
          nextSegmentY = rectY + rectHeight;
        } else {
          nextSegmentX = rectX;
          nextSegmentY = rectY + rectHeight - ((nextNormalizedProgress - 0.75) * 4 * rectHeight);
        }

        // Calculate opacity based on position in trail
        const baseOpacity = isHovering ? 0.9 : 0.7;
        const segmentOpacity = baseOpacity * (1 - (i / trailSegments));

        // Draw trail segment
        ctx.beginPath();
        ctx.moveTo(segmentX, segmentY);
        ctx.lineTo(nextSegmentX, nextSegmentY);

        // Vary line width from head to tail
        const baseWidth = isHovering ? 3 : 2;
        const segmentWidth = baseWidth * (1 - (i / trailSegments)) + 0.5;

        // Set shadow/glow for trail
        ctx.shadowColor = glowColor.replace(/[\d.]+\)$/, segmentOpacity + ')');
        ctx.shadowBlur = (isHovering ? 15 : 10) * (1 - (i / trailSegments)) + 5;

        // Set line style and draw
        ctx.strokeStyle = trailColor.replace(/[\d.]+\)$/, segmentOpacity + ')');
        ctx.lineWidth = segmentWidth;
        ctx.lineCap = 'round';
        ctx.stroke();
      }

      // Set shadow properties for comet head
      ctx.shadowColor = glowColor;
      ctx.shadowBlur = isHovering ? 25 : 15;

      // Larger outer glow
      ctx.beginPath();
      ctx.arc(headX, headY, cometSize, 0, Math.PI * 2);
      ctx.fillStyle = glowColor.replace(/[\d.]+\)$/, isHovering ? '0.9' : '0.7');
      ctx.fill();

      // Brighter inner core
      ctx.beginPath();
      ctx.arc(headX, headY, cometSize * 0.7, 0, Math.PI * 2);
      ctx.fillStyle = glowColor.replace(/[\d.]+\)$/, isHovering ? '1.0' : '0.9');
      ctx.fill();

      // Brightest center point
      ctx.beginPath();
      ctx.arc(headX, headY, cometSize * 0.3, 0, Math.PI * 2);
      ctx.fillStyle = coreColor;
      ctx.fill();

      // Optional: Small stardust particles near the comet
      if (isHovering) {
        // Create particles along the border, not just at the comet head
        const particleCount = 15;
        const particleRadius = 1.2;

        for (let i = 0; i < particleCount; i++) {
          // Random position along the border
          const randomProgress = Math.random();
          let particleX, particleY;

          if (randomProgress < 0.25) {
            particleX = rectX + (randomProgress * 4 * rectWidth);
            particleY = rectY;
          } else if (randomProgress < 0.5) {
            particleX = rectX + rectWidth;
            particleY = rectY + ((randomProgress - 0.25) * 4 * rectHeight);
          } else if (randomProgress < 0.75) {
            particleX = rectX + rectWidth - ((randomProgress - 0.5) * 4 * rectWidth);
            particleY = rectY + rectHeight;
          } else {
            particleX = rectX;
            particleY = rectY + rectHeight - ((randomProgress - 0.75) * 4 * rectHeight);
          }

          // Add small random offset from border (inward/outward)
          const inwardOffset = Math.random() > 0.5 ? -6 : 6;
          if (randomProgress < 0.25 || randomProgress >= 0.75) {
            // Top or left edge - adjust Y
            particleY += inwardOffset;
          } else {
            // Right or bottom edge - adjust X
            particleX += inwardOffset;
          }

          // Calculate pulse effect
          const pulse = Math.sin(timestamp * 0.003 + i * 0.5) * 0.5 + 0.5;

          // Draw particle with pulsing effect
          ctx.beginPath();
          ctx.arc(particleX, particleY, particleRadius * (0.6 + pulse * 0.4), 0, Math.PI * 2);
          ctx.fillStyle = glowColor.replace(/[\d.]+\)$/, (0.2 + pulse * 0.4) + ')');
          ctx.shadowColor = glowColor;
          ctx.shadowBlur = 5 * pulse;
          ctx.fill();
        }
      }

      // Draw subtle glow along the border
      ctx.strokeStyle = 'rgba(191, 173, 127, 0.1)';
      ctx.lineWidth = isHovering ? 2 : 1;
      ctx.strokeRect(rectX, rectY, rectWidth, rectHeight);

      animationRef.current = requestAnimationFrame(drawComet);
    };

    if (isActive) {
      animationRef.current = requestAnimationFrame(drawComet);
    }

    return () => {
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
      resizeObserver.disconnect();
    };
  }, [isActive, isHovering, containerRef]);

  return (
    <canvas ref={canvasRef} className="comet-canvas" />
  );
};

/**
 * CosmicProfileCard Component
 * 
 * An enhanced profile card with cosmic elements including an orbiting comet.
 * 
 * @param {Object} props - Component props
 * @param {Object} props.person - Person data object
 * @param {Object} [props.animationConfig] - Optional animation configuration
 */
const CosmicProfileCard = ({ 
  person, 
  animationConfig = {
    useInternalRef: true,
    threshold: 0.2,
    once: true,
    initialY: 50,
    duration: 0.8,
    ease: "easeOut"
  }
}) => {
  const classes = useStyles();
  const profileRef = useRef(null);
  const [isHovering, setIsHovering] = useState(false);

  // Image positioning state
  const [imagePosition, setImagePosition] = useState({
    x: 50, // 0-100, represents position in percentage
    y: 50,
    scale: 1, // 1-1.5
  });

  // Set up inView detection
  const internalInView = useInView(
    profileRef, 
    { 
      once: animationConfig.once, 
      threshold: animationConfig.threshold 
    }
  );

  const isInView = animationConfig.useInternalRef ? internalInView : animationConfig.isInView;

  // Reset image position
  const resetImagePosition = () => {
    setImagePosition({
      x: 50,
      y: 50,
      scale: 1
    });
  };

  return (
          <motion.div 
      ref={profileRef}
      className={`${classes.profile} ${classes.profileBorder} ${isInView ? classes.inView : ''} ${isHovering ? classes.hovering : ''}`}
      initial={{ opacity: 0, y: animationConfig.initialY }}
      animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: animationConfig.initialY }}
      transition={{ 
        duration: animationConfig.duration, 
        ease: animationConfig.ease 
      }}
      onMouseEnter={() => setIsHovering(true)}
      onMouseLeave={() => setIsHovering(false)}
    >
      {/* Comet animation */}
      <div className={classes.cometCanvas}>
        <CometOrbit 
          containerRef={profileRef}
          isActive={isInView}
          isHovering={isHovering}
        />
      </div>

      <div className={classes.profileImage}>
        <div className={classes.imageWrapper}>
          <img 
            src={person.image} 
            alt={person.name} 
            className={classes.image}
            style={{
              objectPosition: `${imagePosition.x}% ${imagePosition.y}%`,
              transform: `scale(${imagePosition.scale})`
            }}
          />
        </div>

        {/* Image position controls */}
        <div className={classes.imageControls}>
          <div className={classes.controlGroup}>
            <span className={classes.controlLabel}>Horizontal</span>
            <input 
              type="range" 
              min="0" 
              max="100" 
              value={imagePosition.x}
              onChange={(e) => setImagePosition({...imagePosition, x: Number(e.target.value)})}
              className={classes.controlSlider}
            />
          </div>

          <div className={classes.controlGroup}>
            <span className={classes.controlLabel}>Vertical</span>
            <input 
              type="range" 
              min="0" 
              max="100" 
              value={imagePosition.y}
              onChange={(e) => setImagePosition({...imagePosition, y: Number(e.target.value)})}
              className={classes.controlSlider}
            />
          </div>

          <div className={classes.controlGroup}>
            <span className={classes.controlLabel}>Zoom</span>
            <input 
              type="range" 
              min="100" 
              max="150" 
              value={imagePosition.scale * 100}
              onChange={(e) => setImagePosition({...imagePosition, scale: Number(e.target.value) / 100})}
              className={classes.controlSlider}
            />
          </div>

          <button 
            onClick={resetImagePosition} 
            className={classes.controlButton}
          >
            Reset
          </button>
        </div>
      </div>

      <div className={classes.profileDetails}>
        <h2 className={classes.profileName}>{person.name}</h2>
        <p className={classes.profileRole}>{person.role}</p>

        {person.bio.map((paragraph, index) => (
          <p key={index} className={classes.profileBio}>{paragraph}</p>
        ))}

        {person.stats && person.stats.length > 0 && (
          <div className={classes.profileStats}>
            {person.stats.map((stat, index) => (
              <div key={index} className={classes.stat}>
                <div className={classes.statValue}>{stat.value}</div>
                <div className={classes.statLabel}>{stat.label}</div>
              </div>
            ))}
          </div>
        )}
      </div>
    </motion.div>
  );
};

