// src/styles/theme.js
const theme = {
  fonts: {
    heading: "'Avenir', 'Avenir Next', sans-serif",
    body: "'Adobe Caslon Pro', 'Caslon', serif"
  },
  colors: {
    background: "#000000",
    text: "#FFFFFF",
    primary: "#a08e61", // Gold
    primaryLight: "#c6b78a", // Light gold
    primaryDark: "#785d3f", // Dark gold
    accent: "#630000", // Deep red accent
    accentLight: "#8a3232", // Lighter red
    surfaceLight: "#191919", // Light surface color
    surfaceDark: "#0d0d0d" // Dark surface color
  },
  breakpoints: {
    small: "576px",
    medium: "768px",
    large: "992px",
    xlarge: "1200px"
  },
  spacing: {
    xs: "0.25rem",
    sm: "0.5rem",
    md: "1rem",
    lg: "2rem",
    xl: "4rem"
  },
  transitions: {
    default: "0.3s ease",
    slow: "0.6s ease"
  },
  shadows: {
    subtle: "0 2px 10px rgba(0, 0, 0, 0.15)",
    medium: "0 4px 20px rgba(0, 0, 0, 0.2)",
    strong: "0 8px 30px rgba(0, 0, 0, 0.3)"
  },
  borderRadius: {
    small: "2px",
    medium: "4px",
    large: "8px"
  }
};

export default theme;
