import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { createUseStyles } from 'react-jss';

// Styles for the showcase navigation bar
const useStyles = createUseStyles({
  // Main container
  navContainer: {
    display: 'flex',
    flexDirection: 'column',
    width: '100%',
    backgroundColor: 'rgba(15, 15, 20, 0.85)',
    borderBottom: '1px solid rgba(191, 173, 127, 0.4)',
    boxShadow: '0 4px 15px rgba(0, 0, 0, 0.3)',
    zIndex: 100,
  },

  // Upper navigation section
  upperNav: {
    display: 'flex',
    padding: '0.75rem 1.5rem',
    justifyContent: 'space-between',
    alignItems: 'center',
    borderBottom: '1px solid rgba(191, 173, 127, 0.2)',
  },

  // Logo and title section
  titleSection: {
    display: 'flex',
    alignItems: 'center',
    gap: '0.75rem',
  },
  logoIcon: {
    width: '24px',
    height: '24px',
    borderRadius: '50%',
    background: 'linear-gradient(135deg, #bfad7f 0%, #7D6B9E 100%)',
  },
  title: {
    color: '#bfad7f',
    fontSize: '1.5rem',
    margin: 0,
    fontWeight: 300,
    letterSpacing: '0.05em',
    textShadow: '0 2px 5px rgba(0, 0, 0, 0.5)',
  },

  // Control buttons
  controlsSection: {
    display: 'flex',
    gap: '1rem',
    alignItems: 'center',
  },

  // Body shape selector
  bodyShapeSelector: {
    position: 'relative',
  },
  selectorButton: {
    backgroundColor: 'rgba(30, 30, 40, 0.6)',
    border: '1px solid rgba(191, 173, 127, 0.3)',
    color: '#e0e0e0',
    padding: '0.5rem 1rem',
    borderRadius: '4px',
    fontFamily: 'inherit',
    fontSize: '0.9rem',
    cursor: 'pointer',
    display: 'flex',
    alignItems: 'center',
    gap: '0.5rem',
    transition: 'all 0.2s ease',
    '&:hover': {
      backgroundColor: 'rgba(191, 173, 127, 0.2)',
      borderColor: 'rgba(191, 173, 127, 0.6)',
    },
  },
  selectorMenu: {
    position: 'absolute',
    top: '100%',
    left: 0,
    backgroundColor: 'rgba(20, 20, 25, 0.95)',
    minWidth: '250px',
    maxHeight: '60vh',
    overflowY: 'auto',
    borderRadius: '4px',
    padding: '0.5rem',
    boxShadow: '0 5px 15px rgba(0, 0, 0, 0.5)',
    zIndex: 101,
    border: '1px solid rgba(191, 173, 127, 0.3)',
  },
  menuItem: {
    padding: '0.75rem',
    cursor: 'pointer',
    borderRadius: '3px',
    transition: 'all 0.15s ease',
    '&:hover': {
      backgroundColor: 'rgba(191, 173, 127, 0.2)',
    },
  },
  menuItemActive: {
    backgroundColor: 'rgba(191, 173, 127, 0.3)',
  },
  menuItemName: {
    fontSize: '0.95rem',
    color: '#bfad7f',
    fontWeight: 500,
    marginBottom: '0.25rem',
  },
  menuItemDesc: {
    fontSize: '0.8rem',
    color: 'rgba(224, 224, 224, 0.7)',
  },

  // Tabs section
  navTabs: {
    display: 'flex',
    gap: '1rem',
  },
  navTab: {
    padding: '0.5rem 1rem',
    backgroundColor: 'transparent',
    border: 'none',
    borderBottom: '2px solid transparent',
    color: 'rgba(224, 224, 224, 0.7)',
    cursor: 'pointer',
    fontSize: '0.9rem',
    transition: 'all 0.2s ease',
    '&:hover': {
      color: '#bfad7f',
      borderBottomColor: 'rgba(191, 173, 127, 0.3)',
    },
  },
  navTabActive: {
    color: '#bfad7f',
    borderBottomColor: '#bfad7f',
  },

  // Visualization modes section
  lowerNav: {
    display: 'flex',
    padding: '0.75rem 1.5rem',
    flexWrap: 'wrap',
    gap: '1.5rem',
  },
  sectionTitle: {
    color: 'rgba(191, 173, 127, 0.8)',
    fontSize: '1rem',
    margin: 0,
    marginBottom: '0.5rem',
  },
  visualizationButtons: {
    display: 'flex',
    gap: '0.5rem',
  },
  visualButton: {
    backgroundColor: 'transparent',
    border: '1px solid rgba(191, 173, 127, 0.4)',
    color: '#e0e0e0',
    padding: '0.5rem 1rem',
    borderRadius: '4px',
    fontFamily: 'inherit',
    fontSize: '0.9rem',
    cursor: 'pointer',
    transition: 'all 0.2s ease',
    '&:hover': {
      backgroundColor: 'rgba(191, 173, 127, 0.2)',
    },
  },
  visualButtonActive: {
    backgroundColor: 'rgba(191, 173, 127, 0.3)',
    borderColor: 'rgba(191, 173, 127, 0.7)',
  },

  // Probabilities section
  probabilitiesSection: {
    flex: 1,
  },
  emergenceButton: {
    backgroundColor: 'rgba(125, 107, 158, 0.3)',
    border: '1px solid rgba(125, 107, 158, 0.6)',
    color: '#e0e0e0',
    padding: '0.5rem 1rem',
    borderRadius: '4px',
    fontFamily: 'inherit',
    fontSize: '0.9rem',
    cursor: 'pointer',
    transition: 'all 0.2s ease',
    '&:hover': {
      backgroundColor: 'rgba(125, 107, 158, 0.4)',
    },
  },

  // Environmental controls section
  environmentSection: {
    marginTop: '1rem',
    width: '100%',
  },
  sliderGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
    gap: '1rem',
    marginTop: '0.75rem',
  },
  sliderContainer: {
    display: 'flex',
    flexDirection: 'column',
    gap: '0.25rem',
  },
  sliderLabel: {
    color: 'rgba(224, 224, 224, 0.9)',
    fontSize: '0.9rem',
    display: 'flex',
    justifyContent: 'space-between',
  },
  sliderValue: {
    fontWeight: 'bold',
    color: '#bfad7f',
  },
  slider: {
    width: '100%',
    appearance: 'none',
    height: '6px',
    borderRadius: '3px',
    background: 'linear-gradient(to right, #7D6B9E 0%, #bfad7f 100%)',
    '&::-webkit-slider-thumb': {
      appearance: 'none',
      width: '16px',
      height: '16px',
      borderRadius: '50%',
      background: '#bfad7f',
      border: '2px solid rgba(15, 15, 20, 0.85)',
      cursor: 'pointer',
      transition: 'all 0.1s ease',
      '&:hover': {
        transform: 'scale(1.1)',
      },
    },
    '&::-moz-range-thumb': {
      width: '16px',
      height: '16px',
      borderRadius: '50%',
      background: '#bfad7f',
      border: '2px solid rgba(15, 15, 20, 0.85)',
      cursor: 'pointer',
      transition: 'all 0.1s ease',
      '&:hover': {
        transform: 'scale(1.1)',
      },
    },
  },

  // Trait category dropdowns
  categoryDropdowns: {
    display: 'flex',
    flexWrap: 'wrap',
    gap: '0.75rem',
    margin: '0.75rem 0',
  },
  categoryDropdown: {
    position: 'relative',
  },
  dropdownButton: {
    padding: '0.4rem 0.75rem',
    backgroundColor: 'rgba(30, 30, 40, 0.6)',
    border: '1px solid rgba(191, 173, 127, 0.3)',
    borderRadius: '4px',
    color: '#e0e0e0',
    fontSize: '0.85rem',
    cursor: 'pointer',
    display: 'flex',
    alignItems: 'center',
    gap: '0.5rem',
    transition: 'all 0.2s ease',
    '&:hover': {
      backgroundColor: 'rgba(191, 173, 127, 0.2)',
    },
  },
  dropdownIcon: {
    fontSize: '0.7rem',
    marginLeft: '0.25rem',
  },

  // Media queries for responsiveness
  '@media (max-width: 768px)': {
    upperNav: {
      flexDirection: 'column',
      alignItems: 'flex-start',
      gap: '0.75rem',
    },
    controlsSection: {
      width: '100%',
      justifyContent: 'space-between',
    },
    navTabs: {
      marginTop: '0.5rem',
      width: '100%',
      justifyContent: 'space-between',
    },
    lowerNav: {
      flexDirection: 'column',
      padding: '0.75rem',
    },
    visualizationButtons: {
      flexWrap: 'wrap',
      justifyContent: 'center',
    },
  }
});

/**
 * EvoSimShowCaseBar - A specialized navigation component for the Evolution Simulator
 * 
 * @param {Object} props
 * @param {string} props.selectedBodyShape - Currently selected body shape
 * @param {Function} props.onBodyShapeChange - Handler for body shape changes
 * @param {string} props.activeTab - Currently active main tab
 * @param {Function} props.onTabChange - Handler for tab changes
 * @param {string} props.visualizationMode - Current visualization mode
 * @param {Function} props.onVisualizationModeChange - Handler for visualization mode changes
 * @param {Object} props.environment - Current environmental conditions
 * @param {Function} props.onEnvironmentChange - Handler for environment changes
 * @param {boolean} props.showEmergence - Whether to show emergence probabilities
 * @param {Function} props.onToggleEmergence - Handler for toggling emergence probabilities
 * @param {Object} props.selectedTraits - Currently selected traits for each category
 * @param {Function} props.onTraitChange - Handler for trait changes
 */
const EvoSimShowCaseBar = ({
  selectedBodyShape = 'quadrupedal',
  onBodyShapeChange,
  activeTab = 'traits',
  onTabChange,
  visualizationMode = 'direct',
  onVisualizationModeChange,
  environment = {},
  onEnvironmentChange,
  showEmergence = false,
  onToggleEmergence,
  selectedTraits = {},
  onTraitChange
}) => {
  const classes = useStyles();

  // Local state for dropdown menus
  const [showBodyShapeMenu, setShowBodyShapeMenu] = useState(false);
  const [showTraitMenus, setShowTraitMenus] = useState({});

  // Define body shapes
  const bodyShapes = [
    { id: 'bipedal', name: 'Bipedal', description: 'Upright forms with manipulative upper limbs' },
    { id: 'quadrupedal', name: 'Quadrupedal', description: 'Four-limbed forms optimized for stability and movement' },
    { id: 'serpentine', name: 'Serpentine', description: 'Elongated, limbless forms specialized for flexibility' },
    { id: 'amorphous', name: 'Amorphous', description: 'Malleable forms without fixed structure' },
    { id: 'insectoid', name: 'Multi-limbed', description: 'Multi-limbed, segmented forms' },
    { id: 'avian', name: 'Avian', description: 'Forms optimized for flight and aerial movement' },
    { id: 'aquatic', name: 'Aquatic', description: 'Forms specialized for water environments' }
  ];

  // Define trait categories
  const traitCategories = [
    { id: 'locomotion', name: 'Locomotion', color: '#7D6B9E' },
    { id: 'metabolism', name: 'Metabolism', color: '#5C9D8B' },
    { id: 'sensory', name: 'Sensory Acuity', color: '#C99846' },
    { id: 'etheric', name: 'Etheric Adaptation', color: '#B54B4B' },
    { id: 'thermal', name: 'Thermal Regulation', color: '#A67C52' }
  ];

  // Define environmental conditions
  const environmentalConditions = [
    { id: 'eldritch_influence', name: 'Eldritch Influence', default: 0.5 },
    { id: 'corruption_level', name: 'Corruption Level', default: 0.5 },
    { id: 'resource_scarcity', name: 'Resource Scarcity', default: 0.5 },
    { id: 'ambient_magic', name: 'Ambient Magic', default: 0.5 },
    { id: 'darkness', name: 'Darkness', default: 0.5 }
  ];

  // Handle body shape selection
  const handleSelectBodyShape = (shapeId) => {
    onBodyShapeChange(shapeId);
    setShowBodyShapeMenu(false);
  };

  // Handle trait category dropdown toggle
  const handleToggleTraitMenu = (categoryId) => {
    setShowTraitMenus(prev => ({
      ...prev,
      [categoryId]: !prev[categoryId]
    }));

    // Close other menus
    setShowBodyShapeMenu(false);
    Object.keys(showTraitMenus).forEach(key => {
      if (key !== categoryId) {
        setShowTraitMenus(prev => ({
          ...prev,
          [key]: false
        }));
      }
    });
  };

  // Handle environment slider change
  const handleEnvironmentChange = (conditionId, value) => {
    onEnvironmentChange({ 
      ...environment, 
      [conditionId]: parseFloat(value) 
    });
  };

  // Get selected body shape name
  const getSelectedBodyShapeName = () => {
    const shape = bodyShapes.find(shape => shape.id === selectedBodyShape);
    return shape ? shape.name : 'Select Body Shape';
  };

  return (
    <div className={classes.navContainer}>
      {/* Upper navigation bar with title and main controls */}
      <div className={classes.upperNav}>
        <div className={classes.titleSection}>
          <div className={classes.logoIcon}></div>
          <h1 className={classes.title}>Crescent Evolution System</h1>
        </div>

        <div className={classes.controlsSection}>
          {/* Body shape selector */}
          <div className={classes.bodyShapeSelector}>
            <button 
              className={classes.selectorButton}
              onClick={() => setShowBodyShapeMenu(!showBodyShapeMenu)}
            >
              <span>Body Shape:</span>
              <strong>{getSelectedBodyShapeName()}</strong>
              <span className={classes.dropdownIcon}>▼</span>
            </button>

            <AnimatePresence>
              {showBodyShapeMenu && (
                <motion.div
                  className={classes.selectorMenu}
                  initial={{ opacity: 0, y: -10 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -10 }}
                  transition={{ duration: 0.2 }}
                >
                  {bodyShapes.map(shape => (
                    <div 
                      key={shape.id}
                      className={`${classes.menuItem} ${selectedBodyShape === shape.id ? classes.menuItemActive : ''}`}
                      onClick={() => handleSelectBodyShape(shape.id)}
                    >
                      <div className={classes.menuItemName}>{shape.name}</div>
                      <div className={classes.menuItemDesc}>{shape.description}</div>
                    </div>
                  ))}
                </motion.div>
              )}
            </AnimatePresence>
          </div>

          {/* Main navigation tabs */}
          <div className={classes.navTabs}>
            <button 
              className={`${classes.navTab} ${activeTab === 'traits' ? classes.navTabActive : ''}`}
              onClick={() => onTabChange('traits')}
            >
              Traits
            </button>
            <button 
              className={`${classes.navTab} ${activeTab === 'environment' ? classes.navTabActive : ''}`}
              onClick={() => onTabChange('environment')}
            >
              Environment
            </button>
            <button 
              className={`${classes.navTab} ${activeTab === 'synthesis' ? classes.navTabActive : ''}`}
              onClick={() => onTabChange('synthesis')}
            >
              Synthesis
            </button>
          </div>
        </div>
      </div>

      {/* Lower navigation with visualization controls and environmental settings */}
      <div className={classes.lowerNav}>
        {/* Visualization mode selection */}
        <div>
          <h3 className={classes.sectionTitle}>Network Visualization</h3>
          <div className={classes.visualizationButtons}>
            <button 
              className={`${classes.visualButton} ${visualizationMode === 'direct' ? classes.visualButtonActive : ''}`}
              onClick={() => onVisualizationModeChange('direct')}
            >
              Direct Connections
            </button>
            <button 
              className={`${classes.visualButton} ${visualizationMode === 'tree' ? classes.visualButtonActive : ''}`}
              onClick={() => onVisualizationModeChange('tree')}
            >
              Evolutionary Tree
            </button>
            <button 
              className={`${classes.visualButton} ${visualizationMode === 'category' ? classes.visualButtonActive : ''}`}
              onClick={() => onVisualizationModeChange('category')}
            >
              Trait Category
            </button>
          </div>
        </div>

        {/* Trait probabilities section */}
        <div className={classes.probabilitiesSection}>
          <h3 className={classes.sectionTitle}>Trait Probabilities</h3>
          <button 
            className={classes.emergenceButton}
            onClick={onToggleEmergence}
          >
            {showEmergence ? 'Hide' : 'Show'} Emergence Probabilities
          </button>

          {/* Trait category dropdowns would go here if needed */}
          {activeTab === 'traits' && (
            <div className={classes.categoryDropdowns}>
              {traitCategories.map(category => (
                <div className={classes.categoryDropdown} key={category.id}>
                  <button 
                    className={classes.dropdownButton}
                    style={{ borderColor: `${category.color}50` }}
                    onClick={() => handleToggleTraitMenu(category.id)}
                  >
                    {category.name}
                    <span className={classes.dropdownIcon}>▼</span>
                  </button>

                  {/* Trait selection dropdown would appear here */}
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Environmental conditions sliders */}
        {activeTab === 'environment' && (
          <div className={classes.environmentSection}>
            <h3 className={classes.sectionTitle}>Environmental Conditions</h3>
            <div className={classes.sliderGrid}>
              {environmentalConditions.map(condition => (
                <div className={classes.sliderContainer} key={condition.id}>
                  <div className={classes.sliderLabel}>
                    <span>{condition.name}</span>
                    <span className={classes.sliderValue}>
                      {(environment[condition.id] || condition.default).toFixed(1)}
                    </span>
                  </div>
                  <input
                    type="range"
                    min="0"
                    max="1"
                    step="0.1"
                    value={environment[condition.id] || condition.default}
                    onChange={(e) => handleEnvironmentChange(condition.id, e.target.value)}
                    className={classes.slider}
                  />
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default EvoSimShowCaseBar;
